INSERT INTO `erp_jurnal` (`erp_jurnal_ID`, `STR_SQL`, `NO_URUT`, `TRANSAKSI`, `CREATED_BY`, `CREATED_DATE`, `LAST_UPDATE_DATE`, `LAST_UPDATE_BY`) VALUES
	(1, 'SELECT * FROM  \r\n( SELECT\r\n  0 AS URUT,\r\n  a2.INVOICE_ID,\r\n  a2.STATUS_ID,\r\n  a2.PERIOD_NAME, \r\n  a2.DOCUMENT_NO,  \r\n  a2.DOCUMENT_DATE,  \r\n  a2.WAREHOUSE_ID,  \r\n  c2.COA_ID, \r\n  \'PEMBELIAN\' AS DESCRIPTION, \r\n  sum( b2.SUBTOTAL)  as DTOTAL,  \r\n  0 AS CTOTAL   \r\nFROM  \r\n  invoice a2  \r\n  INNER JOIN invoice_detail b2 ON (a2.INVOICE_ID = b2.INVOICE_ID)  \r\n  INNER JOIN item i2 ON (b2.ITEM_ID = i2.ITEM_ID)  \r\n  INNER JOIN coa c2 ON (i2.COA_ID = c2.ACCOUNT_ID) AND (a2.WAREHOUSE_ID = c2.WAREHOUSE_ID) \r\nGROUP BY  \r\n a2.INVOICE_ID,\r\n  a2.STATUS_ID,\r\n  a2.PERIOD_NAME, \r\n  a2.DOCUMENT_NO,  \r\n  a2.DOCUMENT_DATE,  \r\n  a2.WAREHOUSE_ID,  \r\n  c2.COA_ID\r\nUNION ALL\r\nSELECT  \r\n  1 AS URUT,\r\n  a.INVOICE_ID,\r\n  a.STATUS_ID,  \r\n  a.PERIOD_NAME, \r\n  a.DOCUMENT_NO, \r\n  a.DOCUMENT_DATE, \r\n  a.WAREHOUSE_ID, \r\n  c.COA_ID,\r\n  a.NOTE AS DESCRIPTION,\r\n  a.TOTAL_DISCOUNT * -1 as DTOTAL, \r\n  0 AS CTOTAL  \r\nFROM \r\n  invoice a \r\n  INNER JOIN coa c ON (a.WAREHOUSE_ID = c.WAREHOUSE_ID) \r\n  INNER JOIN coa_setup cs ON (c.ACCOUNT_ID = cs.COA_ID) \r\nWHERE \r\n  a.TOTAL_DISCOUNT <> 0 AND  \r\n  cs.PROGRAM_ACCOUNT = \'BPERANTARA\'\r\nUNION ALL\r\nSELECT \r\n  2 AS URUT, \r\n  a1.INVOICE_ID,\r\n  a1.STATUS_ID,\r\n  a1.PERIOD_NAME, \r\n  a1.DOCUMENT_NO, \r\n  a1.DOCUMENT_DATE, \r\n  a1.WAREHOUSE_ID, \r\n  v1.COA_ID,\r\n  \'PEMBELIAN\' AS DESCRIPTION,\r\n  0  as DTOTAL, \r\n  a1.TOTAL_NET AS CTOTAL  \r\nFROM \r\n  invoice a1 \r\n  INNER JOIN PERSON v1 ON (a1.PERSON_ID = v1.PERSON_ID)\r\n) AS XX \r\nWHERE TRUE\r\n&KONDISI\r\nORDER BY INVOICE_ID, URUT\r\n', 1, 'PEMBELIAN', NULL, NULL, '2005-02-25 18:13:54', 4),
	(2, 'SELECT * FROM (\r\nSELECT  \r\n  0 AS URUT, \r\n  a.NOTA_PENJUALAN_ID, \r\n  a.STATUS_ID, \r\n  a.PERIOD_NAME, \r\n  a.DOCUMENT_NO, \r\n  a.DOCUMENT_DATE, \r\n  a.WAREHOUSE_ID, \r\n a.NOTE AS DESCRIPTION, \r\n  a.TOTAL_NET AS DTOTAL, \r\n  0 AS CTOTAL, \r\n  c.COA_ID \r\nFROM \r\n  nota_penjualan a\r\n  INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nUNION ALL  \r\n  SELECT  \r\n  1 AS URUT, \r\n  a1.NOTA_PENJUALAN_ID, \r\n  a1.STATUS_ID, \r\n  a1.PERIOD_NAME, \r\n  a1.DOCUMENT_NO, \r\n  a1.DOCUMENT_DATE, \r\n  a1.WAREHOUSE_ID, \r\n a1.NOTE AS DESCRIPTION,\r\n  a1.TOTAL_DISCOUNT as DTOTAL,  \r\n  0 AS CTOTAL  ,\r\n  c1.COA_ID  \r\nFROM \r\n  nota_penjualan a1 \r\n  INNER JOIN coa c1 ON (a1.WAREHOUSE_ID = c1.WAREHOUSE_ID)  \r\n  INNER JOIN coa_setup cs1 ON (c1.ACCOUNT_ID = cs1.COA_ID)  \r\nWHERE  \r\n  (a1.TOTAL_DISCOUNT <> 0) AND   \r\n  (cs1.PROGRAM_ACCOUNT = \'JUAL_DISC\')\r\n  UNION ALL  \r\n  SELECT \r\n  2 AS URUT, \r\n  a2.NOTA_PENJUALAN_ID, \r\n  a2.STATUS_ID, \r\n  a2.PERIOD_NAME, \r\n  a2.DOCUMENT_NO, \r\n  a2.DOCUMENT_DATE, \r\n  a2.WAREHOUSE_ID, \r\n  a2.NOTE AS DESCRIPTION, \r\n  0 AS DTOTAL, \r\n  a2.TOTAL_AMOUNT AS CTOTAL, \r\n  c2.COA_ID_JUAL \r\nFROM \r\n  nota_penjualan a2\r\n  INNER JOIN PERSON c2 ON (a2.PERSON_ID = c2.PERSON_ID)\r\n  ) AS XX\r\n  WHERE TRUE\r\n &KONDISI \r\n  ORDER BY NOTA_PENJUALAN_ID , URUT', 2, 'PENJUALAN', NULL, NULL, '2005-02-25 18:14:52', 4),
	(3, 'SELECT * FROM ( \r\nSeLECT   \r\n  0 AS URUT,  \r\n  \'HPP\' AS DESCRIPTION,  \r\n  a.ADJUST_QTY_ID,  \r\n  a.STATUS_ID,  \r\n  a.WAREHOUSE_ID,  \r\n  a.PERIOD_NAME,  \r\n  a.DOCUMENT_NO,  \r\n  a.DOCUMENT_DATE,  \r\n  c.COA_ID,  \r\n  ABS(SUM(b.SUBTOTAL)) as DTOTAL,  \r\n   0 AS CTOTAL   \r\nFROM  \r\n  adjust_qty a  \r\n  INNER JOIN adjust_qty_detail b ON (a.ADJUST_QTY_ID = b.ADJUST_QTY_ID)  \r\n  INNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID) \r\n  INNER JOIN coa c ON (i.HPP_ID = c.ACCOUNT_ID) AND (a.WAREHOUSE_ID = c.WAREHOUSE_ID) \r\nWHERE b.SUBTOTAL <> 0  and   (a.DOCUMENT_DATE >= \'2005/01/01\')\r\nGROUP BY \r\n  a.ADJUST_QTY_ID,  \r\n  a.STATUS_ID,  \r\n  a.WAREHOUSE_ID,  \r\n  a.PERIOD_NAME,  \r\n  a.DOCUMENT_NO,  \r\n  a.DOCUMENT_DATE,  \r\n  c.COA_ID  \r\nUNION ALL  \r\n  SELECT   \r\n  1 AS URUT,  \r\n  \'PERSEDIAN\' AS DESCRIPTION,  \r\n  a1.ADJUST_QTY_ID,  \r\n  a1.STATUS_ID,  \r\n  a1.WAREHOUSE_ID,  \r\n  a1.PERIOD_NAME,  \r\n  a1.DOCUMENT_NO,  \r\n  a1.DOCUMENT_DATE,  \r\n  c1.COA_ID,\r\n  0 AS CTOTAL, \r\n  ABS(SUM(b1.SUBTOTAL)) as CTOTAL   \r\nFROM  \r\n  adjust_qty a1  \r\n  INNER JOIN adjust_qty_detail b1 ON (a1.ADJUST_QTY_ID = b1.ADJUST_QTY_ID)  \r\n  INNER JOIN item i1 ON (b1.ITEM_ID = i1.ITEM_ID) \r\n  INNER JOIN coa c1 ON (i1.COA_ID  = c1.ACCOUNT_ID) AND (a1.WAREHOUSE_ID = c1.WAREHOUSE_ID) \r\nWHERE b1.SUBTOTAL <> 0 and   (a1.DOCUMENT_DATE >= \'2005/01/01\')\r\nGROUP BY \r\n  a1.ADJUST_QTY_ID,  \r\n  a1.STATUS_ID,  \r\n  a1.WAREHOUSE_ID,  \r\n  a1.PERIOD_NAME,  \r\n  a1.DOCUMENT_NO,  \r\n  a1.DOCUMENT_DATE,  \r\n  c1.COA_ID   \r\n ) AS X \r\n WHERE TRUE &KONDISI\r\n ORDER BY ADJUST_QTY_ID, URUT', 91, 'ADJUSTMENT / SALAH', NULL, NULL, '2005-02-19 02:04:05', 4),
	(4, 'SELECT * FROM  \r\n( \r\nSELECT \r\n  0 AS URUT,   \r\n  a.PAYMENT_ID,  \r\n  a.PERIOD_NAME, \r\n  a.STATUS_ID,  \r\n  a.WAREHOUSE_ID,  \r\n  a.DOCUMENT_NO,  \r\n  a.DOCUMENT_DATE,  \r\n  v.COA_ID,  \r\n  a.DOCUMENT_CLASS_CODE AS DESCRIPTION , \r\n  SUM(b.AMOUNT) AS DTOTAL , \r\n  0 AS CTOTAL \r\nFROM  \r\n  payment a  \r\n  INNER JOIN payment_detail b ON (a.PAYMENT_ID = b.PAYMENT_ID)  \r\n  INNER JOIN PERSON v ON (b.PERSON_ID = v.PERSON_ID)  \r\n  INNER JOIN bank ON (a.BANK_ID = bank.BANK_ID)  \r\nWHERE  \r\n  bank.COA_ID IS NOT NULL \r\nGROUP BY  \r\na.PAYMENT_ID,  \r\n  a.STATUS_ID,  \r\n  a.PERIOD_NAME, \r\n  a.WAREHOUSE_ID,  \r\n  a.DOCUMENT_NO,  \r\n  a.DOCUMENT_DATE,   \r\n  v.COA_ID  \r\nUNION ALL \r\n  SELECT   \r\n  1 AS URUT , \r\n  a1.PAYMENT_ID,  \r\n   a1.PERIOD_NAME, \r\n  a1.STATUS_ID,  \r\n  a1.WAREHOUSE_ID,  \r\n  a1.DOCUMENT_NO,  \r\n  a1.DOCUMENT_DATE,  \r\n  bank1.COA_ID,  \r\n a1.DOCUMENT_CLASS_CODE AS DESCRIPTION,  \r\n  0 AS DTOTAL, \r\n  SUM(a1.TOTAL_AMOUNT) AS CTOTAL  \r\nFROM  \r\n  payment a1  \r\n  INNER JOIN bank bank1 ON (a1.BANK_ID = bank1.BANK_ID)  \r\nWHERE  \r\n  (a1.DOCUMENT_TYPE_ID = 1) AND\r\n  bank1.COA_ID IS NOT NULL \r\nGROUP BY  \r\na1.PAYMENT_ID,  \r\n  a1.STATUS_ID,  \r\n  a1.PERIOD_NAME, \r\n  a1.WAREHOUSE_ID,  \r\n  a1.DOCUMENT_NO,  \r\n  a1.DOCUMENT_DATE,   \r\n  bank1.COA_ID   \r\n) AS XX \r\nWHERE TRUE \r\n&KONDISI \r\nORDER BY PAYMENT_ID, URUT \r\n', 5, 'PELUNASAN HUTANG', NULL, NULL, '2005-02-25 18:18:52', 4),
	(5, 'SELECT * FROM ( \r\nSELECT  \r\n0 AS URUT,     \r\n  a.RECEIPT_ID,  \r\n  a.STATUS_ID,  \r\n  a.WAREHOUSE_ID,  \r\n  a.DOCUMENT_NO,  \r\n  a.DOCUMENT_DATE,  \r\n  a.PERIOD_NAME,  \r\n  bank.COA_ID,  \r\n    a.DOCUMENT_CLASS_CODE as Description,    \r\n   a.TOTAL_AMOUNT AS DTOTAL , \r\n  0 AS CTOTAL \r\nFROM  \r\n  receipt a  \r\n  INNER JOIN bank ON (a.BANK_ID = bank.BANK_ID)  \r\nWHERE  \r\na.DOCUMENT_TYPE_ID = 1 AND   \r\n  bank.COA_ID IS NOT NULL \r\nUNION ALL \r\nSELECT   \r\n1 AS URUT,     \r\n  a1.RECEIPT_ID,  \r\n  a1.STATUS_ID,  \r\n  a1.WAREHOUSE_ID,  \r\n  a1.DOCUMENT_NO,  \r\n  a1.DOCUMENT_DATE,  \r\n  a1.PERIOD_NAME,  \r\n  c1.COA_ID,  \r\n    a1.DOCUMENT_CLASS_CODE AS Description,  \r\n  0 AS DTOTAL,  \r\n  b1.AMOUNT AS CTOTAL  \r\nFROM  \r\n  receipt a1  \r\n  INNER JOIN receipt_detail b1 ON (a1.RECEIPT_ID = b1.RECEIPT_ID)  \r\n  INNER JOIN PERSON c1 ON (b1.PERSON_ID = c1.PERSON_ID)  \r\n  INNER JOIN bank bank1 ON (a1.BANK_ID = bank1.BANK_ID)  \r\nWHERE  \r\n  bank1.COA_ID IS NOT NULL     \r\n) AS XX  \r\nWHERE TRUE &KONDISI \r\nORDER BY RECEIPT_ID, URUT\r\n', 7, 'PELUNASAN PIUTANG', NULL, NULL, '2005-02-25 18:21:58', 4),
	(8, 'SELECT * FROM ( \r\nSELECT  \r\n0 AS URUT,     \r\n  a.RECEIPT_ID,  \r\n  a.STATUS_ID,  \r\n  a.WAREHOUSE_ID,  \r\n  a.DOCUMENT_NO,  \r\n  a.DOCUMENT_DATE,  \r\n  a.PERIOD_NAME,  \r\n  bank.COA_ID,  \r\n b.NOTE as Description,    \r\n   a.TOTAL_AMOUNT AS DTOTAL , \r\n  0 AS CTOTAL \r\nFROM  \r\n  receipt a \r\n  INNER JOIN receipt_detail b ON (a.RECEIPT_ID = b.RECEIPT_ID)   \r\n  INNER JOIN bank ON (a.BANK_ID = bank.BANK_ID)\r\n  INNER JOIN bank bk ON (b.BANK_ID = bk.BANK_ID)    \r\nWHERE  \r\na.DOCUMENT_TYPE_ID = 3 AND\r\n   bank.COA_ID IS NOT NULL AND\r\n   bk.COA_ID IS NOT NULL  \r\nunioN ALL   \r\n  SELECT   \r\n  3 AS URUT,     \r\n  a3.RECEIPT_ID,  \r\n  a3.STATUS_ID,  \r\n  a3.WAREHOUSE_ID,  \r\n  a3.DOCUMENT_NO,  \r\n  a3.DOCUMENT_DATE,  \r\n  a3.PERIOD_NAME,  \r\n  bank_trf.COA_ID,  \r\n  b3.NOTE AS Description,  \r\n  0 AS DTOTAL,  \r\n  b3.AMOUNT AS CTOTAL \r\nFROM  \r\n  receipt a3 \r\n  INNER JOIN receipt_detail b3 ON (a3.RECEIPT_ID = b3.RECEIPT_ID)  \r\n  INNER JOIN bank bank_trf ON (b3.BANK_ID = bank_trf.BANK_ID)   \r\n  INNER JOIN bank bank3 ON (a3.BANK_ID = bank3.BANK_ID) \r\nWHERE \r\na3.DOCUMENT_TYPE_ID = 3 AND  \r\n  bank3.COA_ID IS NOT NULL  AND\r\n  bank_trf.COA_ID is not null  \r\n) AS XX  \r\nWHERE TRUE &KONDISI \r\nORDER BY RECEIPT_ID, URUT', 9, 'TRANSFER', NULL, NULL, '2005-02-25 18:26:40', 4),
	(9, 'SELECT * FROM (\r\n/*KAS /BANK TUJUAN */\r\nSELECT  \r\n 0 AS URUT,     \r\n  a.RECEIPT_ID,  \r\n  a.STATUS_ID,  \r\n  a.WAREHOUSE_ID,  \r\n  a.DOCUMENT_NO,  \r\n  a.DOCUMENT_DATE,  \r\n  a.PERIOD_NAME,  \r\n  k.COA_ID,  \r\n  b.NOTE as Description,    \r\n   b.AMOUNT AS DTOTAL , \r\n  0 AS CTOTAL\r\nFROM \r\n  receipt a \r\n INNER JOIN receipt_detail b ON (a.RECEIPT_ID = b.RECEIPT_ID) \r\n  INNER JOIN bank k ON (a.BANK_ID = k.BANK_ID) \r\nWHERE \r\n  a.DOCUMENT_TYPE_ID = 4\r\nUNION ALL  \r\n\r\n  SELECT  \r\n  1 AS URUT,     \r\n  a2.RECEIPT_ID,  \r\n  a2.STATUS_ID,  \r\n  a2.WAREHOUSE_ID,  \r\n  a2.DOCUMENT_NO,  \r\n  a2.DOCUMENT_DATE,  \r\n  a2.PERIOD_NAME,  \r\n  coa2.COA_ID,  \r\n  b2.NOTE as Description,    \r\n   0 AS DTOTAL  ,\r\n   b2.AMOUNT AS CTOTAL \r\nFROM \r\n  receipt a2 \r\n  INNER JOIN receipt_detail b2 ON (a2.RECEIPT_ID = b2.RECEIPT_ID) \r\n  INNER JOIN warehouse w ON (b2.WAREHOUSE_ID = w.WAREHOUSE_ID) \r\n  INNER JOIN coa coa2 ON (a2.WAREHOUSE_ID = coa2.WAREHOUSE_ID) AND (w.ACCOUNT_ID = coa2.ACCOUNT_ID) \r\nWHERE \r\n  a2.DOCUMENT_TYPE_ID = 4\r\nUNION ALL  \r\n /* ACCOUNT PERANTARA*/\r\n  SELECT  \r\n  2 AS URUT,     \r\n  a1.RECEIPT_ID,  \r\n  a1.STATUS_ID,  \r\n  a1.WAREHOUSE_ID,  \r\n  a1.DOCUMENT_NO,  \r\n  a1.DOCUMENT_DATE,  \r\n  a1.PERIOD_NAME,  \r\n  coa1.COA_ID,  \r\n  b1.NOTE as Description,    \r\n     b1.AMOUNT AS DTOTAL ,\r\n     0 AS CTOTAL  \r\nFROM \r\n  receipt a1  \r\n  INNER JOIN receipt_detail b1 ON (a1.RECEIPT_ID = b1.RECEIPT_ID) \r\n  INNER JOIN warehouse w1 ON (b1.WAREHOUSE_ID = w1.WAREHOUSE_ID) \r\nINNER JOIN warehouse w11  ON (a1.WAREHOUSE_ID = w11.WAREHOUSE_ID) \r\n  INNER JOIN coa coa1 ON (w1.WAREHOUSE_ID = coa1.WAREHOUSE_ID) AND (w11.ACCOUNT_ID = coa1.ACCOUNT_ID) \r\nWHERE \r\n  a1.DOCUMENT_TYPE_ID = 4\r\nUNION ALL  \r\n  SELECT  \r\n  3 AS URUT, \r\n  a3.RECEIPT_ID,      \r\n  a3.STATUS_ID, \r\n  a3.WAREHOUSE_ID, \r\n  a3.DOCUMENT_NO, \r\n  a3.DOCUMENT_DATE, \r\n  a3.PERIOD_NAME, \r\n  bank.COA_ID,  \r\nb3.NOTE as Description,    \r\n  0 AS DTOTAL ,\r\n  b3.AMOUNT  AS CTOTAL  \r\nFROM \r\n  receipt a3 \r\n  INNER JOIN receipt_detail b3 ON (a3.RECEIPT_ID = b3.RECEIPT_ID) \r\n  INNER JOIN bank ON (b3.BANK_ID = bank.BANK_ID) \r\nWHERE \r\n  a3.DOCUMENT_TYPE_ID = 4\r\n) AS XX WHERE TRUE\r\n&KONDISI\r\nORDER BY RECEIPT_ID, URUT', 10, 'TRANSFER ANTAR CABANG', NULL, NULL, '2005-02-25 18:29:39', 4),
	(10, 'SELECT * FROM   \r\n( \r\nSELECT \r\n  1 AS URUT, \r\n  a1.TAG_ID, \r\n  a1.STATUS_ID, \r\n  a1.PERIOD_NAME,  \r\n  a1.DOCUMENT_NO,   \r\n  a1.DOCUMENT_DATE,   \r\n  a1.TO_WH_ID AS WAREHOUSE_ID,   \r\n  c1.COA_ID,  \r\n  a1.NOTE AS DESCRIPTION,  \r\n  sum( b1.SUBTOTAL)  as DTOTAL,   \r\n  0 AS CTOTAL    \r\nFROM   \r\n  tag a1   \r\n  INNER JOIN tag_detail b1 ON (a1.TAG_ID = b1.TAG_ID)   \r\n  INNER JOIN item i1 ON (b1.ITEM_ID = i1.ITEM_ID)   \r\n  INNER JOIN coa c1 ON (i1.COA_ID = c1.ACCOUNT_ID) AND (a1.TO_WH_ID = c1.WAREHOUSE_ID)  \r\nGROUP BY   \r\n a1.TAG_ID, \r\n  a1.STATUS_ID, \r\n  a1.PERIOD_NAME,  \r\n  a1.DOCUMENT_NO,   \r\n  a1.DOCUMENT_DATE,   \r\n  a1.TO_WH_ID,   \r\n  c1.COA_ID \r\nUNION ALL  \r\nSELECT   \r\n  2 AS URUT,      \r\n  a2.TAG_ID,   \r\n  a2.STATUS_ID,   \r\n  a2.PERIOD_NAME,   \r\n  a2.DOCUMENT_NO,   \r\n  a2.DOCUMENT_DATE,   \r\n  a2.TO_WH_ID,   \r\n  coa2.COA_ID,   \r\n  a2.NOTE  as Description,     \r\n   0 AS DTOTAL  , \r\n   a2.TOTAL_AMOUNT  as CTOTAL  \r\nFROM  \r\n  TAG a2  \r\n  INNER JOIN warehouse w ON (a2.WAREHOUSE_ID = w.WAREHOUSE_ID)  \r\n  INNER JOIN coa coa2 ON (a2.TO_WH_ID = coa2.WAREHOUSE_ID) AND (w.ACCOUNT_ID = coa2.ACCOUNT_ID)  \r\nUNION ALL  \r\n  SELECT   \r\n  3 AS URUT,      \r\n  a3.TAG_ID,   \r\n  a3.STATUS_ID,   \r\n  a3.PERIOD_NAME,   \r\n  a3.DOCUMENT_NO,   \r\n  a3.DOCUMENT_DATE,   \r\n  a3.WAREHOUSE_ID,   \r\n  coa3.COA_ID,   \r\n  a3.NOTE  as Description,     \r\n   a3.TOTAL_AMOUNT  AS DTOTAL  , \r\n   0 as CTOTAL  \r\nFROM  \r\n  TAG a3  \r\n  INNER JOIN warehouse w ON (a3.TO_WH_ID = w.WAREHOUSE_ID)  \r\n  INNER JOIN coa coa3 ON (a3.WAREHOUSE_ID = coa3.WAREHOUSE_ID) AND (w.ACCOUNT_ID = coa3.ACCOUNT_ID)  \r\nUNION ALL\r\nSELECT \r\n  4 AS URUT, \r\n  a4.TAG_ID, \r\n  a4.STATUS_ID, \r\n  a4.PERIOD_NAME,  \r\n  a4.DOCUMENT_NO,   \r\n  a4.DOCUMENT_DATE,   \r\n  a4.WAREHOUSE_ID AS WAREHOUSE_ID,   \r\n  c4.COA_ID,  \r\n  a4.NOTE AS DESCRIPTION,  \r\n  0 as DTOTAL,   \r\n  sum( b4.SUBTOTAL)  AS CTOTAL    \r\nFROM   \r\n  tag a4   \r\n  INNER JOIN tag_detail b4 ON (a4.TAG_ID = b4.TAG_ID)   \r\n  INNER JOIN item i4 ON (b4.ITEM_ID = i4.ITEM_ID)   \r\n  INNER JOIN coa c4 ON (i4.COA_ID = c4.ACCOUNT_ID) AND (a4.WAREHOUSE_ID = c4.WAREHOUSE_ID)  \r\nGROUP BY   \r\n a4.TAG_ID, \r\n  a4.STATUS_ID, \r\n  a4.PERIOD_NAME,  \r\n  a4.DOCUMENT_NO,   \r\n  a4.DOCUMENT_DATE,   \r\n  a4.WAREHOUSE_ID,   \r\n  c4.COA_ID \r\n)AS XX WHERE TRUE \r\n&KONDISI \r\nORDER BY TAG_ID, URUT\r\n  \r\n  \r\n', 4, 'PAC', NULL, NULL, '2005-02-25 18:11:22', 4),
