 AS STATUS\r\nFROM \r\nCHEQUE R\r\nINNER JOIN CHEQUE Q ON (R.CHEQUE_OLD_ID = Q.CHEQUE_ID ) \r\n    inner join PAYMENT R1 ON (Q.PAYMENT_ID = R1.PAYMENT_ID)\r\nleft join coa b on (R.BANK_ID = b.COA_ID)\r\n\r\nLEFT JOIN ERP_LOOKUP_VALUE X ON (R.STATUS_ID = X.ERP_LOOKUP_VALUE_ID) \r\n        \r\nWHERE   (R.STATUS_FLAG = \'K\')\r\nAND IF(:PERSON_ID is null, R1.PERSON_ID is null, R1.PERSON_ID = :PERSON_ID )\r\nAND (R.CREATED_DATE BETWEEN :11 AND :21)\r\n\r\n) AS XX\r\n&kondisi\r\nORDER BY    Date_format(CREATED_DATE , \'%Y-%m-%d\') , URUT', 2644, 4014, 13, '', 2, '2007-09-11 15:27:32', '2007-09-11 18:48:53', 2, NULL, '', NULL, ':11|:21', 3, NULL),
	(' SELECT \r\nSUM(DEBET - KREDIT)  AS SALDO \r\nFROM\r\n(\r\n/* BG TOLAKAN */ \r\nSELECT   STATUS_FLAG, \r\n   C.TOTAL_AMOUNT AS DEBET,\r\n   0 AS KREDIT\r\n  FROM CHEQUE C\r\n  left join PAYMENT r on(c.PAYMENT_ID = r.PAYMENT_ID)\r\nWHERE \r\n  (C.STATUS_ID = @TOLAK )\r\nAND (C.STATUS_FLAG = \'K\')\r\nAND (C.TGL_TOLAK  < :11)\r\nAND IF(:PERSON_ID is null, ((c.payment_id is null and c.PAYMENT_ID is null) OR(R.PERSON_ID IS NULL)), R.PERSON_ID = :PERSON_ID )\r\n\r\nUNION ALL\r\n/* PENGANTIAN BG TOLAKAN DGN BKM */ \r\nSELECT  Q.STATUS_FLAG,  \r\n   0 AS DEBET,  \r\n   RD.AMOUNT KREDIT\r\nFROM \r\nPAYMENT R \r\nINNER JOIN PAYMENT_DETAIL   RD ON  (R.PAYMENT_ID = RD.PAYMENT_ID)\r\nINNER JOIN CHEQUE Q ON (RD.CHEQUE_ID = Q.CHEQUE_ID)       \r\nWHERE  (Q.STATUS_FLAG = \'K\')\r\nAND (R.DOCUMENT_DATE < :11 )\r\nAND IF(:PERSON_ID is null, R.PERSON_ID is null, R.PERSON_ID = :PERSON_ID )\r\n\r\nUNION ALL \r\n/*PENGGANTIAN BG TOLAKAN DGN BG JUGA */ \r\nSELECT  R.STATUS_FLAG,\r\n   0 AS DEBET , \r\n   R.TOTAL_AMOUNT\r\nFROM \r\nCHEQUE R\r\nINNER JOIN CHEQUE Q ON (R.CHEQUE_OLD_ID = Q.CHEQUE_ID )   \r\ninner join PAYMENT R1 ON (Q.PAYMENT_ID = R1.PAYMENT_ID)\r\nWHERE   (R.STATUS_FLAG = \'K\')\r\nAND (R.CREATED_DATE < :11 )\r\nAND IF(:PERSON_ID is null, R1.PERSON_ID is null, R1.PERSON_ID = :PERSON_ID )\r\n\r\n) \r\nAS XX\r\nWHERE TRUE\r\n&KONDISI\r\n', 2645, 4014, 14, NULL, 0, '2011-08-18 15:48:54', '2011-08-18 15:48:54', 0, NULL, NULL, NULL, ':11', 3, NULL),
	('select * from(\r\n	/*YANG ADA TRANSAKSI DI ANTARA TGL PARAMETER*/\r\n	SELECT *\r\n	FROM \r\n		(\r\n			SELECT \r\n			PERSON_NAME, PERSON_ID, PERSON_CODE,\r\n			SUM(DEBET)  AS NILAI\r\n			FROM\r\n			(\r\n			/* BG TOLAKAN */ \r\n			SELECT   \r\n				p.PERSON_NAME , P.Person_code, P.PERSON_ID,\r\n				C.TOTAL_AMOUNT AS DEBET\r\n			FROM CHEQUE C\r\n				inner join PAYMENT R ON (C.PAYMENT_ID = R.PAYMENT_ID)\r\n				left join PERSON p ON (R.PERSON_ID = p.PERSON_ID )\r\n			WHERE \r\n				(C.STATUS_ID = @TOLAK )\r\n				AND (C.STATUS_FLAG = \'K\')\r\n				AND (C.TGL_TOLAK BETWEEN :11 AND :21)\r\n				\r\n			UNION ALL\r\n			\r\n			/* PENGANTIAN BG TOLAKAN DGN BKM */ \r\n			SELECT \r\n				p.PERSON_NAME , P.Person_code, P.PERSON_ID,\r\n				RD.AMOUNT * -1\r\n			FROM \r\n				PAYMENT R \r\n				INNER JOIN PAYMENT_DETAIL   RD ON  (R.PAYMENT_ID = RD.PAYMENT_ID)\r\n				INNER JOIN CHEQUE Q ON (RD.CHEQUE_ID = Q.CHEQUE_ID) \r\n				left join PERSON p ON (R.PERSON_ID = p.PERSON_ID )     \r\n			WHERE  (Q.STATUS_FLAG = \'K\')\r\n				AND (R.DOCUMENT_DATE BETWEEN :11 AND :21)\r\n				\r\n			UNION ALL \r\n			\r\n			/*PENGGANTIAN BG TOLAKAN DGN BG JUGA */ \r\n			SELECT \r\n				p.PERSON_NAME , P.Person_code, P.PERSON_ID, R.TOTAL_AMOUNT *-1\r\n			FROM \r\n				CHEQUE R\r\n				INNER JOIN CHEQUE Q ON (R.CHEQUE_OLD_ID = Q.CHEQUE_ID ) \r\n				inner join PAYMENT R1 ON (Q.PAYMENT_ID = R1.PAYMENT_ID)\r\n				left join PERSON p ON (R1.PERSON_ID = p.PERSON_ID )\r\n			WHERE   (R.STATUS_FLAG = \'K\')\r\n				AND (R.CREATED_DATE BETWEEN :11 AND :21)\r\n			) AS XX\r\n		GROUP BY\r\n		PERSON_NAME , Person_code, PERSON_ID\r\n		) AS YY \r\n	/*WHERE \r\n	NILAI <> 0 */\r\n\r\n\r\n	union all\r\n\r\n	/*YANG PUNYA SALDO AWAL SAMPAI TANGGAL PARAMETER*/\r\n	SELECT\r\n		person_name, p.person_id, p.person_code,\r\n		sum(debet-kredit) as saldo_awal\r\n	FROM\r\n	(\r\n		/* BG TOLAKAN */ \r\n		SELECT   STATUS_FLAG, \r\n			C.TOTAL_AMOUNT AS DEBET,\r\n			0 AS KREDIT,\r\n			r.person_id\r\n		FROM CHEQUE C\r\n			left join PAYMENT r on(c.PAYMENT_ID = r.PAYMENT_ID)\r\n		WHERE \r\n			(C.STATUS_ID = @TOLAK )\r\n			AND (C.STATUS_FLAG = \'K\')\r\n			AND (C.TGL_TOLAK  < :11)\r\n\r\n		UNION ALL\r\n		/* PENGANTIAN BG TOLAKAN DGN BKM */ \r\n		SELECT  Q.STATUS_FLAG,  \r\n			0 AS DEBET,  \r\n			RD.AMOUNT KREDIT,\r\n			r.person_id\r\n		FROM \r\n			PAYMENT R \r\n			INNER JOIN PAYMENT_DETAIL   RD ON  (R.PAYMENT_ID = RD.PAYMENT_ID)\r\n			INNER JOIN CHEQUE Q ON (RD.CHEQUE_ID = Q.CHEQUE_ID)       \r\n		WHERE  (Q.STATUS_FLAG = \'K\')\r\n			AND (R.DOCUMENT_DATE < :11 )\r\n\r\n		UNION ALL \r\n		/*PENGGANTIAN BG TOLAKAN DGN BG JUGA */ \r\n		SELECT  R.STATUS_FLAG,\r\n			0 AS DEBET , \r\n			R.TOTAL_AMOUNT,\r\n			r1.person_id\r\n		FROM \r\n			CHEQUE R\r\n			INNER JOIN CHEQUE Q ON (R.CHEQUE_OLD_ID = Q.CHEQUE_ID )   \r\n			inner join PAYMENT R1 ON (Q.PAYMENT_ID = R1.PAYMENT_ID)\r\n		WHERE   (R.STATUS_FLAG = \'K\')\r\n			AND (R.CREATED_DATE < :11 )\r\n\r\n	) \r\n	AS XX\r\n	left join person p on(xx.person_id = p.person_id)\r\n	WHERE TRUE\r\n\r\n	group by person_id\r\n	having saldo_awal <> 0\r\n) as bb\r\n&KONDISI\r\ngroup by person_id\r\nORDER BY PERSON_NAME\r\n', 2646, 4014, 3, 'debet kredit TOLAKAN', 0, '2011-08-18 15:50:48', '2011-08-18 15:50:48', 0, NULL, NULL, NULL, ':11|:21', 77, NULL),
	('select * from(\r\nSELECT *\r\nFROM \r\n(\r\nSELECT \r\nPERSON_NAME, PERSON_ID, PERSON_CODE,\r\nSUM(DEBET)  AS NILAI\r\n FROM\r\n(\r\n/* BG TOLAKAN */ \r\nSELECT   \r\n   p.PERSON_NAME , P.Person_code, P.PERSON_ID,\r\n   C.TOTAL_AMOUNT AS DEBET\r\n  FROM CHEQUE C\r\n    inner join RECEIPT R ON (C.RECEIPT_ID = R.RECEIPT_ID)\r\n    left join PERSON p ON (R.PERSON_ID = p.PERSON_ID )\r\nWHERE \r\n  (C.STATUS_ID = @TOLAK )\r\nAND (C.STATUS_FLAG = \'M\')\r\nAND (C.TGL_TOLAK BETWEEN :11 AND :21)\r\nUNION ALL\r\n/* PENGANTIAN BG TOLAKAN DGN BKM */ \r\nSELECT \r\n   p.PERSON_NAME , P.Person_code, P.PERSON_ID,\r\n   RD.AMOUNT * -1\r\nFROM \r\nRECEIPT R \r\nINNER JOIN RECEIPT_DETAIL   RD ON  (R.RECEIPT_ID = RD.RECEIPT_ID)\r\nINNER JOIN CHEQUE Q ON (RD.CHEQUE_ID = Q.CHEQUE_ID) \r\nleft join PERSON p ON (R.PERSON_ID = p.PERSON_ID )     \r\nWHERE  (Q.STATUS_FLAG = \'M\')\r\nAND (R.DOCUMENT_DATE BETWEEN :11 AND :21)\r\nUNION ALL \r\n/*PENGGANTIAN BG TOLAKAN DGN BG JUGA */ \r\nSELECT \r\n   p.PERSON_NAME , P.Person_code, P.PERSON_ID, R.TOTAL_AMOUNT *-1\r\nFROM \r\nCHEQUE R\r\nINNER JOIN CHEQUE Q ON (R.CHEQUE_OLD_ID = Q.CHEQUE_ID ) \r\n    inner join RECEIPT R1 ON (Q.RECEIPT_ID = R1.RECEIPT_ID)\r\nleft join PERSON p ON (R1.PERSON_ID = p.PERSON_ID )\r\n        \r\nWHERE   (R.STATUS_FLAG = \'M\')\r\nAND (R.CREATED_DATE BETWEEN :11 AND :21)\r\n) AS XX\r\nGROUP BY\r\n   PERSON_NAME , Person_code, PERSON_ID\r\n) AS YY \r\n/*WHERE \r\nNILAI <> 0 */\r\n\r\n\r\nunion all\r\n\r\nSELECT\r\n	person_name, p.person_id, p.person_code,\r\n	sum(debet-kredit) as saldo_awal\r\nFROM\r\n(\r\n/* BG TOLAKAN */ \r\nSELECT   STATUS_FLAG, \r\n   C.TOTAL_AMOUNT AS DEBET,\r\n   0 AS KREDIT,\r\n   r.person_id\r\n  FROM CHEQUE C\r\n  left join receipt r on(c.receipt_id = r.receipt_id)\r\nWHERE \r\n  (C.STATUS_ID = @TOLAK )\r\nAND (C.STATUS_FLAG = \'M\')\r\nAND (C.TGL_TOLAK  < :11)\r\n\r\nUNION ALL\r\n/* PENGANTIAN BG TOLAKAN DGN BKM */ \r\nSELECT  Q.STATUS_FLAG,  \r\n   0 AS DEBET,  \r\n   RD.AMOUNT KREDIT,\r\n   r.person_id\r\nFROM \r\nRECEIPT R \r\nINNER JOIN RECEIPT_DETAIL   RD ON  (R.RECEIPT_ID = RD.RECEIPT_ID)\r\nINNER JOIN CHEQUE Q ON (RD.CHEQUE_ID = Q.CHEQUE_ID)       \r\nWHERE  (Q.STATUS_FLAG = \'M\')\r\nAND (R.DOCUMENT_DATE < :11 )\r\n\r\nUNION ALL \r\n/*PENGGANTIAN BG TOLAKAN DGN BG JUGA */ \r\nSELECT  R.STATUS_FLAG,\r\n   0 AS DEBET , \r\n   R.TOTAL_AMOUNT,\r\n   r1.person_id\r\nFROM \r\nCHEQUE R\r\nINNER JOIN CHEQUE Q ON (R.CHEQUE_OLD_ID = Q.CHEQUE_ID )   \r\ninner join RECEIPT R1 ON (Q.RECEIPT_ID = R1.RECEIPT_ID)\r\nWHERE   (R.STATUS_FLAG = \'M\')\r\nAND (R.CREATED_DATE < :11 )\r\n\r\n) \r\nAS XX\r\nleft join person p on(xx.person_id = p.person_id)\r\nWHERE TRUE\r\n\r\ngroup by person_id\r\nhaving saldo_awal <> 0\r\n) as bb\r\n&KONDISI\r\ngroup by person_id\r\nORDER BY PERSON_NAME\r\n', 2647, 4013, 3, 'debet kredit TOLAKAN', 2, '2007-09-11 15:27:32', '2007-09-21 11:29:41', 2, NULL, '', NULL, ':11|:21', 77, NULL),
	('SELECT * FROM\r\n(\r\n/* BG TOLAKAN */ \r\nSELECT   \r\n  1 AS URUT, \r\n   C.DOCUMENT_NO as CHECK_NO ,\r\n   \' \'   NO_BG_LAMA ,\r\n   C.CREATED_DATE, \r\n  C.JTEMPO,\r\n   C.TGL_TOLAK , \r\n   C.DOCUMENT_DATE,\r\n   C.TOTAL_AMOUNT AS DEBET,\r\n   0 AS KREDIT,\r\n   b.COA_NAME AS NAMA_BANK ,\r\n   bd.COA_NAME ,\r\n   R.DOCUMENT_NO,\r\n   DISPLAY_NAME  AS STATUS\r\n  FROM CHEQUE C\r\n    left join RECEIPT R ON (C.RECEIPT_ID = R.RECEIPT_ID)\r\n    left join coa b on (R.BANK_ID = b.COA_ID)\r\n    left join coa bd on (c.BANK_ID_DEST = bd.COA_ID)\r\nLEFT JOIN ERP_LOOKUP_VALUE X ON (C.STATUS_ID = X.ERP_LOOKUP_VALUE_ID)\r\nWHERE \r\n  (C.STATUS_ID = @TOLAK )\r\nAND IF(:PERSON_ID is null, ((c.payment_id is null and c.receipt_id is null) or(R.PERSON_ID IS NULL)), R.PERSON_ID = :PERSON_ID )\r\nAND (C.STATUS_FLAG = \'M\')\r\nAND (C.TGL_TOLAK BETWEEN :11 AND :21)\r\nUNION ALL\r\n/* PENGANTIAN BG TOLAKAN DGN BKM */ \r\nSELECT \r\n  2 AS URUT,\r\n   Q.DOCUMENT_NO ,\r\n   \' \'   NO_BG_LAMA ,\r\n       R.DOCUMENT_DATE,\r\n   R.JTEMPO,\r\n   \'\',\r\n   R.DOCUMENT_DATE,\r\n   0 AS DEBET,  \r\n   RD.AMOUNT KREDIT,\r\n   b.COA_NAME AS BANK ,\r\n   \'\'AS BANK_TUJUAN,\r\n   R.DOCUMENT_NO ,\r\n   DISPLAY_NAME  AS STATUS\r\nFROM \r\nRECEIPT R \r\nINNER JOIN RECEIPT_DETAIL   RD ON  (R.RECEIPT_ID = RD.RECEIPT_ID)\r\nINNER JOIN CHEQUE Q ON (RD.CHEQUE_ID = Q.CHEQUE_ID) \r\nleft join coa b on (R.BANK_ID = b.COA_ID)\r\nLEFT JOIN ERP_LOOKUP_VALUE X ON (R.STATUS_ID = X.ERP_LOOKUP_VALUE_ID)\r\n        \r\nWHERE  (Q.STATUS_FLAG = \'M\')\r\nAND IF(:PERSON_ID is null, R.PERSON_ID is null, R.PERSON_ID = :PERSON_ID )\r\nAND (R.DOCUMENT_DATE BETWEEN :11 AND :21)\r\nUNION ALL \r\n/*PENGGANTIAN BG TOLAKAN DGN BG JUGA */ \r\nSELECT \r\n  3 AS URUT,\r\n   R.DOCUMENT_NO ,\r\n   Q.DOCUMENT_NO NO_BG_LAMA ,\r\n  R.CREATED_DATE, \r\n  R.JTEMPO,\r\n   \'\', \r\n   R.DOCUMENT_DATE,\r\n   0 AS DEBET , \r\n   R.TOTAL_AMOUNT,\r\n   b.COA_NAME AS BANK ,\r\n   \'\'AS BANK_TUJUAN, \r\n \' \' ,\r\n   DISPLAY_NAME 