	('SELECT * FROM \r\n(\r\nSELECT c.PERSON_id,\r\nSUM(IF(DATEDIFF(:12, JTEMPO) < 0, (TOTAL )  ,0)) AS BLM,\r\nSUM(IF(DATEDIFF(:12, JTEMPO)  BETWEEN 0 and 30, (TOTAL ) ,0)) AS 1BLN,\r\nSUM(IF(DATEDIFF(:12 , JTEMPO) BETWEEN 31 and 60, (TOTAL ) ,0)) AS 2BLN,\r\nSUM(IF(DATEDIFF(:12, JTEMPO) BETWEEN 61 and 90, (TOTAL ) ,0)) AS 3BLN,\r\nSUM(IF(DATEDIFF(:12, JTEMPO) > 91, (TOTAL ) ,0)) AS 3BLNLEBIH,\r\nSUM(TOTAL) AS TOTAL, \r\nc.PERSON_NAME, \r\nc.PERSON_CODE\r\nFROM\r\nPERSON c \r\nINNER JOIN \r\n( \r\n/* PENJUALAN DAN PELUNASAN */\r\n(SELECT a.PERSON_ID, (TOTAL_NET - IFNULL(BKM_JUAL,0) + IFNULL(BKK_JUAL,0))  AS TOTAL  ,  JTEMPO\r\nFROM nota_penjualan  a \r\nINNER JOIN NOTA_PENJUALAN_AGING z on (a.NOTA_PENJUALAN_ID = z.NOTA_PENJUALAN_ID) AND (JUAL_FLAG = \'Y\')\r\nLEFT JOIN \r\n(  SELECT y.NOTA_PENJUALAN_AGING_ID ,\r\nSUM(AMOUNT +DISCOUNT) AS BKM_JUAL\r\nFROM \r\nreceipt x \r\nINNER JOIN RECEIPT_DETAIL y on  (x.receipt_id = y.receipt_id)\r\nWHERE \r\n( date_format(x.DOCUMENT_DATE, \'%Y-%m-%d\') <= :12 )\r\nGROUP BY y.NOTA_PENJUALAN_AGING_ID \r\n) AS REC ON (z.NOTA_PENJUALAN_AGING_ID = REC.NOTA_PENJUALAN_AGING_ID) \r\nLEFT JOIN \r\n(\r\nSELECT pd.NOTA_PENJUALAN_AGING_ID ,\r\nSUM(AMOUNT + DISCOUNT ) AS BKK_JUAL\r\nFROM \r\nPAYMENT P\r\nINNER JOIN PAYMENT_DETAIL PD on  (P.PAYMENT_id = Pd.PAYMENT_id)\r\nWHERE \r\n( date_format(P.DOCUMENT_DATE, \'%Y-%m-%d\') <= :12 )\r\nGROUP BY Pd.NOTA_PENJUALAN_AGING_ID \r\n) AS PAY ON (z.NOTA_PENJUALAN_AGING_ID = PAY.NOTA_PENJUALAN_AGING_ID) \r\nWHERE \r\n  (a.NET_BYR1 = 0) AND \r\n(date_format(a.DOCUMENT_DATE, \'%Y-%m-%d\')  <= :12 )\r\n)\r\nUNION  ALL \r\n(\r\n/* SALDO AWAL  DAN PELUNASAN */\r\nSELECT A.PERSON_ID, (G.DUE_AMOUNT  - IFNULL(BKM_JUAL,0) )  AS TOTAL  , A.DUE_DATE\r\nFROM \r\nPERSON_SALDO A\r\nINNER JOIN PERSON P ON (A.PERSON_ID = P.PERSON_ID)\r\nINNER JOIN NOTA_PENJUALAN_AGING G ON (A.PERSON_SALDO_ID = G.PERSON_SALDO_ID  )\r\nLEFT JOIN \r\n(  SELECT y.NOTA_PENJUALAN_AGING_ID ,\r\nSUM(AMOUNT + DISCOUNT) AS BKM_JUAL \r\nFROM \r\n