	('SELECT DISTINCT * FROM \r\n(\r\n/* SALDO AWAL  DAN PELUNASAN */\r\nSELECT a.DOCUMENT_NO,\r\nkk.first_name,\r\n\'SALDO AWAL \' DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.DUE_DATE AS JTEMPO,\r\nTIPE,\r\nk.PERSON_NAME,\r\nk.PERSON_ID,\r\na.TOTAL_AMOUNT AS AWAL,\r\n0 AS JUAL,\r\nIFNULL(sa.AMOUNT,0) AS BAYAR_BKM,\r\n0  AS BAYAR_BKK,\r\n0 RETUR_JUAL\r\nFROM \r\nPERSON_SALDO a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nLEFT JOIN KARYAWAN kk ON k.KARYAWAN_ID=kk.KARYAWAN_ID\r\nLEFT JOIN \r\n(SELECT s.PERSON_SALDO_ID ,\r\nSUM(y.AMOUNT + y.DISCOUNT ) AS AMOUNT \r\nFROM \r\nNOTA_PENJUALAN_AGING s\r\nINNER JOIN RECEIPT_DETAIL y on (s.Nota_Penjualan_aging_id = y.Nota_Penjualan_aging_id)\r\nINNER JOIN receipt x on (x.receipt_id = y.receipt_id) and (date_format(x.DOCUMENT_DATE, \'%Y-%m-%d\') <= :13 )\r\nGROUP BY s.PERSON_SALDO_ID \r\n) AS SA ON (a.PERSON_SALDO_ID  = sA.PERSON_SALDO_ID ) \r\nWHERE (FLAG_SUPP = 0 )\r\n&kondisi\r\n/* END SALDO AWAL */  \r\nUNION ALL\r\n/* DARI PENJUALAN */ \r\nSELECT\r\na.DOCUMENT_NO,\r\nkk.first_name,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.JTEMPO,\r\nTIPE, \r\nk.PERSON_NAME,\r\nk.PERSON_ID,\r\n0 AS AWAL,\r\na.TOTAL_NET  AS JUAL,\r\nIFNULL(rec.bayar ,0)  + IFNULL(rec_ret.BAYAR_RET,0) AS BAYAR_BKM,\r\nIFNULL(pay.bayar,0)  + IFNULL(pay_ret.BAYAR_RET,0)   AS BAYAR_BKK,   \r\nIFNULL( c.retur,0) RETUR_JUAL\r\nFROM\r\nnota_penjualan a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nLEFT JOIN KARYAWAN kk ON k.KARYAWAN_ID=kk.KARYAWAN_ID\r\nINNER JOIN NOTA_PENJUALAN_AGING g ON (a.NOTA_PENJUALAN_ID  = g.NOTA_PENJUALAN_ID)  AND (JUAL_FLAG = \'Y\')\r\nLEFT JOIN \r\n/* PELUNASAN NYA */ \r\n(SELECT y.NOTA_PENJUALAN_AGING_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR\r\nFROM\r\nRECEIPT z        \r\nINNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\nWHERE date_format(z.DOCUMENT_DATE, \'%Y-%m-%d\')  <= :13\r\nGROUP BY\r\ny.NOTA_PENJUALAN_AGING_ID\r\n) AS rec ON (g.NOTA_PENJUALAN_AGING_ID = rec.NOTA_PENJUALAN_AGING_ID)\r\nLEFT JOIN \r\n/* PELUNASAN BKK */ \r\n(SELECT pd.NOTA_PENJUALAN_AGING_ID,\r\nSUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR\r\nFROM\r\nPAYMENT p\r\nINNER JOIN  PAYMENT_DETAIL pd ON (p.PAYMENT_ID = pd.PAYMENT_ID)\r\nWHERE (date_format(p.DOCUMENT_DATE, \'%Y-%m-%d\')<= :13)\r\nand (pd.NOTA_PENJUALAN_AGING_ID IS NOT NULL)\r\nGROUP BY pd.NOTA_PENJUALAN_AGING_ID\r\n) AS pay ON (g.NOTA_PENJUALAN_AGING_ID = pay.NOTA_PENJUALAN_AGING_ID)\r\nLEFT JOIN \r\n/* RETUR REFER KE NO PENJUALAN */ \r\n(SELECT NOTA_PENJUALAN_ID AS RETUR_ID, a.NOTA_PENJUALAN_OLD_ID AS NOTA_PENJUALAN_ID,\r\nSUM(a.TOTAL_NET) * -1 as RETUR\r\nFROM\r\nnota_penjualan a\r\nWHERE\r\n(DOCUMENT_TYPE_ID  >= 5 )\r\nAND  (a.NOTA_PENJUALAN_OLD_ID IS NOT NULL )\r\nAND   (date_format(DOCUMENT_DATE, \'%Y-%m-%d\') <= :13 )\r\nGROUP BY a.NOTA_PENJUALAN_OLD_ID\r\n)  AS c on (a.nota_penjualan_id = c.nota_penjualan_id)\r\nLEFT JOIN \r\n/* PELUNASAN NYA RETUR */ \r\n(SELECT r.NOTA_PENJUALAN_OLD_ID AS NOTA_PENJUALAN_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR_RET\r\nFROM\r\nRECEIPT z        \r\nINNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\nINNER JOIN NOTA_PENJUALAN_AGING n ON (y.NOTA_PENJUALAN_AGING_ID = n.NOTA_PENJUALAN_AGING_ID  )\r\nINNER JOIN NOTA_PENJUALAN r ON (n.NOTA_PENJUALAN_ID = r.NOTA_PENJUALAN_ID)    \r\nWHERE (date_format(z.DOCUMENT_DATE, \'%Y-%m-%d\')  <= :13)  \r\nGROUP BY\r\nr.NOTA_PENJUALAN_OLD_ID\r\n) AS rec_ret ON (a.NOTA_PENJUALAN_ID = rec_ret.NOTA_PENJUALAN_ID)\r\nLEFT JOIN \r\n/* PELUNASAN RETUR DGN BKK */ \r\n(SELECT r.NOTA_PENJUALAN_OLD_ID AS NOTA_PENJUALAN_ID,\r\nSUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR_RET\r\nFROM\r\nPAYMENT p\r\nINNER JOIN  PAYMENT_DETAIL pd ON (p.PAYMENT_ID = pd.PAYMENT_ID)\r\nINNER JOIN NOTA_PENJUALAN_AGING n ON (pd.NOTA_PENJUALAN_AGING_ID = n.NOTA_PENJUALAN_AGING_ID  )\r\nINNER JOIN NOTA_PENJUALAN r ON (n.NOTA_PENJUALAN_ID = r.NOTA_PENJUALAN_ID)    \r\nWHERE (date_format(p.DOCUMENT_DATE, \'%Y-%m-%d\')<= :13)\r\nand (pd.NOTA_PENJUALAN_AGING_ID IS NOT NULL)\r\nGROUP BY n.NOTA_PENJUALAN_ID     \r\n) AS pay_ret ON (a.NOTA_PENJUALAN_ID = pay_ret.NOTA_PENJUALAN_ID)   \r\nWHERE \r\n \r\n(\r\n ((a.NET_BYR1 = 0) and   (a.DOCUMENT_TYPE_ID < 5)) OR \r\n(\r\n(a.DOCUMENT_TYPE_ID   >= 5) AND (a.NOTA_PENJUALAN_OLD_ID IS NULL)  \r\n)\r\n)\r\nAND (date_format(a.DOCUMENT_DATE, \'%Y-%m-%d\')<=  :13 )\r\n&kondisi\r\n/* end penjualan */\r\n) as yy\r\nWHERE \r\n(\r\n(YY.AWAL  + YY.JUAL - Bayar_BKM   + bayar_BKK - RETUR_JUAL) <> 0\r\n) \r\nORDER BY PERSON_NAME, DOCUMENT_DATE\r\n', 3108, 3304, 0, NULL, 4, '2017-06-12 14:22:00', '2017-06-12 14:22:55', 0, NULL, '', NULL, ':13', 77, 1),
	('Select j.document_date, j.document_no, a.JTEMPO,  a.document_date as tgl_terima, s.document_no as so_no, c.PERSON_name, sum(b.amount) TERIMA,\r\nc.PERSON_CODE, \r\nmax(j.total_net) total_amount ,\r\nmax(j.total_net) as   total_net \r\nfrom\r\nreceipt a \r\ninner join receipt_detail b on  (a.receipt_id = b.receipt_id)\r\ninner join nota_penjualan_aging g on (b.NOTA_PENJUALAN_AGING_ID =g.NOTA_PENJUALAN_AGING_ID)  \r\ninner join nota_penjualan j on  (g.NOTA_PENJUALAN_ID = j.NOTA_PENJUALAN_ID) \r\ninner join PERSON c on (a.PERSON_id = c.PERSON_id)\r\ninner join warehouse w on (j.warehouse_id = w.warehouse_id)\r\nleft join so s on (j.so_id = s.so_id)\r\nwhere \r\n(j.karyawan_id = :karyawan_id)\r\nand (DATEDIFF(a.document_date ,j.jtempo)<=15)\r\nand (j.invoice_type_id <>  @PREPAYMENT) \r\n&kondisi\r\ngroup by \r\nj.document_date, j.document_no, PERSON_name\r\nUNION ALL\r\nSelect a.document_date, a.document_no, a.JTEMPO,   a.document_date as tgl_terima, s.document_no as so_no, c.PERSON_name, sum(nd.subtotal) *-1 TERIMA,\r\nc.PERSON_CODE, \r\nsum(nd.subtotal)*-1  total_amount ,\r\nsum(nd.subtotal)*-1  as   total_net \r\nfrom\r\nnota_penjualan a\r\ninner join nota_penjualan_detail nd on (a.nota_penjualan_id=nd.nota_penjualan_id)\r\ninner join item i on (nd.Item_id = i.ITEM_ID)\r\ninner join PERSON c on (a.PERSON_id = c.PERSON_id)\r\ninner join warehouse w on (a.warehouse_id = w.warehouse_id)\r\nleft join so s on (a.so_id = s.so_id)\r\nwhere \r\n(a.karyawan_id = :karyawan_id)\r\nand (DATEDIFF(a.document_date ,a.jtempo)<=15)\r\nand (a.invoice_type_id <>  @PREPAYMENT) \r\nand (i.JENIS_ID = @UM)\r\n&kondisi\r\ngroup by \r\na.document_date, a.document_no, PERSON_name\r\n\r\n\r\n', 3109, 1050, 0, NULL, 4, '2008-12-16 18:00:03', '2017-05-31 10:10:33', 0, NULL, NULL, NULL, NULL, 9, 1),
	('Select b.karyawan_id, a.document_date, a.document_no, b.first_name, c.PERSON_name, a.total_amount, a.total_net\r\nfrom\r\nnota_penjualan a\r\ninner join nota_penjualan_detail nd on (a.nota_penjualan_id=nd.nota_penjualan_id)\r\ninner join warehouse w on (a.warehouse_id = w.warehouse_id)\r\ninner join item i on (nd.Item_id = i.ITEM_ID)\r\ninner join PERSON c on (a.PERSON_id = c.PERSON_id)\r\ninner join karyawan b on(a.karyawan_id=b.karyawan_id)\r\nwhere true\r\n&kondisi\r\ngroup by b.first_name\r\norder by b.first_name\r\n ', 3110, 1050, 9, NULL, 4, '2008-12-16 18:00:03', '2017-06-20 12:44:24', 0, NULL, NULL, NULL, NULL, 77, 1),
