	('SELECT \r\n  b.ENTERED_QTY,\r\n  b.BASE_QTY,\r\n  b.UNIT_PRICE,\r\n  b.DISCOUNT_PRICE,\r\n  b.DISCOUNT_PRICE1,\r\n  b.DISCOUNT_PERCEN,\r\n  b.SUBTOTAL,\r\n  b.NOTE,\r\n  b.ENTERED_UOM,\r\n  b.INVOICE_ID,\r\n  b.ITEM_DESCRIPTION,\r\n i.ITEM_CODE\r\nFROM\r\n  INVOICE_detail b\r\n  INNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE\r\n b.INVOICE_ID = :INVOICE_ID\r\n&KONDISI\r\nORDER BY b.INVOICE_DETAIL_ID\r\n', 3112, 20052017, 3, NULL, 4, '2017-06-20 12:44:41', '2017-05-19 08:49:35', 0, NULL, NULL, NULL, NULL, 0, 1),
