	('select\r\n  a.document_date,\r\n  a.document_no,\r\n  a.DOCUMENT_REFF_NO as po_reff,\r\n  b.entered_qty,\r\n  b.unit_price,\r\n  b.item_description,\r\n  b.ENTERED_uom,\r\n  b.subtotal,\r\n  ii.document_date as BTB,\r\n  ii.document_no as BTB_no,\r\n  ii.DOCUMENT_REFF_NO as btb_reff,\r\n  iid.entered_qty as BTB_qty,\r\n  (b.entered_qty - iid.entered_qty) as sisa\r\nfrom\r\n  po a\r\n  inner join invoice ii ON (a.po_id = ii.po_id)\r\n  inner join po_detail b ON (a.po_id = b.po_id)\r\n  inner join invoice_detail iid ON (ii.invoice_id = iid.invoice_id) and(b.po_detail_id = iid.po_detail_id)\r\n  inner join PERSON v ON (a.PERSON_id = v.PERSON_id)\r\n  inner join item i on (b.item_id = i.item_id)\r\nWHERE a.PERSON_id = :PERSON_id\r\nand ((a.STATUS_ID=@complete) or (a.STATUS_ID=@partial))\r\nand ii.DOCUMENT_CLASS_CODE=\'Barang Datang\'\r\nand (ii.STATUS_ID <> @DELETE)\r\n&kondisi\r\norder by \r\na.document_date,\r\nii.document_date', 3114, 19052017, 3, NULL, 262, '2010-09-01 15:10:19', '2017-05-19 08:39:51', 0, NULL, NULL, NULL, NULL, 0, NULL),
	('select v.PERSON_name,\r\nvs.address1,\r\nv.PERSON_id\r\nfrom PERSON v\r\ninner join po a on (v.PERSON_ID = a.PERSON_ID)\r\ninner join po_detail b on (a.po_id=b.po_id)\r\ninner join inventory_in ii ON (a.po_id = ii.po_id)\r\nleft join PERSON_site vs ON (v.PERSON_ID = vs.PERSON_ID)\r\nwhere true\r\nand ((a.STATUS_ID=@complete) or (a.STATUS_ID=@partial))\r\n&kondisi\r\ngroup by v.PERSON_id\r\norder by v.PERSON_name\r\n	', 3115, 2001, 0, NULL, 4, '2010-09-01 15:10:19', '2007-07-20 16:25:35', 4, NULL, NULL, NULL, NULL, 77, 1),
