INSERT INTO `erp_sql` (`ERP_SQL_ID`, `STR_SQL`, `TRANS_NO`, `TRANSACTION`, `CREATED_BY`, `CREATED_DATE`, `LAST_UPDATE_DATE`, `LAST_UPDATE_BY`, `ERP_TABLE_ID`, `NOTE`) VALUES
	(1, 'SELECT \r\n  X.URUT,\r\n  X.URUT1,\r\n  X.COA_NAME,\r\n  X.COA_ID,\r\n  X.INVENTORY_IN_ID,\r\n  X.NOTE,\r\n  SUM(X.DEBET) DEBET,\r\n  SUM(X.KREDIT) KREDIT,\r\nB.DOCUMENT_NO FROM(\r\n/* hutang suspend */\r\nselect\r\n  if(TOTAL_AMOUNT>=0, 1,0) AS URUT,\r\n  1 AS URUT1,\r\n  C.COA_NAME,\r\n  c.COA_ID,\r\n  a.INVENTORY_IN_ID,\r\n  a.NOTE,\r\n  if(TOTAL_AMOUNT<0, -1*TOTAL_AMOUNT,0) AS DEBET,\r\n  if(TOTAL_AMOUNT>=0, TOTAL_AMOUNT,0) AS KREDIT\r\nFROM\r\n  INVENTORY_IN a,\r\n  coa_setup cs\r\n  LEFT  join coa c on (cs.COA_id  = c.COA_id)\r\nWHERE true\r\n and cs.program_account = \'Hutang_s\'\r\nAND a.INVENTORY_IN_ID = :INVENTORY_IN_ID\r\nunion all\r\n/* persediaan */\r\nselect\r\n  IF(b.SUBTOTAL>=0,0 ,1)as URUT,\r\n  2 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_ID,\r\n  B.INVENTORY_IN_ID,\r\n  B.NOTE,\r\n  IF(b.SUBTOTAL>=0,b.SUBTOTAL ,0) AS DEBET,\r\n  IF(b.SUBTOTAL<0,b.SUBTOTAL ,0) AS KREDIT\r\nFROM\r\n  INVENTORY_IN_DETAIL B\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER JOIN coa c ON (I.COA_ID = c.COA_ID)\r\nwhere\r\ntrue\r\nAND B.SUBTOTAL <> 0\r\nAND B.INVENTORY_IN_ID = :INVENTORY_IN_ID\r\n) X\r\ninner join INVENTORY_IN b on (X.INVENTORY_IN_id = B.INVENTORY_IN_id)\r\n\r\nWHERE\r\nTRUE\r\n&KONDISI\r\nGROUP BY X.INVENTORY_IN_ID, X.COA_ID\r\n ORDER BY INVENTORY_IN_ID, URUT, URUT1', 1, 'BPB', NULL, NULL, '2006-02-24 20:56:05', 4, 10, NULL),
	(2, 'SELECT \r\n  X.URUT,\r\n  X.URUT1,\r\n  X.COA_NAME,\r\n  X.COA_ID,\r\n  X.ADJUST_QTY_ID,\r\n  X.NOTE,\r\n  SUM(X.DEBET) DEBET,\r\n  SUM(X.KREDIT) KREDIT,\r\nB.DOCUMENT_NO FROM\r\n(select\r\n  IF(TOTAL_AMOUNT>=0, 1,0) AS URUT,\r\n  1 AS URUT1,\r\n  COA_NAME,\r\n  a.COA_ID,\r\n  a.ADJUST_QTY_ID,\r\n  a.NOTE,\r\n  IF(TOTAL_AMOUNT<0, -1*TOTAL_AMOUNT,0) AS DEBET,\r\n  IF(TOTAL_AMOUNT>=0, TOTAL_AMOUNT,0) AS KREDIT\r\nFROM\r\n  adjust_qty a\r\n  INNER JOIN coa c ON (a.COA_ID = c.COA_ID)\r\nWHERE true\r\nAND a.ADJUST_QTY_ID = :ADJUST_QTY_ID \r\nunion all\r\nselect\r\n  IF(B.SUBTOTAL>= 0, 0, 1) as URUT,\r\n  2 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_ID,\r\n  a.ADJUST_QTY_ID,\r\n  B.NOTE,\r\n  IF(B.SUBTOTAL>= 0, B.SUBTOTAL, 0) AS DEBET,\r\n  IF(B.SUBTOTAL< 0, -1*B.SUBTOTAL, 0) AS KREDIT\r\nFROM\r\n  adjust_qty_detail b\r\n  INNER JOIN adjust_qty a ON (a.ADJUST_QTY_ID = b.ADJUST_QTY_ID)\r\n  INNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\n  INNER JOIN coa c ON (i.coa_ID = c.coa_ID)\r\nwhere\r\ntrue\r\nAND a.ADJUST_QTY_ID = :ADJUST_QTY_ID \r\n) X\r\nINNER JOIN ADJUST_QTY B ON (X.ADJUST_QTY_ID = B.ADJUST_QTY_ID)\r\nWHERE\r\nTRUE\r\n&KONDISI\r\nGROUP BY ADJUST_QTY_ID, COA_ID\r\n ORDER BY ADJUST_QTY_ID, URUT, URUT1\r\n', 2, 'ADJ', NULL, NULL, '2006-02-24 20:18:33', 4, 5, NULL),
	(3, 'SELECT * FROM (\r\nSeLECT\r\n  0 AS URUT,\r\n  \'HPP\' AS DESCRIPTION,\r\n  a.ADJUST_QTY_ID,\r\n  a.STATUS_ID,\r\n  a.WAREHOUSE_ID,\r\n  a.PERIOD_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  c.COA_ID,\r\n  ABS(SUM(b.SUBTOTAL)) as DTOTAL,\r\n   0 AS CTOTAL\r\nFROM\r\n  adjust_qty a\r\n  INNER JOIN adjust_qty_detail b ON (a.ADJUST_QTY_ID = b.ADJUST_QTY_ID)\r\n  INNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\n  INNER JOIN coa c ON (i.HPP_ID = c.ACCOUNT_ID) AND (a.WAREHOUSE_ID = c.WAREHOUSE_ID)\r\nWHERE b.SUBTOTAL <> 0  and   (a.DOCUMENT_DATE >= \'2005/01/01\')\r\nGROUP BY\r\n  a.ADJUST_QTY_ID,\r\n  a.STATUS_ID,\r\n  a.WAREHOUSE_ID,\r\n  a.PERIOD_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  c.COA_ID\r\nUNION ALL\r\n  SELECT\r\n  1 AS URUT,\r\n  \'PERSEDIAN\' AS DESCRIPTION,\r\n  a1.ADJUST_QTY_ID,\r\n  a1.STATUS_ID,\r\n  a1.WAREHOUSE_ID,\r\n  a1.PERIOD_NAME,\r\n  a1.DOCUMENT_NO,\r\n  a1.DOCUMENT_DATE,\r\n  c1.COA_ID,\r\n  0 AS CTOTAL,\r\n  ABS(SUM(b1.SUBTOTAL)) as CTOTAL\r\nFROM\r\n  adjust_qty a1\r\n  INNER JOIN adjust_qty_detail b1 ON (a1.ADJUST_QTY_ID = b1.ADJUST_QTY_ID)\r\n  INNER JOIN item i1 ON (b1.ITEM_ID = i1.ITEM_ID)\r\n  INNER JOIN coa c1 ON (i1.COA_ID  = c1.ACCOUNT_ID) AND (a1.WAREHOUSE_ID = c1.WAREHOUSE_ID)\r\nWHERE b1.SUBTOTAL <> 0 and   (a1.DOCUMENT_DATE >= \'2005/01/01\')\r\nGROUP BY\r\n  a1.ADJUST_QTY_ID,\r\n  a1.STATUS_ID,\r\n  a1.WAREHOUSE_ID,\r\n  a1.PERIOD_NAME,\r\n  a1.DOCUMENT_NO,\r\n  a1.DOCUMENT_DATE,\r\n  c1.COA_ID\r\n ) AS X\r\n WHERE TRUE &KONDISI\r\n ORDER BY ADJUST_QTY_ID, URUT', 91, 'ADJUSTMENT / SALAH', NULL, NULL, NULL, 4, NULL, NULL),
	(4, 'SELECT \r\n  X.URUT,\r\n  X.URUT1,\r\n  X.COA_NAME,\r\n  X.COA_ID,\r\n  X.INVOICE_ID,\r\n  X.NOTE,\r\n  SUM(X.DEBET) DEBET,\r\n  SUM(X.KREDIT) KREDIT,\r\ni.document_no FROM\r\n(\r\n/*biaya-biaya yang ada di detail*/\r\nSELECT\r\n  \r\n  IF(b2.SUBTOTAL>=0,0,1) AS URUT,\r\n  1 AS URUT1,\r\n  C2.COA_NAME,\r\n  c2.COA_ID,\r\n  a2.INVOICE_ID,\r\n  b2.NOTE,\r\n  IF(b2.SUBTOTAL>=0,(b2.SUBTOTAL),0)  as DEBET,\r\n  IF(b2.SUBTOTAL<0,-1*(b2.SUBTOTAL),0)  as KREDIT\r\nFROM\r\n  invoice a2\r\n  INNER JOIN invoice_detail b2 ON (a2.INVOICE_ID = b2.INVOICE_ID)\r\n  INNER JOIN coa c2 ON (C2.COA_ID = B2.COA_ID)\r\nWHERE\r\nTRUE\r\nAND a2.INVOICE_ID =  :INVOICE_ID\r\nUNION ALL\r\n/*DISCOUNT*/\r\nSELECT\r\n\r\n  IF(TOTAL_DISCOUNT>=0,0,1) AS URUT,\r\n  4 AS URUT1,\r\n  C.COA_NAME,\r\n  c.COA_ID,\r\n  a2.INVOICE_ID,\r\n  a2.NOTE,\r\n  IF(TOTAL_DISCOUNT>0, TOTAL_DISCOUNT, 0) as DEBET,\r\n  IF(TOTAL_DISCOUNT<0, -1*TOTAL_DISCOUNT, 0) as KREDIT\r\nFROM\r\n  invoice a2,\r\n  coa_setup cs\r\n  inner join coa c on (cs.COA_id  = c.COA_id)\r\nWHERE TRUE\r\nand cs.program_account = \'DISCOUNT_BELI\'\r\nAND TOTAL_DISCOUNT <> 0\r\n\r\nAND a2.INVOICE_ID = :INVOICE_ID\r\nunion all\r\n\r\n/*hutang dagang yang ada di header*/\r\nSELECT\r\n  \r\n  IF(TOTAL_AMOUNT>=0,1,0) AS URUT,\r\n  2 AS URUT1,\r\n  C2.COA_NAME,\r\n  c2.COA_ID,\r\n  a2.INVOICE_ID,\r\n  a2.NOTE,\r\n  IF(TOTAL_NET<0, -1*TOTAL_NET, 0) as DEBET,\r\n  IF(TOTAL_NET>=0, TOTAL_NET, 0) as KREDIT\r\nFROM\r\n  invoice a2\r\n  INNER JOIN PERSON B2 ON (a2.PERSON_ID = b2.PERSON_ID)\r\n  INNER JOIN coa c2 ON (C2.COA_ID = B2.COA_ID)\r\nWHERE\r\nTRUE\r\nAND a2.INVOICE_ID = :INVOICE_ID\r\n) X\r\ninner join invoice i on (x.invoice_id = i.invoice_id)\r\nWHERE\r\nTRUE\r\n&KONDISI\r\n\r\nGROUP BY X.INVOICE_ID, X.COA_ID\r\nORDER BY x.INVOICE_ID,URUT,URUT1\r\n\r\n', 5, 'INVOICE BELI', NULL, NULL, '2006-02-24 23:21:36', 4, 11, NULL),
	(5, 'SELECT \r\n  X.URUT,\r\n  X.URUT1,\r\n  X.COA_NAME,\r\n  X.COA_ID,\r\n  X.PAYMENT_ID,\r\n  X.NOTE,\r\n  SUM(X.DEBET) DEBET,\r\n  SUM(X.KREDIT) KREDIT,\r\nP.DOCUMENT_NO FROM\r\n(\r\n/*\r\n  BKK, BUKAN PELUNASAN\r\n*/\r\nSELECT DISTINCT\r\nIF(A.TOTAL_AMOUNT>0,1,0) AS URUT,\r\n1 AS URUT1,\r\nC.COA_NAME,\r\nC.COA_ID,\r\nA.PAYMENT_ID,\r\nA.NOTE,\r\nIF(A.TOTAL_AMOUNT<0,-1*A.TOTAL_AMOUNT,0)  AS DEBET,\r\nIF(A.TOTAL_AMOUNT>=0,A.TOTAL_AMOUNT,0)  KREDIT\r\nFROM\r\nPAYMENT A\r\nINNER JOIN PAYMENT_DETAIL A1 ON (A1.PAYMENT_ID = A.PAYMENT_ID)\r\nINNER JOIN BANK B ON (A.BANK_ID = B.BANK_ID)\r\nINNER JOIN COA C ON (B.COA_ID = C.COA_ID)\r\nWHERE TRUE\r\nAND A.PAYMENT_ID = :PAYMENT_ID\r\nAND A1.COA_ID IS NOT NULL\r\n\r\nUNION ALL\r\nSELECT\r\nIF(AMOUNT>=0,0,1) AS URUT,\r\n2 AS URUT1,\r\nC.COA_NAME,\r\nC.COA_ID,\r\nA.PAYMENT_ID,\r\nA.NOTE,\r\nIF(AMOUNT>=0,AMOUNT, 0) AS DEBET,\r\nIF(AMOUNT<0,-1*AMOUNT, 0) AS KREDIT\r\nFROM\r\nPAYMENT_DETAIL A\r\nINNER JOIN PAYMENT A1 ON (A.PAYMENT_ID = A1.PAYMENT_ID)\r\nINNER JOIN COA C ON (A.COA_ID = C.COA_ID)\r\nWHERE\r\nTRUE\r\nAND A.PAYMENT_ID = :PAYMENT_ID\r\nAND A.COA_ID IS NOT NULL\r\nUNION ALL\r\n\r\n/*\r\n  BKK, PELUNASAN\r\n*/\r\n\r\nSELECT DISTINCT\r\nIF(A.TOTAL_AMOUNT>0,1,0) AS URUT,\r\n1 AS URUT1,\r\nC.COA_NAME,\r\nC.COA_ID,\r\nA.PAYMENT_ID,\r\nA.NOTE,\r\nIF(A.TOTAL_AMOUNT<0,-1*A.TOTAL_AMOUNT,0)  AS DEBET,\r\nIF(A.TOTAL_AMOUNT>=0,A.TOTAL_AMOUNT,0)  KREDIT\r\nFROM\r\nPAYMENT A\r\nINNER JOIN PAYMENT_DETAIL A1 ON (A1.PAYMENT_ID = A.PAYMENT_ID)\r\nINNER JOIN BANK B ON (A.BANK_ID = B.BANK_ID)\r\nINNER JOIN COA C ON (B.COA_ID = C.COA_ID)\r\nWHERE  TRUE\r\nAND A.PAYMENT_ID = :PAYMENT_ID\r\nAND A1.COA_ID IS NULL\r\nUNION ALL\r\n\r\nSELECT\r\nIF(AMOUNT>=0,0,1) AS URUT,\r\n2 AS URUT1,\r\nCOA_NAME,\r\nV.COA_ID,\r\nA.PAYMENT_ID,\r\nA.NOTE,\r\nIF(AMOUNT>=0,AMOUNT, 0) AS DEBET,\r\nIF(AMOUNT<0,-1*AMOUNT, 0) AS KREDIT\r\nFROM\r\nPAYMENT_DETAIL A\r\nINNER JOIN PAYMENT B ON (A.PAYMENT_ID = B.PAYMENT_ID)\r\nINNER JOIN PERSON V ON (B.PERSON_ID = V.PERSON_ID)\r\nINNER JOIN COA C ON (V.COA_ID = C.COA_ID)\r\nWHERE TRUE\r\nAND A.PAYMENT_ID = :PAYMENT_ID\r\nAND A.COA_ID IS NULL\r\n\r\n\r\n)\r\nX\r\nINNER JOIN PAYMENT P ON (X.PAYMENT_ID = P.PAYMENT_ID)\r\nWHERE\r\nTRUE\r\n\r\n&KONDISI\r\nGROUP BY X.PAYMENT_ID, X.COA_ID\r\nORDER BY PAYMENT_ID, URUT,URUT1\r\n', 7, 'BKK', NULL, NULL, '2006-02-24 23:22:44', 4, 17, NULL),
	(8, 'SELECT\r\n  0 as URUT,\r\n  0 as TIPE,\r\n  A.PERIOD_NAME,\r\n  0 AS HPP_FLAG,  \r\n  \'SALDO\' AS DOCUMENT_CLASS_CODE,\r\n  \'\' AS DOCUMENT_NO,\r\n  \'2000/01/01\' as DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID ,\r\n  a.ITEM_ID,\r\n  \'\' as DTL_ID, \r\n  a.QTY_AWAL AS SALDO,\r\n  a.QTY_AWAL * a.HPP AS SALDO_RP,\r\n  0 AS MASUK,\r\n  0 AS MASUK_RP,\r\n  0 AS KELUAR,\r\n  0 AS KELUAR_RP,  \r\n  0 as BAHAN,  \r\n  0 AS BAHAN_RP,  \r\n  0 AS JADI ,  \r\n  0 AS JADI_RP,\r\n  0 AS ADJ ,  \r\n  0 AS ADJ_RP,\r\n  a.HPP as HARGA, \r\n  a.QTY_AWAL * a.HPP AS SUBTOTAL\r\nFROM\r\n    item_balance a    \r\n    INNER JOIN ITEM i ON (a.ITEM_ID = i.ITEM_ID)#,\r\n   #NOL\r\nWHERE (i.JENIS_ID = @GOODS)\r\n&X1/*ini tidak ada*/\r\n&Y1\r\n\r\nUNION ALL\r\n/* TRANSAKSI BELI */\r\n/*     INVENTORY_IN */\r\nSELECT\r\n  1 as URUT,\r\n  1 as TIPE,\r\n  A.PERIOD_NAME,\r\n  1  AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.INVENTORY_IN_DETAIL_ID as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n  b.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n  b.Subtotal -discount_price1   AS MASUK_RP,\r\n  0 AS KELUAR,\r\n  0 AS KELUAR_RP, \r\n  0 as BAHAN,  \r\n  0 AS BAHAN_RP,  \r\n  0  AS JADI ,  \r\n  0 AS JADI_RP,\r\n  0  AS ADJ ,  \r\n  0 AS ADJ_RP,\r\n\r\n  b.UNIT_PRICE - (b.discount_price1/b.ENTERED_QTY )    as HARGA,\r\n  b.Subtotal-b.discount_price1  AS SUBTOTAL\r\n\r\nFROM\r\n   INVENTORY_IN a\r\n  INNER JOIN INVENTORY_IN_DETAIL b ON (a.INVENTORY_IN_ID = b.INVENTORY_IN_ID )  \r\n    INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\n  \r\n  \r\nWHERE (i.JENIS_ID = @GOODS)\r\n&X2\r\n&Y1\r\nUNION ALL\r\n/*     INVOICE tanpa matching barang datang    */\r\nSELECT\r\n  1 as URUT,\r\n  3 as TIPE,\r\n  A.PERIOD_NAME,\r\n  1  AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.INVOICE_DETAIL_ID as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n  b.ENTERED_QTY * b.BASE_QTY  AS MASUK,\r\n  b.Subtotal -discount_price1   AS MASUK_RP,\r\n  0 AS KELUAR,\r\n  0 AS KELUAR_RP,  \r\n  0 as BAHAN,  \r\n  0 AS BAHAN_RP,  \r\n  0  AS JADI ,  \r\n  0 AS JADI_RP,\r\n0  AS ADJ ,  \r\n  0 AS ADJ_RP,\r\n\r\n\r\n  b.UNIT_PRICE - (b.discount_price1/b.ENTERED_QTY ) as HARGA,\r\n  b.Subtotal -discount_price1 AS SUBTOTAL\r\nFROM\r\n   INVOICE a\r\n   INNER JOIN INVOICE_DETAIL b ON (a.INVOICE_ID = b.INVOICE_ID )\r\n   INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE (i.JENIS_ID = @GOODS) AND  \r\n  (b.INVENTORY_IN_ID IS NULL)\r\n&X2\r\n&Y1\r\nUNION ALL\r\n/* TRANSAKSI JUAL */\r\n/* NOTA_PENJUALAN            */\r\nSELECT\r\n  4 as URUT,\r\n  4 as TIPE,\r\n  A.PERIOD_NAME,\r\n  0  AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.NOTA_PENJUALAN_DETAIL_ID as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n  0 AS MASUK,\r\n0  AS MASUK_RP,\r\n  b.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\n  b.ENTERED_QTY * b.HPP  AS KELUAR_RP,  \r\n  0 as BAHAN,  0 AS BAHAN_RP,  0  AS JADI ,  0 AS JADI_RP,\r\n0  AS ADJ ,  \r\n  0 AS ADJ_RP,\r\n b.HPP    as HARGA,\r\n  b.ENTERED_QTY *  b.HPP    AS SUBTOTAL\r\nFROM\r\n   NOTA_PENJUALAN a\r\n  INNER JOIN NOTA_PENJUALAN_DETAIL b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID )\r\n    INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE \r\n\r\n(b.inventory_out_detail_id is null) and\r\n(i.JENIS_ID = @GOODS) \r\n&X2\r\n&Y1\r\n\r\nUNION ALL\r\n/* S.JALAN */\r\n/* INVENTORY OUT            */\r\nSELECT\r\n  4 as URUT,\r\n  4 as TIPE,\r\n  A.PERIOD_NAME,\r\n  0  AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.INVENTORY_OUT_DETAIL_ID as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n  0 AS MASUK,\r\n0  AS MASUK_RP,\r\n  b.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\n  b.ENTERED_QTY * b.HPP  AS KELUAR_RP,  \r\n  0 as BAHAN,  0 AS BAHAN_RP,  0  AS JADI ,  0 AS JADI_RP,\r\n0  AS ADJ ,  \r\n  0 AS ADJ_RP,\r\n b.HPP    as HARGA,\r\n  b.ENTERED_QTY *  b.HPP    AS SUBTOTAL\r\nFROM\r\n   INVENTORY_OUT  a\r\n  INNER JOIN INVENTORY_OUT_DETAIL b ON (a.INVENTORY_OUT_ID = b.INVENTORY_OUT_ID)\r\n    INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE \r\n(i.JENIS_ID = @GOODS) \r\n&X2\r\n&Y1\r\n\r\nUNION ALL\r\n\r\n/* TRANSAKSI ADJUSTMENT QTY */\r\n/* ADJ_QTY           */\r\nSELECT\r\n  2 as URUT,\r\n  5 as TIPE,\r\n  A.PERIOD_NAME,\r\n  IF(a.COA_ID = @HPP, 0,1)  AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.adjust_qty_detail_ID  as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n0  AS MASUK,\r\n0 AS MASUK_RP,\r\n  0 AS KELUAR,\r\n  0 AS KELUAR_RP,  \r\n  0 as BAHAN,  0 AS BAHAN_RP,  0  AS JADI ,  0 AS JADI_RP,\r\n  b.ENTERED_QTY * b.BASE_QTY AS ADJ,\r\nb.Subtotal AS ADJ_RP,\r\n  b.UNIT_PRICE as HARGA,\r\n  b.Subtotal AS SUBTOTAL\r\nFROM\r\n  adjust_qty a\r\n  INNER JOIN adjust_qty_detail b ON (a.ADJUST_QTY_ID = b.ADJUST_QTY_ID)\r\n  \r\n  INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE \r\n(i.JENIS_ID = @GOODS) \r\n\r\n&X2\r\n&Y1\r\nUNION ALL\r\n\r\n/* TRANSAKSI ADJUSTMENT AMOUNT */\r\n/* ADJ_AMOUNT           */\r\nSELECT\r\n  2 as URUT,\r\n  6 as TIPE,\r\n  A.PERIOD_NAME,\r\n  IF(a.COA_ID = @HPP, 0,1)  AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  0 AS WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.adjust_AMOUNT_detail_ID  as DTL_ID, \r\n  0 AS SALDO,\r\n  0 AS SALDO_RP,\r\n  0 AS MASUK,\r\n  0 AS MASUK_RP,\r\n  0 AS KELUAR,\r\n0  AS KELUAR_RP,  \r\n  0 as BAHAN,  0 AS BAHAN_RP,  0  AS JADI ,  0 AS JADI_RP,\r\n0  AS ADJ ,  \r\n    b.ENTERED_AMOUNT AS ADJ_RP,\r\n\r\n  0 as HARGA,\r\n  b.ENTERED_AMOUNT AS ENTERED_AMOUNT\r\nFROM\r\n  adjust_AMOUNT a\r\n  INNER JOIN adjust_AMOUNT_detail b ON (a.ADJUST_AMOUNT_ID = b.ADJUST_AMOUNT_ID)\r\n  \r\n  INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE\r\n(i.JENIS_ID = @GOODS) \r\n&X2\r\n&Y1\r\nUNION ALL\r\n/* TRANSAKSI RAKIT */\r\n/* BUILD  BAHAN BAKU       */\r\nSELECT\r\n  5 as URUT,\r\n  7 as TIPE,\r\n  A.PERIOD_NAME,\r\n  0 AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.build_detail_ID as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n 0 AS MASUK,\r\n 0 AS MASUK_RP,\r\n  0 AS KELUAR,\r\n 0 AS KELUAR_RP,  \r\n  b.ENTERED_QTY * b.BASE_QTY AS BAHAN,  \r\n   b.SUBTOTAL  AS BAHAN_RP, \r\n  0  AS JADI ,  \r\n  0 AS JADI_RP,\r\n0  AS ADJ ,  \r\n  0 AS ADJ_RP,\r\n\r\n  b.UNIT_PRICE  AS HARGA,\r\n  b.SUBTOTAL  AS SUBTOTAL\r\nFROM\r\n  build a\r\n  INNER JOIN build_detail b ON (a.build_ID = b.build_ID)\r\n  \r\n    INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE  (i.JENIS_ID = @GOODS)\r\n&X2\r\n&Y1\r\nUNION ALL\r\n\r\n\r\n/* TRANSAKSI RAKIT */\r\n/* BUILD  ITEM YG DIBUAT       */\r\nSELECT\r\n  100 as URUT,\r\n  7 as TIPE,\r\n  A.PERIOD_NAME,\r\n  1 AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  a.ITEM_ID ,\r\n  a.build_ID as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n  0 AS MASUK,\r\n  0 MASUK_RP,\r\n  0 AS KELUAR,\r\n  0  AS KELUAR_RP,  \r\n  0 as BAHAN,  \r\n  0 AS BAHAN_RP,  \r\n  a.ENTERED_QTY   * a.BASE_QTY AS JADI ,\r\n  a.TOTAL_AMOUNT AS JADI_RP,\r\n   0  AS ADJ ,  \r\n  0 AS ADJ_RP,\r\n\r\n   a.TOTAL_AMOUNT / (a.ENTERED_QTY * a.BASE_QTY)  AS HARGA,\r\n  a.TOTAL_AMOUNT  AS SUBTOTAL\r\nFROM\r\n  build a\r\n    INNER JOIN ITEM i ON (a.ITEM_ID = i.ITEM_ID)\r\n  \r\nWHERE (i.JENIS_ID = @GOODS)\r\n&X4\r\n&Y1', 71, 'TRANSAKSI STOK DILUAR TAG', NULL, '2010-04-05 21:45:50', '2013-09-18 11:29:09', 0, NULL, 'TRANSAKSI STOK DILUAR TAG'),
	(9, 'SELECT * FROM (\r\n/*KAS /BANK TUJUAN */\r\nSELECT\r\n 0 AS URUT,\r\n  a.RECEIPT_ID,\r\n  a.STATUS_ID,\r\n  a.WAREHOUSE_ID,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.PERIOD_NAME,\r\n  k.COA_ID,\r\n  b.NOTE as Description,\r\n   b.AMOUNT AS DTOTAL ,\r\n  0 AS CTOTAL\r\nFROM\r\n  receipt a\r\n INNER JOIN receipt_detail b ON (a.RECEIPT_ID = b.RECEIPT_ID)\r\n  INNER JOIN bank k ON (a.BANK_ID = k.BANK_ID)\r\nWHERE\r\n  a.DOCUMENT_TYPE_ID = 4\r\nUNION ALL\r\n\r\n  SELECT\r\n  1 AS URUT,\r\n  a2.RECEIPT_ID,\r\n  a2.STATUS_ID,\r\n  a2.WAREHOUSE_ID,\r\n  a2.DOCUMENT_NO,\r\n  a2.DOCUMENT_DATE,\r\n  a2.PERIOD_NAME,\r\n  coa2.COA_ID,\r\n  b2.NOTE as Description,\r\n   0 AS DTOTAL  ,\r\n   b2.AMOUNT AS CTOTAL\r\nFROM\r\n  receipt a2\r\n  INNER JOIN receipt_detail b2 ON (a2.RECEIPT_ID = b2.RECEIPT_ID)\r\n  INNER JOIN warehouse w ON (b2.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\n  INNER JOIN coa coa2 ON (a2.WAREHOUSE_ID = coa2.WAREHOUSE_ID) AND (w.ACCOUNT_ID = coa2.ACCOUNT_ID)\r\nWHERE\r\n  a2.DOCUMENT_TYPE_ID = 4\r\nUNION ALL\r\n /* ACCOUNT PERANTARA*/\r\n  SELECT\r\n  2 AS URUT,\r\n  a1.RECEIPT_ID,\r\n  a1.STATUS_ID,\r\n  a1.WAREHOUSE_ID,\r\n  a1.DOCUMENT_NO,\r\n  a1.DOCUMENT_DATE,\r\n  a1.PERIOD_NAME,\r\n  coa1.COA_ID,\r\n  b1.NOTE as Description,\r\n     b1.AMOUNT AS DTOTAL ,\r\n     0 AS CTOTAL\r\nFROM\r\n  receipt a1\r\n  INNER JOIN receipt_detail b1 ON (a1.RECEIPT_ID = b1.RECEIPT_ID)\r\n  INNER JOIN warehouse w1 ON (b1.WAREHOUSE_ID = w1.WAREHOUSE_ID)\r\nINNER JOIN warehouse w11  ON (a1.WAREHOUSE_ID = w11.WAREHOUSE_ID)\r\n  INNER JOIN coa coa1 ON (w1.WAREHOUSE_ID = coa1.WAREHOUSE_ID) AND (w11.ACCOUNT_ID = coa1.ACCOUNT_ID)\r\nWHERE\r\n  a1.DOCUMENT_TYPE_ID = 4\r\nUNION ALL\r\n  SELECT\r\n  3 AS URUT,\r\n  a3.RECEIPT_ID,\r\n  a3.STATUS_ID,\r\n  a3.WAREHOUSE_ID,\r\n  a3.DOCUMENT_NO,\r\n  a3.DOCUMENT_DATE,\r\n  a3.PERIOD_NAME,\r\n  bank.COA_ID,\r\nb3.NOTE as Description,\r\n  0 AS DTOTAL ,\r\n  b3.AMOUNT  AS CTOTAL\r\nFROM\r\n  receipt a3\r\n  INNER JOIN receipt_detail b3 ON (a3.RECEIPT_ID = b3.RECEIPT_ID)\r\n  INNER JOIN bank ON (b3.BANK_ID = bank.BANK_ID)\r\nWHERE\r\n  a3.DOCUMENT_TYPE_ID = 4\r\n) AS XX WHERE TRUE\r\n&KONDISI\r\nORDER BY RECEIPT_ID, URUT', 73, 'TRANSFER ANTAR CABANG', NULL, NULL, '2007-05-24 13:58:12', 4, NULL, 'TRANSFER ANTAR CABANG'),
	(10, 'SELECT\r\n  X.URUT,\r\n  X.URUT1,\r\n  X.COA_NAME,\r\n  X.COA_ID,\r\n  X.BUILD_ID,\r\n  X.NOTE,\r\n  SUM(X.DEBET) DEBET,\r\n  SUM(X.KREDIT) KREDIT,\r\nB.DOCUMENT_NO FROM\r\n(\r\nSELECT\r\n  IF(B.SUBTOTAL>= 0, 1,0) AS URUT,\r\n  1 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_ID,\r\n  B.BUILD_ID,\r\n  B.NOTE,\r\n  IF(B.SUBTOTAL< 0, -1*B.SUBTOTAL, 0) AS DEBET,\r\n  IF(B.SUBTOTAL>= 0, B.SUBTOTAL, 0) AS KREDIT\r\nFROM\r\nBUILD A\r\nINNER JOIN BUILD_DETAIL B ON (A.BUILD_ID = B.BUILD_ID)\r\nINNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nINNER JOIN coa c ON (i.coa_ID = c.coa_ID)\r\nWHERE\r\n  TRUE\r\nAND A.BUILD_ID = :BUILD_ID  \r\nUNION ALL\r\nSELECT\r\n  IF(A.TOTAL_AMOUNT>= 0, 0,1) AS URUT,\r\n  2 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_ID,\r\n  A.BUILD_ID,\r\n  A.NOTE,\r\n  IF(A.TOTAL_AMOUNT>= 0, A.TOTAL_AMOUNT, 0) AS DEBET,\r\n  IF(A.TOTAL_AMOUNT< 0, -1*A.TOTAL_AMOUNT, 0) AS KREDIT\r\nFROM\r\nBUILD A\r\nINNER JOIN item i ON (A.ITEM_ID = i.ITEM_ID)\r\nINNER JOIN coa c ON (i.coa_ID = c.coa_ID)\r\nWHERE\r\n  TRUE\r\nAND A.BUILD_ID = :BUILD_ID\r\n\r\n)\r\nX\r\nINNER JOIN BUILD B ON (X.BUILD_ID = B.BUILD_ID)\r\nWHERE\r\nTRUE\r\n&KONDISI\r\nGROUP BY X.BUILD_ID , URUT,X.COA_ID\r\nORDER BY X.BUILD_ID, URUT, URUT1\r\n', 4, 'PRODUKSI', NULL, NULL, '2006-02-24 20:18:37', 4, 7, NULL),
	(11, 'SELECT\r\n  X.URUT,\r\n  X.URUT1,\r\n  X.COA_NAME,\r\n  X.COA_ID,\r\n  X.INVENTORY_OUT_ID,\r\n  X.NOTE,\r\n  SUM(X.DEBET) DEBET,\r\n  SUM(X.KREDIT) KREDIT,\r\nB.DOCUMENT_NO FROM(\r\nSELECT\r\n/* piutang suspen */\r\n  IF(TOTAL_AMOUNT>=0, 0,1) AS URUT,\r\n  1 AS URUT1,\r\n  1 AS GROUP1,\r\n  C.COA_NAME,\r\n  C.COA_ID,\r\n  A.INVENTORY_OUT_ID,\r\n  A.NOTE,\r\n  IF(TOTAL_AMOUNT>=0, TOTAL_AMOUNT,0) AS DEBET,\r\n  IF(TOTAL_AMOUNT<0, -1*TOTAL_AMOUNT,0) AS KREDIT\r\nFROM\r\n  INVENTORY_OUT A,\r\n  COA_SETUP CS\r\n  INNER JOIN COA C ON (CS.ACCOUNT_ID  = C.ACCOUNT_ID)\r\nWHERE TRUE\r\nAND CS.PROGRAM_ACCOUNT = \'PIUTANG_S\'\r\nAND A.INVENTORY_OUT_ID = :INVENTORY_OUT_ID\r\nGROUP BY C.COA_ID,A.INVENTORY_OUT_ID\r\nUNION ALL\r\n/* Penjualan*/\r\nSELECT\r\n  IF(TOTAL_AMOUNT>=0, 1,0) AS URUT,\r\n  2 AS URUT1,\r\n  1 AS GROUP1,\r\n  C.COA_NAME,\r\n  C.COA_ID,\r\n  A.INVENTORY_OUT_ID,\r\n  A.NOTE,\r\n  IF(TOTAL_AMOUNT<0, -1*TOTAL_AMOUNT,0) AS DEBET,\r\n    IF(TOTAL_AMOUNT>=0, TOTAL_AMOUNT,0) AS KREDIT\r\nFROM\r\n  INVENTORY_OUT A\r\n  INNER JOIN PERSON CS ON (A.PERSON_ID = CS.PERSON_ID)\r\n  INNER JOIN COA C ON (CS.COA_ID  = C.COA_ID)\r\nWHERE \r\nA.INVENTORY_OUT_ID = :INVENTORY_OUT_ID\r\nGROUP BY C.COA_ID,A.INVENTORY_OUT_ID\r\n\r\n\r\n) X\r\nINNER JOIN INVENTORY_OUT B ON (X.INVENTORY_OUT_ID = B.INVENTORY_OUT_ID)\r\n\r\nWHERE\r\nTRUE\r\n&KONDISI\r\nGROUP BY X.INVENTORY_OUT_ID, X.COA_ID\r\n ORDER BY INVENTORY_OUT_ID, GROUP1, URUT, URUT1\r\n\r\n/* \r\nHPP DAN PERSEDIAN UTK BISNIS PRODUKSI TDK DIPAKAI\r\nUNION ALL\r\nSELECT\r\n  IF(B.SUBTOTAL>=0,0 ,1)AS URUT,\r\n  3 AS URUT1,\r\n  2 AS GROUP1,\r\n  C.COA_NAME,\r\n  C.COA_ID,\r\n  B.INVENTORY_OUT_ID,\r\n  B.NOTE,\r\n  IF(B.HPP>=0,B.HPP ,0) AS DEBET,\r\n  IF(B.HPP<0,-1*B.HPP ,0) AS KREDIT\r\nFROM\r\n  INVENTORY_OUT_DETAIL B\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER JOIN COA C ON (I.HPP_ID = C.COA_ID)\r\nWHERE\r\nTRUE\r\nAND B.INVENTORY_OUT_ID = :INVENTORY_OUT_ID\r\nUNION ALL\r\nSELECT\r\n  IF(B.SUBTOTAL>=0,1 ,0)AS URUT,\r\n  4 AS URUT1,\r\n  2 AS GROUP1,\r\n  C.COA_NAME,\r\n  C.COA_ID,\r\n  B.INVENTORY_OUT_ID,\r\n  B.NOTE,\r\n  IF(B.HPP<0,-1*B.HPP ,0) AS DEBET,\r\n  IF(B.HPP>=0,B.HPP ,0) AS KREDIT\r\nFROM\r\n  INVENTORY_OUT_DETAIL B\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER JOIN COA C ON (I.COA_ID = C.COA_ID)\r\nWHERE\r\nTRUE\r\nAND B.INVENTORY_OUT_ID = :INVENTORY_OUT_ID\r\n*/\r\n', 1, 'SURAT JALAN', NULL, NULL, '2006-02-24 23:15:40', 4, 14, NULL),
	(12, 'SELECT\r\n  X.URUT,\r\n  X.URUT1,\r\n  X.COA_NAME,\r\n  X.COA_ID,\r\n  X.NOTA_PENJUALAN_ID,\r\n  X.NOTE,\r\n  SUM(X.DEBET) DEBET,\r\n  SUM(X.KREDIT) KREDIT,\r\nN.DOCUMENT_NO FROM\r\n(\r\n/*Piutang suspend*/\r\nSELECT\r\n  DISTINCT\r\n  IF(b2.SUBTOTAL>=0,1,0) AS URUT,\r\n  4 AS URUT1,\r\n  C.COA_NAME,\r\n  c.COA_ID,\r\n  a2.NOTA_PENJUALAN_ID,\r\n  b2.NOTE,\r\n  IF(b2.SUBTOTAL<0,(-1*b2.SUBTOTAL),0)  as DEBET,\r\n  IF(b2.SUBTOTAL>=0,(b2.SUBTOTAL),0)  as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A2\r\n  INNER JOIN NOTA_PENJUALAN_DETAIL B2 ON (a2.NOTA_PENJUALAN_ID = b2.NOTA_PENJUALAN_ID),\r\n  coa_setup cs\r\n  inner join coa c on (cs.COA_id  = c.COA_id)\r\nWHERE true\r\n\r\nAND a2.NOTA_PENJUALAN_ID = :NOTA_PENJUALAN_ID\r\nand cs.program_account = \'PIUTANG_S\'\r\n\r\nunion all\r\n/*Pph*/\r\nSELECT\r\n  DISTINCT\r\n  IF(b2.SUBTOTAL>=0,1,0) AS URUT,\r\n  2 AS URUT1,\r\n  C.COA_NAME,\r\n  c.COA_ID,\r\n  a2.NOTA_PENJUALAN_ID,\r\n  b2.NOTE,\r\n  IF(b2.SUBTOTAL<0,(-1*b2.SUBTOTAL),0)  as DEBET,\r\n  IF(b2.SUBTOTAL>=0,(b2.SUBTOTAL),0)  as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A2\r\n  INNER JOIN NOTA_PENJUALAN_DETAIL B2 ON (a2.NOTA_PENJUALAN_ID = b2.NOTA_PENJUALAN_ID)\r\n  INNER JOIN ITEM I ON (b2.ITEM_ID = I.ITEM_ID)\r\n  INNER JOIN coa c ON (I.COA_ID = c.COA_ID)\r\nWHERE true\r\n\r\nAND a2.NOTA_PENJUALAN_ID = :NOTA_PENJUALAN_ID\r\n\r\nunion all\r\n/*ppn*/\r\nSELECT\r\n  DISTINCT\r\n  IF(PPN_AMOUNT>=0,1,0) AS URUT,\r\n  5 AS URUT1,\r\n  C.COA_NAME,\r\n  c.COA_ID,\r\n  a2.NOTA_PENJUALAN_ID,\r\n  a2.NOTE,\r\n  IF(PPN_AMOUNT<0, -1*PPN_AMOUNT, 0) as DEBET,\r\n  IF(PPN_AMOUNT>=0, PPN_AMOUNT, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A2,\r\n  coa_setup cs\r\n  inner join coa c on (cs.COA_id  = c.COA_id)\r\nWHERE TRUE\r\nAND PPN_AMOUNT <> 0\r\nand cs.program_account = \'PPN_JUAL\'\r\nAND a2.NOTA_PENJUALAN_ID = :NOTA_PENJUALAN_ID\r\nunion all\r\n/*DISCOUNT*/\r\nSELECT\r\n  DISTINCT\r\n  IF(TOTAL_DISCOUNT>=0,0,1) AS URUT,\r\n  3 AS URUT1,\r\n  C.COA_NAME,\r\n  c.COA_ID,\r\n  a2.NOTA_PENJUALAN_ID,\r\n  a2.NOTE,\r\n  IF(TOTAL_DISCOUNT>=0, TOTAL_DISCOUNT, 0) as DEBET,\r\n  IF(TOTAL_DISCOUNT<0, -1*TOTAL_DISCOUNT, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A2,\r\n  coa_setup cs\r\n  inner join coa c on (cs.COA_id  = c.COA_id)\r\nWHERE TRUE\r\nand cs.program_account = \'DISCOUNT_JUAL\'\r\nAND TOTAL_DISCOUNT <> 0\r\nAND a2.NOTA_PENJUALAN_ID = :NOTA_PENJUALAN_ID\r\nUNION ALL\r\n/*piutang dagang*/\r\nSELECT\r\n  DISTINCT\r\n  IF(TOTAL_NET>=0,0,1) AS URUT,\r\n  1 AS URUT1,\r\n  C2.COA_NAME,\r\n  c2.COA_ID,\r\n  a2.NOTA_PENJUALAN_ID,\r\n  a2.NOTE,\r\n  IF(TOTAL_NET>=0, TOTAL_NET, 0) as DEBET,\r\n  IF(TOTAL_NET<0, -1*TOTAL_NET, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A2\r\n  INNER JOIN PERSON B2 ON (a2.PERSON_ID = b2.PERSON_ID)\r\n  INNER JOIN coa c2 ON (C2.COA_ID = B2.COA_ID)\r\nWHERE\r\nTRUE\r\nAND TOTAL_NET <> 0\r\nAND a2.NOTA_PENJUALAN_ID = :NOTA_PENJUALAN_ID\r\n) X\r\nINNER JOIN NOTA_PENJUALAN N ON (N.NOTA_PENJUALAN_ID = X.NOTA_PENJUALAN_ID)\r\nWHERE\r\nTRUE\r\n&KONDISI\r\ngroup by   X.NOTA_PENJUALAN_ID, X.COA_ID\r\nORDER BY NOTA_PENJUALAN_ID,URUT,URUT1', 6, '09. JORNAL NOTA PENJUALAN (SUKSES TAPI FORM BELUM', NULL, NULL, '2006-02-24 23:21:02', 4, 15, NULL),
	(13, 'SELECT \r\n  X.URUT,\r\n  X.URUT1,\r\n  X.COA_NAME,\r\n  X.COA_ID,\r\n  X.RECEIPT_ID,\r\n  X.NOTE,\r\n  SUM(X.DEBET) DEBET,\r\n  SUM(X.KREDIT) KREDIT,\r\nB.DOCUMENT_NO FROM(\r\n/*\r\n  BKM, BUKAN PELUNASAN\r\n*/\r\nSELECT DISTINCT\r\nIF(A.TOTAL_AMOUNT>0,0,1) AS URUT,\r\n1 AS URUT1,\r\nC.COA_NAME,\r\nC.COA_ID,\r\nA.RECEIPT_ID,\r\nA.NOTE,\r\nIF(A.TOTAL_AMOUNT>=0,A.TOTAL_AMOUNT,0)  DEBET,\r\nIF(A.TOTAL_AMOUNT<0,-1*A.TOTAL_AMOUNT,0)  AS KREDIT\r\nFROM\r\nRECEIPT A\r\nLEFT OUTER JOIN RECEIPT_DETAIL A1 ON (A.RECEIPT_ID = A1.RECEIPT_ID)\r\nINNER JOIN BANK B ON (A.BANK_ID = B.BANK_ID)\r\nINNER JOIN COA C ON (B.COA_ID = C.COA_ID)\r\nWHERE\r\nTRUE\r\nAND A.RECEIPT_ID = :RECEIPT_ID\r\nAND A1.COA_ID IS NOT NULL\r\nUNION ALL\r\nSELECT\r\nIF(AMOUNT>=0,1,0) AS URUT,\r\n2 AS URUT1,\r\nC.COA_NAME,\r\nC.COA_ID,\r\nA.RECEIPT_ID,\r\nA.NOTE,\r\nIF(AMOUNT<0,-1*AMOUNT, 0) AS DEBET,\r\nIF(AMOUNT>=0,AMOUNT, 0) AS KREDIT\r\nFROM\r\nRECEIPT_DETAIL A\r\nINNER JOIN RECEIPT A1 ON (A.RECEIPT_ID = A1.RECEIPT_ID)\r\nINNER JOIN COA C ON (A.COA_ID = C.COA_ID)\r\nWHERE\r\nTRUE\r\nAND A.RECEIPT_ID = :RECEIPT_ID\r\n\r\nUNION ALL\r\n\r\n/*\r\n  BKM, PELUNASAN\r\n*/\r\n\r\nSELECT DISTINCT\r\nIF(A.TOTAL_AMOUNT>0,0,1) AS URUT,\r\n1 AS URUT1,\r\nC.COA_NAME,\r\nC.COA_ID,\r\nA.RECEIPT_ID,\r\nA.NOTE,\r\nIF(A.TOTAL_AMOUNT>=0,A.TOTAL_AMOUNT,0)  DEBET,\r\nIF(A.TOTAL_AMOUNT<0,-1*A.TOTAL_AMOUNT,0)  AS KREDIT\r\nFROM\r\nRECEIPT A\r\nINNER JOIN RECEIPT_DETAIL A1 ON (A1.RECEIPT_ID = A.RECEIPT_ID)\r\nINNER JOIN BANK B ON (A.BANK_ID = B.BANK_ID)\r\nINNER JOIN COA C ON (B.COA_ID = C.COA_ID)\r\nWHERE\r\nTRUE\r\nAND A.RECEIPT_ID = :RECEIPT_ID\r\nAND A1.COA_ID IS NULL\r\nUNION ALL\r\nSELECT\r\nIF(AMOUNT>=0,1,0) AS URUT,\r\n2 AS URUT1,\r\nCOA_NAME,\r\nC.COA_ID,\r\nA.RECEIPT_ID,\r\nA.NOTE,\r\nIF(AMOUNT<0,-1*AMOUNT, 0) AS DEBET,\r\nIF(AMOUNT>=0,AMOUNT, 0) AS KREDIT\r\nFROM\r\nRECEIPT_DETAIL A\r\nINNER JOIN RECEIPT B ON (A.RECEIPT_ID = B.RECEIPT_ID)\r\nINNER JOIN PERSON C ON (B.PERSON_ID = C.PERSON_ID)\r\nINNER JOIN COA ON (C.COA_ID = COA.COA_ID)\r\nWHERE\r\nA.COA_ID IS NULL\r\nAND A.RECEIPT_ID = :RECEIPT_ID\r\n\r\n)\r\nX\r\nINNER JOIN RECEIPT B ON (X.RECEIPT_ID = B.RECEIPT_ID)\r\nWHERE\r\nTRUE\r\n&KONDISI\r\nGROUP BY X.RECEIPT_ID, X.COA_ID\r\nORDER BY RECEIPT_ID, URUT,URUT1', 8, 'BKM', NULL, NULL, '2006-02-24 23:23:33', 4, 18, NULL),
