	(15, 'SELECT   \'HPP\' AS DESCRIPTION,\r\n  \'HPP \' AS DOCUMENT_NO,\r\n  SYSDATE()  AS DOCUMENT_DATE,\r\n II.WAREHOUSE_ID, PERIOD_NAME, c1.COA_ID AS HPP_ID, c2.COA_ID ,SUM(TOTAL) AS TOTAL  FROM (\r\nSELECT * FROM (\r\nSELECT WAREHOUSE_ID, PERIOD_NAME, COA_ID, HPP_ID ,SUM(TOTAL) AS TOTAL  FROM (\r\n/* saldo awal */\r\nSELECT\r\n  a0.wAREHOUSE_ID, a0.PERIOD_NAME , i0.COA_ID, i0.HPP_ID,\r\n  sum(a0.QTY_AWAL *  HPP) as TOTAL\r\nFROM\r\n  item i0\r\n  INNER JOIN item_balance a0 on (i0.ITEM_ID = a0.ITEM_ID )\r\ngroup by  a0.PERIOD_NAME ,a0.wAREHOUSE_ID, i0.COA_ID, i0.HPP_ID\r\nUNION ALL\r\n/* beli */\r\nSELECT a.WAREHOUSE_ID,a.PERIOD_NAME , i.COA_ID, i.HPP_ID,\r\n  SUM(b.SUBTOTAL ) AS TOTAL\r\nFROM\r\nitem i\r\n  INNER JOIN invoice_detail b on (i.ITEM_ID = b.ITEM_ID )\r\n  INNER JOIN invoice a on (b.INVOICE_ID = a.INVOICE_ID )\r\ngroup by  a.PERIOD_NAME ,a.wAREHOUSE_ID, i.COA_ID, i.HPP_ID\r\nUNION ALL\r\n/* adjusment */\r\nSELECT a.WAREHOUSE_ID,a.PERIOD_NAME ,  i1.COA_ID, i1.HPP_ID,\r\n    SUM(b.SUBTOTAL) AS TOTAL\r\nFROM\r\nitem i1\r\n  INNER JOIN ADJUST_QTY_DETAIL b on (i1.ITEM_ID = b.ITEM_ID )\r\n  INNER JOIN ADJUST_QTY a on (b.ADJUST_QTY_ID = a.ADJUST_QTY_ID)\r\nWHERE a.DOCUMENT_CLASS_CODE = \'ADJ\'\r\ngroup by  a.PERIOD_NAME ,a.wAREHOUSE_ID, i1.COA_ID, i1.HPP_ID\r\n UNION ALL\r\n/*pag */\r\nSELECT a4.WAREHOUSE_ID,a4.PERIOD_NAME ,  i4.COA_ID, i4.HPP_ID,\r\n  SUM(b4.SUBTOTAL)* -1 AS TOTAL\r\nFROM\r\nitem i4\r\n  INNER JOIN   TAG_DETAIL  b4  on (i4.ITEM_ID = b4.ITEM_ID )\r\n  INNER JOIN   TAG  a4  on (b4.TAG_ID = a4.TAG_ID )\r\ngroup by  a4.PERIOD_NAME ,a4.wAREHOUSE_ID, i4.COA_ID, i4.HPP_ID\r\nUNION ALL\r\n/* pag tujuan */\r\nSELECT a5.TO_WH_ID,a5.PERIOD_NAME ,  i5.COA_ID, i5.HPP_ID,\r\n  SUM(b5.SUBTOTAL) AS TOTAL\r\nFROM\r\nitem i5\r\n  INNER JOIN   TAG_DETAIL  b5  on (i5.ITEM_ID = b5.ITEM_ID )\r\n  INNER JOIN   TAG  a5  on (b5.TAG_ID = a5.TAG_ID )\r\ngroup by  a5.PERIOD_NAME ,a5.wAREHOUSE_ID, i5.COA_ID, i5.HPP_ID\r\n)AS XX\r\nWHERE TRUE &KONDISI0\r\nGROUP BY PERIOD_NAME , WAREHOUSE_ID,COA_ID, HPP_ID\r\n)AS YY\r\nUNION ALL\r\n/* SALDO AKHIR */\r\nSELECT\r\n  a9.wAREHOUSE_ID, a9.PERIOD_NAME , i9.COA_ID, i9.HPP_ID,\r\n  sum(a9.QTY_AWAL *  HPP * -1) as TOTAL\r\nFROM\r\n  item i9\r\n  INNER JOIN item_balance a9 on (i9.ITEM_ID = a9.ITEM_ID )\r\nWHERE TRUE &KONDISI1\r\ngroup by  a9.PERIOD_NAME ,a9.wAREHOUSE_ID, i9.COA_ID, i9.HPP_ID\r\n)AS II\r\nINNER JOIN coa c1 ON (II.HPP_ID = c1.ACCOUNT_ID) AND (II.WAREHOUSE_ID = c1.WAREHOUSE_ID)\r\nINNER JOIN coa c2 ON (II.COA_ID = c2.ACCOUNT_ID) AND (II.WAREHOUSE_ID = c2.WAREHOUSE_ID)\r\n GROUP BY II.WAREHOUSE_ID, c1.COA_ID, c2.COA_ID\r\n/* GROUP BY PERIOD_NAME , II.WAREHOUSE_ID, c1.COA_ID, c2.COA_ID  jika ingin dipecah jadi 2 awal + beli dan - akhir) */', 99, 'HPP AWAL +BELI - SALDO AKHIR', 4, NULL, NULL, 4, NULL, NULL),
