	(17, 'SELECT\r\n0 as URUT,\r\n0 as TIPE,\r\np.PERIOD_NAME,\r\n0 AS HPP_FLAG,\r\n\'SALDO\' AS DOCUMENT_CLASS_CODE,\r\n\'\' AS DOCUMENT_NO,\r\n\'\' AS DOCUMENT_REFF_NO,\r\nSTART_DATE as DOCUMENT_DATE,\r\na.WAREHOUSE_ID ,\r\n\'\' AS NOTE,\r\nI.ITEM_ID,\r\na.QTY_AWAL AS MASUK,\r\n0 AS KELUAR,\r\na.HPP as HARGA,\r\n0 as DISKON,\r\na.QTY_AWAL * a.HPP   AS SUBTOTAL,\r\n-1 as PERSON_ID,\r\n0 as ID\r\nFROM\r\nitem_balance a\r\nINNER JOIN PERIOD p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\nINNER JOIN ITEM I ON (a.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE True\r\n&X1\r\nUNION ALL\r\n/* TRANSAKSI BELI */\r\n/*     INVOICE         */\r\nSELECT\r\n1 as URUT,\r\n1 as TIPE,\r\nPERIOD_NAME,\r\n1  AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE, \r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\nb.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n0 AS KELUAR,\r\n(b.Subtotal / b.entered_qty  -  b.discount_price1/b.entered_qty  )/b.BASE_QTY  as HARGA,\r\nabs((b.DISCOUNT_PRICE * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1))   AS SUBTOTAL,\r\na.PERSON_ID,\r\na.INVENTORY_IN_ID AS ID\r\nFROM\r\nINVENTORY_IN a\r\nINNER JOIN INVENTORY_IN_detail b ON (a.INVENTORY_IN_ID = b.INVENTORY_IN_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE TRUE\r\n&T1\r\n&X2\r\nUNION ALL\r\n/* TRANSAKSI JUAL */\r\n/* NOTA_PENJUALAN            */\r\nSELECT\r\n1 as URUT,\r\n2 as TIPE,\r\na.PERIOD_NAME,\r\n0  AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\nb.HPP/b.BASE_QTY  AS HARGA  ,\r\nabs((b.DISCOUNT_PRICE  * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1))    AS SUBTOTAL,\r\na.PERSON_ID,\r\na.INVENTORY_OUT_ID AS ID\r\nFROM\r\nINVENTORY_OUT a\r\nINNER JOIN INVENTORY_OUT_detail b ON (a.INVENTORY_OUT_ID = b.INVENTORY_OUT_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE TRUE\r\n&T1\r\n&X2\r\nUNION ALL\r\n/* TRANSAKSI Pembelian yg ga pake BPB  */\r\n/* INVOICE          */\r\nSELECT\r\n1 as URUT,\r\n3 as TIPE,\r\na.PERIOD_NAME,\r\n0 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\nb.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n0 AS KELUAR,\r\n(b.Subtotal / b.entered_qty  -  b.discount_price1/b.entered_qty  )/b.BASE_QTY  as HARGA,\r\nabs((b.DISCOUNT_PRICE  * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1) )  AS SUBTOTAL,\r\na.PERSON_ID,\r\na.INVOICE_ID AS ID\r\nFROM\r\nINVOICE a\r\nINNER JOIN INVOICE_DETAIL b ON (a.INVOICE_ID = b.INVOICE_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE b.INVENTORY_IN_DETAIL_ID is NULL\r\n&T1\r\n&X2\r\nUNION ALL\r\n/* TRANSAKSI Penjualan yg ga pake SJ  */\r\n/* NOTA_PENJUALAN           */\r\nSELECT\r\n1 as URUT,\r\n4 as TIPE,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\nb.HPP/b.BASE_QTY   as HARGA,\r\nabs((b.DISCOUNT_PRICE  * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1)  )  AS SUBTOTAL,\r\na.PERSON_ID,\r\na.NOTA_PENJUALAN_ID AS ID\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER JOIN NOTA_PENJUALAN_DETAIL b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE b.INVENTORY_OUT_DETAIL_ID IS NULL\r\n&T1\r\n&X2\r\nUNION ALL\r\n/* TRANSAKSI ADJUSTMENT QTY */\r\n/* ADJ_QTY           */\r\nSELECT\r\n2 as URUT,\r\n5 as TIPE,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\nb.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n0 AS KELUAR,\r\nb.UNIT_PRICE /b.BASE_QTY as HARGA,\r\n0 as DISKON ,\r\nb.Subtotal AS SUBTOTAL,\r\n-1 as PERSON_ID,\r\na.ADJUST_QTY_ID AS ID\r\nFROM\r\nadjust_qty a\r\nINNER JOIN adjust_qty_detail b ON (a.ADJUST_QTY_ID = b.ADJUST_QTY_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE b.ENTERED_QTY  > 0\r\n&T1\r\n&X2\r\n&TGL_ADJUST\r\nUNION ALL\r\n/* TRANSAKSI ADJUSTMENT QTY */\r\n/* ADJ_QTY           */\r\nSELECT\r\n2 as URUT,\r\n5 as TIPE,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * -1 * b.BASE_QTY    AS KELUAR,\r\nb.UNIT_PRICE / b.BASE_QTY as HARGA,\r\n0 as DISKON ,\r\nb.Subtotal AS SUBTOTAL ,\r\n-1 as PERSON_ID,\r\na.ADJUST_QTY_ID AS ID\r\nFROM\r\nadjust_qty a\r\nINNER JOIN adjust_qty_detail b ON (a.ADJUST_QTY_ID = b.ADJUST_QTY_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE b.ENTERED_QTY  < 0\r\n&T1\r\n&X2\r\n&TGL_ADJUST\r\nUNION ALL\r\n/* TRANSAKSI PAG, GUDANG ASAL  */\r\n/* TAG         */\r\nSELECT\r\n1 as URUT,\r\n6 as TIPE,\r\na.PERIOD_NAME,\r\n0 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\nb.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\nifnull(l.Hpp,0) as HARGA,\r\n0 AS DISKON, \r\n0 AS SUBTOTAL,\r\n-1 as PERSON_ID,\r\na.TAG_ID AS ID\r\nFROM\r\ntag a\r\nINNER JOIN tag_detail b ON (a.tag_ID = b.tag_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nLEFT  JOIN Item_balance l on (b.item_id = l.item_id) and (a.period_name = l.period_name) and (a.warehouse_id = l.warehouse_id)\r\nWHERE TRUE\r\n&T1\r\n&X2\r\nUNION ALL\r\n/* TRANSAKSI PAG, GUDANG TUJUAN  */\r\n/* TAG         */\r\nSELECT\r\n1 as URUT,\r\n6 as TIPE,\r\na.PERIOD_NAME,\r\n0 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.to_wh_ID as WAREHOUSE_ID,\r\nb.NOTE,\r\nI.ITEM_ID ,\r\nb.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n0 AS KELUAR,\r\nifnull(l.Hpp,0) as HARGA,\r\n0 as DISKON ,\r\n0 AS SUBTOTAL,\r\n-1 as PERSON_ID,\r\na.TAG_ID AS ID\r\nFROM\r\ntag a\r\nINNER JOIN tag_detail b ON (a.tag_ID = b.tag_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nLEFT  JOIN Item_balance l on (b.item_id = l.item_id) and (a.period_name = l.period_name) and (a.to_wh_id = l.warehouse_id)\r\nWHERE TRUE\r\n&T1\r\n&X3\r\nUNION ALL\r\n/* TRANSAKSI RAKIT */\r\n/* BUILD  BAHAN BAKU       */\r\nSELECT\r\n1 as URUT,\r\n7 as TIPE,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\nb.UNIT_PRICE /b.BASE_QTY   AS HARGA,\r\n0 as DISKON ,\r\nb.SUBTOTAL  AS SUBTOTAL, \r\n-1 as PERSON_ID,\r\na.BUILD_ID AS ID\r\nFROM\r\nbuild a\r\nINNER JOIN build_detail b ON (a.build_ID = b.build_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE  true\r\n&T1\r\n&X2\r\nUNION ALL\r\n/* TRANSAKSI RAKIT */\r\n/* BUILD  ITEM YG DIBUAT       */\r\nSELECT\r\n1 as URUT,\r\n7 as TIPE,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\na.ENTERED_QTY *  a.BASE_QTY  AS MASUK,\r\n0 AS KELUAR,\r\na.TOTAL_AMOUNT / a.ENTERED_QTY / a.BASE_QTY  AS HARGA,\r\n0 as DISKON ,\r\na.TOTAL_AMOUNT AS SUBTOTAL,\r\n-1 as PERSON_ID,\r\na.BUILD_ID AS ID\r\nFROM\r\nbuild a\r\nINNER JOIN ITEM I ON (a.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE true \r\n&T1\r\n&X4', 70, 'STOK1', 4, '2009-01-03 10:10:44', '2019-06-29 21:24:49', 0, NULL, 'STOK1'),
	(18, '/*\r\n hpp_flag = 1 =>  berpengaruh pd nilai HPP\r\n hpp_flag = 0 => TIDAK berpengaruh pd nilai HPP\r\n\r\n URUT :\r\n Agar transaksi sesuai urutannya, agar beli duluan baru jualan pd tgl yg sama. disort dari no urut\r\n\r\n 10=  unit_price di adjust_qty_detail diset sesuai hpp\r\n 20= unit_price di build_detail di set sesuai hpp.\r\n 30= unit_price di inventory_out_detail( penjualan) di set sesuai hpp\r\n 40= unit_price di nota_penjualan_detail( penjualan) di set sesuai hpp\r\n 41=retur beli pake BTB\r\n 42=retur beli tanpa BTB \r\n\r\n 2,0,6-100 = tdk diset apa 2x.\r\n\r\n\r\n */\r\n SELECT\r\n   0 as URUT,\r\n   PERIOD_NAME,\r\n   1 AS HPP_FLAG,\r\n    \'SALDO\' AS DOCUMENT_CLASS_CODE,\r\n   \'\' AS DOCUMENT_NO,   \'\' AS DOCUMENT_REFF_NO,\r\nsubdate(STOCK_DATE,1000) as DOCUMENT_DATE,\r\n   a.WAREHOUSE_ID ,\r\n   a.ITEM_ID,\r\n   \'\' as DTL_ID,\r\n   SUM(a.QTY_AWAL) AS MASUK,\r\n   0 AS KELUAR,\r\n   MAX(a.HPP) as HARGA,\r\n     SUM(a.QTY_AWAL)  *   MAX(a.HPP)  as SUBTOTAL,\r\n   null as PERSON_ID,\r\n   1 AS TRANS_ID,\r\n  0 AS TRANS_OLD_ID\r\n FROM\r\n     item_balance a\r\n     inner join item i on (a.item_id = i.item_id)\r\n WHERE (i.JENIS_ID = @GOODS)\r\n &X1/*ini tidak ada*/\r\n&X4\r\n GROUP BY\r\n   PERIOD_NAME,ITEM_ID\r\n\r\n UNION ALL\r\n /* BTB */\r\n SELECT\r\n   1 as URUT,\r\n   PERIOD_NAME,\r\n   1  AS HPP_FLAG,\r\n   a.DOCUMENT_CLASS_CODE,\r\n   a.DOCUMENT_NO,\r\n a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.WAREHOUSE_ID,\r\n   b.ITEM_ID ,\r\n   b.INVENTORY_IN_DETAIL_ID as DTL_ID,\r\n   b.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n   0 AS KELUAR,\r\n   (b.UNIT_PRICE - b.DISCOUNT_PRICE - b.DISCOUNT_PRICE1/b.ENTERED_QTY)    / b.BASE_QTY as HARGA,\r\n   b.Subtotal  - b.DISCOUNT_PRICE1  AS SUBTOTAL,\r\n   a.PERSON_ID,\r\n   1 AS TRANS_ID,\r\n   0 AS TRANS_OLD_ID\r\n\r\n FROM\r\n    INVENTORY_IN a\r\n   INNER JOIN INVENTORY_IN_DETAIL b ON (a.INVENTORY_IN_ID = b.INVENTORY_IN_ID )\r\n   INNER JOIN ITEM i on (b.ITEM_ID = i.ITEM_ID)\r\n WHERE (i.JENIS_ID = @GOODS)\r\nAND (DOCUMENT_TYPE_ID<5)\r\n &T1\r\n &X2\r\n UNION ALL\r\n /*     INVOICE utk yg tanpa BTB   */\r\n SELECT\r\n   2 as URUT,\r\n   PERIOD_NAME,\r\n   1  AS HPP_FLAG,\r\n   a.DOCUMENT_CLASS_CODE,\r\n   a.DOCUMENT_NO,\r\n a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.WAREHOUSE_ID,\r\n   b.ITEM_ID ,\r\n   b.INVENTORY_IN_DETAIL_ID as DTL_ID,\r\n   b.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n   0 AS KELUAR,\r\n   (b.UNIT_PRICE - b.DISCOUNT_PRICE - b.DISCOUNT_PRICE1/b.ENTERED_QTY)    / b.BASE_QTY as HARGA,\r\n   b.Subtotal  - b.DISCOUNT_PRICE1  AS SUBTOTAL,\r\n   a.PERSON_ID,\r\n   1 AS TRANS_ID,\r\n   0 AS TRANS_OLD_ID\r\n\r\n  FROM\r\n    INVOICE a\r\n   INNER JOIN INVOICE_DETAIL b ON (a.INVOICE_ID = b.INVOICE_ID )\r\n   INNER JOIN COA c ON (b.COA_ID = c.COA_ID)\r\n   INNER JOIN ITEM i on (b.ITEM_ID = i.ITEM_ID)\r\n  \r\n WHERE (i.JENIS_ID = @GOODS) and\r\n  (INVENTORY_IN_DETAIL_ID IS NULL)\r\nAND (DOCUMENT_TYPE_ID<5)\r\n &T1\r\n &X2\r\n\r\nUNION ALL\r\n /* RETUR BELI PAKE BTB */\r\n SELECT\r\n   41 as URUT,\r\n   PERIOD_NAME,\r\n   1  AS HPP_FLAG,\r\n   a.DOCUMENT_CLASS_CODE,\r\n   a.DOCUMENT_NO,\r\n   a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.WAREHOUSE_ID,\r\n   b.ITEM_ID ,\r\n   b.INVENTORY_IN_DETAIL_ID as DTL_ID,\r\n   0 AS MASUK,\r\n   b.ENTERED_QTY * b.BASE_QTY * -1  AS KELUAR,\r\n   (b.UNIT_PRICE - b.DISCOUNT_PRICE - b.DISCOUNT_PRICE1/b.ENTERED_QTY)    / b.BASE_QTY as HARGA,\r\n   (b.Subtotal  - b.DISCOUNT_PRICE1)   AS SUBTOTAL,\r\n   a.PERSON_ID,\r\n   1 AS TRANS_ID,\r\n   0 AS TRANS_OLD_ID\r\n\r\n \r\n FROM\r\n    INVENTORY_IN a\r\n   INNER JOIN INVENTORY_IN_DETAIL b ON (a.INVENTORY_IN_ID = b.INVENTORY_IN_ID )\r\n   INNER JOIN ITEM i on (b.ITEM_ID = i.ITEM_ID)\r\nWHERE (i.JENIS_ID = @GOODS)\r\n               AND (DOCUMENT_TYPE_ID>=5)\r\n &T1\r\n &X2\r\n\r\n UNION ALL\r\n /*     RETUR BELI tanpa BPB   */\r\n SELECT\r\n   42 as URUT,\r\n   PERIOD_NAME,\r\n   1  AS HPP_FLAG,\r\n   a.DOCUMENT_CLASS_CODE,\r\n   a.DOCUMENT_NO,\r\n a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.WAREHOUSE_ID,\r\n   b.ITEM_ID ,\r\n   b.INVENTORY_IN_DETAIL_ID as DTL_ID,\r\n   0 AS MASUK,\r\n   b.ENTERED_QTY * b.BASE_QTY * -1 AS KELUAR,\r\n   (b.UNIT_PRICE - b.DISCOUNT_PRICE - b.DISCOUNT_PRICE1/b.ENTERED_QTY)    / b.BASE_QTY as HARGA,\r\n   (b.Subtotal  - b.DISCOUNT_PRICE1)   AS SUBTOTAL,\r\n   a.PERSON_ID,\r\n   1 AS TRANS_ID,\r\n   0 AS TRANS_OLD_ID\r\n\r\n \r\n FROM\r\n    INVOICE a\r\n   INNER JOIN INVOICE_DETAIL b ON (a.INVOICE_ID = b.INVOICE_ID )\r\n   INNER JOIN COA c ON (b.COA_ID = c.COA_ID)\r\n   INNER JOIN ITEM i on (b.ITEM_ID = i.ITEM_ID)\r\n  \r\n WHERE (i.JENIS_ID = @GOODS) and\r\n  (INVENTORY_IN_DETAIL_ID IS NULL)\r\n AND (DOCUMENT_TYPE_ID=5)\r\n\r\n &T1\r\n &X2\r\n\r\nUNION ALL\r\n /* SJ */\r\n SELECT\r\n   30 as URUT,\r\n   PERIOD_NAME,\r\n   0  AS HPP_FLAG,\r\n   a.DOCUMENT_CLASS_CODE,\r\n   a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.WAREHOUSE_ID,\r\n   b.ITEM_ID ,\r\n   b.INVENTORY_OUT_DETAIL_ID as DTL_ID,\r\n   IF(b.ENTERED_QTY < 0,  b.ENTERED_QTY * b.BASE_QTY *-1 , 0 )AS MASUK,\r\n   IF(b.ENTERED_QTY > 0,  b.ENTERED_QTY * b.BASE_QTY , 0 )AS KELUAR,\r\n   b.HPP / b.BASE_QTY as HARGA,\r\n    b.HPP *  ENTERED_QTY  AS SUBTOTAL,\r\n   a.PERSON_ID,\r\n   1 AS TRANS_ID,\r\n   IF( a.DOCUMENT_TYPE_ID>=5,INVOICE_BASE_QTY,0) AS TRANS_OLD_ID\r\n\r\n \r\n FROM\r\n    INVENTORY_OUT a\r\n   INNER JOIN INVENTORY_OUT_DETAIL b ON (a.INVENTORY_OUT_ID = b.INVENTORY_OUT_ID )\r\n   INNER JOIN ITEM i on (b.ITEM_ID = i.ITEM_ID)\r\n WHERE (i.JENIS_ID = @GOODS)\r\n &T1\r\n &X2\r\n\r\n UNION ALL\r\n\r\n /* TRANSAKSI JUAL */\r\n /* NOTA_PENJUALAN yg tanpa SJ        */\r\n SELECT\r\n   40 as URUT,\r\n   PERIOD_NAME,\r\n   0  AS HPP_FLAG,\r\n   a.DOCUMENT_CLASS_CODE,\r\n   a.DOCUMENT_NO,\r\n   a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.WAREHOUSE_ID,\r\n   b.ITEM_ID ,\r\n   b.NOTA_PENJUALAN_DETAIL_ID as DTL_ID,\r\n   IF(b.ENTERED_QTY < 0,  b.ENTERED_QTY * b.BASE_QTY *-1 , 0 )AS MASUK,\r\n   IF(b.ENTERED_QTY > 0,  b.ENTERED_QTY * b.BASE_QTY , 0 )AS KELUAR,\r\n   b.HPP   / b.BASE_QTY  AS HARGA,\r\n    b.ENTERED_QTY * b.HPP    AS SUBTOTAL,\r\n   a.PERSON_ID,\r\n   1 AS TRANS_ID\r\n,\r\n   NOTA_PENJUALAN_PRE_ID AS TRANS_OLD_ID\r\n\r\n \r\n FROM\r\n    NOTA_PENJUALAN a\r\n   INNER JOIN NOTA_PENJUALAN_DETAIL b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID )\r\n   INNER JOIN ITEM i on (b.ITEM_ID = i.ITEM_ID)\r\n\r\n WHERE (b.INVENTORY_OUT_DETAIL_ID IS NULL) AND (i.JENIS_ID = @GOODS)\r\n &T1\r\n &X2\r\nUNION ALL\r\n /* TRANSAKSI ADJUSTMENT QTY */\r\n /* ADJ_QTY           */\r\n /* PENGARUH KE HPP */\r\n SELECT\r\n   if(b.entered_qty >0,2,75) as URUT,\r\n   PERIOD_NAME,\r\n   1 AS HPP_FLAG,\r\n   a.DOCUMENT_CLASS_CODE,\r\n   a.DOCUMENT_NO,\r\n   a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.WAREHOUSE_ID,\r\n   b.ITEM_ID ,\r\n   b.adjust_qty_detail_ID  as DTL_ID,\r\n   b.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n   0 AS KELUAR,\r\n   b.UNIT_PRICE  / b.BASE_QTY as HARGA,\r\n   b.Subtotal    AS SUBTOTAL,\r\n   null as PERSON_ID,\r\n   1 AS TRANS_ID\r\n,\r\n   0 AS TRANS_OLD_ID\r\n\r\n \r\n FROM\r\n   adjust_qty a\r\n   INNER JOIN adjust_qty_detail b ON (a.ADJUST_QTY_ID = b.ADJUST_QTY_ID)\r\n   INNER JOIN ITEM i on (b.ITEM_ID = i.ITEM_ID)\r\n WHERE  a.ADJUST_TYPE_ID=1 and (i.JENIS_ID = @GOODS)\r\n &T1\r\n &X2\r\n UNION ALL\r\n\r\n   /* TRANSAKSI ADJUSTMENT QTY */\r\n /* ADJ_QTY           */\r\n /* TDK PENGARUH KE HPP, MINTA DI ISI HARGANYA */\r\n SELECT\r\n 25 as URUT,\r\n   PERIOD_NAME,\r\n  0 AS HPP_FLAG,\r\n   a.DOCUMENT_CLASS_CODE,\r\n   a.DOCUMENT_NO,\r\n   a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.WAREHOUSE_ID,\r\n   b.ITEM_ID ,\r\n   b.adjust_qty_detail_ID  as DTL_ID,\r\n   b.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n   0 AS KELUAR,\r\n   b.UNIT_PRICE  / b.BASE_QTY as HARGA,\r\n   b.Subtotal    AS SUBTOTAL,\r\n   null as PERSON_ID,\r\n   A.ADJUST_QTY_ID AS TRANS_ID\r\n,\r\n  0 AS TRANS_OLD_ID\r\n\r\n \r\n FROM\r\n   adjust_qty a\r\n   INNER JOIN adjust_qty_detail b ON (a.ADJUST_QTY_ID = b.ADJUST_QTY_ID)\r\n   INNER JOIN ITEM i on (b.ITEM_ID = i.ITEM_ID)\r\n WHERE /*a.coa_id is NOT NULL*/ a.ADJUST_TYPE_ID=0 and (i.JENIS_ID = @GOODS)\r\n &T1\r\n &X2\r\n\r\n\r\n UNION ALL\r\n  /* TRANSAKSI ADJUSTMENT AMOUNT */\r\n /* ADJ_AMOUNT           */\r\n /* PENGARUH KE HPP */\r\n SELECT\r\n   3 as URUT,\r\n   PERIOD_NAME,\r\n   1 AS HPP_FLAG,\r\n   a.DOCUMENT_CLASS_CODE,\r\n   a.DOCUMENT_NO,\r\n   a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n  0 AS WAREHOUSE_ID,\r\n   b.ITEM_ID ,\r\n   b.adjust_AMOUNT_detail_ID  as DTL_ID,\r\n   0 AS MASUK,\r\n   0 AS KELUAR,\r\n   0    as HARGA,\r\n   b.ENTERED_AMOUNT    AS SUBTOTAL,\r\n   null as PERSON_ID,\r\n   1 AS TRANS_ID\r\n,\r\n   0 AS TRANS_OLD_ID\r\n\r\n \r\n FROM\r\n   adjust_AMOUNT a\r\n   INNER JOIN adjust_AMOUNT_detail b ON (a.ADJUST_AMOUNT_ID = b.ADJUST_AMOUNT_ID)\r\n   INNER JOIN ITEM i on (b.ITEM_ID = i.ITEM_ID)\r\n\r\n   \r\n WHERE  (i.JENIS_ID = @GOODS)\r\n &T1\r\n &X2\r\n UNION ALL\r\n\r\n /* TRANSAKSI RAKIT */\r\n /* BUILD  BAHAN BAKU       */\r\n SELECT\r\n   20 as URUT,\r\n   PERIOD_NAME,\r\n   0 AS HPP_FLAG,\r\n   a.DOCUMENT_CLASS_CODE,\r\n   a.DOCUMENT_NO,\r\n   a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.WAREHOUSE_ID,\r\n   b.ITEM_ID ,\r\n   b.build_detail_ID as DTL_ID,\r\n   0 AS MASUK,\r\n     b.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\n    b.UNIT_PRICE / b.BASE_QTY  AS HARGA,\r\n    SUBTOTAL,\r\n    null as PERSON_ID,\r\n   a.BUILD_ID AS TRANS_ID\r\n,\r\n   0 AS TRANS_OLD_ID\r\n\r\n \r\n FROM\r\n   build a\r\n   INNER JOIN build_detail b ON (a.build_ID = b.build_ID)\r\n   INNER JOIN ITEM i on (b.ITEM_ID = i.ITEM_ID)\r\n\r\n   \r\n WHERE (i.JENIS_ID = @GOODS)\r\n &T1\r\n &X2\r\n UNION ALL\r\n\r\n\r\n /* TRANSAKSI RAKIT */\r\n /* BUILD  ITEM YG DIBUAT       */\r\n SELECT\r\n   16 as URUT,\r\n   PERIOD_NAME,\r\n   1 AS HPP_FLAG,\r\n   a.DOCUMENT_CLASS_CODE,\r\n   a.DOCUMENT_NO,\r\n   a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.WAREHOUSE_ID,\r\n   a.ITEM_ID ,\r\n   a.build_ID as DTL_ID,\r\n   a.ENTERED_QTY   * a.BASE_QTY AS MASUK,\r\n   0 AS KELUAR,\r\n   (a.TOTAL_AMOUNT / a.ENTERED_QTY) / a.BASE_QTY  AS HARGA,\r\n   a.TOTAL_AMOUNT  AS SUBTOTAL,\r\n   null as PERSON_ID,\r\n   1 AS TRANS_ID\r\n,\r\n   0 AS TRANS_OLD_ID\r\n\r\n \r\n FROM\r\n   build a\r\n   INNER JOIN ITEM i on (a.ITEM_ID = i.ITEM_ID)\r\n\r\n   \r\n WHERE (i.JENIS_ID = @GOODS)\r\n &T1\r\n&X4\r\n\r\n', 100, 'HPP STOK dgn Faktur Penjualan', 4, '2010-04-19 14:21:58', '2019-06-27 10:37:14', 1, NULL, 'hpp'),
	(19, '\r\n\r\n/*\r\nTIPE --> digit menunjukan Jurnal BELI-JUAL-INV-BKK-BKM-GL\r\n\r\n&P1  = PERIODE\r\n&T1  = TANGGAL\r\n&T2 = TANGGAL TOLAK\r\n\r\n&A1 = ACCOUNT\r\n*/\r\n\r\n/*saldo awal */\r\nselect\r\n  \'111111A\' as TIPE,\r\n  \'SALDO AWAL\' AS DOCUMENT_CLASS_CODE,\r\n  \' \' AS DOCUMENT_NO ,\r\nBALANCE_DATE AS  DOCUMENT_DATE,\r\n  \' \' AS DOCUMENT_REFF_NO ,\r\n  0  AS URUT,\r\n  1 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,\r\nC.ACCOUNT_ID ,\r\n \' \'  ITEM_DESCRIPTION ,\r\n  0 AS ID,\r\n  \' SALDO AWAL \' AS NOTE,\r\n   COA_SALDO AS AWAL,\r\n  0 AS DEBET,\r\n  0 AS KREDIT\r\nFROM\r\n  COA_BALANCE a\r\n  INNER join V_ACCOUNT c on (a.COA_ID = c.COA_id)\r\nWHERE TRUE \r\n&X1  \r\n&P1\r\n&A1\r\nUNION ALL \r\n\r\n/*---- ADJUSTMENT */\r\n/* persediaan */\r\nselect\r\n  \'001001B\' as TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,0 ,1)as URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.ADJUST_QTY_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(b.SUBTOTAL>=0,b.SUBTOTAL ,0) AS DEBET,\r\n  IF(b.SUBTOTAL<0,b.SUBTOTAL *-1 ,0) AS KREDIT\r\nFROM\r\n  ADJUST_QTY a\r\n  INNER JOIN ADJUST_QTY_DETAIL B on (a.ADJUST_QTY_ID = B.ADJUST_QTY_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nwhere\r\n B.SUBTOTAL <> 0\r\n&P1  &T1  &A1\r\n&KONDISI\r\nUNION ALL \r\n/*  BIAYA / BONUS */\r\n\r\nselect\r\n  \'001001B\' as TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  if(TOTAL_AMOUNT>=0, 1,0) AS URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,\r\n \'   \' ITEM_DESCRIPTION ,\r\n  a.ADJUST_QTY_ID,\r\n  a.NOTE,\r\n  0 AS AWAL,\r\n  if(TOTAL_AMOUNT<0, -1*TOTAL_AMOUNT,0) AS DEBET,\r\n  if(TOTAL_AMOUNT>=0, TOTAL_AMOUNT,0) AS KREDIT\r\nFROM\r\n  ADJUST_QTY a\r\n    INNER  join V_ACCOUNT c on (a.COA_ID = c.COA_id)\r\n\r\nWHERE  A.TOTAL_AMOUNT <> 0\r\n&P1  &T1  &A1\r\n&KONDISI\r\n/* END ADJUST_QTY */\r\n\r\nUNION ALL \r\n/*---- ADJUSTMENT NILAI*/\r\n/* persediaan */\r\nselect\r\n  \'001001C\' as TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.ENTERED_AMOUNT>=0,0 ,1)as URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.ADJUST_AMOUNT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(b.ENTERED_AMOUNT>=0,b.ENTERED_AMOUNT ,0) AS DEBET,\r\n  IF(b.ENTERED_AMOUNT<0,b.ENTERED_AMOUNT *-1 ,0) AS KREDIT\r\nFROM\r\n  ADJUST_AMOUNT a\r\n  INNER JOIN ADJUST_AMOUNT_DETAIL B on (a.ADJUST_AMOUNT_ID = B.ADJUST_AMOUNT_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nwhere\r\n B.ENTERED_AMOUNT <> 0\r\n&P1  &T1  &A1\r\n&KONDISI\r\nUNION ALL \r\n/*  BIAYA / BONUS */\r\n\r\nselect\r\n  \'001001C\' as TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  if(TOTAL_AMOUNT>=0, 1,0) AS URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,\r\n \'   \' ITEM_DESCRIPTION ,\r\n  a.ADJUST_AMOUNT_ID,\r\n  a.NOTE,\r\n  0 AS AWAL,\r\n  if(TOTAL_AMOUNT<0, -1*TOTAL_AMOUNT,0) AS DEBET,\r\n  if(TOTAL_AMOUNT>=0, TOTAL_AMOUNT,0) AS KREDIT\r\nFROM\r\n  ADJUST_AMOUNT a\r\n    INNER  join V_ACCOUNT c on (a.COA_ID = c.COA_id)\r\n\r\nWHERE  A.TOTAL_AMOUNT <> 0\r\n&P1  &T1  &A1\r\n&KONDISI\r\n/* END ADJUST_AMOUNT */\r\n\r\nUNION ALL \r\n\r\n\r\n/*---- INVENTORY_IN*/\r\n/* persediaan */\r\nselect\r\n  \'001001C\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,0 ,1)as URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.INVENTORY_IN_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(b.SUBTOTAL>=0,b.SUBTOTAL -b.DISCOUNT_PRICE1 ,0) AS DEBET,\r\n  IF(b.SUBTOTAL<0,(b.SUBTOTAL -b.DISCOUNT_PRICE1 )*-1 ,0) AS KREDIT\r\nFROM\r\n  INVENTORY_IN a\r\n  INNER JOIN INVENTORY_IN_DETAIL B on (a.INVENTORY_IN_ID = B.INVENTORY_IN_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nwhere\r\n B.SUBTOTAL <> 0\r\n&P1  &T1  &A1\r\n&KONDISI\r\nUNION ALL \r\n/*  hutang suspend */\r\n\r\nselect\r\n  \'001001C\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  if(SUBTOTAL>=0, 1,0) AS URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,\r\n I.ITEM_DESCRIPTION ,\r\n  a.INVENTORY_IN_ID,\r\n  a.NOTE,\r\n  0 AS AWAL,\r\n  if(SUBTOTAL<0, -1*(SUBTOTAL -b.DISCOUNT_PRICE1),0) AS DEBET,\r\n  if(SUBTOTAL>=0, SUBTOTAL-b.DISCOUNT_PRICE1,0) AS KREDIT\r\nFROM\r\n  INVENTORY_IN a\r\n  INNER JOIN INVENTORY_IN_DETAIL B on (a.INVENTORY_IN_ID = B.INVENTORY_IN_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_SUSPEND_ID  = c.COA_id)\r\n\r\nWHERE  B.SUBTOTAL <> 0\r\n&P1  &T1  &A1\r\n&KONDISI\r\n/* END INVENTORY_IN */\r\n\r\nUNION ALL\r\n/* INVOICE\r\n biaya-biaya yang ada di detail\r\n Biaya   1000\r\n ppn      90\r\n diskon        100\r\n hutang        990\r\n\r\n */\r\nSELECT\r\n  IF(a.INVENTORY_IN_ID IS NULL, \'101001D\',\'100001D\') AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,0,1) AS URUT,\r\n  20 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  a.INVOICE_ID,\r\n  b.NOTE,   0 AS AWAL,\r\n  IF(b.SUBTOTAL>=0,(b.SUBTOTAL -b.DISCOUNT_PRICE1),0)  as DEBET,\r\n  IF(b.SUBTOTAL<0,-1*(b.SUBTOTAL -b.DISCOUNT_PRICE1),0)  as KREDIT\r\nFROM\r\n  invoice a\r\n  INNER JOIN invoice_detail b ON (a.INVOICE_ID = b.INVOICE_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_ID)\r\nWHERE\r\n  B.SUBTOTAL <> 0 \r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\nUNION ALL\r\n/* INVOICE\r\n/*PPN*/\r\nSELECT\r\n  IF(a.INVENTORY_IN_ID IS NULL, \'101001D\',\'100001D\') AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(PPN_AMOUNT>=0,0,1) AS URUT,\r\n  20 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'PPN \' AS ITEM_DESCRIPTION ,\r\n  a.INVOICE_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n    IF(PPN_AMOUNT>0, PPN_AMOUNT, 0) as DEBET,\r\n  IF(PPN_AMOUNT<0, -1*PPN_AMOUNT, 0) as KREDIT\r\n\r\nFROM\r\n  invoice a,\r\n  V_ACCOUNT c\r\nWHERE TRUE\r\nand (c.COA_ID = @BELI_PPN)\r\nAND PPN_AMOUNT <> 0\r\nAND  (STATUS_ID <> @DELETE)\r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\n\r\n\r\nUNION ALL\r\n\r\n/*hutang dagang yang ada di header*/\r\nSELECT\r\n   IF(a.INVENTORY_IN_ID IS NULL, \'101001D\',\'100001D\') AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_AMOUNT>=0,1,0) AS URUT,\r\n 20 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  PERSON_NAME AS ITEM_DESCRIPTION ,\r\n  a.INVOICE_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(TOTAL_NET<0, -1*TOTAL_NET, 0) as DEBET,\r\n  IF(TOTAL_NET>=0, TOTAL_NET, 0) as KREDIT\r\nFROM\r\n  invoice a\r\n  INNER JOIN PERSON B ON (a.PERSON_ID = b.PERSON_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_HUT_ID)\r\n\r\nWHERE TOTAL_NET <> 0\r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\n\r\n\r\n/* END INVOICE */\r\n\r\nUNION ALL\r\n\r\n/*  BUILD BARANG JADI */\r\nSELECT\r\n   \'001001E\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(A.TOTAL_AMOUNT>= 0, 0,1) AS URUT,\r\n  30 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  A.BUILD_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n  IF(A.TOTAL_AMOUNT>= 0, A.TOTAL_AMOUNT, 0) AS DEBET,\r\n  IF(A.TOTAL_AMOUNT< 0, -1*A.TOTAL_AMOUNT, 0) AS KREDIT\r\nFROM\r\nBUILD A\r\nINNER JOIN item i ON (A.ITEM_ID = i.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\n WHERE (TOTAL_AMOUNT <> 0 )\r\n&P1  &T1  &A1\r\n&KONDISI\r\nUNION ALL\r\n\r\n/*  BUILD BAHAN BAKU NYA */\r\nSELECT\r\n  \'001001E\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.SUBTOTAL>= 0, 1,0) AS URUT,\r\n  30 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.BUILD_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(B.SUBTOTAL< 0, -1*B.SUBTOTAL, 0) AS DEBET,\r\n  IF(B.SUBTOTAL>= 0, B.SUBTOTAL, 0) AS KREDIT\r\nFROM\r\nBUILD A\r\nINNER JOIN BUILD_DETAIL B ON (A.BUILD_ID = B.BUILD_ID)\r\nINNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nINNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nWHERE\r\nB.SUBTOTAL<> 0\r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\n/* END BUILD */\r\n\r\n\r\n\r\nUNION ALL\r\n\r\n/* PENJUALAN */\r\n/*piutang dagang*/\r\nSELECT\r\n   \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_NET>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  PERSON_NAME AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(TOTAL_NET>=0, TOTAL_NET, 0) as DEBET,\r\n  IF(TOTAL_NET<0, -1*TOTAL_NET, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN PERSON B ON (a.PERSON_ID = b.PERSON_ID)\r\n  INNER JOIN V_ACCOUNT c ON (B.COA_PIUT_ID = C.COA_ID )\r\nWHERE\r\n   (TOTAL_NET <> 0)\r\n and ( a.TOTAL_BYR1 = 0 )  \r\n&P1  &T1  &A1\r\n&KONDISI\r\nUNION ALL\r\n\r\n/* PENJUALAN TUNAI pembayaran I */\r\nSELECT \r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(NET_BYR1>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  c.COA_NAME,\r\n  c.COA_CODE,\r\n  c.COA_ID,c.ACCOUNT_ID ,  \'CASH1 \'  AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(  NET_BYR1>=0, NET_BYR1, 0) as DEBET,\r\n  IF(NET_BYR1<0, -1*NET_BYR1, 0) as KREDIT\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER  join V_ACCOUNT c on (a.COA_BYR1 =c.COA_ID)\r\nWHERE \r\n (  a.NET_BYR1  > 0 )\r\nAND ( a.Status_ID<>@DELETE )\r\nAND (a.TOTAL_NET > 0)\r\n&P1  &T1  &A1\r\n&KONDISI\r\nUNION ALL\r\n/* PENJUALAN TUNAI pembayaran II */\r\nSELECT\r\n  \'010001F\' AS TIPE, \r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_BYR2>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  c.COA_NAME,\r\n  c.COA_CODE,\r\n  c.COA_ID,c.ACCOUNT_ID ,  \'CASH2 \'  AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(  TOTAL_BYR2>=0, TOTAL_BYR2, 0) as DEBET,\r\n  IF(TOTAL_BYR2<0, -1*TOTAL_BYR2, 0) as KREDIT\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER Join V_ACCOUNT c on (a.COA_BYR2 =c.COA_ID)\r\n\r\nWHERE \r\n   ( a.TOTAL_BYR2   > 0 )\r\nAND ( a.Status_ID<>@DELETE )\r\nAND (a.TOTAL_NET > 0)\r\n&P1  &T1  &A1\r\n&KONDISI\r\nUNION ALL\r\n\r\n/*DISCOUNT TOTAL*/\r\nSELECT\r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_DISCOUNT>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'DISKON TOTAL \' AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(TOTAL_DISCOUNT>=0, TOTAL_DISCOUNT, 0) as DEBET,\r\n  IF(TOTAL_DISCOUNT<0, -1*TOTAL_DISCOUNT, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A,\r\n  V_ACCOUNT c\r\nWHERE   TRUE\r\n&P1  &T1  &A1\r\n&KONDISI\r\nand (c.COA_ID = @JUAL_DISC)\r\nAND TOTAL_DISCOUNT <> 0\r\n\r\n\r\nUNION ALL\r\n/*DISCOUNT PER ITEM */\r\nSELECT\r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.DISCOUNT_PRICE * B.ENTERED_QTY>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID