N (A.PAYMENT_ID = B.PAYMENT_ID)\r\n  INNER JOIN PERSON V ON (A.PERSON_ID = V.PERSON_ID)\r\n  INNER JOIN V_ACCOUNT C ON ((V.COA_HUT_ID = C.COA_ID AND B.INVOICE_DUE_ID IS NOT NULL) OR \r\n  (V.COA_PIUT_ID = C.COA_ID AND B.NOTA_PENJUALAN_AGING_ID IS NOT NULL) )\r\nWHERE (AMOUNT + B.DISCOUNT) <> 0 \r\nAND (B.COA_ID IS NULL ) \r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\nUNION ALL\r\n/* POTONG HUTANG*/\r\n\r\nSELECT\r\n  \'000101I\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(DISCOUNT>=0,0,1) AS URUT,\r\n  70 AS URUT1,\r\n  COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'POTONGAN HUTANG\' AS ITEM_DESCRIPTION ,\r\n  A.PAYMENT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(DISCOUNT<0,-1*DISCOUNT, 0)  AS DEBET,\r\n  IF(DISCOUNT>=0,DISCOUNT, 0)AS KREDIT\r\nFROM\r\nPAYMENT  A\r\nINNER JOIN PAYMENT_DETAIL B ON (A.PAYMENT_ID = B.PAYMENT_ID)\r\n,\r\nV_ACCOUNT c\r\nWHERE \r\nDISCOUNT <> 0\r\nAND (((c.COA_ID = @POT_HUTANG) AND (B.INVOICE_DUE_ID IS NOT NULL )) OR\r\n ((c.COA_ID = @POT_PIUTANG) AND (B.NOTA_PENJUALAN_AGING_ID IS NOT NULL )))\r\nAND (B.COA_ID IS NULL)\r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\nUNION ALL\r\n\r\n/*\r\n  BKK\r\nBANK\r\n*/\r\nSELECT\r\n  \'000101I\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  1 AS URUT,\r\n  70 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  COA_NAME AS ITEM_DESCRIPTION ,\r\n  A.PAYMENT_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n  0  AS DEBET,\r\n  A.TOTAL_AMOUNT KREDIT\r\nFROM\r\n  PAYMENT A\r\n  INNER JOIN V_ACCOUNT C ON (A.BANK_ID = C.COA_ID)\r\nWHERE   \r\n(A.TOTAL_AMOUNT  <> 0) \r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\n/* END OF BKK */\r\n\r\n\r\n\r\nUNION ALL\r\n/* JURNAL MEMO RIAL  */\r\nSELECT\r\n  \'000001J\' AS TIPE,\r\n\'JURNAL MEMO \' ,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  1 URUT,\r\n  80 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   a.NOTE as  ITEM_DESCRIPTION ,\r\n  a.JOURNAL_ID,\r\n  b.NOTE,   0 AS AWAL,\r\n  AMOUNT as DEBET,\r\nAMOUNT_CR as KREDIT\r\nFROM\r\n  JOURNAL a\r\n  INNER JOIN JOURNAL_detail b ON (a.JOURNAL_ID = b.JOURNAL_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_ID)\r\nWHERE\r\n (STATUS_ID <> @DELETE)\r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\n\r\nUNION ALL\r\n\r\n/* BG */ \r\n\r\n/*  BG  CAIR / TOLAK BANK TUJUAN */\r\nSELECT\r\n   IF(a.RECEIPT_ID IS NULL, \'000101K\', \'000011K\') AS TIPE,\r\n  \'PENCAIRAN BG \' ,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  IF(STATUS_FLAG = \'M\' , 0, 1)  URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  a.CHEQUE_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT ,0)as DEBET,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT,0 )as KREDIT\r\nFROM\r\n  CHEQUE a\r\n  INNER JOIN V_ACCOUNT c ON (a.BANK_ID_DEST = C.COA_ID )\r\nWHERE\r\n (STATUS_ID in (@CLEAR, @TOLAK ))\r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\nUNION ALL\r\n/* BG  CAIR / TOLAK BANK ASAL */\r\nSELECT\r\n   IF(a.RECEIPT_ID IS NULL, \'000101K\', \'000011K\') AS TIPE,\r\n  \'PENCAIRAN BG \' ,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  IF(STATUS_FLAG = \'M\' , 1, 0)  URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  a.CHEQUE_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT,0 )as DEBET,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT,0 )as KREDIT\r\nFROM\r\n  CHEQUE a\r\n  INNER JOIN V_ACCOUNT c ON (a.BANK_ID  = C.COA_ID )\r\nWHERE\r\n (STATUS_ID in  (@CLEAR, @TOLAK ))\r\n&P1  &T1  &A1\r\n&KONDISI\r\nUNION ALL\r\n\r\n/* BG  TOLAK BANK ASAL */\r\nSELECT\r\n   IF(p.RECEIPT_ID IS NULL, \'000101K\', \'000011K\') AS TIPE,\r\n  \'TOLAKAN BG \' ,\r\n  p.DOCUMENT_NO ,\r\n  p.TGL_TOLAK,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  1 URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  p.CHEQUE_ID,\r\n  p.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT,0 )as DEBET,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT,0 )as KREDIT\r\n\r\nFROM\r\n  CHEQUE p\r\n  INNER JOIN V_ACCOUNT c ON (p.BANK_ID = c.COA_ID)\r\n  INNER JOIN PERIOD a on (EXTRACT(YEAR_MONTH FROM p.tgl_tolak)=a.PERIOD_NAME)\r\nWHERE\r\n (STATUS_ID = @TOLAK)\r\n&P1  &T2  &A1\r\nUNION ALL\r\n/* BG   TOLAK BANK TUJUAN */\r\nSELECT\r\n    IF(p.RECEIPT_ID IS NULL, \'000101K\', \'000011K\') AS TIPE,\r\n  \'TOLAKAN BG \' ,\r\n  p.DOCUMENT_NO ,\r\n  p.TGL_TOLAK,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  2 URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  p.CHEQUE_ID,\r\n  p.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT  ,0 )as DEBET,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT ,0)as KREDIT\r\n\r\nFROM\r\n  CHEQUE p\r\n  INNER JOIN V_ACCOUNT c ON (p.BANK_ID_DEST = c.COA_ID)\r\n  INNER JOIN PERIOD a on (EXTRACT(YEAR_MONTH FROM p.tgl_tolak)=a.PERIOD_NAME)\r\nWHERE\r\n (STATUS_ID = @TOLAK)\r\n&P1  &T2  &A1', 77, 'QUERY JURNAL', 4, '2009-01-09 15:56:17', '2010-06-19 12:41:26', 0, NULL, 'QUERY JURNAL'),
	(20, '/* TEST SINKRON PO & BPB */\r\nSelect X.DOCUMENT_NO, A.RECEIVED_ENTERED_QTY, JML,  A.*\r\n        from\r\n         PO_DETAIL A\r\n         INNER JOIN PO X ON (A.PO_ID = X.PO_ID)\r\n         INNER JOIN\r\n         ( SELECT PO_DETAIL_ID, SUM(ENTERED_QTY * BASE_QTY) AS JML\r\n         FROM\r\n         INVENTORY_IN_DETAIL\r\n         GROUP BY PO_DETAIL_ID\r\n         ) AS B ON (A.PO_DETAIL_ID = B.PO_DETAIL_ID)\r\n        where\r\n         ABS(A.RECEIVED_ENTERED_QTY - JML) > 0.0001\r\n\r\n\r\n     /*\r\n==============\r\nSOLUSI jika banyak\r\n==============\r\n1.TRIGGER DI UBAH\r\n2. UPDATE RECEIVED_ENTERED_QTY = 0 KE PO_DETAIL\r\n3. UPDATE INVENTORY_IN_DETAIL AGAR JALAN TRIGGER NYA\r\n4. KEMBALIKAN KE SEMULA\r\n5. FINISH\r\n\r\n     */', 700, 'TES  SINKRONISASI 700 - 719', 4, NULL, NULL, 4, NULL, 'PO dgn BPB'),
	(21, '/* TEST SINKRON BPB vs INVOICE */\r\nSelect X.DOCUMENT_NO,\r\n     A.INVOICE_ENTERED_QTY ,\r\n   JML,\r\n A.*\r\n        from\r\n         INVENTORY_IN_DETAIL A\r\n         INNER JOIN INVENTORY_IN X ON (A.INVENTORY_IN_ID = X.INVENTORY_IN_ID)\r\n         INNER JOIN\r\n         ( SELECT INVENTORY_IN_DETAIL_ID, SUM(ENTERED_QTY * BASE_QTY) AS JML\r\n         FROM\r\n         INVOICE_DETAIL\r\n         GROUP BY INVENTORY_IN_DETAIL_ID\r\n         ) AS B ON (A.INVENTORY_IN_DETAIL_ID = B.INVENTORY_IN_DETAIL_ID)\r\n        where\r\n    ABS(A.INVOICE_ENTERED_QTY -  JML) > 0.0001\r\n\r\n\r\n     /*\r\n\r\n\r\n// LIAT INVENTORY_IN YG SUDAH TERISI PADAHAL BLM ADA INVOICE\r\n\r\n\r\n  Select X.DOCUMENT_NO, A.*\r\n   from\r\n    INVENTORY_IN_DETAIL A\r\n    INNER JOIN INVENTORY_IN X ON (A.INVENTORY_IN_ID = X.INVENTORY_IN_ID)\r\n    LEFT JOIN INVOICE_DETAIL  B ON (A.INVENTORY_IN_DETAIL_ID = B.INVENTORY_IN_DETAIL_ID)\r\n   where\r\n     B.INVENTORY_IN_DETAIL_ID IS NULL  AND\r\n     A.INVOICE_ENTERED_QTY > 0\r\n\r\n==============\r\nSOLUSI UTK PROBLEM 1\r\n==============\r\n1.TRIGGER DI UBAH\r\n2. UPDATE 0 KE INVENTORY_IN_DETAIL\r\n3. UPDATE INVOICE_DETAIL AGAR JALAN TRIGGER NYA\r\n4. KEMBALIKAN KE SEMULA\r\n5. FINISH\r\n\r\n\r\n1.\r\nDROP TRIGGER INVOICE_DETAIL_UPD_B|\r\n     CREATE TRIGGER INVOICE_DETAIL_UPD_B BEFORE UPDATE ON INVOICE_DETAIL\r\n     FOR EACH ROW\r\n     BEGIN\r\n       IF (NEW.Inventory_in_id IS NOT NULL) THEN\r\n         UPDATE Inventory_in_detail\r\n         set INVOICE_ENTERED_QTY = INVOICE_ENTERED_QTY + NEW.ENTERED_QTY * NEW.BASE_QTY\r\n         where (Inventory_in_detail_id = NEW.Inventory_in_detail_id) ;\r\n\r\n\r\n         CALL STATUS_INV_IN(NEW.Inventory_in_id);\r\n\r\n       END IF;\r\n\r\n       IF OLD.INVOICE_PRE_ID <> 0 THEN\r\n         uPDATE INVOICE SET UMUKA_FLAG = \'Y\' WHERE INVOICE_ID = OLD.INVOICE_PRE_ID ;\r\n       END IF;\r\n\r\n       IF NEW.INVOICE_PRE_ID <> 0 THEN\r\n         UPDATE INVOICE SET UMUKA_FLAG = \'N\' WHERE INVOICE_ID = NEW.INVOICE_PRE_ID ;\r\n       END IF;\r\n     END ;\r\n|\r\n\r\n\r\n2.\r\nUPDATE inventory_in_detail SET  INVOICE_ENTERED_QTY = 0\r\n\r\n3.\r\nUPDATE invoice_detail SET F = 7\r\n\r\n\r\n4. kembalikan lagi triggernya\r\n\r\nDROP TRIGGER INVOICE_DETAIL_UPD_B|\r\nCREATE TRIGGER INVOICE_DETAIL_UPD_B BEFORE UPDATE ON INVOICE_DETAIL\r\nFOR EACH ROW\r\nBEGIN\r\n  IF (NEW.Inventory_in_id IS NOT NULL) THEN\r\n    UPDATE Inventory_in_detail\r\n    set INVOICE_ENTERED_QTY = INVOICE_ENTERED_QTY + NEW.ENTERED_QTY * NEW.BASE_QTY\r\n    where (Inventory_in_detail_id = NEW.Inventory_in_detail_id) ;\r\n\r\n    UPDATE Inventory_in_detail\r\n    set INVOICE_ENTERED_QTY = INVOICE_ENTERED_QTY - OLD.ENTERED_QTY * OLD.BASE_QTY\r\n    where (Inventory_in_detail_id = OLD.Inventory_in_detail_id) ;\r\n\r\n    CALL STATUS_INV_IN(OLD.Inventory_in_id);\r\n    CALL STATUS_INV_IN(NEW.Inventory_in_id);\r\n\r\n  END IF;\r\n\r\n  IF OLD.INVOICE_PRE_ID <> 0 THEN\r\n    uPDATE INVOICE SET UMUKA_FLAG = \'Y\' WHERE INVOICE_ID = OLD.INVOICE_PRE_ID ;\r\n  END IF;\r\n\r\n  IF NEW.INVOICE_PRE_ID <> 0 THEN\r\n    UPDATE INVOICE SET UMUKA_FLAG = \'N\' WHERE INVOICE_ID = NEW.INVOICE_PRE_ID ;\r\n  END IF;\r\nEND ;\r\n|\r\n*/\r\n', 701, NULL, 4, NULL, NULL, 4, NULL, 'BPB dgn Invoice'),
	(22, '/* TOTAL PELUNASAN TDK SAMA DGN DI INVOICE_DUE\r\n=================================================\r\n*/\r\n SELECT A.PAID_AMOUNT , B.AMOUNT, A.* FROM \r\n  INVOICE_DUE A\r\n  LEFT JOIN \r\n  (\r\n  SELECT INVOICE_DUE_ID, SUM(AMOUNT + DISCOUNT) AMOUNT FROM \r\n  	PAYMENT_DETAIL \r\n  GROUP BY INVOICE_DUE_ID\r\n  )B ON (A.INVOICE_DUE_ID = B.INVOICE_DUE_ID)\r\n  LEFT JOIN \r\n  (\r\n  SELECT INVOICE_DUE_ID, SUM(AMOUNT + DISCOUNT) AMOUNT FROM \r\n  	RECEIPT_DETAIL \r\n  GROUP BY INVOICE_DUE_ID\r\n  )R ON (A.INVOICE_DUE_ID = R.INVOICE_DUE_ID)\r\n WHERE abs(A.PAID_AMOUNT - IFNULL(B.AMOUNT  ,0)  + IFNULL(R.AMOUNT  ,0) ) > 0.00001\r\n\r\n\r\n /*\r\nUPDATE KE BENAR\r\n=================\r\n update INVOICE_DUE  x set PAID_AMOUNT =\r\n (SELECT SUM(AMOUNT) FROM\r\n `PAYMENT` A\r\n INNER JOIN PAYMENT_DETAIL B USING (PAYMENT_ID)\r\n where (INVOICE_DUE_ID = x.INVOICE_DUE_ID)\r\n GROUP BY INVOICE_DUE_ID)\r\n */', 706, NULL, 4, NULL, '2007-11-05 11:38:08', 0, NULL, 'Invoice dgn Pelunasan Hutang (Payment)'),
	(23, '(SELECT 1 as tipe,b.po_id,b.document_no,b.status_id,sum(a.entered_qty*a.BASE_QTY),\r\n       sum(a.Received_entered_qty)\r\nFROM PO_DETAIL a, `po` b\r\nwhere a.PO_ID=b.po_id\r\nand status_id<>@New and status_id<>@Delete\r\nGROUP BY b.po_id,b.status_id\r\nhaving sum(a.Received_entered_qty)=0)\r\nUNION\r\n(SELECT 2 as tipe,b.po_id,b.document_no,b.status_id,sum(a.entered_qty*a.BASE_QTY),\r\n       sum(a.Received_entered_qty)\r\nFROM PO_DETAIL a, `po` b\r\nwhere a.PO_ID=b.po_id\r\nand status_id<>@Partial\r\nGROUP BY b.po_id,b.status_id\r\nhaving sum(a.Received_entered_qty)<sum(a.entered_qty*a.BASE_QTY)\r\nand sum(a.Received_entered_qty)>0)\r\nUNION\r\n(SELECT 3 as tipe,b.po_id,b.document_no,b.status_id,sum(a.entered_qty*a.BASE_QTY),\r\n       sum(a.Received_entered_qty)\r\nFROM PO_DETAIL a, `po` b\r\nwhere a.PO_ID=b.po_id\r\nand status_id<>@Complete\r\nGROUP BY b.po_id,b.status_id\r\nhaving sum(a.Received_entered_qty)=sum(a.entered_qty*a.BASE_QTY))', 720, 'TES STATUS', 4, NULL, NULL, 4, NULL, 'STATUS PO'),
	(24, '(SELECT \'Tot Amount\' as Tipe, a.PO_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT,\r\n  SUM(b.SUBTOTAL) as seharusnya\r\nfrom po a,po_detail b\r\nwhere a.PO_ID=b.PO_ID\r\nGROUP BY a.PO_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT\r\nHAVING abs(SUM(b.SUBTOTAL)-(a.TOTAL_AMOUNT))>0.00001)\r\nUNION\r\n(SELECT \'DISC\' as Tipe, a.PO_ID,a.DOCUMENT_NO,a.TOTAL_DISCOUNT,\r\n SUM(b.SUBTOTAL) * (TOTAL_DISCOUNT_PERCEN / 100)\r\nfrom po a,po_detail b\r\nwhere a.PO_ID=b.PO_ID\r\nand TOTAL_DISCOUNT_PERCEN>0\r\nGROUP BY a.PO_ID,a.DOCUMENT_NO,a.TOTAL_DISCOUNT,TOTAL_DISCOUNT_PERCEN\r\nHAVING ABS((SUM(b.SUBTOTAL) * (a.TOTAL_DISCOUNT_PERCEN / 100))-TOTAL_DISCOUNT)>0.00001\r\n)\r\nUNION\r\n(SELECT \'PPN\' as Tipe, a.PO_ID,a.DOCUMENT_NO,PPN_AMOUNT,\r\n ((SUM(b.SUBTOTAL) - TOTAL_DISCOUNT) * (PPN_PERCEN /100))\r\nfrom po a,po_detail b\r\nwhere a.PO_ID=b.PO_ID\r\nGROUP BY a.PO_ID,a.DOCUMENT_NO,a.TOTAL_DISCOUNT,PPN_AMOUNT,PPN_PERCEN\r\nHAVING abs(PPN_AMOUNT-((SUM(b.SUBTOTAL) - TOTAL_DISCOUNT) * (PPN_PERCEN /100))) >0.00001\r\n)\r\nUNION\r\n(SELECT \'Net\' as Tipe, a.PO_ID,a.DOCUMENT_NO,TOTAL_NET,\r\n (SUM(b.SUBTOTAL) -TOTAL_DISCOUNT +PPN_AMOUNT)\r\nfrom po a,po_detail b\r\nwhere a.PO_ID=b.PO_ID\r\nGROUP BY a.PO_ID,a.DOCUMENT_NO,a.TOTAL_DISCOUNT,PPN_AMOUNT,TOTAL_NET\r\nHAVING abs(TOTAL_NET -(SUM(b.SUBTOTAL) -TOTAL_DISCOUNT +PPN_AMOUNT)) >0.00001\r\n)', 740, 'TES TOTAL', 4, NULL, NULL, 4, NULL, 'Total PO'),
	(25, 'Select a.PO_ID,a.ITEM_ID,b.UOM_CODE,c.FROM_UOM,a.ENTERED_UOM,c.TO_UOM,\r\n  BASE_QTY,c.TO_QTY as konvGlobal,d.To_QTY as konvKhusus\r\nfrom PO_Detail a\r\ninner join item b on a.ITEM_ID=b.ITEM_ID\r\ninner join item_convertion c on b.UOM_CODE=c.FROM_UOM\r\nand a.ENTERED_UOM=c.TO_UOM\r\ninner join item_uom d on d.item_id=b.item_id\r\nand a.ENTERED_UOM=d.UOM_CODE\r\nwhere abs(a.BASE_QTY-c.TO_QTY)>0.001\r\nand abs(a.base_QTY-d.TO_QTY)>0.001', 760, 'TES KONVERSI', 4, NULL, NULL, 4, NULL, 'Konversi Satuan di PO'),
	(26, 'Select X.DOCUMENT_NO, A.RECEIVED_ENTERED_QTY, JML,  I.ITEM_DESCRIPTION, A.*\r\n        from\r\n         SO_DETAIL A	\r\n         INNER JOIN SO X ON (A.SO_ID = X.SO_ID)\r\n        INNER JOIN ITEM I ON (A.ITEM_ID = I.ITEM_ID)\r\n         INNER JOIN\r\n         ( SELECT SO_DETAIL_ID, SUM(B.ENTERED_QTY * B.BASE_QTY) AS JML\r\n         FROM\r\n         INVENTORY_OUT_DETAIL B\r\n        INNER JOIN INVENTORY_OUT A ON (B.INVENTORY_OUT_ID = A.INVENTORY_OUT_ID)\r\n         WHERE DOCUMENT_TYPE_ID < 5\r\n         GROUP BY SO_DETAIL_ID\r\n         ) AS B ON (A.SO_DETAIL_ID = B.SO_DETAIL_ID)\r\n        where\r\n         ABS(A.RECEIVED_ENTERED_QTY - JML) > 0.0001', 710, NULL, 4, NULL, NULL, 62, NULL, 'SO dgn Surat Jalan (Inventory_Out)'),
	(28, 'SELECT A.TOTAL_NET,\r\nB.DUE_AMOUNT,\r\nA.* FROM\r\n NOTA_PENJUALAN A\r\n INNER JOIN NOTA_PENJUALAN_AGING B ON (A.NOTA_PENJUALAN_ID = B.NOTA_PENJUALAN_ID)\r\n WHERE\r\n    A.TOTAL_NET <> B.DUE_AMOUNT', 712, NULL, 4, NULL, NULL, 4, NULL, 'Nota_Penjualan dgn Nota_Penjualan_Aging'),
	(29, '/* cek status SO compare antara so dan s. jalan */\r\n(SELECT 1 as tipe,b.SO_id,b.document_no,b.status_id,sum(a.entered_qty*a.BASE_QTY),\r\n       sum(a.Received_entered_qty)\r\nFROM SO_DETAIL a, SO b\r\nwhere a.SO_ID=b.SO_id\r\nand status_id NOT IN (@new ,@Delete, @CLOSED)\r\nGROUP BY b.SO_id,b.status_id\r\nhaving sum(a.Received_entered_qty)=0)\r\nUNION\r\n(SELECT 2 as tipe,b.SO_id,b.document_no,b.status_id,sum(a.entered_qty*a.BASE_QTY),\r\n       sum(a.Received_entered_qty)\r\nFROM SO_DETAIL a, SO b\r\nwhere a.SO_ID=b.SO_id\r\nand status_id NOT IN (@Partial, @CLOSED)\r\nGROUP BY b.SO_id,b.status_id\r\nhaving sum(a.Received_entered_qty)<sum(a.entered_qty*a.BASE_QTY)\r\nand sum(a.Received_entered_qty)>0)\r\nUNION\r\n(SELECT 3 as tipe,b.SO_id,b.document_no,b.status_id,sum(a.entered_qty*a.BASE_QTY),\r\n       sum(a.Received_entered_qty)\r\nFROM SO_DETAIL a, SO b\r\nwhere a.SO_ID=b.SO_id\r\nand status_id NOT IN (@complete, @CLOSED)\r\nGROUP BY b.SO_id,b.status_id\r\nhaving sum(a.Received_entered_qty)=sum(a.entered_qty*a.BASE_QTY))', 721, NULL, 4, NULL, '2007-11-05 14:29:58', 0, NULL, 'STATUS SO'),
	(30, '(SELECT 1 as tipe,b.INVENTORY_IN_ID,\r\n  b.document_no,b.status_id,sum(a.entered_qty*a.BASE_QTY),\r\n       sum(a.Invoice_entered_qty)\r\nFROM INVENTORY_IN_DETAIL a, INVENTORY_IN b\r\nwhere a.INVENTORY_IN_ID=b.INVENTORY_IN_id\r\nand status_id<>@New and status_id<>@Delete\r\nGROUP BY b.INVENTORY_IN_ID,b.status_id\r\nhaving sum(a.Invoice_entered_qty)=0)\r\nUNION\r\n(SELECT 2 as tipe,b.INVENTORY_IN_ID,\r\n  b.document_no,b.status_id,sum(a.entered_qty*a.BASE_QTY),\r\n       sum(a.Invoice_entered_qty)\r\nFROM INVENTORY_IN_DETAIL a, INVENTORY_IN b\r\nwhere a.INVENTORY_IN_ID=b.INVENTORY_IN_id\r\nand status_id<>@Partial\r\nGROUP BY b.INVENTORY_IN_ID,b.status_id\r\nhaving (ABS(sum(a.Invoice_entered_qty) - sum(a.entered_qty*a.BASE_QTY)) > 0.0001)\r\nand sum(a.Invoice_entered_qty)>0)\r\nUNION\r\n(SELECT 3 as tipe,b.INVENTORY_IN_ID,\r\n  b.document_no,b.status_id,sum(a.entered_qty*a.BASE_QTY),\r\n       sum(a.Invoice_entered_qty)\r\nFROM INVENTORY_IN_DETAIL a, INVENTORY_IN b\r\nwhere a.INVENTORY_IN_ID=b.INVENTORY_IN_id\r\nand status_id<>@Complete\r\nGROUP BY b.INVENTORY_IN_ID,b.status_id\r\nhaving sum(a.Invoice_entered_qty)=sum(a.entered_qty*a.BASE_QTY))', 722, NULL, 4, NULL, '2007-07-03 12:18:48', 4, NULL, 'STATUS BPB (Inventory_IN)'),
	(31, '(SELECT 1 as tipe,b.INVENTORY_OUT_ID,\r\n  b.document_no,b.status_id,sum(a.entered_qty*a.BASE_QTY),\r\n       sum(a.Invoice_entered_qty)\r\nFROM INVENTORY_OUT_DETAIL a, INVENTORY_OUT b\r\nwhere a.INVENTORY_OUT_ID=b.INVENTORY_OUT_id\r\nand status_id<>@New and status_id<>@Delete\r\nGROUP BY b.INVENTORY_OUT_ID,b.status_id\r\nhaving sum(a.Invoice_entered_qty)=0)\r\nUNION\r\n(SELECT 2 as tipe,b.INVENTORY_OUT_ID,\r\n  b.document_no,b.status_id,sum(a.entered_qty*a.BASE_QTY),\r\n       sum(a.Invoice_entered_qty)\r\nFROM INVENTORY_OUT_DETAIL a, INVENTORY_OUT b\r\nwhere a.INVENTORY_OUT_ID=b.INVENTORY_OUT_id\r\nand status_id<>@Partial\r\nGROUP BY b.INVENTORY_OUT_ID,b.status_id\r\nhaving (sum(a.Invoice_entered_qty)- sum(a.entered_qty*a.BASE_QTY) > 0.0001)\r\nand sum(a.Invoice_entered_qty)>0)\r\nUNION\r\n(SELECT 3 as tipe,b.INVENTORY_OUT_ID,\r\n  b.document_no,b.status_id,sum(a.entered_qty*a.BASE_QTY),\r\n       sum(a.Invoice_entered_qty)\r\nFROM INVENTORY_OUT_DETAIL a, INVENTORY_OUT b\r\nwhere a.INVENTORY_OUT_ID=b.INVENTORY_OUT_id\r\nand status_id<>@Complete\r\nGROUP BY b.INVENTORY_OUT_ID,b.status_id\r\nhaving sum(a.Invoice_entered_qty)=sum(a.entered_qty*a.BASE_QTY))', 723, NULL, 4, NULL, NULL, 4, NULL, 'STATUS Surat Jalan (Inventory_OUT)'),
	(32, '(SELECT 1 as Tipe, c.INVOICE_ID,c.document_no,c.status_ID,a.DUE_AMOUNT,\r\na.PAID_AMOUNT, (a.DUE_AMOUNT - a.PAID_AMOUNT) as sisa\r\nFROM  invoice_due a,Payment_detail b, invoice c\r\nWHERE (a.INVOICE_DUE_ID = b.invoice_due_ID)\r\nand (a.invoice_ID=c.Invoice_ID)\r\nand a.PAID_AMOUNT=0\r\nand c.Status_ID<>@New and c.Status_ID<>@DELETE)\r\nUNION\r\n(SELECT 2 as Tipe, c.INVOICE_ID,c.document_no,c.status_ID,a.DUE_AMOUNT,\r\na.PAID_AMOUNT, (a.DUE_AMOUNT - a.PAID_AMOUNT) as sisa\r\nFROM  invoice_due a,Payment_detail b, invoice c\r\nWHERE (a.INVOICE_DUE_ID = b.invoice_due_ID)\r\nand (a.invoice_ID=c.Invoice_ID)\r\nand a.PAID_AMOUNT>0 and (a.DUE_AMOUNT - a.PAID_AMOUNT)>0\r\nand c.Status_ID<>@PARTIAL_PAID )\r\nUNION\r\n(SELECT 3 as Tipe, c.INVOICE_ID,c.document_no,c.status_ID,a.DUE_AMOUNT,\r\na.PAID_AMOUNT, (a.DUE_AMOUNT - a.PAID_AMOUNT) as sisa\r\nFROM  invoice_due a,Payment_detail b, invoice c\r\nWHERE (a.INVOICE_DUE_ID = b.invoice_due_ID)\r\nand (a.invoice_ID=c.Invoice_ID)\r\nand a.PAID_AMOUNT>0 and (a.DUE_AMOUNT - a.PAID_AMOUNT)=0\r\nand c.Status_ID<>@FULL_PAID )', 724, NULL, 4, NULL, NULL, 4, NULL, 'STATUS Invoice'),
	(33, '(SELECT 1 as Tipe, c.NOTA_PENJUALAN_ID,c.document_no,c.status_ID,a.DUE_AMOUNT,\r\na.RECEIVED_AMOUNT, (a.DUE_AMOUNT - a.RECEIVED_AMOUNT)  as sisa\r\nFROM  \r\nnota_penjualan c\r\nINNER JOIN nota_penjualan_aging a ON (c.NOTA_PENJUALAN_ID = a.NOTA_PENJUALAN_ID)\r\nLEFT JOIN receipt_detail b ON (a.NOTA_PENJUALAN_AGING_ID = b.NOTA_PENJUALAN_AGING_ID) \r\nWHERE \r\na.RECEIVED_AMOUNT=0\r\nand c.Status_ID<>@NEW and c.Status_ID<>@DELETE)\r\nUNION\r\n\r\n(SELECT 2 as Tipe, c.NOTA_PENJUALAN_ID,c.document_no,c.status_ID,a.DUE_AMOUNT,\r\na.RECEIVED_AMOUNT, (a.DUE_AMOUNT - a.RECEIVED_AMOUNT)  as sisa\r\nFROM  nota_penjualan_aging a, receipt_detail b, nota_penjualan c\r\nWHERE (a.NOTA_PENJUALAN_AGING_ID = b.NOTA_PENJUALAN_AGING_ID)\r\nand (a.NOTA_PENJUALAN_ID=c.NOTA_PENJUALAN_ID)\r\nand a.RECEIVED_AMOUNT>0 and (a.DUE_AMOUNT - a.RECEIVED_AMOUNT)>0\r\nand c.Status_ID<>@PARTIAL_PAID )\r\nUNION\r\n(SELECT 3 as Tipe, c.NOTA_PENJUALAN_ID,c.document_no,c.status_ID,a.DUE_AMOUNT,\r\na.RECEIVED_AMOUNT, (a.DUE_AMOUNT - a.RECEIVED_AMOUNT)  as sisa\r\nFROM  nota_penjualan_aging a, receipt_detail b, nota_penjualan c\r\nWHERE (a.NOTA_PENJUALAN_AGING_ID = b.NOTA_PENJUALAN_AGING_ID)\r\nand (a.NOTA_PENJUALAN_ID=c.NOTA_PENJUALAN_ID)\r\nand a.RECEIVED_AMOUNT>0 and (a.DUE_AMOUNT - a.RECEIVED_AMOUNT)=0\r\nand c.Status_ID<>@FULL_PAID )', 725, NULL, 4, NULL, '2007-08-28 18:10:38', 1, NULL, 'STATUS Nota_Penjualan'),
	(34, '(SELECT \'Tot Amount\' as Tipe, a.SO_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT,\r\n  SUM(b.SUBTOTAL) as seharusnya\r\nfrom SO a,SO_detail b\r\nwhere a.SO_ID=b.SO_ID\r\nGROUP BY a.SO_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT\r\nHAVING abs(SUM(b.SUBTOTAL)-(a.TOTAL_AMOUNT))>0.00001)\r\nUNION\r\n(SELECT \'DISC\' as Tipe, a.SO_ID,a.DOCUMENT_NO,a.TOTAL_DISCOUNT,\r\n SUM(b.SUBTOTAL) * (TOTAL_DISCOUNT_PERCEN / 100)\r\nfrom SO a,SO_detail b\r\nwhere a.SO_ID=b.SO_ID\r\nand TOTAL_DISCOUNT_PERCEN>0\r\nGROUP BY a.SO_ID,a.DOCUMENT_NO,a.TOTAL_DISCOUNT,TOTAL_DISCOUNT_PERCEN\r\nHAVING ABS((SUM(b.SUBTOTAL) * (a.TOTAL_DISCOUNT_PERCEN / 100))-TOTAL_DISCOUNT)>0.00001\r\n)\r\nUNION\r\n(SELECT \'PPN\' as Tipe, a.SO_ID,a.DOCUMENT_NO,PPN_AMOUNT,\r\n ((SUM(b.SUBTOTAL) - TOTAL_DISCOUNT) * (PPN_PERCEN /100))\r\nfrom SO a,SO_detail b\r\nwhere a.SO_ID=b.SO_ID\r\nGROUP BY a.SO_ID,a.DOCUMENT_NO,a.TOTAL_DISCOUNT,PPN_AMOUNT,PPN_PERCEN\r\nHAVING abs(PPN_AMOUNT-((SUM(b.SUBTOTAL) - TOTAL_DISCOUNT) * (PPN_PERCEN /100))) >0.00001\r\n)\r\nUNION\r\n(SELECT \'Net\' as Tipe, a.SO_ID,a.DOCUMENT_NO,TOTAL_NET,\r\n (SUM(b.SUBTOTAL) -TOTAL_DISCOUNT +PPN_AMOUNT)\r\nfrom SO a,SO_detail b\r\nwhere a.SO_ID=b.SO_ID\r\nGROUP BY a.SO_ID,a.DOCUMENT_NO,a.TOTAL_DISCOUNT,PPN_AMOUNT,TOTAL_NET\r\nHAVING abs(TOTAL_NET -(SUM(b.SUBTOTAL) -TOTAL_DISCOUNT +PPN_AMOUNT)) >0.00001\r\n)', 741, NULL, 4, NULL, NULL, 4, NULL, 'Total SO'),
	(35, 'SELECT a.INVENTORY_IN_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT,\r\n  SUM(b.SUBTOTAL)\r\nfrom inventory_in a,inventory_in_detail b\r\nwhere a.INVENTORY_IN_ID=b.INVENTORY_IN_ID\r\nGROUP BY a.INVENTORY_IN_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT\r\nHAVING abs(SUM(b.SUBTOTAL)-(a.TOTAL_AMOUNT))>0.00001', 742, NULL, 4, NULL, NULL, 4, NULL, 'Total Inventory_IN'),
	(36, 'SELECT a.INVENTORY_OUT_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT,\r\n  SUM(b.SUBTOTAL)\r\nfrom inventory_OUT a,inventory_OUT_detail b\r\nwhere a.INVENTORY_OUT_ID=b.INVENTORY_OUT_ID\r\nGROUP BY a.INVENTORY_OUT_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT\r\nHAVING abs(SUM(b.SUBTOTAL)-(a.TOTAL_AMOUNT))>0.00001', 743, NULL, 4, NULL, NULL, 4, NULL, 'Total Inventory_OUT'),
	(37, '(SELECT \'Tot Amount\' as Tipe, a.INVOICE_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT,\r\n  SUM(b.SUBTOTAL) as seharusnya\r\nfrom INVOICE a,INVOICE_detail b\r\nwhere a.INVOICE_ID=b.INVOICE_ID\r\nGROUP BY a.INVOICE_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT\r\nHAVING abs(SUM(b.SUBTOTAL)-(a.TOTAL_AMOUNT))>0.00001)\r\nUNION\r\n(SELECT \'DISC\' as Tipe, a.INVOICE_ID,a.DOCUMENT_NO,a.TOTAL_DISCOUNT,\r\n SUM(b.SUBTOTAL) * (TOTAL_DISCOUNT_PERCEN / 100)\r\nfrom INVOICE a,INVOICE_detail b\r\nwhere a.INVOICE_ID=b.INVOICE_ID\r\nand TOTAL_DISCOUNT_PERCEN>0\r\nGROUP BY a.INVOICE_ID,a.DOCUMENT_NO,a.TOTAL_DISCOUNT,TOTAL_DISCOUNT_PERCEN\r\nHAVING ABS((SUM(b.SUBTOTAL) * (a.TOTAL_DISCOUNT_PERCEN / 100))-TOTAL_DISCOUNT)>0.00001\r\n)\r\nUNION\r\n(SELECT \'PPN\' as Tipe, a.INVOICE_ID,a.DOCUMENT_NO,PPN_AMOUNT,\r\n FLOOR((SUM(b.SUBTOTAL) - TOTAL_DISCOUNT) * (PPN_PERCEN /100))\r\nfrom INVOICE a,INVOICE_detail b\r\nwhere a.INVOICE_ID=b.INVOICE_ID\r\nGROUP BY a.INVOICE_ID,a.DOCUMENT_NO,a.TOTAL_DISCOUNT,PPN_AMOUNT,PPN_PERCEN\r\nHAVING abs(PPN_AMOUNT-FLOOR((SUM(b.SUBTOTAL) - TOTAL_DISCOUNT) * (PPN_PERCEN /100))) >0.00001\r\n)\r\nUNION\r\n(SELECT \'Net\' as Tipe, a.INVOICE_ID,a.DOCUMENT_NO,TOTAL_NET,\r\n (SUM(b.SUBTOTAL) -TOTAL_DISCOUNT +PPN_AMOUNT)\r\nfrom INVOICE a,INVOICE_detail b\r\nwhere a.INVOICE_ID=b.INVOICE_ID\r\nGROUP BY a.INVOICE_ID,a.DOCUMENT_NO,a.TOTAL_DISCOUNT,PPN_AMOUNT,TOTAL_NET\r\nHAVING abs(TOTAL_NET -(SUM(b.SUBTOTAL) -TOTAL_DISCOUNT +PPN_AMOUNT)) >0.00001)', 744, NULL, 4, NULL, NULL, 4, NULL, 'Total Invoice'),
	(38, '(SELECT \'Tot Amount\' as Tipe, a.NOTA_PENJUALAN_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT,\r\n  SUM(b.SUBTOTAL) as seharusnya\r\nfrom NOTA_PENJUALAN a,NOTA_PENJUALAN_detail b\r\nwhere a.NOTA_PENJUALAN_ID=b.NOTA_PENJUALAN_ID\r\nGROUP BY a.NOTA_PENJUALAN_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT\r\nHAVING abs(SUM(b.SUBTOTAL)-(a.TOTAL_AMOUNT))>0.00001)\r\nUNION\r\n(SELECT \'DISC\' as Tipe, a.NOTA_PENJUALAN_ID,a.DOCUMENT_NO,a.TOTAL_DISCOUNT,\r\n SUM(b.SUBTOTAL) * (TOTAL_DISCOUNT_PERCEN / 100)\r\nfrom NOTA_PENJUALAN a,NOTA_PENJUALAN_detail b\r\nwhere a.NOTA_PENJUALAN_ID=b.NOTA_PENJUALAN_ID\r\nand TOTAL_DISCOUNT_PERCEN>0\r\nGROUP BY a.NOTA_PENJUALAN_ID,a.DOCUMENT_NO,a.TOTAL_DISCOUNT,TOTAL_DISCOUNT_PERCEN\r\nHAVING ABS((SUM(b.SUBTOTAL) * (a.TOTAL_DISCOUNT_PERCEN / 100))-TOTAL_DISCOUNT)>0.00001\r\n)\r\nUNION\r\n(SELECT \'PPN\' as Tipe, a.NOTA_PENJUALAN_ID,a.DOCUMENT_NO,PPN_AMOUNT,\r\n ((SUM(b.SUBTOTAL) - TOTAL_DISCOUNT) * (PPN_PERCEN /100))\r\nfrom NOTA_PENJUALAN a,NOTA_PENJUALAN_detail b\r\nwhere a.NOTA_PENJUALAN_ID=b.NOTA_PENJUALAN_ID\r\nGROUP BY a.NOTA_PENJUALAN_ID,a.DOCUMENT_NO,a.TOTAL_DISCOUNT,PPN_AMOUNT,PPN_PERCEN\r\nHAVING abs(PPN_AMOUNT-((SUM(b.SUBTOTAL) - TOTAL_DISCOUNT) * (PPN_PERCEN /100))) >0.00001\r\n)\r\nUNION\r\n(SELECT \'Net\' as Tipe, a.NOTA_PENJUALAN_ID,a.DOCUMENT_NO,TOTAL_NET,\r\n (SUM(b.SUBTOTAL) -TOTAL_DISCOUNT +PPN_AMOUNT)\r\nfrom NOTA_PENJUALAN a,NOTA_PENJUALAN_detail b\r\nwhere a.NOTA_PENJUALAN_ID=b.NOTA_PENJUALAN_ID\r\nGROUP BY a.NOTA_PENJUALAN_ID,a.DOCUMENT_NO,a.TOTAL_DISCOUNT,PPN_AMOUNT,TOTAL_NET\r\nHAVING abs(TOTAL_NET -(SUM(b.SUBTOTAL) -TOTAL_DISCOUNT +PPN_AMOUNT)) >0.00001)', 745, NULL, 4, NULL, NULL, 4, NULL, 'Total Nota_Penjualan'),
	(39, 'SELECT a.PAYMENT_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT,\r\n  SUM(b.AMOUNT)\r\nfrom PAYMENT a,PAYMENT_detail b\r\nwhere a.PAYMENT_ID=b.PAYMENT_ID\r\nGROUP BY a.PAYMENT_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT\r\nHAVING abs(SUM(b.AMOUNT)-(a.TOTAL_AMOUNT))>0.00001', 746, NULL, 4, NULL, NULL, 4, NULL, 'Total Payment'),
	(40, 'SELECT a.RECEIPT_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT,\r\n  SUM(b.AMOUNT)\r\nfrom RECEIPT a,RECEIPT_detail b\r\nwhere a.RECEIPT_ID=b.RECEIPT_ID\r\nGROUP BY a.RECEIPT_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT\r\nHAVING abs(SUM(b.AMOUNT)-(a.TOTAL_AMOUNT))>0.00001', 747, NULL, 4, NULL, NULL, 4, NULL, 'Total Receipt'),
	(41, '\r\nSELECT a.JOURNAL_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT,\r\n  SUM(b.AMOUNT)\r\nfrom JOURNAL a,JOURNAL_detail b\r\nwhere a.JOURNAL_ID=b.JOURNAL_ID\r\nGROUP BY a.JOURNAL_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT\r\nHAVING abs(SUM(b.AMOUNT)-(a.TOTAL_AMOUNT))>0.00001', 748, NULL, 4, NULL, NULL, 4, NULL, 'Total Jurnal'),
	(42, 'SELECT a.BUILD_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT,\r\n  SUM(b.SUBTOTAL)\r\nfrom BUILD a,BUILD_detail b\r\nwhere a.BUILD_ID=b.BUILD_ID\r\nGROUP BY a.BUILD_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT\r\nHAVING abs(SUM(b.SUBTOTAL)-(a.TOTAL_AMOUNT))>0.00001', 749, NULL, 4, NULL, NULL, 4, NULL, 'Total Build'),
	(43, 'SELECT a.ADJUST_QTY_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT,\r\n  SUM(b.SUBTOTAL)\r\nfrom ADJUST_QTY a,ADJUST_QTY_detail b\r\nwhere a.ADJUST_QTY_ID=b.ADJUST_QTY_ID\r\nGROUP BY a.ADJUST_QTY_ID,a.DOCUMENT_NO,a.TOTAL_AMOUNT\r\nHAVING abs(SUM(b.SUBTOTAL)-(a.TOTAL_AMOUNT))>0.00001', 750, NULL, 4, NULL, NULL, 4, NULL, 'Total Adjustment'),
	(44, 'Select X.DOCUMENT_NO,\r\n     A.INVOICE_ENTERED_QTY ,\r\n   JML,\r\n A.*\r\n        from\r\n         INVENTORY_OUT_DETAIL A\r\n         INNER JOIN INVENTORY_OUT X ON (A.INVENTORY_OUT_ID = X.INVENTORY_OUT_ID)\r\n         INNER JOIN\r\n         ( SELECT INVENTORY_OUT_DETAIL_ID, SUM(ENTERED_QTY * BASE_QTY) AS JML\r\n         FROM\r\n         NOTA_PENJUALAN_DETAIL\r\n         GROUP BY INVENTORY_OUT_DETAIL_ID\r\n         ) AS B ON (A.INVENTORY_OUT_DETAIL_ID = B.INVENTORY_OUT_DETAIL_ID)\r\n        where\r\n    ABS(A.INVOICE_ENTERED_QTY -  JML) > 0.0001\r\n\r\n/* LIAT INVENTORY_OUT YG SUDAH TERISI PADAHAL BLM ADA NOTA_PENJUALAN\r\n     Select X.DOCUMENT_NO, A.*\r\n   from\r\n    INVENTORY_OUT_DETAIL A\r\n    INNER JOIN INVENTORY_OUT X ON (A.INVENTORY_OUT_ID = X.INVENTORY_OUT_ID)\r\n    LEFT JOIN NOTA_PENJUALAN_DETAIL  B ON (A.INVENTORY_OUT_DETAIL_ID = B.INVENTORY_OUT_DETAIL_ID)\r\n   where\r\n     B.INVENTORY_OUT_DETAIL_ID IS NULL  AND\r\n     A.INVOICE_ENTERED_QTY > 0\r\n*/', 711, NULL, 4, NULL, NULL, 4, NULL, 'Surat Jalan ( Inventory_out ) dgn Nota_Penjualan'),
	(45, '/* TEST SINKRON INVOICE & INVOICE_DUE */\r\nSELECT A.TOTAL_NET,\r\nB.DUE_AMOUNT,\r\nA.* FROM\r\n INVOICE A\r\n INNER JOIN INVOICE_DUE B ON (A.INVOICE_ID = B.INVOICE_ID)\r\n WHERE\r\n    A.TOTAL_NET <> B.DUE_AMOUNT', 703, NULL, 4, NULL, NULL, 4, NULL, 'Invoice dgn Invoice_Due'),
	(46, '/* TOTAL PELUNASAN TDK SAMA DGN DI NOTA_PENJUALAN_AGING\r\n=================================================\r\n*/\r\n\r\n SELECT RECEIVED_AMOUNT, YY.JUMLAH, R.JUMLAH, X.* FROM\r\n NOTA_PENJUALAN_AGING X\r\nLEFT JOIN (\r\n SELECT B.NOTA_PENJUALAN_AGING_ID, SUM(AMOUNT + DISCOUNT) AS JUMLAH FROM\r\n `receipt` A\r\n INNER JOIN RECEIPT_DETAIL B USING (receipt_ID)\r\n GROUP BY NOTA_PENJUALAN_AGING_ID\r\n )\r\n AS YY USING (NOTA_PENJUALAN_AGING_ID)\r\nLEFT JOIN (\r\n SELECT B.NOTA_PENJUALAN_AGING_ID, SUM(AMOUNT + DISCOUNT) AS JUMLAH FROM\r\n PAYMENT A\r\n INNER JOIN PAYMENT_DETAIL B USING (PAYMENT_ID)\r\n GROUP BY NOTA_PENJUALAN_AGING_ID\r\n )\r\n AS R ON (X.NOTA_PENJUALAN_AGING_ID = R.NOTA_PENJUALAN_AGING_ID)\r\n\r\nWHERE ABS( RECEIVED_AMOUNT -  IFNULL(YY.JUMLAH,0) +  IFNULL(R.JUMLAH,0) )   > 0.00001\r\n /*\r\n === update ke yg benar\r\n update NOTA_PENJUALAN_AGING  x set RECEIVED_AMOUNT =\r\n (SELECT SUM(AMOUNT) FROM\r\n `receipt` A\r\n INNER JOIN RECEIPT_DETAIL B USING (receipt_ID)\r\n where (NOTA_PENJUALAN_AGING_ID = x.NOTA_PENJUALAN_AGING_ID)\r\n GROUP BY NOTA_PENJUALAN_AGING_ID\r\n)\r\n*/\r\n\r\n', 715, NULL, 4, NULL, '2007-11-05 14:16:52', 0, NULL, 'Nota  Penjualan dgn Pelunasan'),
	(47, '/*\r\ncek periode penjualan yg salah periode\r\n==============================\r\n*/\r\nSELECT  A.DOCUMENT_NO, A.* FROM NOTA_PENJUALAN A\r\n  INNER JOIN PERIOD B ON (A.PERIOD_NAME = B.PERIOD_NAME)\r\nWHERE  NOT\r\n( date_format(a.DOCUMENT_DATE, \'%Y-%m-%d\')   BETWEEN \r\ndate_format(B.START_DATE, \'%Y-%m-%d\')   AND\r\ndate_format(B.END_DATE, \'%Y-%m-%d\')   )\r\n\r\n/* \r\nupdate periode penjualan yg salah periode\r\n======================\r\n\r\nUPDATE NOTA_PENJUALAN A SET PERIOD_NAME =\r\n (SELECT PERIOD_NAME\r\nFROM PERIOD B\r\nWHERE (A.DOCUMENT_DATE BETWEEN B.START_DATE AND B.END_DATE))\r\n\r\n\r\nupdate periode INVENTORY_OUT yg salah periode\r\n======================\r\n  UPDATE INVENTORY_OUT A SET PERIOD_NAME =\r\n   (SELECT PERIOD_NAME\r\n  FROM PERIOD B\r\nWHERE (A.DOCUMENT_DATE BETWEEN B.START_DATE AND B.END_DATE))\r\n\r\n\r\nupdate periode\r\n========================\r\nUPDATE  PERIOD SET END_DATE = DATE_ADD(END_DATE , INTERVAL \'23:59\' HOUR_MINUTE)\r\n\r\n\r\nSELECT DATE_ADD(END_DATE , INTERVAL \'23:59\' HOUR_MINUTE\r\n) FROM PERIOD;\r\n\r\n\r\nCEK PERIODE YG BEDA ANTAR FAKTUR DAN S.JALAN\r\n==============\r\nSELECT B.PERIOD_NAME, A.* FROM NOTA_PENJUALAN A\r\n  INNER JOIN NOTA_PENJUALAN_DETAIL X ON (A.NOTA_PENJUALAN_ID = X.NOTA_PENJUALAN_ID)\r\n  INNER JOIN INVENTORY_OUT B ON (X.INVENTORY_OUT_ID =\r\nB.INVENTORY_OUT_ID)\r\nWHERE\r\n(A.PERIOD_NAME <> B.PERIOD_NAME)\r\nAND A.PERIOD_NAME = \'2007-01\'\r\n\r\n\r\nUPDATE NOTA_PENJUALAN A SET DOCUMENT_DATE =\r\n ( SELECT DOCUMENT_DATE FROM INVENTORY_OUT B\r\n   INNER JOIN NOTA_PENJUALAN_DETAIL X ON (X.INVENTORY_OUT_ID =\r\nB.INVENTORY_OUT_ID)\r\n  WHERE X.NOTA_PENJUALAN_ID = A.NOTA_PENJUALAN_ID)\r\n\r\nGANTI TANGGAL YG SALAH\r\n=================\r\nUPDATE NOTA_PENJUALAN A SET DOCUMENT_DATE =\r\n ( SELECT MIN(DOCUMENT_DATE) FROM INVENTORY_OUT B\r\n   INNER JOIN NOTA_PENJUALAN_DETAIL X ON (X.INVENTORY_OUT_ID =\r\nB.INVENTORY_OUT_ID)\r\n  WHERE X.NOTA_PENJUALAN_ID = A.NOTA_PENJUALAN_ID\r\nGROUP BY NOTA_PENJUALAN_ID\r\n)\r\nWHERE INVOICE_TYPE_ID = 230047\r\nAND A.NOTA_PENJUALAN_ID IN\r\n ( SELECT  X.NOTA_PENJUALAN_ID  FROM INVENTORY_OUT B\r\n   INNER JOIN NOTA_PENJUALAN_DETAIL X ON (X.INVENTORY_OUT_ID =\r\nB.INVENTORY_OUT_ID )\r\n  )\r\n*/\r\n\r\n', 780, 'TEST & PERBAIKAN PERIODE', 4, NULL, '2007-11-05 15:16:54', 0, NULL, NULL),
	(48, '/* TEST KONVERSI SO */\r\nSelect S.DOCUMENT_NO , B.ITEM_DESCRIPTION ,\r\na.SO_ID,a.ITEM_ID,b.UOM_CODE,c.FROM_UOM,a.ENTERED_UOM,c.TO_UOM,\r\n  BASE_QTY,c.TO_QTY as konvGlobal,d.To_QTY as konvKhusus,\r\n  abs(a.BASE_QTY-c.TO_QTY),abs(a.base_QTY-d.TO_QTY)\r\nfrom SO_Detail a\r\ninner join SO s on (a.SO_ID = s.SO_ID)\r\ninner join item b on a.ITEM_ID=b.ITEM_ID\r\ninner join item_convertion c on b.UOM_CODE=c.FROM_UOM\r\nand a.ENTERED_UOM=c.TO_UOM\r\ninner join item_uom d on d.item_id=b.item_id\r\nand a.ENTERED_UOM=d.UOM_CODE\r\nwhere abs(a.BASE_QTY-c.TO_QTY)>0.001\r\nand abs(a.base_QTY-d.TO_QTY)>0.001', 761, NULL, 4, NULL, NULL, 4, NULL, 'Konversi Satuan di SO'),
