	(63, '/*\r\n&P1  = PERIODE\r\n&T1  = TANGGAL\r\n&A1 = ACCOUNT\r\n*/\r\n\r\n/*saldo awal */\r\nselect\r\n  \'SALDO AWAL\' AS DOCUMENT_CLASS_CODE,\r\n  \' \' AS DOCUMENT_NO ,\r\n  \' \' AS  DOCUMENT_DATE,\r\n  \' \' AS DOCUMENT_REFF_NO ,\r\n  0  AS URUT,\r\n  1 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,\r\nC.ACCOUNT_ID ,\r\n \' \'  ITEM_DESCRIPTION ,\r\n  0 AS ID,\r\n  \' SALDO AWAL \' AS NOTE,\r\n   COA_SALDO AS AWAL,\r\n  0 AS DEBET,\r\n  0 AS KREDIT\r\nFROM\r\n  COA_BALANCE a\r\n  INNER join V_ACCOUNT c on (a.COA_ID = c.COA_id)\r\nWHERE TRUE \r\n&X1  \r\n&P1\r\n&A1\r\nUNION ALL \r\n\r\n/*---- INVENTORY_IN*/\r\n/* persediaan */\r\nselect\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,0 ,1)as URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.INVENTORY_IN_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(b.SUBTOTAL>=0,b.SUBTOTAL ,0) AS DEBET,\r\n  IF(b.SUBTOTAL<0,b.SUBTOTAL *-1 ,0) AS KREDIT\r\nFROM\r\n  INVENTORY_IN a\r\n  INNER JOIN INVENTORY_IN_DETAIL B on (a.INVENTORY_IN_ID = B.INVENTORY_IN_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  LEFT  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nwhere\r\n(STATUS_ID <> @DELETE)\r\nAND B.SUBTOTAL <> 0\r\n&P1  &T1  &A1\r\n&KONDISI\r\nUNION ALL \r\n/*  hutang suspend */\r\n\r\nselect\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  if(SUBTOTAL>=0, 1,0) AS URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,\r\n I.ITEM_DESCRIPTION ,\r\n  a.INVENTORY_IN_ID,\r\n  a.NOTE,\r\n  0 AS AWAL,\r\n  if(SUBTOTAL<0, -1*SUBTOTAL,0) AS DEBET,\r\n  if(SUBTOTAL>=0, SUBTOTAL,0) AS KREDIT\r\nFROM\r\n  INVENTORY_IN a\r\n  INNER JOIN INVENTORY_IN_DETAIL B on (a.INVENTORY_IN_ID = B.INVENTORY_IN_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  LEFT  join V_ACCOUNT c on (I.COA_SUSPEND_ID  = c.COA_id)\r\n\r\nWHERE\r\n (STATUS_ID <> @DELETE)\r\n&P1  &T1  &A1\r\n&KONDISI\r\n/* END INVENTORY_IN */\r\n\r\nUNION ALL\r\n/* INVOICE\r\n biaya-biaya yang ada di detail\r\n Biaya   1000\r\n ppn      90\r\n diskon        100\r\n hutang        990\r\n\r\n */\r\nSELECT\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,0,1) AS URUT,\r\n  20 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  b.ITEM_DESCRIPTION ,\r\n  a.INVOICE_ID,\r\n  b.NOTE,   0 AS AWAL,\r\n  IF(b.SUBTOTAL>=0,(b.SUBTOTAL),0)  as DEBET,\r\n  IF(b.SUBTOTAL<0,-1*(b.SUBTOTAL),0)  as KREDIT\r\nFROM\r\n  invoice a\r\n  INNER JOIN invoice_detail b ON (a.INVOICE_ID = b.INVOICE_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_ID)\r\nWHERE\r\n (STATUS_ID <> @DELETE)\r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\nUNION ALL\r\n/* INVOICE\r\n/*PPN*/\r\nSELECT\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(PPN_AMOUNT>=0,0,1) AS URUT,\r\n  20 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'PPN \' AS ITEM_DESCRIPTION ,\r\n  a.INVOICE_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n    IF(PPN_AMOUNT>0, PPN_AMOUNT, 0) as DEBET,\r\n  IF(PPN_AMOUNT<0, -1*PPN_AMOUNT, 0) as KREDIT\r\n\r\nFROM\r\n  invoice a,\r\n  V_ACCOUNT c\r\nWHERE TRUE\r\nand (c.COA_ID = @BELI_PPN)\r\nAND PPN_AMOUNT <> 0\r\nAND  (STATUS_ID <> @DELETE)\r\n&P1  &T1  &A1\r\n&KONDISI\r\nUNION ALL\r\n/* INVOICE\r\n/*DISCOUNT*/\r\nSELECT\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_DISCOUNT>=0,1,0) AS URUT,\r\n  20 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'DISKON \' AS ITEM_DESCRIPTION ,\r\n  a.INVOICE_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(TOTAL_DISCOUNT<0, -1*TOTAL_DISCOUNT, 0) as DEBET,\r\n  IF(TOTAL_DISCOUNT>0, TOTAL_DISCOUNT, 0) as KREDIT\r\nFROM\r\n  invoice a,\r\n  V_ACCOUNT c\r\nWHERE TRUE\r\nand c.COA_ID = @BELI_DISC\r\nAND TOTAL_DISCOUNT <> 0\r\nAND  (STATUS_ID <> @DELETE)\r\n&P1  &T1  &A1\r\n&KONDISI\r\nUNION ALL\r\n\r\n/*hutang dagang yang ada di header*/\r\nSELECT\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_AMOUNT>=0,1,0) AS URUT,\r\n 20 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  PERSON_NAME AS ITEM_DESCRIPTION ,\r\n  a.INVOICE_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(TOTAL_NET<0, -1*TOTAL_NET, 0) as DEBET,\r\n  IF(TOTAL_NET>=0, TOTAL_NET, 0) as KREDIT\r\nFROM\r\n  invoice a\r\n  INNER JOIN PERSON B ON (a.PERSON_ID = b.PERSON_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_HUT_ID)\r\n\r\nWHERE\r\n (STATUS_ID <> @DELETE)\r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\n/* END INVOICE */\r\n\r\nUNION ALL\r\n\r\n/*  BUILD BARANG JADI */\r\nSELECT\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(A.TOTAL_AMOUNT>= 0, 0,1) AS URUT,\r\n  30 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  A.BUILD_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n  IF(A.TOTAL_AMOUNT>= 0, A.TOTAL_AMOUNT, 0) AS DEBET,\r\n  IF(A.TOTAL_AMOUNT< 0, -1*A.TOTAL_AMOUNT, 0) AS KREDIT\r\nFROM\r\nBUILD A\r\nINNER JOIN item i ON (A.ITEM_ID = i.ITEM_ID)\r\n  LEFT  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\n WHERE\r\n (STATUS_ID <> @DELETE)\r\n&P1  &T1  &A1\r\n&KONDISI\r\nUNION ALL\r\n\r\n/*  BUILD BAHAN BAKU NYA */\r\nSELECT\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.SUBTOTAL>= 0, 1,0) AS URUT,\r\n  30 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.BUILD_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(B.SUBTOTAL< 0, -1*B.SUBTOTAL, 0) AS DEBET,\r\n  IF(B.SUBTOTAL>= 0, B.SUBTOTAL, 0) AS KREDIT\r\nFROM\r\nBUILD A\r\nINNER JOIN BUILD_DETAIL B ON (A.BUILD_ID = B.BUILD_ID)\r\nINNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nLEFT  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nWHERE\r\n (STATUS_ID <> @DELETE)\r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\n/* END BUILD */\r\n\r\n\r\nUNION ALL\r\n\r\n/* INVENTORY_OUT  */\r\n/* INVENTORY_OUT\r\nHPP DAN PERSEDIAN\r\n*/\r\nSELECT\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.HPP * B.ENTERED_QTY * B.BASE_QTY>=0,0 ,1)AS URUT,\r\n  40 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.INVENTORY_OUT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(B.HPP * B.ENTERED_QTY * B.BASE_QTY>=0,B.HPP * B.ENTERED_QTY * B.BASE_QTY,0) AS DEBET,\r\n  IF(B.HPP * B.ENTERED_QTY * B.BASE_QTY<0,-1*B.HPP * B.ENTERED_QTY * B.BASE_QTY ,0) AS KREDIT\r\nFROM\r\n  INVENTORY_OUT A\r\n  INNER JOIN  INVENTORY_OUT_DETAIL B on (a.INVENTORY_OUT_ID = B.INVENTORY_OUT_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER JOIN V_ACCOUNT C ON (I.COA_HPP_ID = C.COA_ID)\r\nWHERE\r\n (STATUS_ID <> @DELETE)\r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\nUNION ALL\r\n/* INVENTORY_OUT\r\n PERSEDIAN\r\n*/\r\nSELECT\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.HPP * B.ENTERED_QTY * B.BASE_QTY>=0,1 ,0)AS URUT,\r\n  40 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.INVENTORY_OUT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(B.HPP * B.ENTERED_QTY * B.BASE_QTY<0,-1*B.HPP * B.ENTERED_QTY * B.BASE_QTY ,0) AS DEBET,\r\n  IF(B.HPP * B.ENTERED_QTY * B.BASE_QTY>=0,B.HPP * B.ENTERED_QTY * B.BASE_QTY,0) AS KREDIT\r\nFROM\r\n  INVENTORY_OUT A\r\n  INNER JOIN  INVENTORY_OUT_DETAIL B on (a.INVENTORY_OUT_ID = B.INVENTORY_OUT_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  LEFT  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nWHERE\r\n (STATUS_ID <> @DELETE)\r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\n/*END INVENTORY_OUT */\r\n\r\nUNION ALL\r\n\r\n/* PENJUALAN */\r\n/*piutang dagang*/\r\nSELECT\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_NET>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  PERSON_NAME AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(TOTAL_NET>=0, TOTAL_NET, 0) as DEBET,\r\n  IF(TOTAL_NET<0, -1*TOTAL_NET, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN PERSON B ON (a.PERSON_ID = b.PERSON_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_PIUT_ID)\r\nWHERE\r\n  (STATUS_ID <> @DELETE)\r\nAND TOTAL_NET <> 0\r\n&P1  &T1  &A1\r\n&KONDISI\r\nUNION ALL\r\n/*DISCOUNT TOTAL*/\r\nSELECT\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_DISCOUNT>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'DISKON TOTAL \' AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(TOTAL_DISCOUNT>=0, TOTAL_DISCOUNT, 0) as DEBET,\r\n  IF(TOTAL_DISCOUNT<0, -1*TOTAL_DISCOUNT, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A,\r\n  V_ACCOUNT c\r\nWHERE   (STATUS_ID <> @DELETE)\r\n&P1  &T1  &A1\r\n&KONDISI\r\nand (c.COA_ID = @JUAL_DISC)\r\nAND TOTAL_DISCOUNT <> 0\r\n\r\n\r\nUNION ALL\r\n/*DISCOUNT PER ITEM */\r\nSELECT\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.DISCOUNT_PRICE * B.ENTERED_QTY>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(B.DISCOUNT_PRICE * B.ENTERED_QTY>=0, B.DISCOUNT_PRICE * B.ENTERED_QTY, 0) as DEBET,\r\n  IF(B.DISCOUNT_PRICE * B.ENTERED_QTY<0, -1*B.DISCOUNT_PRICE * B.ENTERED_QTY, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN NOTA_PENJUALAN_DETAIL B ON (A.NOTA_PENJUALAN_ID = B.NOTA_PENJUALAN_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  LEFT  join V_ACCOUNT c on (I.COA_DISC_JUAL_ID  = c.COA_ID)\r\nWHERE   (STATUS_ID <> @DELETE)\r\n&P1  &T1  &A1\r\n&KONDISI\r\nAND B.DISCOUNT_PRICE * B.ENTERED_QTY <> 0\r\nUNION ALL\r\n\r\n/*Penjualan*/\r\nSELECT\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,1,0) AS URUT,\r\n 50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  b.NOTE,   0 AS AWAL,\r\n  IF(b.UNIT_PRICE * b.ENTERED_QTY <0,(-1*b.UNIT_PRICE * b.ENTERED_QTY),0)  as DEBET,\r\n  IF(b.UNIT_PRICE * b.ENTERED_QTY>=0,(b.UNIT_PRICE * b.ENTERED_QTY),0)  as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN NOTA_PENJUALAN_DETAIL B ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  LEFT  join V_ACCOUNT c on (I.COA_JUAL_ID  = c.COA_ID)\r\n\r\nWHERE\r\n  (STATUS_ID <> @DELETE)\r\n&P1  &T1  &A1\r\n&KONDISI\r\nUNION ALL\r\n/*ppn*/\r\nSELECT\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(PPN_AMOUNT>=0,1,0) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'PPN PENJUALAN \' AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(PPN_AMOUNT<0, -1*PPN_AMOUNT, 0) as DEBET,\r\n  IF(PPN_AMOUNT>=0, PPN_AMOUNT, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A,\r\n  V_ACCOUNT c\r\nWHERE\r\n  (STATUS_ID <> @DELETE)\r\nAND PPN_AMOUNT <> 0\r\n&P1  &T1  &A1\r\n&KONDISI\r\nand c.COA_ID = @JUAL_PPN\r\n\r\n/* END PENJUALAN */\r\nUNION ALL\r\n\r\n\r\n/*\r\n  BKM\r\nBANK\r\n\r\nKAS                   1000\r\nPOT. PIUTANG   100\r\n  PIUTANG              1100\r\n\r\n*/\r\nSELECT\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO ,\r\na.DOCUMENT_DATE,\r\na.DOCUMENT_REFF_NO ,\r\n0 AS URUT,\r\n60 AS URUT1,\r\nC.COA_NAME,\r\n C.COA_CODE,\r\nC.COA_ID,C.ACCOUNT_ID ,  COA_NAME AS ITEM_DESCRIPTION ,\r\nA.RECEIPT_ID,\r\nA.NOTE,   0 AS AWAL,\r\nA.TOTAL_AMOUNT  DEBET,\r\n0 AS KREDIT\r\nFROM\r\nRECEIPT A\r\nINNER JOIN V_ACCOUNT C ON (A.BANK_ID = C.COA_ID)\r\nWHERE\r\n(A.TOTAL_AMOUNT  <> 0) AND \r\n(A.STATUS_ID <> @DELETE)\r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\nUNION ALL\r\n/* POTONGAN  BKM */\r\nSELECT\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO ,\r\na.DOCUMENT_DATE,\r\na.DOCUMENT_REFF_NO ,\r\nIF(DISCOUNT >=0,0,1) AS URUT,\r\n60 AS URUT1,\r\nCOA_NAME,\r\n C.COA_CODE,\r\nC.COA_ID,C.ACCOUNT_ID ,  \'POTONGAN PIUTANG\' AS ITEM_DESCRIPTION ,\r\nA.RECEIPT_ID,\r\nA.NOTE,   0 AS AWAL,\r\nIF(DISCOUNT > 0, DISCOUNT , 0) AS DEBET,\r\nIF( DISCOUNT < 0,-1* DISCOUNT , 0) AS KREDIT\r\nFROM\r\nRECEIPT A\r\nINNER JOIN RECEIPT_DETAIL  B ON (A.RECEIPT_ID = B.RECEIPT_ID)\r\n,\r\nV_ACCOUNT c\r\n\r\nWHERE \r\n(B.COA_ID IS NULL AND B.DISCOUNT <> 0 )\r\nAND (c.COA_ID = @POT_PIUTANG)\r\nAND (A.STATUS_ID <> @DELETE)\r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\n\r\nUNION ALL\r\n/* BUKAN PELUNASAN BKM */\r\nSELECT\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO ,\r\na.DOCUMENT_DATE,\r\na.DOCUMENT_REFF_NO ,\r\nIF(AMOUNT>=0,1,0) AS URUT,\r\n60 AS URUT1,\r\nC.COA_NAME,\r\n C.COA_CODE,\r\nC.COA_ID,C.ACCOUNT_ID ,  A1.NOTE AS ITEM_DESCRIPTION ,\r\nA.RECEIPT_ID,\r\nA1.NOTE,   0 AS AWAL,\r\nIF(A1.AMOUNT<0,-1*A1.AMOUNT, 0) AS DEBET,\r\nIF(A1.AMOUNT>=0,A1.AMOUNT, 0) AS KREDIT\r\nFROM\r\nRECEIPT  A\r\nINNER JOIN RECEIPT_DETAIL  A1 ON (A.RECEIPT_ID = A1.RECEIPT_ID)\r\nINNER JOIN V_ACCOUNT C ON (A1.COA_ID = C.COA_ID)\r\nWHERE\r\n(A1.AMOUNT  <> 0) AND \r\n(A1.COA_ID IS NOT NULL)\r\nAND (A.STATUS_ID <> @DELETE)\r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\n\r\nUNION ALL\r\n/* PELUNASAN BKM */\r\nSELECT\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO ,\r\na.DOCUMENT_DATE,\r\na.DOCUMENT_REFF_NO ,\r\nIF(AMOUNT  >=0,1,0) AS URUT,\r\n60 AS URUT1,\r\nCOA_NAME,\r\n C.COA_CODE,\r\nC.COA_ID,C.ACCOUNT_ID ,  PERSON_NAME AS ITEM_DESCRIPTION ,\r\nA.RECEIPT_ID,\r\nA.NOTE,   0 AS AWAL,\r\nIF(AMOUNT + B.DISCOUNT   <0,-1*(AMOUNT + B.DISCOUNT ) , 0) AS DEBET,\r\nIF(AMOUNT + B.DISCOUNT   >=0,AMOUNT + B.DISCOUNT   , 0) AS KREDIT\r\nFROM\r\nRECEIPT A\r\nINNER JOIN RECEIPT_DETAIL  B ON (A.RECEIPT_ID = B.RECEIPT_ID)\r\nINNER JOIN PERSON V ON (A.PERSON_ID = V.PERSON_ID)\r\nINNER JOIN V_ACCOUNT C ON (V.COA_PIUT_ID = C.COA_ID)\r\nWHERE\r\n((B.AMOUNT + B.DISCOUNT)  <> 0) AND \r\n(B.COA_ID IS NULL )\r\nAND (A.STATUS_ID <> @DELETE)\r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\n/* END BKM */\r\n\r\n\r\nUNION ALL\r\n/* BKK\r\nHUTANG    1000\r\n   KAS         900\r\n   POT.	       100\r\n\r\n*/\r\n/* BKK */\r\nSELECT\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO ,\r\na.DOCUMENT_DATE,\r\na.DOCUMENT_REFF_NO ,\r\nIF(AMOUNT>=0,0,1) AS URUT,\r\n70 AS URUT1,\r\nC.COA_NAME,\r\nC.COA_CODE,\r\nC.COA_ID,C.ACCOUNT_ID ,  A1.NOTE AS ITEM_DESCRIPTION ,\r\nA.PAYMENT_ID,\r\nA.NOTE,   0 AS AWAL,\r\nIF(AMOUNT>=0,AMOUNT, 0) AS DEBET,\r\nIF(AMOUNT<0,-1*AMOUNT, 0) AS KREDIT\r\nFROM\r\nPAYMENT A\r\nINNER JOIN PAYMENT_DETAIL A1 ON (A.PAYMENT_ID = A1.PAYMENT_ID)\r\nINNER JOIN V_ACCOUNT C ON (A1.COA_ID = C.COA_ID)\r\nWHERE\r\n(A1.AMOUNT  <> 0) AND \r\n (STATUS_ID <> @DELETE)\r\nAND A1.COA_ID IS NOT NULL\r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\nUNION ALL\r\n/* PELUNASAN HUTANG */\r\nSELECT\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO ,\r\na.DOCUMENT_DATE,\r\na.DOCUMENT_REFF_NO ,\r\nIF(AMOUNT >=0,0,1) AS URUT,\r\n70 AS URUT1,\r\nCOA_NAME,\r\n C.COA_CODE,\r\nC.COA_ID,C.ACCOUNT_ID ,  PERSON_NAME AS ITEM_DESCRIPTION ,\r\nA.PAYMENT_ID,\r\nB.NOTE,   0 AS AWAL,\r\nIF(AMOUNT + B.DISCOUNT >=0,AMOUNT + B.DISCOUNT , 0) AS DEBET,\r\nIF(AMOUNT + B.DISCOUNT <0,-1*(AMOUNT + B.DISCOUNT ) , 0) AS KREDIT\r\nFROM\r\nPAYMENT  A\r\nINNER JOIN PAYMENT_DETAIL B ON (A.PAYMENT_ID = B.PAYMENT_ID)\r\nINNER JOIN PERSON V ON (A.PERSON_ID = V.PERSON_ID)\r\nINNER JOIN V_ACCOUNT C ON (V.COA_HUT_ID = C.COA_ID)\r\nWHERE (STATUS_ID <> @DELETE)\r\nAND (B.INVOICE_DUE_ID IS NOT NULL)\r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\nUNION ALL\r\n/* POTONG HUTANG*/\r\n\r\nSELECT\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO ,\r\na.DOCUMENT_DATE,\r\na.DOCUMENT_REFF_NO ,\r\nIF(DISCOUNT>=0,0,1) AS URUT,\r\n70 AS URUT1,\r\nCOA_NAME,\r\nC.COA_CODE,\r\nC.COA_ID,C.ACCOUNT_ID ,  \'POTONGAN HUTANG\' AS ITEM_DESCRIPTION ,\r\nA.PAYMENT_ID,\r\nB.NOTE,   0 AS AWAL,\r\nIF(DISCOUNT<0,-1*DISCOUNT, 0)  AS DEBET,\r\nIF(DISCOUNT>=0,DISCOUNT, 0)AS KREDIT\r\nFROM\r\nPAYMENT  A\r\nINNER JOIN PAYMENT_DETAIL B ON (A.PAYMENT_ID = B.PAYMENT_ID)\r\n,\r\nV_ACCOUNT c\r\nWHERE (STATUS_ID <> @DELETE)\r\nAND DISCOUNT <> 0\r\nAND (c.COA_ID = @POT_HUTANG)\r\nAND (B.COA_ID IS NULL)\r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\nUNION ALL\r\n\r\n/*\r\n  BKK\r\nBANK\r\n*/\r\nSELECT\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO ,\r\na.DOCUMENT_DATE,\r\na.DOCUMENT_REFF_NO ,\r\n1 AS URUT,\r\n70 AS URUT1,\r\nC.COA_NAME,\r\nC.COA_CODE,\r\nC.COA_ID,C.ACCOUNT_ID ,  COA_NAME AS ITEM_DESCRIPTION ,\r\nA.PAYMENT_ID,\r\nA.NOTE,   0 AS AWAL,\r\n 0  AS DEBET,\r\nA.TOTAL_AMOUNT KREDIT\r\nFROM\r\nPAYMENT A\r\nINNER JOIN V_ACCOUNT C ON (A.BANK_ID = C.COA_ID)\r\nWHERE   \r\n(A.TOTAL_AMOUNT  <> 0) AND \r\n(STATUS_ID <> @DELETE)\r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\n/* END OF BKK */\r\n\r\n\r\n\r\nUNION ALL\r\n/* JURNAL MEMO RIAL  */\r\nSELECT\r\n\'JURNAL MEMO \' ,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  1 URUT,\r\n  80 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  a.JOURNAL_ID,\r\n  b.NOTE,   0 AS AWAL,\r\n  AMOUNT as DEBET,\r\nAMOUNT_CR as KREDIT\r\nFROM\r\n  JOURNAL a\r\n  INNER JOIN JOURNAL_detail b ON (a.JOURNAL_ID = b.JOURNAL_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_ID)\r\nWHERE\r\n (STATUS_ID <> @DELETE)\r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\n\r\nUNION ALL\r\n\r\n/* BG */ \r\n\r\n/*  BG  CAIR / TOLAK BANK TUJUAN */\r\nSELECT\r\n\'PENCAIRAN BG \' ,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  IF(STATUS_FLAG = \'M\' , 0, 1)  URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  a.CHEQUE_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT ,0)as DEBET,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT,0 )as KREDIT\r\nFROM\r\n  CHEQUE a\r\n  INNER JOIN COA b ON (a.BANK_ID_DEST = b.COA_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_ID)\r\nWHERE\r\n (STATUS_ID in (@CLEAR, @TOLAK ))\r\n&P1  &T1  &A1\r\n&KONDISI\r\n\r\nUNION ALL\r\n/* BG  CAIR / TOLAK BANK ASAL */\r\nSELECT\r\n\'PENCAIRAN BG \' ,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  IF(STATUS_FLAG = \'M\' , 1, 0)  URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  a.CHEQUE_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT,0 )as DEBET,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT,0 )as KREDIT\r\nFROM\r\n  CHEQUE a\r\n  INNER JOIN COA b ON (a.BANK_ID = b.COA_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_ID)\r\nWHERE\r\n (STATUS_ID in  (@CLEAR, @TOLAK ))\r\n&P1  &T1  &A1\r\n&KONDISI\r\nUNION ALL\r\n\r\n/* BG  TOLAK BANK ASAL */\r\nSELECT\r\n\'PENCAIRAN BG \' ,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  1 URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  a.CHEQUE_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT,0 )as DEBET,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT,0 )as KREDIT\r\n\r\nFROM\r\n  CHEQUE a\r\n  INNER JOIN COA b ON (a.BANK_ID = b.COA_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_ID)\r\nWHERE\r\n (STATUS_ID = @TOLAK)\r\n&P1  &T1  &A1\r\n&KONDISI\r\nUNION ALL\r\n/* BG   TOLAK BANK TUJUAN */\r\nSELECT\r\n\'TOLAKAN BG \' ,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  2 URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  a.CHEQUE_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT  ,0 )as DEBET,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT ,0)as KREDIT\r\n\r\nFROM\r\n  CHEQUE a\r\n  INNER JOIN COA b ON (a.BANK_ID_DEST = b.COA_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_ID)\r\nWHERE\r\n (STATUS_ID = @TOLAK)\r\n&P1  &T1  &A1\r\n&KONDISI\r\n', 78, 'SQL JURNAL + SURAT JALAN', 4, NULL, '2007-08-31 16:00:58', 2, NULL, 'SQL JURNAL + SURAT JALAN'),
	(64, 'SELECT SUM(COA_DEBET), SUM(COA_CREDIT) , SUM(COA_DEBET - COA_CREDIT)\r\nFROM COA_BALANCE\r\nWHERE PERIOD_NAME = \'2007-01\'\r\n', 790, 'TEST AKUNTANSI', 4, NULL, NULL, 4, NULL, 'TEST BALANCE (COA_BALANCE)'),
	(65, '/* TEST INVOICE YG TDK ADA INVOICE_DUE*/\r\nSELECT * FROM INVOICE A\r\n  LEFT JOIN INVOICE_DUE B ON (A.INVOICE_ID = B.INVOICE_ID)\r\nWHERE\r\n  B.INVOICE_ID IS NULL	 AND A.STATUS_ID  <> @DELETE', 708, NULL, 4, NULL, NULL, 4, NULL, 'TEST INVOICE YG TDK ADA INVOICE_DUE'),
	(66, '/* TEST INVOICE_DUE YG TDK ADA PELUNASAN NAMUN TERISI*/\r\nSELECT * FROM INVOICE_DUE A\r\n  LEFT JOIN PAYMENT_DETAIL B ON (A.INVOICE_DUE_ID = B.INVOICE_DUE_ID)\r\nLEFT JOIN RECEIPT_DETAIL R ON (A.INVOICE_DUE_ID = R.INVOICE_DUE_ID)\r\n\r\nWHERE\r\n  (B.INVOICE_DUE_ID IS NULL AND R.INVOICE_DUE_ID IS NULL)\r\nAND A.PAID_AMOUNT <> 0    ', 709, NULL, 4, NULL, '2007-11-05 11:40:34', 0, NULL, 'TEST INVOICE_DUE YG TDK ADA PELUNASAN NAMUN TERISI'),
	(67, '/* TEST NOTA_PENJUALAN YG TDK ADA AGING */\r\nSELECT * FROM NOTA_PENJUALAN A\r\n  LEFT JOIN NOTA_PENJUALAN_AGING B ON (A.NOTA_PENJUALAN_ID = B.NOTA_PENJUALAN_ID)\r\nWHERE\r\n  B.NOTA_PENJUALAN_ID IS NULL\r\nand (A.STATUS_ID <> @DELETE)\r\n\r\n/* PERBAIKAN\r\nINSERT INTO NOTA_PENJUALAN_AGING (\r\n	NOTA_PENJUALAN_ID,\r\n	JUAL_FLAG,\r\n	DOCUMENT_NO,\r\n	CUSTOMER_ID,\r\n	CUSTOMER_SITE_ID,\r\n	WAREHOUSE_ID,\r\n	DUE_DATE,\r\n	DUE_AMOUNT,\r\n	CREATED_BY,\r\n	CREATED_DATE,\r\n	LAST_UPDATE_DATE,\r\n	LAST_UPDATE_BY)   VALUES (\r\n	 11513,\r\n	 \'Y\',\'PJL-A1-07-01-00039\',\r\n6873,1816,1,1816, \'2007/03/02 4:34:47 PM\',338150,86, sysdate() ,\r\n	 sysdate() , 86);\r\n\r\n*/', 717, NULL, 4, NULL, NULL, 4, NULL, 'TEST NOTA_PENJUALAN YG TDK ADA AGING'),
	(68, '/* TEST NOTA_PENJUALAN_AGING YG TDK ADA PELUNASAN NAMUN TERISI*/\r\n SELECT * FROM NOTA_PENJUALAN_AGING A\r\n  LEFT JOIN RECEIPT_DETAIL B ON (A.NOTA_PENJUALAN_AGING_ID = B.NOTA_PENJUALAN_AGING_ID)\r\n  LEFT JOIN PAYMENT_DETAIL P ON (A.NOTA_PENJUALAN_AGING_ID = P.NOTA_PENJUALAN_AGING_ID)\r\n\r\nWHERE\r\n  (B.NOTA_PENJUALAN_AGING_ID IS NULL AND P.NOTA_PENJUALAN_AGING_ID IS NULL)\r\nAND A.RECEIVED_AMOUNT <> 0  ', 718, NULL, 4, NULL, '2007-11-05 14:21:51', 0, NULL, 'TEST NOTA_PENJUALAN_AGING YG TDK ADA PELUNASAN NAMUN TERISI'),
	(69, '/* TDK ADA S.JALAN INVOICE ENTERED_QTY DI SO TERISI */ \r\n\r\nSelect   X.DOCUMENT_NO NO_SO, A.* \r\n        from\r\n         SO_DETAIL A	\r\n         INNER JOIN SO X ON (A.SO_ID = X.SO_ID)\r\n         LEFT JOIN  INVENTORY_OUT_DETAIL B ON (A.SO_DETAIL_ID = B.SO_DETAIL_ID)\r\n        where B.SO_DETAIL_ID IS NULL AND A.RECEIVED_ENTERED_QTY <> 0  ', 714, NULL, 4, NULL, '2007-07-03 12:03:00', 4, NULL, '/* TDK ADA S.JALAN INVOICE ENTERED_QTY DI SO TERISI */\r\n'),
	(70, '/* TDK ADA BPB INVOICE ENTERED_QTY DI PO TERISI */\r\nSelect  A.*\r\n        from\r\n         PO_DETAIL A	\r\n         INNER JOIN PO X ON (A.PO_ID = X.PO_ID)\r\n         LEFT JOIN  INVENTORY_IN_DETAIL B ON (A.PO_DETAIL_ID = B.PO_DETAIL_ID)\r\n        where B.PO_DETAIL_ID IS NULL AND A.RECEIVED_ENTERED_QTY <> 0  ', 704, NULL, 4, NULL, NULL, 4, NULL, 'TDK ADA BPB INVOICE ENTERED_QTY DI PO TERISI'),
	(71, '/* \r\nX1 = COA  UTK SALDO AWAL \r\nX2 = COA  UTK TRANSAKSI \r\nP = PERIODE\r\nT = TANGGAL\r\nT2= TGL_TOLAK\r\nP2= PERIODE TOLAK\r\n\r\n*/\r\n/* SALDO AWAL*/\r\nSELECT \r\n  0 AS URUT, \r\n  \'SALDO AWAL\' as TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  a.COA_SALDO as SALDO,\r\n   0 AS DEBET,\r\n  0 as KREDIT,\r\n  null as PERSON_ID,\r\n  null as PERSON_CODE,\r\n  null as PERSON_NAME,\r\n  null as DOCUMENT_NO,\r\n  null as DOCUMENT_REFF_NO,\r\n  null as DOCUMENT_DATE,\r\n  \'SALDO AWAL\' as NOTE,\r\n  a.PERIOD_NAME,\r\n  \'\' as check_no\r\nFROM\r\nCOA b\r\nINNER JOIN COA_BALANCE a on (a.COA_ID=b.COA_ID)\r\nINNER JOIN ACCOUNT c on (c.ACCOUNT_ID=b.ACCOUNT_ID)\r\nWHERE c.ACCOUNT_TYPE_ID IN (@ACC_KASBANK,@ACC_GIRO)\r\n&X1\r\n&P1\r\n\r\nUNION ALL\r\n/* BKM*/\r\nSELECT \r\n  1 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 as SALDO,\r\n  a.TOTAL_AMOUNT as DEBET,\r\n  0 as KREDIT,\r\n  P.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS  NOTE,\r\n  a.PERIOD_NAME,\r\n  a.check_no\r\nFROM\r\nRECEIPT a\r\nINNER JOIN COA b on (a.BANK_ID=b.COA_ID)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE a.Status_ID<>@DELETE and a.TOTAL_AMOUNT<>0\r\n&X2\r\n&P1\r\n&T1\r\n\r\nUNION ALL\r\n/* PENJUALAN TUNAI pembayaran I */\r\nSELECT \r\n  1 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 as SALDO,\r\n  a.NET_BYR1  as DEBET,\r\n  0 as KREDIT,\r\n  P.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS NOTE,\r\n  a.PERIOD_NAME,\r\n  \'\' as check_no\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER JOIN COA b on (a.COA_BYR1 =b.COA_ID)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE \r\n (  a.NET_BYR1  > 0 )\r\nand (a.TOTAL_NET > 0) \r\nAND ( a.Status_ID<>@DELETE )\r\n&X2\r\n&P1\r\n&T1\r\n\r\nUNION ALL\r\n/* PENJUALAN TUNAI pembayaran II */\r\nSELECT \r\n  1 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 as SALDO,\r\n  a.TOTAL_BYR2  as DEBET,\r\n  0 as KREDIT,\r\n a.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS  NOTE,\r\n  a.PERIOD_NAME,\r\n  \'\' as check_no\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER JOIN COA b on (a.COA_BYR2 =b.COA_ID)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE \r\n  ( a.TOTAL_BYR2   > 0 )\r\nand (a.TOTAL_NET > 0) \r\nAND ( a.Status_ID<>@DELETE )\r\nAND (a.TOTAL_AMOUNT<>0 )\r\n&X2\r\n&P1\r\n&T1\r\n\r\nUNION ALL\r\n/* BKK*/\r\nSELECT \r\n  5 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n 0 as SALDO,\r\n  0 as DEBET,\r\n  a.TOTAL_AMOUNT as KREDIT,\r\n  P.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS  NOTE,\r\n  a.PERIOD_NAME,\r\n  a.check_no\r\nFROM\r\nPAYMENT a\r\nINNER JOIN COA b on (a.BANK_ID=b.COA_ID)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE a.Status_ID<>@DELETE and a.TOTAL_AMOUNT<>0\r\n&X2\r\n&P1\r\n&T1\r\n\r\nUNION ALL\r\n/* BKK DETAIL YG BANK */\r\nSELECT \r\n  2 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n 0 as SALDO,\r\n  d.AMOUNT  as DEBET,\r\n  0 as KREDIT,\r\n  0 PERSON_ID,\r\n  \' \'AS PERSON_CODE,\r\n  \'\' ASPERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(x.COA_NAME,\'\'),\' - \', IFNULL(d.NOTE,\'\')) AS  NOTE,\r\n  a.PERIOD_NAME,\r\n  a.check_no\r\nFROM\r\nPAYMENT a\r\nINNER JOIN PAYMENT_DETAIL d on (a.PAYMENT_ID = d.PAYMENT_ID)\r\nINNER JOIN COA b on (d.COA_ID=b.COA_ID)\r\nINNER JOIN ACCOUNT c on (c.ACCOUNT_ID=b.ACCOUNT_ID)\r\nINNER JOIN COA x on (a.BANK_ID=x.COA_ID)\r\nWHERE (c.ACCOUNT_TYPE_ID IN (@ACC_KASBANK ,@ACC_GIRO))\r\nand (a.Status_ID<>@DELETE )\r\n&X2\r\n&P1\r\n&T1\r\n\r\nUNION ALL\r\n/* BKM YG BANK */\r\nSELECT \r\n  6 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO,\r\n  0 as DEBET,\r\n   d.AMOUNT as KREDIT,\r\n  0 PERSON_ID,\r\n  \' \'AS PERSON_CODE,\r\n  \'\' ASPERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(x.COA_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS NOTE,\r\n  a.PERIOD_NAME,\r\n  a.check_no\r\nFROM\r\nRECEIPT a\r\nINNER JOIN RECEIPT_DETAIL d on (a.RECEIPT_ID = d.RECEIPT_ID )\r\nINNER JOIN COA b on (d.COA_ID=b.COA_ID)\r\nINNER JOIN ACCOUNT c on (c.ACCOUNT_ID=b.ACCOUNT_ID)\r\nINNER JOIN COA x on (a.BANK_ID=x.COA_ID)\r\nWHERE (c.ACCOUNT_TYPE_ID in (@ACC_KASBANK,@ACC_GIRO))\r\nand (a.Status_ID<>@DELETE )\r\n&X2\r\n&P1\r\n&T1\r\n\r\nUNION ALL\r\n/* GIRO / CHEQUE TUJUAN */\r\nSELECT \r\n  IF(STATUS_FLAG = \'M\',3 ,7) as URUT, \r\n  \'PENCAIRAN \' AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO, \r\n   (IF(STATUS_FLAG = \'M\', a.TOTAL_AMOUNT,0)) as DEBET,\r\n   (IF(STATUS_FLAG = \'K\', a.TOTAL_AMOUNT,0)) as KREDIT,\r\n   0 as PERSON_ID,\r\n   \'\' as PERSON_CODE,\r\n  \' \' PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  \' \' AS DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.PERIOD_NAME,\r\n  a.document_no as check_no\r\nFROM\r\n  cheque a   \r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n INNER JOIN COA b on (a.BANK_ID_DEST =b.COA_ID)\r\nWHERE\r\n  a.STATUS_ID in ( @CLEAR, @TOLAK)\r\n&X2\r\n&P1\r\n&T1\r\n\r\nUNION ALL\r\n/* GIRO / CHEQUE ASAL */\r\nSELECT \r\n  IF(STATUS_FLAG = \'M\',7,3) as URUT, \r\n  \'PENCAIRAN \' AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO, \r\n   (IF(STATUS_FLAG = \'K\', a.TOTAL_AMOUNT,0)) as DEBET,\r\n   (IF(STATUS_FLAG = \'M\', a.TOTAL_AMOUNT,0) ) as KREDIT,\r\n   0 as PERSON_ID,\r\n   \'\' as PERSON_CODE,\r\n  \' \' PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  \' \' AS DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.PERIOD_NAME,\r\n  a.document_no as check_no\r\nFROM\r\n  cheque a   \r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN COA b on (a.BANK_ID =b.COA_ID)\r\nWHERE\r\n  a.STATUS_ID in ( @CLEAR, @TOLAK)\r\n&X2\r\n&P1\r\n&T1\r\n\r\nUNION ALL\r\n/* GIRO / CHEQUE TUJUAN yg tolak  */\r\nSELECT \r\n  IF(STATUS_FLAG = \'M\',3 ,7) as URUT, \r\n  \'TOLAK \' AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO, \r\n   (IF(STATUS_FLAG = \'K\', a.TOTAL_AMOUNT,0)) as DEBET,\r\n   (IF(STATUS_FLAG = \'M\', a.TOTAL_AMOUNT,0)) as KREDIT,\r\n   0 as PERSON_ID,\r\n   \'\' as PERSON_CODE,\r\n  \' \' PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  \' \' AS DOCUMENT_REFF_NO,\r\n  a.TGL_TOLAK as DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.PERIOD_NAME,\r\n  a.document_no as check_no\r\nFROM\r\n  cheque a   \r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN COA b on (a.BANK_ID_DEST =b.COA_ID)\r\n  INNER JOIN PERIOD P on (EXTRACT(YEAR_MONTH FROM a.tgl_tolak)=P.PERIOD_NAME)\r\nWHERE\r\n  a.STATUS_ID in (  @TOLAK)\r\n&X2\r\n&P2\r\n&T2\r\n\r\nUNION ALL\r\n/* GIRO / CHEQUE ASAL YG TOLAK */\r\nSELECT \r\n  IF(STATUS_FLAG = \'M\',7,3) as URUT, \r\n  \'TOLAK \' AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO, \r\n   (IF(STATUS_FLAG = \'M\', a.TOTAL_AMOUNT,0) ) as DEBET,\r\n   (IF(STATUS_FLAG = \'K\', a.TOTAL_AMOUNT,0)) as KREDIT,\r\n   0 as PERSON_ID,\r\n   \'\' as PERSON_CODE,\r\n  \' \' PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  \' \' AS DOCUMENT_REFF_NO,\r\n  a.TGL_TOLAK as DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.PERIOD_NAME,\r\n  a.document_no as check_no\r\nFROM\r\n  cheque a   \r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN COA b on (a.BANK_ID =b.COA_ID)\r\n  INNER JOIN PERIOD P on (EXTRACT(YEAR_MONTH FROM a.tgl_tolak)=P.PERIOD_NAME)\r\nWHERE\r\n  (a.STATUS_ID =  @TOLAK)\r\n&X2\r\n&P2\r\n&T2\r\n\r\nUNION ALL\r\n/* JURNAL */\r\nSELECT \r\n  4 as URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO, \r\n  AMOUNT as DEBET,\r\n  AMOUNT_CR as KREDIT,\r\n   0 as PERSON_ID,\r\n   \'\' as PERSON_CODE,\r\n  \' \' PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  \' \' AS DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.PERIOD_NAME,\r\n  \'\' as check_no\r\nFROM\r\n  JOURNAL a   \r\n  INNER JOIN JOURNAL_DETAIL d ON (a.JOURNAL_ID = d.JOURNAL_ID)\r\n INNER JOIN COA b on (d.COA_ID =b.COA_ID)\r\nWHERE\r\n true \r\n&X2\r\n&P1\r\n&T1', 76, 'KARTU KAS/BANK', 5, '2010-11-19 00:25:45', '2010-06-19 12:48:16', 0, NULL, 'KARTU KAS/BANK'),
	(72, ' \r\n/* \r\nHUTANG \r\nX1 = PERIODE UTK SALDO AWAL \r\nV1 = TRANSAKSI \r\nT1 = TANGGAL / PERIODE\r\n\r\n*/\r\n\r\n/* SALDO AWAL */ \r\nSELECT\r\n  0 AS URUT, \r\n \'SALDO\' as DOCUMENT_CLASS_CODE,\r\n  \'\' as DOCUMENT_NO,\r\n  null as DOCUMENT_DATE,\r\n  c.PERSON_NAME,\r\n  c.PERSON_CODE,\r\n  a.SALDO_HUTANG AS SALDO, \r\n  0 AS KREDIT,\r\n  0 AS DEBET,\r\n  a.PERSON_ID,\r\n  \'SALDO AWAL\' as NOTE\r\n FROM \r\n  PERSON_balance a\r\n INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nWHERE   LEFT(c.TIPE,1) = 1  \r\n&V1\r\n&X1\r\n\r\nUNION ALL\r\n/* PEMBELIAN */ \r\nSELECT\r\n  1 AS URUT, \r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  c.PERSON_NAME,\r\n  c.PERSON_CODE,\r\n  0 AS SALDO,\r\n  a.TOTAL_NET as KREDIT,\r\n  0 as DEBET,\r\n  /*IF(a.TOTAL_NET<>0,a.TOTAL_NET,(SELECT SUM(SUBTOTAL) FROM INVOICE_DETAIL b,ITEM i WHERE a.INVOICE_ID=b.INVOICE_ID AND i.ITEM_ID=b.ITEM_ID AND i.JENIS_ID<>@UM)) AS    KREDIT,\r\n  IF(a.TOTAL_NET<>0,0,(SELECT SUM(SUBTOTAL*-1) FROM INVOICE_DETAIL b,ITEM i WHERE a.INVOICE_ID=b.INVOICE_ID AND i.ITEM_ID=b.ITEM_ID AND i.JENIS_ID=@UM)) AS DEBET,*/\r\n  a.PERSON_ID,\r\n  a.NOTE\r\nFROM\r\n  INVOICE a\r\n  INNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\n  INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nWHERE TRUE\r\n&V1\r\n&T1\r\n\r\nUNION ALL\r\n/* PELUNASAN DGN BKK */\r\nSELECT\r\n  2 AS URUT, \r\n a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  c.PERSON_NAME,\r\n  c.PERSON_CODE,\r\n0 AS SALDO,\r\n  0  AS KREDIT,\r\n  b.AMOUNT+b.DISCOUNT AS DEBET,\r\n\r\n  a.PERSON_ID,\r\n  CONCAT(TRIM(IFNULL(b.NOTE, \' \' )) ,\'  \',  x.DOCUMENT_NO ) AS NOTE\r\nFROM\r\n  PAYMENT a\r\n  INNER JOIN PAYMENT_detail b ON (a.PAYMENT_ID = b.PAYMENT_ID)\r\n  INNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\n  INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN INVOICE_DUE x ON ( b.INVOICE_DUE_ID = x.INVOICE_DUE_ID)\r\nWHERE TRUE\r\n&V1\r\n&T1\r\n\r\n\r\nUNION ALL\r\n\r\n/* PELUNASAN DGN BKM */\r\n\r\nSELECT\r\n  4 AS URUT, \r\n a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  c.PERSON_NAME,\r\n  c.PERSON_CODE,\r\n  0 AS SALDO,\r\n  0  AS KREDIT,  \r\n  (b.AMOUNT+b.DISCOUNT) * -1  AS DEBET,\r\n  \r\n  a.PERSON_ID,\r\n  CONCAT(TRIM(IFNULL(b.NOTE, \' \' )) ,\'  \',  x.DOCUMENT_NO ) AS NOTE\r\nFROM\r\n  receipt a\r\n   INNER JOIN receipt_detail b ON (a.RECEIPT_ID = b.RECEIPT_ID) \r\n INNER JOIN PERSON c On (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\n  INNER JOIN INVOICE_DUE x ON ( b.INVOICE_DUE_ID = x.INVOICE_DUE_ID)\r\nWHERE  TRUE\r\n  &V1  \r\n  &T1\r\n\r\n\r\n', 75, 'KARTU HUTANG', 5, '2014-12-12 15:06:48', '2014-04-14 11:20:01', 0, NULL, 'KARTU HUTANG'),
	(73, 'SELECT \r\n  STATUS_FLAG,\r\n  A.PERIOD_NAME, \r\n  A.COA_ID, \r\n  sum(COA_SALDO + COA_DEBET - COA_CREDIT) as SALDO\r\nFROM \r\n COA_BALANCE A\r\n INNER JOIN COA c ON (A.COA_ID = c.COA_ID )\r\n INNER JOIN ACCOUNT x ON (c.ACCOUNT_ID = x.ACCOUNT_ID )\r\n \r\nWHERE \r\n  (A.PERIOD_NAME = :PERIOD_NAME )\r\nand (x.ACCOUNT_TYPE_ID   IN (@ACC_KASBANK, @ACC_GIRO, @A1, @A2, @A3, @A4, @A5, @A6,  @A7,@P1, \r\n@P2, @P3, @P4, @P5, @P6, @P7)  \r\n)\r\nGROUP BY \r\n  A.PERIOD_NAME, \r\n  A.COA_ID   \r\n\r\n', 430, NULL, 4, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 0, NULL, 'Closing - Saldo  COA'),
	(74, 'SELECT  cs.coa_id, xx.*\r\n From \r\n   coa_setup cs,\r\n(\r\nSELECT\r\n   sum(COA_SALDO + COA_DEBET - COA_CREDIT) as total_rl\r\n    FROM\r\n    COA_BALANCE b \r\n   INNER JOIN COA c ON (b.COA_ID = c.COA_ID)\r\n   INNER JOIN ACCOUNT a ON (c.ACCOUNT_ID = a.ACCOUNT_ID)\r\n     WHERE\r\n(period_name = :PERIOD_NAME)\r\n AND  (a.ACCOUNT_TYPE_ID  in ( @R1, @R2, @R3, @R4,@R5,@R6,@R7,@R8))\r\n\r\n) as xx\r\nWhere    (cs.PROGRAM_ACCOUNT = \'LABA\')', 435, NULL, 4, '2007-07-12 14:14:36', '2008-04-08 15:35:53', 0, NULL, 'closing - rugi laba'),
	(76, '/* \r\nX1 = PERIODE UTK SALDO AWAL \r\nV1 = TRANSAKSI \r\nT1 = TANGGAL / PERIODE\r\n\r\n*/\r\n/* SALDO AWAL */\r\nSELECT\r\n  0 AS URUT, \r\n \'SALDO\' as DOCUMENT_CLASS_CODE,\r\n  \'\' as DOCUMENT_NO,\r\n  null as DOCUMENT_DATE,\r\n  c.PERSON_NAME,\r\n  c.PERSON_CODE,\r\n  a.SALDO_AWAL AS SALDO, \r\n  0 AS KREDIT,\r\n  0 AS DEBET,\r\n  a.PERSON_ID,\r\n  \'SALDO AWAL\' as NOTE\r\n FROM \r\n  PERSON_balance a\r\n INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nWHERE RIGHT(c.TIPE,1) = 1  \r\n&V1\r\n&X1\r\n\r\nUNION ALL\r\n\r\n/* PENJUALAN DAN RETUR */\r\nSELECT\r\n  3 AS URUT, \r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  c.PERSON_NAME,\r\n  c.PERSON_CODE,\r\n  0 AS SALDO,\r\n  /*IF(a.TOTAL_NET<>0,0,(SELECT SUM(SUBTOTAL*-1) FROM NOTA_PENJUALAN_DETAIL b,ITEM i WHERE a.NOTA_PENJUALAN_ID=b.NOTA_PENJUALAN_ID AND i.ITEM_ID=b.ITEM_ID AND i.JENIS_ID=@UM)) AS KREDIT,\r\n  IF(a.TOTAL_NET<>0,a.TOTAL_NET,(SELECT SUM(SUBTOTAL) FROM NOTA_PENJUALAN_DETAIL b,ITEM i WHERE a.NOTA_PENJUALAN_ID=b.NOTA_PENJUALAN_ID AND i.ITEM_ID=b.ITEM_ID AND i.JENIS_ID<>@UM)) AS   DEBET,*/\r\n  0 as KREDIT,\r\n  a.TOTAL_NET as DEBET,\r\n a.PERSON_ID,\r\n  a.NOTE\r\nFROM\r\n  nota_penjualan a\r\n  INNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\n INNER JOIN PERSON c On (a.PERSON_ID = c.PERSON_ID)\r\nWHERE  (NET_BYR1 = 0) AND (STATUS_ID <> @DELETE)\r\n   &V1 \r\n  &T1\r\n\r\nUNION ALL\r\n/* PELUNASAN DGN BKM */\r\nSELECT\r\n  4 AS URUT, \r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  c.PERSON_NAME,\r\n  c.PERSON_CODE,\r\n  0 AS SALDO,\r\n  b.AMOUNT + b.DISCOUNT AS KREDIT,\r\n  0  AS DEBET,\r\n  a.PERSON_ID,\r\n  CONCAT(TRIM(IFNULL(b.NOTE, \' \' )) ,\'  \',  x.DOCUMENT_NO ) AS NOTE\r\nFROM\r\n  receipt a\r\n   INNER JOIN receipt_detail b ON (a.RECEIPT_ID = b.RECEIPT_ID) \r\n INNER JOIN PERSON c On (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\n  INNER JOIN NOTA_PENJUALAN_AGING x ON (b.NOTA_PENJUALAN_AGING_ID = x.NOTA_PENJUALAN_AGING_ID)\r\nWHERE  TRUE\r\n  &V1  \r\n  &T1\r\n\r\nUNION ALL\r\n/* PELUNASAN DGN BKK */ \r\nSELECT\r\n  5 AS URUT, \r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  c.PERSON_NAME,\r\n  c.PERSON_CODE,\r\n  0 AS SALDO,\r\n  (b.AMOUNT + b.DISCOUNT) * -1 AS KREDIT,\r\n  0  AS DEBET,\r\n  a.PERSON_ID,\r\n  CONCAT(TRIM(IFNULL(b.NOTE, \' \' )) ,\'  \',  x.DOCUMENT_NO ) AS NOTE\r\nFROM\r\n  PAYMENT a\r\n   INNER JOIN PAYMENT_detail b ON (a.PAYMENT_ID = b.PAYMENT_ID) \r\n INNER JOIN PERSON c On (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\n  INNER JOIN NOTA_PENJUALAN_AGING x ON (b.NOTA_PENJUALAN_AGING_ID = x.NOTA_PENJUALAN_AGING_ID)\r\nWHERE  TRUE\r\n  &V1  \r\n  &T1\r\n\r\n\r\n', 74, 'KARTU PIUTANG', 4, '2007-07-18 17:04:27', '2014-04-14 11:18:56', 0, NULL, 'KARTU PIUTANG'),
	(77, '/* GIRO / CHEQUE ASAL */\r\nSELECT \r\n  IF(STATUS_FLAG = \'M\',7,3) as URUT, \r\n  \'PENCAIRAN \' AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 AS SALDO, \r\n   (IF(STATUS_FLAG = \'M\', a.TOTAL_AMOUNT,0) ) as DEBET,\r\n   (IF(STATUS_FLAG = \'K\', a.TOTAL_AMOUNT,0)) as KREDIT,\r\n\r\n   0 as PERSON_ID,\r\n   \'\' as PERSON_CODE,\r\n  \' \' PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  \' \' AS DOCUMENT_REFF_NO,\r\n\r\n  a.DOCUMENT_DATE,\r\n  a.NOTE,\r\n  a.PERIOD_NAME\r\nFROM\r\n  cheque a   \r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\nWHERE\r\n  a.STATUS_ID in ( @CLEAR, @TOLAK)\r\n&X2\r\n&P1\r\n&T1\r\n', 79, NULL, 1, '2007-08-20 09:44:01', '2007-08-20 09:47:58', 1, NULL, NULL),
	(78, '(SELECT  \r\n  \'PO\' as TIPE,\r\n  DATE_FORMAT(DOCUMENT_DATE,\'%Y%m\' ),\r\n  PERIOD_NAME,\r\n  DOCUMENT_DATE,\r\n  PO_ID as TRANS_ID,\r\n  DOCUMENT_NO\r\nFROM PO\r\nWHERE \r\n  PERIOD_NAME<>DATE_FORMAT(DOCUMENT_DATE,\'%Y%m\' )\r\n)\r\nUNION\r\n(SELECT  \r\n  \'SO\' as TIPE,\r\n  DATE_FORMAT(DOCUMENT_DATE,\'%Y%m\' ),\r\n  PERIOD_NAME,\r\n  DOCUMENT_DATE,\r\n  SO_ID as TRANS_ID,\r\n  DOCUMENT_NO\r\nFROM SO\r\nWHERE \r\n  PERIOD_NAME<>DATE_FORMAT(DOCUMENT_DATE,\'%Y%m\' )\r\n)\r\nUNION\r\n(SELECT  \r\n  \'INVOICE\' as TIPE,\r\n  DATE_FORMAT(DOCUMENT_DATE,\'%Y%m\' ),\r\n  PERIOD_NAME,\r\n  DOCUMENT_DATE,\r\n  INVOICE_ID as TRANS_ID,\r\n  DOCUMENT_NO\r\nFROM INVOICE\r\nWHERE \r\n  PERIOD_NAME<>DATE_FORMAT(DOCUMENT_DATE,\'%Y%m\' )\r\n)\r\nUNION\r\n(SELECT  \r\n  \'NOTA_PENJUALAN\' as TIPE,\r\n  DATE_FORMAT(DOCUMENT_DATE,\'%Y%m\' ),\r\n  PERIOD_NAME,\r\n  DOCUMENT_DATE,\r\n  NOTA_PENJUALAN_ID as TRANS_ID,\r\n  DOCUMENT_NO\r\nFROM NOTA_PENJUALAN\r\nWHERE \r\n  PERIOD_NAME<>DATE_FORMAT(DOCUMENT_DATE,\'%Y%m\' )\r\n)\r\nUNION\r\n(SELECT  \r\n  \'RECEIPT\' as TIPE,\r\n  DATE_FORMAT(DOCUMENT_DATE,\'%Y%m\' ),\r\n  PERIOD_NAME,\r\n  DOCUMENT_DATE,\r\n  RECEIPT_ID as TRANS_ID,\r\n  DOCUMENT_NO\r\nFROM RECEIPT\r\nWHERE \r\n  PERIOD_NAME<>DATE_FORMAT(DOCUMENT_DATE,\'%Y%m\' )\r\n)\r\nUNION\r\n(SELECT  \r\n  \'PAYMENT\' as TIPE,\r\n  DATE_FORMAT(DOCUMENT_DATE,\'%Y%m\' ),\r\n  PERIOD_NAME,\r\n  DOCUMENT_DATE,\r\n  PAYMENT_ID as TRANS_ID,\r\n  DOCUMENT_NO\r\nFROM PAYMENT\r\nWHERE \r\n  PERIOD_NAME<>DATE_FORMAT(DOCUMENT_DATE,\'%Y%m\' )\r\n)\r\nUNION\r\n(SELECT  \r\n  \'ADJ_STOK\' as TIPE,\r\n  DATE_FORMAT(DOCUMENT_DATE,\'%Y%m\' ),\r\n  PERIOD_NAME,\r\n  DOCUMENT_DATE,\r\n  ADJUST_QTY_ID as TRANS_ID,\r\n  DOCUMENT_NO\r\nFROM ADJUST_QTY\r\nWHERE \r\n  PERIOD_NAME<>DATE_FORMAT(DOCUMENT_DATE,\'%Y%m\' )\r\n)\r\nUNION\r\n(SELECT  \r\n  \'BUILD\' as TIPE,\r\n  DATE_FORMAT(DOCUMENT_DATE,\'%Y%m\' ),\r\n  PERIOD_NAME,\r\n  DOCUMENT_DATE,\r\n  BUILD_ID as TRANS_ID,\r\n  DOCUMENT_NO\r\nFROM BUILD\r\nWHERE \r\n  PERIOD_NAME<>DATE_FORMAT(DOCUMENT_DATE,\'%Y%m\' )\r\n)', 799, 'TEST PERIOD', 1, '2007-08-29 12:35:17', '2007-08-29 12:53:26', 1, NULL, 'TEST Tanggal transaksi sesuai period'),
	(79, '/*\r\n hpp_flag = 1 =>  berpengaruh pd nilai HPP\r\n hpp_flag = 0 => TIDAK berpengaruh pd nilai HPP\r\n\r\n URUT :\r\n Agar transaksi sesuai urutannya, agar beli duluan baru jualan pd tgl yg sama. disort dari no urut\r\n\r\n 10=  unit_price di adjust_qty_detail diset sesuai hpp\r\n 20= unit_price di build_detail di set sesuai hpp.\r\n 30= unit_price di inventory_out_detail( penjualan) di set sesuai hpp\r\n 40= unit_price di nota_penjualan_detail( penjualan) di set sesuai hpp\r\n\r\n 2,0,6-100 = tdk diset apa 2x.\r\n\r\n\r\n */\r\n SELECT\r\n   0 as URUT,\r\n   PERIOD_NAME,\r\n   1 AS HPP_FLAG,\r\n    \'SALDO\' AS DOCUMENT_CLASS_CODE,\r\n   \'\' AS DOCUMENT_NO,   \'\' AS DOCUMENT_REFF_NO,\r\n   \'2000/01/01\' as DOCUMENT_DATE,\r\n   a.WAREHOUSE_ID ,\r\n   a.ITEM_ID,\r\n   \'\' as DTL_ID,\r\n   SUM(a.QTY_AWAL) AS MASUK,\r\n   0 AS KELUAR,\r\n   MAX(HPP) as HARGA,\r\n     SUM(a.QTY_AWAL)  *   MAX(HPP)  as SUBTOTAL,\r\n   null as PERSON_ID\r\n FROM\r\n     item_balance a\r\n WHERE TRUE\r\n &X1/*ini tidak ada*/\r\n GROUP BY\r\n   PERIOD_NAME,ITEM_ID\r\n\r\n UNION ALL\r\n /* BTB */\r\n SELECT\r\n   1 as URUT,\r\n   PERIOD_NAME,\r\n   1  AS HPP_FLAG,\r\n   a.DOCUMENT_CLASS_CODE,\r\n   a.DOCUMENT_NO,\r\n a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.WAREHOUSE_ID,\r\n   b.ITEM_ID ,\r\n   b.INVENTORY_IN_DETAIL_ID as DTL_ID,\r\n   b.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n   0 AS KELUAR,\r\n   b.UNIT_PRICE as HARGA,\r\n   b.Subtotal AS SUBTOTAL,\r\n   a.PERSON_ID\r\n FROM\r\n    INVENTORY_IN a\r\n   INNER JOIN INVENTORY_IN_DETAIL b ON (a.INVENTORY_IN_ID = b.INVENTORY_IN_ID )\r\n   inner join item i on b.item_id = i.item_id\r\n WHERE TRUE and i.jenis_id = @goods\r\n &T1\r\n &X2\r\n UNION ALL\r\n /*     INVOICE utk yg tanpa BTB   */\r\n SELECT\r\n   2 as URUT,\r\n   PERIOD_NAME,\r\n   1  AS HPP_FLAG,\r\n   a.DOCUMENT_CLASS_CODE,\r\n   a.DOCUMENT_NO,\r\n a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.WAREHOUSE_ID,\r\n   b.ITEM_ID ,\r\n   b.INVENTORY_IN_DETAIL_ID as DTL_ID,\r\n   b.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n   0 AS KELUAR,\r\n   b.UNIT_PRICE as HARGA,\r\n   b.Subtotal AS SUBTOTAL,\r\n   a.PERSON_ID\r\n FROM\r\n    INVOICE a\r\n   INNER JOIN INVOICE_DETAIL b ON (a.INVOICE_ID = b.INVOICE_ID )\r\n   inner join item i on b.item_id = i.item_id\r\n   INNER JOIN COA c ON (b.COA_ID = c.COA_ID)\r\n   \r\n WHERE\r\n  (INVENTORY_IN_DETAIL_ID IS NULL) and i.jenis_id = @goods\r\n\r\n &T1\r\n &X2\r\n UNION ALL\r\n\r\n /* TRANSAKSI JUAL  SURAT JALAN */\r\n /* INVENTORY_OUT */\r\n SELECT\r\n   30 as URUT,\r\n   PERIOD_NAME,\r\n   0  AS HPP_FLAG,\r\n   a.DOCUMENT_CLASS_CODE,\r\n   a.DOCUMENT_NO,\r\n   a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.WAREHOUSE_ID,\r\n   b.ITEM_ID ,\r\n   b.INVENTORY_OUT_DETAIL_ID as DTL_ID,\r\n   0 AS MASUK,\r\n   b.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\n   b.HPP   AS HARGA,\r\n    b.ENTERED_QTY * b.BASE_QTY  * b.HPP    AS SUBTOTAL,\r\n    a.PERSON_ID\r\n FROM\r\n    INVENTORY_OUT a\r\n   INNER JOIN INVENTORY_OUT_DETAIL b ON (a.INVENTORY_OUT_ID = b.INVENTORY_OUT_ID )\r\n \r\n WHERE TRUE\r\n &T1\r\n &X2\r\n\r\n UNION ALL\r\n\r\n /* TRANSAKSI JUAL */\r\n /* NOTA_PENJUALAN            */\r\n SELECT\r\n   40 as URUT,\r\n   PERIOD_NAME,\r\n   0  AS HPP_FLAG,\r\n   a.DOCUMENT_CLASS_CODE,\r\n   a.DOCUMENT_NO,\r\n   a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.WAREHOUSE_ID,\r\n   b.ITEM_ID ,\r\n   b.NOTA_PENJUALAN_DETAIL_ID as DTL_ID,\r\n   0 AS MASUK,\r\n   b.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\n   b.HPP   AS HARGA,\r\n    b.ENTERED_QTY * b.BASE_QTY  * b.HPP    AS SUBTOTAL,\r\n   a.PERSON_ID\r\n FROM\r\n    NOTA_PENJUALAN a\r\n   INNER JOIN NOTA_PENJUALAN_DETAIL b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID )\r\n WHERE (INVENTORY_OUT_DETAIL_ID IS NULL )\r\n &T1\r\n &X2\r\nUNION ALL\r\n /* TRANSAKSI ADJUSTMENT QTY */\r\n /* ADJ_QTY           */\r\n /* PENGARUH KE HPP */\r\n SELECT\r\n   2 as URUT,\r\n   PERIOD_NAME,\r\n   1 AS HPP_FLAG,\r\n   a.DOCUMENT_CLASS_CODE,\r\n   a.DOCUMENT_NO,\r\n   a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.WAREHOUSE_ID,\r\n   b.ITEM_ID ,\r\n   b.adjust_qty_detail_ID  as DTL_ID,\r\n   b.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n   0 AS KELUAR,\r\n   b.UNIT_PRICE   as HARGA,\r\n   b.Subtotal    AS SUBTOTAL,\r\n   null as PERSON_ID\r\n FROM\r\n   adjust_qty a\r\n   INNER JOIN adjust_qty_detail b ON (a.ADJUST_QTY_ID = b.ADJUST_QTY_ID)\r\n   \r\n WHERE a.coa_id is null\r\n &T1\r\n &X2\r\n UNION ALL\r\n  /* TRANSAKSI ADJUSTMENT AMOUNT */\r\n /* ADJ_AMOUNT           */\r\n /* PENGARUH KE HPP */\r\n SELECT\r\n   2 as URUT,\r\n   PERIOD_NAME,\r\n   1 AS HPP_FLAG,\r\n   a.DOCUMENT_CLASS_CODE,\r\n   a.DOCUMENT_NO,\r\n   a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n  0 AS WAREHOUSE_ID,\r\n   b.ITEM_ID ,\r\n   b.adjust_AMOUNT_detail_ID  as DTL_ID,\r\n   0 AS MASUK,\r\n   0 AS KELUAR,\r\n   0    as HARGA,\r\n   b.ENTERED_AMOUNT    AS SUBTOTAL,\r\n   null as PERSON_ID\r\n FROM\r\n   adjust_AMOUNT a\r\n   INNER JOIN adjust_AMOUNT_detail b ON (a.ADJUST_AMOUNT_ID = b.ADJUST_AMOUNT_ID)\r\n   \r\n WHERE  TRUE\r\n &T1\r\n &X2\r\n UNION ALL\r\n /* TRANSAKSI ADJUSTMENT QTY */\r\n /* ADJ_QTY           */\r\n /* TDK PENGARUH KE HPP, MINTA DI ISI HARGANYA */\r\n SELECT\r\n 10 as URUT,\r\n   PERIOD_NAME,\r\n  0 AS HPP_FLAG,\r\n   a.DOCUMENT_CLASS_CODE,\r\n   a.DOCUMENT_NO,\r\n   a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.WAREHOUSE_ID,\r\n   b.ITEM_ID ,\r\n   b.adjust_qty_detail_ID  as DTL_ID,\r\n   b.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n   0 AS KELUAR,\r\n   b.UNIT_PRICE   as HARGA,\r\n   b.Subtotal    AS SUBTOTAL,\r\n   null as PERSON_ID\r\n FROM\r\n   adjust_qty a\r\n   INNER JOIN adjust_qty_detail b ON (a.ADJUST_QTY_ID = b.ADJUST_QTY_ID)\r\n   \r\n WHERE a.coa_id is NOT NULL\r\n &T1\r\n &X2\r\n UNION ALL\r\n /* TRANSAKSI RAKIT */\r\n /* BUILD  BAHAN BAKU       */\r\n SELECT\r\n   20 as URUT,\r\n   PERIOD_NAME,\r\n   0 AS HPP_FLAG,\r\n   a.DOCUMENT_CLASS_CODE,\r\n   a.DOCUMENT_NO,\r\n   a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.WAREHOUSE_ID,\r\n   b.ITEM_ID ,\r\n   b.build_detail_ID as DTL_ID,\r\n   0 AS MASUK,\r\n     b.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\n    b.UNIT_PRICE   AS HARGA,\r\n    SUBTOTAL,\r\n    null as PERSON_ID\r\n FROM\r\n   build a\r\n   INNER JOIN build_detail b ON (a.build_ID = b.build_ID)\r\n   \r\n WHERE true\r\n &T1\r\n &X2\r\n UNION ALL\r\n\r\n\r\n /* TRANSAKSI RAKIT */\r\n /* BUILD  ITEM YG DIBUAT       */\r\n SELECT\r\n   100 as URUT,\r\n   PERIOD_NAME,\r\n   1 AS HPP_FLAG,\r\n   a.DOCUMENT_CLASS_CODE,\r\n   a.DOCUMENT_NO,\r\n   a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.WAREHOUSE_ID,\r\n   a.ITEM_ID ,\r\n   a.build_ID as DTL_ID,\r\n   a.ENTERED_QTY   * a.BASE_QTY AS MASUK,\r\n   0 AS KELUAR,\r\n    a.TOTAL_AMOUNT / a.ENTERED_QTY * a.BASE_QTY  AS HARGA,\r\n   a.TOTAL_AMOUNT  AS SUBTOTAL,\r\n   null as PERSON_ID\r\n FROM\r\n   build a\r\n   \r\n WHERE true\r\n &T1\r\n&X4', 111, 'HPP dgn Surat Jalan', 2, '2007-08-31 16:24:20', '2019-06-27 10:47:45', 1, NULL, NULL),
	(80, '/*ini untuk cek account item yg sudah diganti tapi ga sinkron sama transaksinya*/\r\n/*ini utk ngecek pembelian*/\r\n(\r\nSELECT\r\n     \'PEMBELIAN\' as TIPE,\r\n     b.DOCUMENT_NO,\r\n     b.PERIOD_NAME,\r\n     I.ITEM_CODE,\r\n     I.ITEM_DESCRIPTION, \r\n     a.COA_ID as COA_ID_TRANS, \r\n     ca.COA_NAME as COA_TRANS, \r\n     I.COA_ID as COA_ID_ITEM,\r\n     cb.COA_NAME as COA_ITEM \r\nFROM \r\n    INVOICE_DETAIL a\r\nLEFT JOIN INVOICE b ON (a.INVOICE_ID=b.INVOICE_ID)\r\nLEFT JOIN ITEM I on (I.ITEM_ID=a.ITEM_ID)\r\nLEFT JOIN COA cb ON (I.COA_ID=cb.COA_ID)\r\nLEFT JOIN COA ca ON (a.COA_ID=ca.COA_ID)\r\nWHERE \r\n  b.DOCUMENT_TYPE_ID<>5\r\nAND I.COA_ID<>a.COA_ID \r\n)\r\nUNION ALL\r\n/*retur beli*/\r\n(\r\nSELECT\r\n    \'RETUR BELI\' as TIPE,\r\n     b.DOCUMENT_NO,\r\n     b.PERIOD_NAME,\r\n     I.ITEM_CODE,\r\n     I.ITEM_DESCRIPTION, \r\n     a.COA_ID as COA_ID_TRANS, \r\n     ca.COA_NAME as COA_TRANS, \r\n     I.COA_ID as COA_ID_ITEM,\r\n     cb.COA_NAME as COA_ITEM \r\nFROM \r\n    INVOICE_DETAIL a\r\nLEFT JOIN INVOICE b ON (a.INVOICE_ID=b.INVOICE_ID)\r\nLEFT JOIN ITEM I on (I.ITEM_ID=a.ITEM_ID)\r\nLEFT JOIN COA cb ON (I.COA_RET_BELI_ID=cb.COA_ID)\r\nLEFT JOIN COA ca ON (a.COA_ID=ca.COA_ID)\r\nWHERE \r\n  b.DOCUMENT_TYPE_ID=5\r\nAND I.COA_RET_BELI_ID<>a.COA_ID \r\n)\r\n\r\nUNION ALL\r\n\r\n(\r\nSELECT\r\n    \'PENJUALAN\' as TIPE,\r\n     b.DOCUMENT_NO,\r\n     b.PERIOD_NAME,\r\n     I.ITEM_CODE,\r\n     I.ITEM_DESCRIPTION, \r\n     a.COA_ID as COA_ID_TRANS, \r\n     ca.COA_NAME as COA_TRANS, \r\n     I.COA_ID as COA_ID_ITEM,\r\n     cb.COA_NAME as COA_ITEM \r\nFROM \r\n    NOTA_PENJUALAN_DETAIL a\r\nLEFT JOIN NOTA_PENJUALAN b ON (a.NOTA_PENJUALAN_ID=b.NOTA_PENJUALAN_ID)\r\nLEFT JOIN ITEM I on (I.ITEM_ID=a.ITEM_ID)\r\nLEFT JOIN COA cb ON (I.COA_JUAL_ID=cb.COA_ID)\r\nLEFT JOIN COA ca ON (a.COA_ID=ca.COA_ID)\r\nWHERE \r\n  b.DOCUMENT_TYPE_ID<>5\r\nAND I.COA_JUAL_ID<>a.COA_ID \r\n)\r\n\r\nUNION ALL\r\n\r\n(\r\nSELECT\r\n    \'RETUR JUAL\' as TIPE,\r\n     b.DOCUMENT_NO,\r\n     b.PERIOD_NAME,\r\n     I.ITEM_CODE,\r\n     I.ITEM_DESCRIPTION, \r\n     a.COA_ID as COA_ID_TRANS, \r\n     ca.COA_NAME as COA_TRANS, \r\n     I.COA_ID as COA_ID_ITEM,\r\n     cb.COA_NAME as COA_ITEM \r\nFROM \r\n    NOTA_PENJUALAN_DETAIL a\r\nLEFT JOIN NOTA_PENJUALAN b ON (a.NOTA_PENJUALAN_ID=b.NOTA_PENJUALAN_ID)\r\nLEFT JOIN ITEM I on (I.ITEM_ID=a.ITEM_ID)\r\nLEFT JOIN COA cb ON (I.COA_RET_JUAL_ID=cb.COA_ID)\r\nLEFT JOIN COA ca ON (a.COA_ID=ca.COA_ID)\r\nWHERE \r\n  b.DOCUMENT_TYPE_ID=5\r\nAND I.COA_RET_JUAL_ID<>a.COA_ID \r\n)', 798, 'TEST SINKRON ACC ITEM', 3, '2007-09-04 13:44:10', '2007-09-04 14:00:38', 3, NULL, NULL),
	(81, '/* TEST SINKRON PO & INVOICE */\r\nSelect X.DOCUMENT_NO, A.RECEIVED_ENTERED_QTY, JML,  A.*\r\n        from\r\n         PO_DETAIL A\r\n         INNER JOIN PO X ON (A.PO_ID = X.PO_ID)\r\n         INNER JOIN\r\n         ( SELECT PO_DETAIL_ID, SUM(ENTERED_QTY * BASE_QTY) AS JML\r\n         FROM\r\n        INVOICE_DETAIL\r\n         GROUP BY PO_DETAIL_ID\r\n         ) AS B ON (A.PO_DETAIL_ID = B.PO_DETAIL_ID)\r\n        where\r\n         ABS(A.RECEIVED_ENTERED_QTY - JML) > 0.0001\r\n\r\n\r\n', 702, NULL, 0, '2007-09-27 15:21:59', '2007-09-27 15:22:28', 0, NULL, '/* TEST SINKRON PO & INVOICE */'),
	(82, 'Select * from receipt r \r\n left join cheque  c on (r.cheque_id = c.cheque_id) \r\nwhere r.cheque_id is not null  and c.cheque_id is null and r.status_id <> @delete\r\nand r.total_amount <> 0 ', 755, NULL, 0, '2007-09-28 14:18:43', '2007-09-28 14:19:15', 0, NULL, 'TEST BG MASUK'),
	(83, 'Select * from PAYMENT r \r\n left join cheque  c on (r.cheque_id = c.cheque_id) \r\nwhere r.cheque_id is not null  and c.cheque_id is null and r.status_id <> @delete\r\nand r.total_amount <> 0 ', 756, NULL, 0, '2007-09-28 14:19:01', '2007-09-28 14:19:45', 0, NULL, 'TEST BG KELUAR'),
	(84, 'SELECT  * \r\nFROM COA\r\nWHERE \r\nCOA_ID  =   :COA_DEBET_ID \r\nUNION ALL\r\nSELECT  * \r\nFROM COA\r\nWHERE \r\nCOA_ID  =  :COA_KREDIT_ID ', 170, 'PENYUSUTAN', 0, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 0, NULL, NULL),
	(85, 'Select \'BTB\' AS TRANSAKSI,\r\n   a.inventory_in_detail_id as id, \r\n   i.item_description nama_item\r\n       \r\n from inventory_in_detail  a\r\n inner join item i on (a.item_id = i.item_id)\r\nwhere base_qty > 1 and i.uom_code = a.entered_uom\r\nunion all\r\nSelect \'S.Jalan\' AS TRANSAKSI,\r\n   a.inventory_out_detail_id as id, \r\n   i.item_description nama_item\r\n       \r\n from inventory_out_detail  a\r\n inner join item i on (a.item_id = i.item_id)\r\nwhere base_qty > 1 and i.uom_code = a.entered_uom\r\nunion all\r\nSelect \'po\' AS TRANSAKSI,\r\n   a.po_detail_id as id, \r\n   i.item_description nama_item\r\n       \r\n from po_detail  a\r\n inner join item i on (a.item_id = i.item_id)\r\nwhere base_qty > 1 and i.uom_code = a.entered_uom\r\nunion all\r\nSelect \'So\' AS TRANSAKSI,\r\n   a.so_detail_id as id, \r\n   i.item_description nama_item\r\n       \r\n from so_detail  a\r\n inner join item i on (a.item_id = i.item_id)\r\nwhere base_qty > 1 and i.uom_code = a.entered_uom\r\nunion all\r\nSelect \'JUAL\' AS TRANSAKSI,\r\n   a.nota_penjualan_detail_id as id, \r\n   i.item_description nama_item\r\n       \r\n from nota_penjualan_detail  a\r\n inner join item i on (a.item_id = i.item_id)\r\nwhere base_qty > 1 and i.uom_code = a.entered_uom\r\nunion all\r\nSelect \'Invoice\' AS TRANSAKSI,\r\n   a.invoice_detail_id as id, \r\n   i.item_description nama_item\r\n       \r\n from invoice_detail  a\r\n inner join item i on (a.item_id = i.item_id)\r\nwhere base_qty > 1 and i.uom_code = a.entered_uom\r\nunion all\r\nSelect \'tag\' AS TRANSAKSI,\r\n   a.tag_detail_id as id, \r\n   i.item_description nama_item\r\n       \r\n from tag_detail  a\r\n inner join item i on (a.item_id = i.item_id)\r\nwhere base_qty > 1 and i.uom_code = a.entered_uom\r\n', 719, NULL, 0, '2008-08-07 13:33:39', '2008-08-07 13:33:39', 0, NULL, NULL),
	(86, 'SELECT\r\n0 as URUT,\r\n0 as TIPE,\r\np.PERIOD_NAME,\r\n0 AS HPP_FLAG,\r\n\'SALDO\' AS DOCUMENT_CLASS_CODE,\r\n\'\' AS DOCUMENT_NO,\r\n\'\' AS DOCUMENT_REFF_NO,\r\nSTART_DATE as DOCUMENT_DATE,\r\na.WAREHOUSE_ID ,\r\n\'\' AS NOTE,\r\nI.ITEM_ID,\r\na.QTY_AWAL AS MASUK,\r\n0 AS KELUAR,\r\na.HPP as HARGA,\r\n0 as DISKON,\r\na.QTY_AWAL * a.HPP   AS SUBTOTAL,\r\n-1 as PERSON_ID\r\nFROM\r\nitem_balance a\r\nINNER JOIN PERIOD p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\nINNER JOIN ITEM I ON (a.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE True\r\n&X1\r\nUNION ALL\r\n/* TRANSAKSI BELI */\r\n/*     INVOICE         */\r\nSELECT\r\n1 as URUT,\r\n1 as TIPE,\r\nPERIOD_NAME,\r\n1  AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE, \r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\nb.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n0 AS KELUAR,\r\n(b.Subtotal / b.entered_qty  -  b.discount_price1/b.entered_qty  )/b.BASE_QTY  as HARGA,\r\nabs((b.DISCOUNT_PRICE * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1))   AS SUBTOTAL,\r\na.PERSON_ID\r\nFROM\r\nINVENTORY_IN a\r\nINNER JOIN INVENTORY_IN_detail b ON (a.INVENTORY_IN_ID = b.INVENTORY_IN_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE TRUE\r\n&T1\r\n&X2\r\nUNION ALL\r\n/* TRANSAKSI JUAL */\r\n/* NOTA_PENJUALAN            */\r\nSELECT\r\n1 as URUT,\r\n2 as TIPE,\r\na.PERIOD_NAME,\r\n0  AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\nb.HPP AS HARGA  ,\r\nabs((b.DISCOUNT_PRICE  * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1))    AS SUBTOTAL,\r\na.PERSON_ID\r\nFROM\r\nINVENTORY_OUT a\r\nINNER JOIN INVENTORY_OUT_detail b ON (a.INVENTORY_OUT_ID = b.INVENTORY_OUT_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE TRUE\r\n&T1\r\n&X2\r\nUNION ALL\r\n/* TRANSAKSI Pembelian yg ga pake BPB  */\r\n/* INVOICE          */\r\nSELECT\r\n1 as URUT,\r\n3 as TIPE,\r\na.PERIOD_NAME,\r\n0 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\nb.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n0 AS KELUAR,\r\n(b.Subtotal / b.entered_qty  -  b.discount_price1/b.entered_qty  )/b.BASE_QTY  as HARGA,\r\nabs((b.DISCOUNT_PRICE  * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1) )  AS SUBTOTAL,\r\na.PERSON_ID\r\nFROM\r\nINVOICE a\r\nINNER JOIN INVOICE_DETAIL b ON (a.INVOICE_ID = b.INVOICE_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE b.INVENTORY_IN_DETAIL_ID is NULL\r\n&T1\r\n&X2\r\nUNION ALL\r\n/* TRANSAKSI Penjualan yg ga pake SJ  */\r\n/* NOTA_PENJUALAN           */\r\nSELECT\r\n1 as URUT,\r\n4 as TIPE,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\nb.HPP  as HARGA,\r\nabs((b.DISCOUNT_PRICE  * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1)  )  AS SUBTOTAL,\r\na.PERSON_ID\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER JOIN NOTA_PENJUALAN_DETAIL b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE b.INVENTORY_OUT_DETAIL_ID IS NULL\r\n&T1\r\n&X2\r\nUNION ALL\r\n/* TRANSAKSI ADJUSTMENT QTY */\r\n/* ADJ_QTY           */\r\nSELECT\r\n2 as URUT,\r\n5 as TIPE,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\nb.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n0 AS KELUAR,\r\nb.UNIT_PRICE /b.BASE_