	(1, 'No PO', 1, 'No Purchase Order', 'v_par_dokumen', 'Y', 1, 2, 'SELECT a.DOCUMENT_NO, \r\na.DOCUMENT_NO, \r\na.DOCUMENT_NO as NO_PO, \r\na.DOCUMENT_DATE, \r\na.NOTE FROM PO a\r\nWHERE document_type_id <> 5 \r\n&kondisi\r\norder by a.DOCUMENT_NO\r\nlimit &limit ', 'NO_PO', 'DOCUMENT_NO', 0, '2006-11-08 11:56:44', 1, '2007-08-25 13:48:02', NULL, NULL, NULL, NULL),
	(2, 'CUSTOMER (2 Par.)', 1, 'Customer', 'v_par_customer', 'Y', 1, 2, 'SELECT a.PERSON_NAME, a.PERSON_NAME AS NAMA_PERSON,\r\na.PERSON_ID,\r\na.PERSON_CODE \r\n FROM \r\nPERSON a\r\nWHERE (a.active_flag = \'Y\'  ) \r\nand (TIPE  LIKE \'%1\')\r\n\r\n&kondisi\r\n\r\nORDER BY  a.PERSON_NAME\r\nlimit &limit', 'NAMA_PERSON', 'PERSON_NAME', 0, '2006-11-08 11:56:44', 4, '2007-07-19 11:00:07', NULL, NULL, NULL, NULL),
	(3, 'Tanggal (2 Par.) Default Not All', 2, 'Tanggal', 'v_par_tgl', 'Y', 0, 2, NULL, 'DOCUMENT_DATE', 'DOCUMENT_DATE', 0, '2006-11-08 11:56:44', 0, '2008-05-12 11:01:24', NULL, NULL, NULL, NULL),
	(4, 'Gudang (2 Par.)', 1, 'Gudang', 'v_par_gudang', '', 1, 2, 'SELECT a.WAREHOUSE_ID ,\r\n a.WAREHOUSE_NAME ,\r\na.ADDRESS_ID, a.PRIMARY_FLAG,\r\n a.WAREHOUSE_NAME as NAMA_GUDANG\r\n FROM warehouse a \r\nWHERE  active_flag = \'Y\' \r\n&kondisi\r\nORDER BY a.PRIMARY_FLAG DESC, a.WAREHOUSE_NAME\r\nlimit &limit', 'NAMA_GUDANG', 'WAREHOUSE_NAME', 0, '2006-11-08 11:56:44', 0, '0000-00-00 00:00:00', NULL, NULL, NULL, NULL),
	(5, 'Group Item (1 Par.)', 3, 'Group Item', 'v_par_group', 'Y', 1, 1, 'select\r\nERP_LOOKUP_SET_ID,\r\nERP_LOOKUP_SET_NAME,\r\nPROGRAM_CODE,\r\nPROGRAM_CODE as KODE_PROGAM\r\n from erp_lookup_set \r\nwhere true \r\nAND (PROGRAM_CODE = \'GROUP\' \r\nOR PROGRAM_CODE = \'GRADE\'\r\nOR PROGRAM_CODE = \'JENIS\'\r\nOR PROGRAM_CODE = \'ACCOUNT\'\r\nOR PROGRAM_CODE = \'COA\'\r\nOR PROGRAM_CODE = \'HPP\'\r\nOR PROGRAM_CODE = \'PRODUCT\'\r\nOR PROGRAM_CODE = \'MADE_IN\'\r\nOR PROGRAM_CODE = \'MEREK\'\r\nOR PROGRAM_CODE = \'TIPE\')\r\n&kondisi limit &limit', 'KODE_PROGAM', 'PROGRAM_CODE', 0, '2006-11-08 11:56:44', 63, '2006-02-24 09:56:13', NULL, NULL, NULL, NULL),
	(6, 'Item (2 Par.) All', 1, 'Nama Item', 'v_par_item_all', 'Y', 1, 2, 'SELECT i.ITEM_ID ,\r\ni.ITEM_DESCRIPTION ,\r\ni.ITEM_DESCRIPTION as NAMA_ITEM,\r\ni.ITEM_CODE,i.UOM_CODE\r\n FROM item i \r\nWHERE true &kondisi \r\nORDER BY  i.ITEM_DESCRIPTION\r\nlimit &limit', 'NAMA_ITEM', 'ITEM_DESCRIPTION', 0, '2006-11-08 11:56:44', 4, '2007-05-24 11:21:34', NULL, NULL, NULL, NULL),
	(7, 'Status Transaksi (1 Par.)', 1, 'Status Transaksi', 'v_par_status', 'Y', 1, 1, 'SELECT B.DISPLAY_NAME,\r\nERP_LOOKUP_VALUE_ID as STATUS_ID\r\nFROM ERP_LOOKUP_SET A\r\nINNER JOIN ERP_LOOKUP_VALUE B ON (A.ERP_LOOKUP_SET_ID = B.ERP_LOOKUP_SET_ID)\r\nWHERE PROGRAM_CODE = \'STATUS_ORDER\'\r\nAND B.Active_Flag = \'Y\' \r\n&kondisi \r\norder by B.primary_flag desc, b.seq\r\nlimit &limit', 'STATUS_ID', 'DISPLAY_NAME', 0, '2006-11-08 11:56:44', 4, '2007-03-17 14:25:58', NULL, NULL, NULL, NULL),
	(8, 'Nilai Tot. net <(1 Par.)', 1, 'Nilai Kurang Dari', 'v_par_nilai', 'N', 1, 2, NULL, NULL, NULL, 0, '2006-11-08 11:56:44', 0, '0000-00-00 00:00:00', NULL, NULL, NULL, NULL),
	(9, 'Bank (2 Par.)', 1, 'Bank', 'v_par_bank', 'Y', 1, 2, 'select a.COA_ID,\r\nA.COA_NAME,\r\nA.COA_NAME as NAMA_COA,\r\nb.ACCOUNT_NAME BANK_NAME,COA_CODE, a.COA_ID AS BANK_ID\r\nFROM\r\n  COA a\r\nINNER JOIN ACCOUNT b ON (a.ACCOUNT_ID=b.ACCOUNT_ID)\r\nWHERE a.ACTIVE_FLAG = \'Y\' and (b.ACCOUNT_TYPE_ID in (231277,231301))\r\n\r\n &kondisi\r\norder by PRIMARY_FLAG DESC , ACCOUNT_NAME\r\nlimit &limit\r\n', 'NAMA_COA', 'COA_NAME', 0, '2006-11-08 11:56:44', 1, '2007-08-09 12:48:52', NULL, NULL, NULL, NULL),
	(10, 'Perkiraan (2 Par.)', 1, 'Perkiraan', 'v_par_perkiraan', 'Y', 1, 2, 'SELECT a.PERKIRAAN_ID,\r\na.PERKIRAAN_NAME,\r\na.ACTIVE_FLAG, a.PRIMARY_FLAG \r\nFROM PERKIRAAN a \r\nwhere a.ACTIVE_FLAG = \'Y\' \r\n&kondisi\r\nOrder by a.PERKIRAAN_NAME\r\nlimit &limit', 'PERKIRAAN_ID', 'PERKIRAAN_NAME', 0, '2006-11-08 11:56:44', 0, '0000-00-00 00:00:00', NULL, NULL, NULL, NULL),
	(11, 'Account (2 Par.)', 4, 'Account', 'v_par_account_left', 'Y', 1, 2, 'SELECT a.ACCOUNT_ID,\r\na.ACCOUNT_NAME,\r\na.ACCOUNT_CODE,\r\na.ACCOUNT_NAME as NAMA_ACCOUNT\r\nFROM account a WHERE (a.ACTIVE_FLAG = \'Y\')\r\n&kondisi \r\nORDER BY a.ACCOUNT_CODE\r\nlimit &limit', 'NAMA_ACCOUNT', 'ACCOUNT_NAME', 0, '2006-11-08 11:56:44', 0, '2007-12-07 12:25:11', NULL, NULL, NULL, 'and ( LEFT (&allias.ACCOUNT_CODE, LENGTH(\'&param1\')) BETWEEN \'&param1\' and \'&param2\') '),
	(12, 'Periode (1 Par.) 1 Limited', 1, 'Periode', 'v_par_periodelimited', 'N', 1, 1, 'SELECT\r\n  A.*,\r\n  DATE_FORMAT(A.PERIOD_DATE,\'%Y-%m-%d\') PERIOD_STR,\r\n  DATE_ADD(A.PERIOD_DATE, INTERVAL 1 MONTH) TGL_BARU\r\nFROM\r\n  period A\r\nWHERE (OPEN_FLAG = \'Y\')\r\n&KONDISI\r\n&KONDISI2\r\nLIMIT 1\r\nUNION ALL \r\nSELECT\r\n  A.*,\r\n  DATE_FORMAT(A.PERIOD_DATE,\'%Y-%m-%d\') PERIOD_STR,\r\n  DATE_ADD(A.PERIOD_DATE, INTERVAL 1 MONTH) TGL_BARU\r\nFROM\r\n  period A\r\nWHERE (OPEN_FLAG = \'N\')\r\n&kondisi\r\nORDER BY PERIOD_NAME  desc', 'PERIOD_NAME', 'PERIOD_NAME', 0, '2006-11-08 11:56:44', 1, '2007-08-16 15:21:32', NULL, NULL, NULL, NULL),
