INSERT INTO `web_rpt_sql` (`WEB_RPT_SQL_ID`, `WEB_RPT_ID`, `WEB_RPT_GROUP_ID`, `STR_SQL`, `NO_URUT`, `NOTE`, `HEAD_ID`) VALUES
	(1, 10, 0, 'SELECT \r\n  i.ITEM_ID,\r\n  i.LOKASI,\r\n  i.ITEM_CODE,\r\n  i.ITEM_DESCRIPTION,\r\n  i.PART_NUMBER,\r\n  i.UOM_CODE,\r\n  i.MIN_STOCK,\r\n  i.ACTIVE_FLAG,\r\n  i.NOTE,\r\n  JENIS.DISPLAY_NAME AS JENIS,\r\n  GROUP_ITEM.DISPLAY_NAME AS KELOMPOK,\r\n  TIPE.DISPLAY_NAME AS TIPE,\r\n  MEREK.DISPLAY_NAME AS MEREK,\r\n  MADEIN.DISPLAY_NAME AS MADEIN,\r\n  VGRADE.DISPLAY_NAME AS GRADE\r\nFROM\r\n  item i\r\n  LEFT OUTER JOIN erp_lookup_value JENIS ON (i.JENIS_ID = JENIS.ERP_LOOKUP_VALUE_ID)\r\n  LEFT OUTER JOIN erp_lookup_value GROUP_ITEM ON (i.GROUP_ID = GROUP_ITEM.ERP_LOOKUP_VALUE_ID)\r\n  LEFT OUTER JOIN erp_lookup_value TIPE ON (i.TIPE_ID = TIPE.ERP_LOOKUP_VALUE_ID)\r\n  LEFT OUTER JOIN erp_lookup_value MEREK ON (i.MEREK_ID = MEREK.ERP_LOOKUP_VALUE_ID)\r\n  LEFT OUTER JOIN erp_lookup_value MADEIN ON (i.MADE_IN_ID = MADEIN.ERP_LOOKUP_VALUE_ID)\r\n  LEFT OUTER JOIN erp_lookup_value VGRADE ON (i.GRADE_ID = VGRADE.ERP_LOOKUP_VALUE_ID)\r\nwhere\r\ntrue \r\n&KONDISI\r\nORDER BY\r\n  i.ITEM_DESCRIPTION', 1, 'i', NULL),
	(2, 15, 0, 'SELECT \r\n  a.TO_QTY  AS QTY_KONVERSI,\r\n  i.ITEM_DESCRIPTION,\r\n  a.UOM_CODE,\r\n  i.ITEM_ID,\r\n  i.ITEM_CODE ,\r\n  i.PART_NUMBER,\r\n  i.UOM_CODE satuan,\r\n  i.MIN_STOCK,\r\n  i.ACTIVE_FLAG,\r\n  i.CREATED_BY,\r\n  i.CREATED_DATE,\r\n  i.LAST_UPDATE_DATE,\r\n  i.LAST_UPDATE_BY,\r\n  i.JENIS_ID,\r\n  i.GROUP_ID,\r\n  i.MADE_IN_ID,\r\n  i.GRADE_ID,\r\n  i.TIPE_ID,\r\n  i.MEREK_ID,\r\n  i.LOKASI\r\nFROM\r\n  item i \r\n  INNER JOIN item_uom a ON (a.ITEM_ID = i.ITEM_ID)\r\n \r\nWHERE true\r\n  &kondisi\r\nORDER BY\r\n  i.ITEM_DESCRIPTION', 1, 'ze', NULL),
	(3, 20, 0, 'select * from warehouse w\r\nwhere true\r\n&kondisi\r\n', 1, '', NULL),
	(4, 25, 0, 'select * from warehouse w\r\nwhere true\r\n&kondisi\r\n', 1, '', NULL),
	(5, 30, 9, 'SELECT \r\n  i.ITEM_CODE,\r\n  i.ITEM_DESCRIPTION,\r\n  i.PART_NUMBER,\r\n  i.ITEM_ID as ITEM,\r\n  i.UOM_CODE,\r\n sum(MASUK - KELUAR)  AS STOK\r\n FROM\r\n    item i\r\n    INNER JOIN ( SELECT\r\n0 as URUT,\r\n0 as TIPE,\r\np.PERIOD_NAME,\r\n0 AS HPP_FLAG,\r\n\'SALDO\' AS DOCUMENT_CLASS_CODE,\r\n\'\' AS DOCUMENT_NO,\r\n\'\' AS DOCUMENT_REFF_NO,\r\nSTART_DATE as DOCUMENT_DATE,\r\na.WAREHOUSE_ID ,\r\n\'\' AS NOTE,\r\nI.ITEM_ID,\r\na.QTY_AWAL AS MASUK,\r\n0 AS KELUAR,\r\na.HPP as HARGA,\r\n0 as DISKON,\r\na.QTY_AWAL * a.HPP   AS SUBTOTAL,\r\n-1 as PERSON_ID,\r\n0 as ID\r\nFROM\r\nitem_balance a\r\nINNER JOIN PERIOD p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\nINNER JOIN ITEM I ON (a.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE True\r\n  AND (a.PERIOD_NAME = :PERIOD_NAME1) \r\nUNION ALL\r\n/* TRANSAKSI BELI */\r\n/*     INVOICE         */\r\nSELECT\r\n1 as URUT,\r\n1 as TIPE,\r\nPERIOD_NAME,\r\n1  AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE, \r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\nb.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n0 AS KELUAR,\r\n(b.Subtotal / b.entered_qty  -  b.discount_price1/b.entered_qty  )/b.BASE_QTY  as HARGA,\r\nabs((b.DISCOUNT_PRICE * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1))   AS SUBTOTAL,\r\na.PERSON_ID,\r\na.INVENTORY_IN_ID AS ID\r\nFROM\r\nINVENTORY_IN a\r\nINNER JOIN INVENTORY_IN_detail b ON (a.INVENTORY_IN_ID = b.INVENTORY_IN_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE TRUE\r\n AND (a.DOCUMENT_DATE between  :11  and  :21) \r\n  AND (a.PERIOD_NAME >= :PERIOD_NAME1)\r\nUNION ALL\r\n/* TRANSAKSI JUAL */\r\n/* NOTA_PENJUALAN            */\r\nSELECT\r\n1 as URUT,\r\n2 as TIPE,\r\na.PERIOD_NAME,\r\n0  AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\nb.HPP/b.BASE_QTY  AS HARGA  ,\r\nabs((b.DISCOUNT_PRICE  * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1))    AS SUBTOTAL,\r\na.PERSON_ID,\r\na.INVENTORY_OUT_ID AS ID\r\nFROM\r\nINVENTORY_OUT a\r\nINNER JOIN INVENTORY_OUT_detail b ON (a.INVENTORY_OUT_ID = b.INVENTORY_OUT_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE TRUE\r\n AND (a.DOCUMENT_DATE between  :11  and  :21) \r\n  AND (a.PERIOD_NAME >= :PERIOD_NAME1)\r\nUNION ALL\r\n/* TRANSAKSI Pembelian yg ga pake BPB  */\r\n/* INVOICE          */\r\nSELECT\r\n1 as URUT,\r\n3 as TIPE,\r\na.PERIOD_NAME,\r\n0 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\nb.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n0 AS KELUAR,\r\n(b.Subtotal / b.entered_qty  -  b.discount_price1/b.entered_qty  )/b.BASE_QTY  as HARGA,\r\nabs((b.DISCOUNT_PRICE  * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1) )  AS SUBTOTAL,\r\na.PERSON_ID,\r\na.INVOICE_ID AS ID\r\nFROM\r\nINVOICE a\r\nINNER JOIN INVOICE_DETAIL b ON (a.INVOICE_ID = b.INVOICE_ID)\r\nINNER JOIN ITEM I ON (b.ITEM_ID=I.ITEM_ID and I.JENIS_ID=@GOODS)\r\nWHERE b.INVENTORY_IN_DETAIL_ID is NULL\r\n AND (a.DOCUMENT_DATE between  :11  and  :21) \r\n  AND (a.PERIOD_NAME >= :PERIOD_NAME1)\r\nUNION ALL\r\n/* TRANSAKSI Penjualan yg ga pake SJ  */\r\n/* NOTA_PENJUALAN           */\r\nSELECT\r\n1 as URUT,\r\n4 as TIPE,\r\na.PERIOD_NAME,\r\n1 AS HPP_FLAG,\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.WAREHOUSE_ID,\r\na.NOTE,\r\nI.ITEM_ID ,\r\n0 AS MASUK,\r\nb.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\nb.HPP/b.BASE_QTY   as HARGA,\r\nabs((b.DISCOUNT_PRICE  * b.ENTERED_QTY +  b.discount_price1) /b.BASE_QTY ) AS DISKON, \r\nabs((b.Subtotal - b.discount_price1)  )  AS SUBTOTAL,\r\na.PERSON_ID,\r\na.NOTA_PENJUALA