suk,0)+if(xx.keluar<0,abs(xx.keluar),0))-SUM(if(xx.Masuk<0,abs(xx.masuk),0)+if(xx.keluar>0,xx.keluar,0)) as AKHIR,\r\n0  as AWAL_rp, \r\nSUM( IF( (xx.TIPE=1 or xx.TIPE=3), IF( xx.Masuk>0,xx.masuk,0) + IF( xx.keluar<0 , abs(xx.keluar), 0),\r\n0)*xx.HARGA\r\n) as BELI_rp, \r\nSUM( IF( (xx.TIPE=1 or xx.TIPE=3), IF (xx.Masuk<0, abs(xx.masuk), 0) + IF(xx.keluar>0,xx.keluar,0),\r\n0)*xx.HARGA\r\n) as RB_rp,\r\nSUM( IF( (xx.TIPE=2 or xx.TIPE=4),IF( xx.Keluar>0,xx.keluar,0) + IF( xx.Masuk<0 , abs(xx.masuk), 0),\r\n0)*xx.HARGA\r\n) as JUAL_rp, \r\nSUM( IF( (xx.TIPE=2 or xx.TIPE=4),  IF (xx.Keluar<0, abs(xx.keluar), 0) + IF(xx.masuk>0,xx.masuk,0),\r\n0)*xx.HARGA\r\n) as RJ_rp,\r\nSUM( IF( xx.TIPE=5, xx.Masuk-xx.keluar, 0) *xx.HARGA ) as ADJ_rp, \r\nSUM( IF( xx.TIPE=6, xx.Masuk-xx.keluar, 0) *xx.HARGA ) as PAG_rp,\r\nSUM( IF( xx.TIPE=7, xx.Masuk-xx.keluar, 0) *xx.HARGA ) as BUILD_rp,\r\n(SELECT SUM(ENTERED_AMOUNT) \r\nFrom adjust_amount_detail b,adjust_amount a \r\nWHERE b.item_id=i.item_id and a.adjust_amount_id=b.adjust_amount_id AND (DATE(a.DOCUMENT_DATE) >=  :11 AND DATE(a.DOCUMENT_DATE) <= :21 )   \r\nGROUP BY ITEM_ID\r\n) as ADJ_NILAI_rp,\r\nSUM(if(xx.Masuk>0,xx.masuk,0)+if(xx.keluar<0,abs(xx.keluar),0)*xx.HARGA)- SUM(if(xx.Masuk<0,abs(xx.masuk),0)+if(xx.keluar>0,xx.keluar,0)*xx.HARGA) as AKHIR_rp\r\nFROM \r\nITEM i \r\ninner join (\r\n/*=====*/\r\n\r\n\r\nSELECT\r\n0 as URUT,\r\n0 as TIPE,\r\np.PERIOD_NAME,\r\n0 AS HPP_FLAG,\r\n\'SALDO\' AS DOCUMENT_CLASS_CODE,\r\n\'\' AS DOCUMENT_NO,\r\n\'\' AS DOCUMENT_REFF_NO,\r\nSTART_DATE as DOCUMENT_DATE,\r\na.WAREHOUSE_ID ,\r\n\'\' AS NOTE,\r\nI.ITEM_ID,\r\na.QTY_AWAL AS MASUK,\r\n0 AS KELUAR,\r\na.HPP as HARGA,\r\n0 as DISKON,\r\na.QTY_AWAL * a.HPP   AS SUBTOTAL,\r\n-1 as PERSON_ID,\r\n0 as ID\r\nFROM\r\nitem_balance a\r\nINNER JOIN PERIOD p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\nINNER JOIN ITEM I ON (a.ITEM_ID=I.ITEM_ID and I.JENIS_ID=1)\r\nWHERE True\r\nAND FALSE\r\nUNION ALL\r\n/* TRANSAKSI BELI */\r\n/*     INVOICE         */