	(1184, 660, 0, 'SELECT \r\nP.PERSON_NAME,\r\nP.PERSON_ID,\r\nP.PERSON_CODE,\r\nSUM(xx.DEBET) as DEBET,\r\nSUM(xx.KREDIT) as KREDIT, \r\nSUM(xx.TUNAI) as TUNAI,\r\nSUM(xx.saldo) AS SALDO\r\nFROM\r\nPERSON P\r\nLEFT JOIN \r\n(\r\n/* SALDO AWAL */\r\nSELECT\r\n\' \' AS DOCUMENT_CLASS_CODE,\r\n\' \' AS DOCUMENT_NO,\r\n:12 AS DOCUMENT_DATE,\r\na.SALDO_HUTANG AS SALDO, \r\nnol  AS KREDIT,\r\nnol  AS DEBET,\r\nnol AS TUNAI,\r\na.PERSON_ID,\r\n\'SALDO AWAL \'  AS NOTE\r\nFROM \r\nPERSON_balance a,\r\nnol \r\nWHERE true\r\nand a.period_name= :PERIOD_NAME1\r\nUNION ALL\r\n/* PENJUALAN DAN RETUR */\r\nSELECT\r\n\' \' AS DOCUMENT_CLASS_CODE,\r\n\' \' AS DOCUMENT_NO,\r\n:12 AS DOCUMENT_DATE,\r\na.TOTAL_NET AS SALDO,\r\n0  AS KREDIT,\r\n0  AS DEBET,\r\n0 AS TUNAI,\r\na.PERSON_ID,\r\n\'SALDO AWAL \'  AS NOTE\r\nFROM\r\nINVOICE a\r\nWHERE  a.document_type_id not in (3,4)\r\nand a.period_name= :PERIOD_NAME1\r\nand a.document_date < :12\r\nUNION ALL\r\n/* PELUNASAN DGN BKM */\r\nSELECT\r\n\' \' AS DOCUMENT_CLASS_CODE,\r\n\' \' AS DOCUMENT_NO,\r\n:12 AS DOCUMENT_DATE,\r\n(b.AMOUNT + b.DISCOUNT)   AS SALDO,\r\n0  AS KREDIT,\r\n0  AS DEBET,\r\n0 AS TUNAI,\r\na.PERSON_ID,\r\n\'SALDO AWAL \'  AS NOTE\r\nFROM\r\nreceipt a\r\nINNER JOIN receipt_detail b ON (a.RECEIPT_ID = b.RECEIPT_ID) \r\nINNER JOIN INVOICE_DUE x ON (b.INVOICE_DUE_ID = x.INVOICE_DUE_ID)\r\nWHERE  TRUE\r\nand a.period_name= :PERIOD_NAME1\r\nand a.document_date < :12\r\nUNION ALL\r\n/* PELUNASAN DGN BKK */ \r\nSELECT\r\n\' \' AS DOCUMENT_CLASS_CODE,\r\n\' \' AS DOCUMENT_NO,\r\n:12 AS DOCUMENT_DATE,\r\n(b.AMOUNT + b.DISCOUNT ) * -1 AS SALDO,\r\n0  AS KREDIT,\r\n0  AS DEBET,\r\n0 AS TUNAI,\r\na.PERSON_ID,\r\n\'SALDO AWAL \'  AS NOTE\r\nFROM\r\nPAYMENT a\r\nINNER JOIN PAYMENT_detail b ON (a.PAYMENT_ID = b.PAYMENT_ID) \r\nINNER JOIN INVOICE_DUE x ON (b.INVOICE_DUE_ID = x.INVOICE_DUE_ID)\r\nWHERE  TRUE\r\nand a.period_name= :PERIOD_NAME1\r\nand a.document_date < :12\r\nUNION ALL\r\n/* PEMBELIAN DAN RETUR */\r\nSELECT\r\na.DOCUME