	(1188, 1165, 0, 'SELECT * FROM \r\n(\r\nSELECT PERSON_id,\r\nSUM(IF(DATEDIFF(:12, JTEMPO) < 0, (TOTAL )  ,0)) AS BLM,\r\nSUM(IF(DATEDIFF(:12, JTEMPO)  BETWEEN 0 and 30, (TOTAL ) ,0)) AS BLN1,\r\nSUM(IF(DATEDIFF(:12 , JTEMPO) BETWEEN 31 and 60, (TOTAL ) ,0)) AS BLN2,\r\nSUM(IF(DATEDIFF(:12, JTEMPO) BETWEEN 61 and 90, (TOTAL ) ,0)) AS BLN3,\r\nSUM(IF(DATEDIFF(:12, JTEMPO) > 91, (TOTAL ) ,0)) AS BLNLEBIH3,\r\nSUM(TOTAL) AS TOTAL, \r\nPERSON_NAME, \r\nPERSON_CODE\r\nFROM\r\n(\r\n/* SALDO AWAL  DAN PELUNASAN */\r\nSELECT a.DOCUMENT_NO,\r\n\'SALDO AWAL \' DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.DUE_DATE AS JTEMPO,\r\nTIPE,\r\nk.PERSON_NAME,\r\nk.PERSON_CODE,\r\nk.PERSON_ID,\r\na.TOTAL_AMOUNT - IFNULL(sa.AMOUNT,0) AS TOTAL\r\nFROM \r\nPERSON_SALDO a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nLEFT JOIN \r\n(SELECT s.PERSON_SALDO_ID ,\r\nSUM(AMOUNT + y.DISCOUNT ) AS AMOUNT \r\nFROM \r\nNOTA_PENJUALAN_AGING s\r\nINNER JOIN RECEIPT_DETAIL y on (s.Nota_Penjualan_aging_id = y.Nota_Penjualan_aging_id)\r\nINNER JOIN receipt x on (x.receipt_id = y.receipt_id) and (date(x.DOCUMENT_DATE) <= :12 )\r\nGROUP BY s.PERSON_SALDO_ID \r\n) AS SA ON (a.PERSON_SALDO_ID  = sA.PERSON_SALDO_ID ) \r\nWHERE  k.FLAG_SUPP <> 1\r\n&kondisi\r\n/* END SALDO AWAL */  \r\nUNION ALL\r\n/* DARI PENJUALAN */ \r\nSELECT\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.JTEMPO,\r\nTIPE, \r\nk.PERSON_NAME,\r\nk.PERSON_CODE, \r\nk.PERSON_ID,\r\na.TOTAL_NET  - (IFNULL(rec.bayar,0)    +    IFNULL(rec_ret.bayar_ret,0) ) - (IFNULL(pay.bayar,0)   +    IFNULL(pay_ret.bayar_ret,0)) - IFNULL( c.retur,0) TOTAL\r\nFROM\r\nnota_penjualan a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nINNER JOIN NOTA_PENJUALAN_AGING g ON (a.NOTA_PENJUALAN_ID  = g.NOTA_PENJUALAN_ID)  AND (JUAL_FLAG = \'Y\')\r\nLEFT JOIN \r\n/* PELUNASAN NYA */ \r\n(SELECT y.NOTA_PENJUALAN_AGING_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR\r\nFROM\r\nRECEIPT z        \r\nINNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\nWHERE (date(z.DOCUMENT_DATE)   <= :12)\r\nGROUP BY\r\ny.NOTA_PENJUALAN_AGING_ID\r\n) AS rec ON (g.NOTA_PENJUALAN_AGING_ID = rec.NOTA_PENJUALAN_AGING_ID)\r\nLEFT JOIN \r\n/* PELUNASAN BKK */ \r\n(SELECT pd.NOTA_PENJUALAN_AGING_ID,\r\nSUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR\r\nFROM\r\nPAYMENT p\r\nINNER JOIN  PAYMENT_DETAIL pd ON (p.PAYMENT_ID = pd.PAYMENT_ID)\r\nWHERE (date(p.DOCUMENT_DATE)<= :12)\r\nand (pd.NOTA_PENJUALAN_AGING_ID IS NOT NULL)\r\nGROUP BY pd.NOTA_PENJUALAN_AGING_ID\r\n) AS pay ON (g.NOTA_PENJUALAN_AGING_ID = pay.NOTA_PENJUALAN_AGING_ID)\r\nLEFT JOIN \r\n/* RETUR REFER KE NO PENJUALAN */ \r\n(SELECT a.NOTA_PENJUALAN_OLD_ID AS NOTA_PENJUALAN_ID,\r\nSUM(a.TOTAL_NET) * -1 as RETUR\r\nFROM\r\nnota_penjualan a\r\nWHERE\r\n(DOCUMENT_TYPE_ID  >= 5 )\r\nAND  (a.NOTA_PENJUALAN_OLD_ID IS NOT NULL )\r\nAND   (date(DOCUMENT_DATE) <= :12 ) \r\nGROUP BY a.NOTA_PENJUALAN_OLD_ID    \r\n)  AS c on (a.nota_penjualan_id = c.nota_penjualan_id)\r\nLEFT JOIN \r\n/* PELUNASAN NYA RETUR */ \r\n(SELECT r.NOTA_PENJUALAN_OLD_ID AS NOTA_PENJUALAN_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR_RET\r\nFROM\r\nRECEIPT z        \r\nINNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\nINNER JOIN NOTA_PENJUALAN_AGING n ON (y.NOTA_PENJUALAN_AGING_ID = n.NOTA_PENJUALAN_AGING_ID  )\r\nINNER JOIN NOTA_PENJUALAN r ON (n.NOTA_PENJUALAN_ID = r.NOTA_PENJUALAN_ID)    \r\nWHERE (date(z.DOCUMENT_DATE)  <= :12)  \r\nGROUP BY\r\nr.NOTA_PENJUALAN_OLD_ID        \r\n) AS rec_ret ON (a.NOTA_PENJUALAN_ID = rec_ret.NOTA_PENJUALAN_ID)\r\nleft join\r\n/* PELUNASAN RETUR DGN BKK */ \r\n(SELECT r.NOTA_PENJUALAN_OLD_ID AS NOTA_PENJUALAN_ID,\r\nSUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR_RET\r\nFROM\r\nPAYMENT p\r\nINNER JOIN  PAYMENT_DETAIL pd ON (p.PAYMENT_ID = pd.PAYMENT_ID)\r\nINNER JOIN NOTA_PENJUALAN_AGING n ON (pd.NOTA_PENJUALAN_AGING_ID = n.NOTA_PENJUALAN_AGING_ID  )\r\nINNER JOIN NOTA_PENJUALAN r ON (n.NOTA_PENJUALAN_ID = r.NOTA_PENJUALAN_ID)    \r\nWHERE (date(p.DOCUMENT_DATE)<= :12)\r\nand (pd.NOTA_PENJUALAN_AGING_ID IS NOT NULL)\r\nGROUP BY n.NOTA_PENJUALAN_ID     \r\n) AS pay_ret ON (a.NOTA_PENJUALAN_ID = pay_ret.NOTA_PENJUALAN_ID)\r\nWHERE RIGHT(TIPE,1) = 1  AND\r\n(\r\n ((a.NET_BYR1 = 0) and   (a.DOCUMENT_TYPE_ID < 5)) OR \r\n(\r\n(a.DOCUMENT_TYPE_ID   >= 5) AND (a.NOTA_PENJUALAN_OLD_ID IS NULL)  \r\n)\r\n)\r\nAND (date(a.DOCUMENT_DATE)<=  :12 )\r\n&kondisi\r\n/* end penjualan */\r\n) as yy\r\nWHERE \r\n(TOTAL <> 0 ) \r\nGROUP BY \r\nPERSON_NAME ,\r\nPERSON_ID, \r\nPERSON_CODE \r\n\r\n) AS XX WHERE TOTAL <> 0 \r\n', NULL, '', NULL),
	(1189, 1167, 0, 'SELECT DISTINCT * FROM \r\n(\r\n/* SALDO AWAL  DAN PELUNASAN */\r\nSELECT a.DOCUMENT_NO,\r\n\'SALDO AWAL \' DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.DUE_DATE AS JTEMPO,\r\nTIPE,\r\nk.PERSON_NAME,\r\nk.PERSON_ID,\r\na.TOTAL_AMOUNT AS AWAL,\r\n0 AS JUAL,\r\nIFNULL(sa.AMOUNT,0) AS BAYAR_BKM,\r\n0  AS BAYAR_BKK,\r\n0 RETUR_JUAL\r\nFROM \r\nPERSON_SALDO a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nLEFT JOIN \r\n(SELECT s.PERSON_SALDO_ID ,\r\nSUM(y.AMOUNT + y.DISCOUNT ) AS AMOUNT \r\nFROM \r\nNOTA_PENJUALAN_AGING s\r\nINNER JOIN RECEIPT_DETAIL y on (s.Nota_Penjualan_aging_id = y.Nota_Penjualan_aging_id)\r\nINNER JOIN receipt x on (x.receipt_id = y.receipt_id) and (date(x.DOCUMENT_DATE) <= :13 )\r\nGROUP BY s.PERSON_SALDO_ID \r\n) AS SA ON (a.PERSON_SALDO_ID  = sA.PERSON_SALDO_ID ) \r\nWHERE   k.FLAG_SUPP <> 1\r\n&kondisi\r\n/* END SALDO AWAL */  \r\nUNION ALL\r\n/* DARI PENJUALAN */ \r\nSELECT\r\na.DOCUMENT_NO,\r\na.DOCUMENT_REFF_NO,\r\na.DOCUMENT_DATE,\r\na.JTEMPO,\r\nTIPE, \r\nk.PERSON_NAME,\r\nk.PERSON_ID,\r\n0 AS AWAL,\r\na.TOTAL_NET  AS JUAL,\r\nIFNULL(rec.bayar ,0)  + IFNULL(rec_ret.BAYAR_RET,0) AS BAYAR_BKM,\r\nIFNULL(pay.bayar,0)  + IFNULL(pay_ret.BAYAR_RET,0)   AS BAYAR_BKK,   \r\nIFNULL( c.retur,0) RETUR_JUAL\r\nFROM\r\nnota_penjualan a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\nINNER JOIN NOTA_PENJUALAN_AGING g ON (a.NOTA_PENJUALAN_ID  = g.NOTA_PENJUALAN_ID)  AND (JUAL_FLAG = \'Y\')\r\nLEFT JOIN \r\n/* PELUNASAN NYA */ \r\n(SELECT y.NOTA_PENJUALAN_AGING_ID,\r\nSUM(y.DISCOUNT + y.AMOUNT) as BAYAR\r\nFROM\r\nRECEIPT z        \r\nINNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\nWHERE date(z.DOCUMENT_DATE)  <= :13\r\nGROUP BY\r\ny.NOTA_PENJUALAN_AGING_ID\r\n) AS rec ON (g.NOTA_PENJUALAN_AGING_ID = rec.NOTA_PENJUALAN_AGING_ID)\r\nLEFT JOIN \r\n/* PELUNASAN BKK */ \r\n(SELECT pd.NOTA_PENJUALAN_AGING_ID,\r\nSUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR\r\nFROM\r\nPAYMENT p\r\nINNER JOIN  PAYMENT_DETAIL pd ON (p.PA