ASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  0 AS WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.adjust_AMOUNT_detail_ID  as DTL_ID, \r\n  0 AS SALDO,\r\n  0 AS SALDO_RP,\r\n  0 AS MASUK,\r\n  0 AS MASUK_RP,\r\n  0 AS KELUAR,\r\n0  AS KELUAR_RP,  \r\n  0 as BAHAN,  0 AS BAHAN_RP,  0  AS JADI ,  0 AS JADI_RP,\r\n0  AS ADJ ,  \r\n    b.ENTERED_AMOUNT AS ADJ_RP,\r\n\r\n  0 as HARGA,\r\n  b.ENTERED_AMOUNT AS ENTERED_AMOUNT\r\nFROM\r\n  adjust_AMOUNT a\r\n  INNER JOIN adjust_AMOUNT_detail b ON (a.ADJUST_AMOUNT_ID = b.ADJUST_AMOUNT_ID)\r\n  \r\n  INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE\r\n(i.JENIS_ID = 1) \r\n\r\nand A.period_name = :PERIOD_NAME1\r\nUNION ALL\r\n/* TRANSAKSI RAKIT */\r\n/* BUILD  BAHAN BAKU       */\r\nSELECT\r\n  5 as URUT,\r\n  7 as TIPE,\r\n  A.PERIOD_NAME,\r\n  0 AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.build_detail_ID as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n 0 AS MASUK,\r\n 0 AS MASUK_RP,\r\n  0 AS KELUAR,\r\n 0 AS KELUAR_RP,  \r\n  b.ENTERED_QTY * b.BASE_QTY AS BAHAN,  \r\n   b.SUBTOTAL  AS BAHAN_RP, \r\n  0  AS JADI ,  \r\n  0 AS JADI_RP,\r\n0  AS ADJ ,  \r\n  0 AS ADJ_RP,\r\n\r\n  b.UNIT_PRICE  AS HARGA,\r\n  b.SUBTOTAL  AS SUBTOTAL\r\nFROM\r\n  build a\r\n  INNER JOIN build_detail b ON (a.build_ID = b.build_ID)\r\n  \r\n    INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE  (i.JENIS_ID = 1)\r\n\r\nand A.period_name = :PERIOD_NAME1\r\nUNION ALL\r\n\r\n\r\n/* TRANSAKSI RAKIT */\r\n/* BUILD  ITEM YG DIBUAT       */\r\nSELECT\r\n  100 as URUT,\r\n  7 as TIPE,\r\n  A.PERIOD_NAME,\r\n  1 AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  a.ITEM_ID ,\r\n  a.build_ID as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n  0 AS MASUK,\r\n  0 MASUK_RP,\r\n  0 AS KELUAR,\r\n  0  AS KELUAR_RP,  \r\n  0 as BAHAN,  \r\n  0 AS BAHAN_RP,  \r\n  a.ENTERED_QTY   * a.BASE_QTY AS JADI ,\r\n  a.TOTAL_AMOUNT AS JADI_RP,\r\n   0  AS ADJ ,  \r\n  0 AS ADJ_RP,\r\n\r\n   a.TOTAL_AMOUNT / (a.ENTERED_QTY * a.BASE_QTY)  AS HARGA,\r\n  a.TOTAL_AMOUNT  AS SUBTOTAL\r\nFROM\r\n  build a\r\n    INNER JOIN ITEM i ON (a.ITEM_ID = i.ITEM_ID)\r\n  \r\nWHERE (i.JENIS_ID = 1)\r\n\r\nand A.period_name = :PERIOD_NAME1\r\n	 )\r\nAS XX\r\n   INNER JOIN ITEM i ON (XX.ITEM_ID = i.ITEM_ID)\r\nWHERE\r\n   \r\nTRUE\r\n  &KONDISI\r\nGROUP BY\r\n  XX.ITEM_ID\r\nORDER BY ITEM_CODE\r\n) AS YY WHERE \r\n(ABS(SALDO) + ABS(KELUAR)+    ABS(BAHAN)+  ABS(JADI)+  ABS(ADJ)+  ABS( MASUK ) > 0 )\r\nOR \r\n(ABS(SALDO_RP)+ABS(MASUK_RP)+ABS(KELUAR_RP)+  ABS( BAHAN_RP)+ABS(JADI_RP)+  ABS(ADJ_RP) > 0 )\r\n\r\n', NULL, NULL, NULL),
	(1205, 1181, 0, 'select \r\n*\r\nFrom\r\n(\r\n\r\n\r\n\r\n/*\r\nTIPE --> digit menunjukan Jurnal BELI-JUAL-INV-BKK-BKM-GL\r\n\r\n  = PERIODE\r\n  = TANGGAL\r\nAND (p.TGL_TOLAK between :11 and :21 ) = TANGGAL TOLAK\r\n\r\n = ACCOUNT\r\n*/\r\n\r\n/*saldo awal */\r\nselect\r\n  \'111111A\' as TIPE,\r\n  \'SALDO AWAL\' AS DOCUMENT_CLASS_CODE,\r\n  \' \' AS DOCUMENT_NO ,\r\nBALANCE_DATE AS  DOCUMENT_DATE,\r\n  \' \' AS DOCUMENT_REFF_NO ,\r\n  0  AS URUT,\r\n  1 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,\r\nC.ACCOUNT_ID ,\r\n \' \'  ITEM_DESCRIPTION ,\r\n  0 AS ID,\r\n  \' SALDO AWAL \' AS NOTE,\r\n   COA_SALDO AS AWAL,\r\n  0 AS DEBET,\r\n  0 AS KREDIT\r\nFROM\r\n  COA_BALANCE a\r\n  INNER join V_ACCOUNT c on (a.COA_ID = c.COA_id)\r\nWHERE TRUE \r\nAND FALSE  \r\n\r\n\r\nUNION ALL \r\n\r\n/*---- ADJUSTMENT */\r\n/* persediaan */\r\nselect\r\n  \'001001B\' as TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,0 ,1)as URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.ADJUST_QTY_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(b.SUBTOTAL>=0,b.SUBTOTAL ,0) AS DEBET,\r\n  IF(b.SUBTOTAL<0,b.SUBTOTAL *-1 ,0) AS KREDIT\r\nFROM\r\n  ADJUST_QTY a\r\n  INNER JOIN ADJUST_QTY_DETAIL B on (a.ADJUST_QTY_ID = B.ADJUST_QTY_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nwhere\r\n B.SUBTOTAL <> 0\r\n    \r\n&KONDISI\r\nUNION ALL \r\n/*  BIAYA / BONUS */\r\n\r\nselect\r\n  \'001001B\' as TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  if(TOTAL_AMOUNT>=0, 1,0) AS URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,\r\n \'   \' ITEM_DESCRIPTION ,\r\n  a.ADJUST_QTY_ID,\r\n  a.NOTE,\r\n  0 AS AWAL,\r\n  if(TOTAL_AMOUNT<0, -1*TOTAL_AMOUNT,0) AS DEBET,\r\n  if(TOTAL_AMOUNT>=0, TOTAL_AMOUNT,0) AS KREDIT\r\nFROM\r\n  ADJUST_QTY a\r\n    INNER  join V_ACCOUNT c on (a.COA_ID = c.COA_id)\r\n\r\nWHERE  A.TOTAL_AMOUNT <> 0\r\n    \r\n&KONDISI\r\n/* END ADJUST_QTY */\r\n\r\nUNION ALL \r\n/*---- ADJUSTMENT NILAI*/\r\n/* persediaan */\r\nselect\r\n  \'001001C\' as TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.ENTERED_AMOUNT>=0,0 ,1)as URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.ADJUST_AMOUNT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(b.ENTERED_AMOUNT>=0,b.ENTERED_AMOUNT ,0) AS DEBET,\r\n  IF(b.ENTERED_AMOUNT<0,b.ENTERED_AMOUNT *-1 ,0) AS KREDIT\r\nFROM\r\n  ADJUST_AMOUNT a\r\n  INNER JOIN ADJUST_AMOUNT_DETAIL B on (a.ADJUST_AMOUNT_ID = B.ADJUST_AMOUNT_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nwhere\r\n B.ENTERED_AMOUNT <> 0\r\n    \r\n&KONDISI\r\nUNION ALL \r\n/*  BIAYA / BONUS */\r\n\r\nselect\r\n  \'001001C\' as TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  if(TOTAL_AMOUNT>=0, 1,0) AS URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,\r\n \'   \' ITEM_DESCRIPTION ,\r\n  a.ADJUST_AMOUNT_ID,\r\n  a.NOTE,\r\n  0 AS AWAL,\r\n  if(TOTAL_AMOUNT<0, -1*TOTAL_AMOUNT,0) AS DEBET,\r\n  if(TOTAL_AMOUNT>=0, TOTAL_AMOUNT,0) AS KREDIT\r\nFROM\r\n  ADJUST_AMOUNT a\r\n    INNER  join V_ACCOUNT c on (a.COA_ID = c.COA_id)\r\n\r\nWHERE  A.TOTAL_AMOUNT <> 0\r\n    \r\n&KONDISI\r\n/* END ADJUST_AMOUNT */\r\n\r\nUNION ALL \r\n\r\n\r\n/*---- INVENTORY_IN*/\r\n/* persediaan */\r\nselect\r\n  \'001001C\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,0 ,1)as URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.INVENTORY_IN_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(b.SUBTOTAL>=0,b.SUBTOTAL -b.DISCOUNT_PRICE1 ,0) AS DEBET,\r\n  IF(b.SUBTOTAL<0,(b.SUBTOTAL -b.DISCOUNT_PRICE1 )*-1 ,0) AS KREDIT\r\nFROM\r\n  INVENTORY_IN a\r\n  INNER JOIN INVENTORY_IN_DETAIL B on (a.INVENTORY_IN_ID = B.INVENTORY_IN_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nwhere\r\n B.SUBTOTAL <> 0\r\n    \r\n&KONDISI\r\nUNION ALL \r\n/*  hutang suspend */\r\n\r\nselect\r\n  \'001001C\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  if(SUBTOTAL>=0, 1,0) AS URUT,\r\n  10 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,\r\n I.ITEM_DESCRIPTION ,\r\n  a.INVENTORY_IN_ID,\r\n  a.NOTE,\r\n  0 AS AWAL,\r\n  if(SUBTOTAL<0, -1*(SUBTOTAL -b.DISCOUNT_PRICE1),0) AS DEBET,\r\n  if(SUBTOTAL>=0, SUBTOTAL-b.DISCOUNT_PRICE1,0) AS KREDIT\r\nFROM\r\n  INVENTORY_IN a\r\n  INNER JOIN INVENTORY_IN_DETAIL B on (a.INVENTORY_IN_ID = B.INVENTORY_IN_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_SUSPEND_ID  = c.COA_id)\r\n\r\nWHERE  B.SUBTOTAL <> 0\r\n    \r\n&KONDISI\r\n/* END INVENTORY_IN */\r\n\r\nUNION ALL\r\n/* INVOICE\r\n biaya-biaya yang ada di detail\r\n Biaya   1000\r\n ppn      90\r\n diskon        100\r\n hutang        990\r\n\r\n */\r\nSELECT\r\n  IF(a.INVENTORY_IN_ID IS NULL, \'101001D\',\'100001D\') AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,0,1) AS URUT,\r\n  20 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  a.INVOICE_ID,\r\n  b.NOTE,   0 AS AWAL,\r\n  IF(b.SUBTOTAL>=0,(b.SUBTOTAL -b.DISCOUNT_PRICE1),0)  as DEBET,\r\n  IF(b.SUBTOTAL<0,-1*(b.SUBTOTAL -b.DISCOUNT_PRICE1),0)  as KREDIT\r\nFROM\r\n  invoice a\r\n  INNER JOIN invoice_detail b ON (a.INVOICE_ID = b.INVOICE_ID)\r\n  INNER JOIN ITEM I ON (b.ITEM_ID = I.ITEM_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_ID)\r\nWHERE\r\n  B.SUBTOTAL <> 0 \r\n    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* INVOICE\r\n/*PPN*/\r\nSELECT\r\n  IF(a.INVENTORY_IN_ID IS NULL, \'101001D\',\'100001D\') AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(PPN_AMOUNT>=0,0,1) AS URUT,\r\n  20 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'PPN \' AS ITEM_DESCRIPTION ,\r\n  a.INVOICE_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n    IF(PPN_AMOUNT>0, PPN_AMOUNT, 0) as DEBET,\r\n  IF(PPN_AMOUNT<0, -1*PPN_AMOUNT, 0) as KREDIT\r\n\r\nFROM\r\n  invoice a,\r\n  V_ACCOUNT c\r\nWHERE TRUE\r\nand (c.COA_ID = @BELI_PPN)\r\nAND PPN_AMOUNT <> 0\r\nAND  (STATUS_ID <> @DELETE)\r\n    \r\n&KONDISI\r\n\r\n\r\n\r\nUNION ALL\r\n\r\n/*hutang dagang yang ada di header*/\r\nSELECT\r\n   IF(a.INVENTORY_IN_ID IS NULL, \'101001D\',\'100001D\') AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_AMOUNT>=0,1,0) AS URUT,\r\n 20 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  PERSON_NAME AS ITEM_DESCRIPTION ,\r\n  a.INVOICE_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(TOTAL_NET<0, -1*TOTAL_NET, 0) as DEBET,\r\n  IF(TOTAL_NET>=0, TOTAL_NET, 0) as KREDIT\r\nFROM\r\n  invoice a\r\n  INNER JOIN PERSON B ON (a.PERSON_ID = b.PERSON_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_HUT_ID)\r\n\r\nWHERE TOTAL_NET <> 0\r\n    \r\n&KONDISI\r\n\r\n\r\n\r\n/* END INVOICE */\r\n\r\nUNION ALL\r\n\r\n/*  BUILD BARANG JADI */\r\nSELECT\r\n   \'001001E\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(A.TOTAL_AMOUNT>= 0, 0,1) AS URUT,\r\n  30 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  A.BUILD_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n  IF(A.TOTAL_AMOUNT>= 0, A.TOTAL_AMOUNT, 0) AS DEBET,\r\n  IF(A.TOTAL_AMOUNT< 0, -1*A.TOTAL_AMOUNT, 0) AS KREDIT\r\nFROM\r\nBUILD A\r\nINNER JOIN item i ON (A.ITEM_ID = i.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\n WHERE A.STATUS_ID = @APPROVED AND (TOTAL_AMOUNT <> 0 )\r\n    \r\n&KONDISI\r\nUNION ALL\r\n\r\n/*  BUILD BAHAN BAKU NYA */\r\nSELECT\r\n  \'001001E\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.SUBTOTAL>= 0, 1,0) AS URUT,\r\n  30 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  B.BUILD_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(B.SUBTOTAL< 0, -1*B.SUBTOTAL, 0) AS DEBET,\r\n  IF(B.SUBTOTAL>= 0, B.SUBTOTAL, 0) AS KREDIT\r\nFROM\r\nBUILD A\r\nINNER JOIN BUILD_DETAIL B ON (A.BUILD_ID = B.BUILD_ID)\r\nINNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nINNER  join V_ACCOUNT c on (I.COA_ID  = c.COA_ID)\r\nWHERE A.STATUS_ID = @APPROVED AND \r\nB.SUBTOTAL<> 0\r\n    \r\n&KONDISI\r\n\r\n/* END BUILD */\r\n\r\n\r\n\r\nUNION ALL\r\n\r\n/* PENJUALAN */\r\n/*piutang dagang*/\r\nSELECT\r\n   \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_NET>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  PERSON_NAME AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(TOTAL_NET>=0, TOTAL_NET, 0) as DEBET,\r\n  IF(TOTAL_NET<0, -1*TOTAL_NET, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN PERSON B ON (a.PERSON_ID = b.PERSON_ID)\r\n  INNER JOIN V_ACCOUNT c ON (B.COA_PIUT_ID = C.COA_ID )\r\nWHERE\r\n   (TOTAL_NET <> 0)\r\n and ( a.TOTAL_BYR1 = 0 )  \r\n    \r\n&KONDISI\r\nUNION ALL\r\n\r\n/* PENJUALAN TUNAI pembayaran I */\r\nSELECT \r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(NET_BYR1>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  c.COA_NAME,\r\n  c.COA_CODE,\r\n  c.COA_ID,c.ACCOUNT_ID ,  \'CASH1 \'  AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(  NET_BYR1>=0, NET_BYR1, 0) as DEBET,\r\n  IF(NET_BYR1<0, -1*NET_BYR1, 0) as KREDIT\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER  join V_ACCOUNT c on (a.COA_BYR1 =c.COA_ID)\r\nWHERE \r\n (  a.NET_BYR1  > 0 )\r\nAND ( a.Status_ID<>@DELETE )\r\nAND (a.TOTAL_NET > 0)\r\n    \r\n&KONDISI\r\nUNION ALL\r\n/* PENJUALAN TUNAI pembayaran II */\r\nSELECT\r\n  \'010001F\' AS TIPE, \r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_BYR2>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  c.COA_NAME,\r\n  c.COA_CODE,\r\n  c.COA_ID,c.ACCOUNT_ID ,  \'CASH2 \'  AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(  TOTAL_BYR2>=0, TOTAL_BYR2, 0) as DEBET,\r\n  IF(TOTAL_BYR2<0, -1*TOTAL_BYR2, 0) as KREDIT\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER Join V_ACCOUNT c on (a.COA_BYR2 =c.COA_ID)\r\n\r\nWHERE \r\n   ( a.TOTAL_BYR2   > 0 )\r\nAND ( a.Status_ID<>@DELETE )\r\nAND (a.TOTAL_NET > 0)\r\n    \r\n&KONDISI\r\nUNION ALL\r\n\r\n/*DISCOUNT TOTAL*/\r\nSELECT\r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(TOTAL_DISCOUNT>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'DISKON TOTAL \' AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(TOTAL_DISCOUNT>=0, TOTAL_DISCOUNT, 0) as DEBET,\r\n  IF(TOTAL_DISCOUNT<0, -1*TOTAL_DISCOUNT, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A,\r\n  V_ACCOUNT c\r\nWHERE   TRUE\r\n    \r\n&KONDISI\r\nand (c.COA_ID = @JUAL_DISC)\r\nAND TOTAL_DISCOUNT <> 0\r\n\r\n\r\nUNION ALL\r\n/*DISCOUNT PER ITEM */\r\nSELECT\r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(B.DISCOUNT_PRICE * B.ENTERED_QTY>=0,0,1) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(B.DISCOUNT_PRICE * B.ENTERED_QTY>=0, B.DISCOUNT_PRICE * B.ENTERED_QTY, 0) as DEBET,\r\n  IF(B.DISCOUNT_PRICE * B.ENTERED_QTY<0, -1*B.DISCOUNT_PRICE * B.ENTERED_QTY, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN NOTA_PENJUALAN_DETAIL B ON (A.NOTA_PENJUALAN_ID = B.NOTA_PENJUALAN_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (I.COA_DISC_JUAL_ID  = c.COA_ID)\r\nWHERE   (STATUS_ID <> @DELETE)\r\n    \r\n&KONDISI\r\nAND B.DISCOUNT_PRICE * B.ENTERED_QTY <> 0\r\nUNION ALL\r\n\r\n/*Penjualan*/\r\nSELECT\r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(b.SUBTOTAL>=0,1,0) AS URUT,\r\n 50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  I.ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  b.NOTE,   0 AS AWAL,\r\n  IF(b.UNIT_PRICE * b.ENTERED_QTY <0,(-1*b.UNIT_PRICE * b.ENTERED_QTY),0)  as DEBET,\r\n  IF(b.UNIT_PRICE * b.ENTERED_QTY>=0,(b.UNIT_PRICE * b.ENTERED_QTY),0)  as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A\r\n  INNER JOIN NOTA_PENJUALAN_DETAIL B ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\n  INNER JOIN ITEM I ON (B.ITEM_ID = I.ITEM_ID)\r\n  INNER  join V_ACCOUNT c on (B.COA_ID  = c.COA_ID)\r\n\r\nWHERE\r\n  (b.UNIT_PRICE * b.ENTERED_QTY <> 0)\r\n    \r\n&KONDISI\r\nUNION ALL\r\n/*ppn*/\r\nSELECT\r\n  \'010001F\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(PPN_AMOUNT>=0,1,0) AS URUT,\r\n  50 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'PPN PENJUALAN \' AS ITEM_DESCRIPTION ,\r\n  a.NOTA_PENJUALAN_ID,\r\n  a.NOTE,   0 AS AWAL,\r\n  IF(PPN_AMOUNT<0, -1*PPN_AMOUNT, 0) as DEBET,\r\n  IF(PPN_AMOUNT>=0, PPN_AMOUNT, 0) as KREDIT\r\nFROM\r\n  NOTA_PENJUALAN A,\r\n  V_ACCOUNT c\r\nWHERE\r\n  ( PPN_AMOUNT <> 0)\r\n    \r\n&KONDISI\r\nand c