ON ((V.COA_HUT_ID = C.COA_ID AND B.INVOICE_DUE_ID IS NOT NULL) OR \r\n  (V.COA_PIUT_ID = C.COA_ID AND B.NOTA_PENJUALAN_AGING_ID IS NOT NULL) )\r\nWHERE (AMOUNT + B.DISCOUNT) <> 0 \r\nAND (B.COA_ID IS NULL ) \r\n    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* POTONG HUTANG*/\r\n\r\nSELECT\r\n  \'000101I\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  IF(DISCOUNT>=0,0,1) AS URUT,\r\n  70 AS URUT1,\r\n  COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  \'POTONGAN HUTANG\' AS ITEM_DESCRIPTION ,\r\n  A.PAYMENT_ID,\r\n  B.NOTE,   0 AS AWAL,\r\n  IF(DISCOUNT<0,-1*DISCOUNT, 0)  AS DEBET,\r\n  IF(DISCOUNT>=0,DISCOUNT, 0)AS KREDIT\r\nFROM\r\nPAYMENT  A\r\nINNER JOIN PAYMENT_DETAIL B ON (A.PAYMENT_ID = B.PAYMENT_ID)\r\n,\r\nV_ACCOUNT c\r\nWHERE \r\nDISCOUNT <> 0\r\nAND (((c.COA_ID = @POT_HUTANG) AND (B.INVOICE_DUE_ID IS NOT NULL )) OR\r\n ((c.COA_ID = @POT_PIUTANG) AND (B.NOTA_PENJUALAN_AGING_ID IS NOT NULL )))\r\nAND (B.COA_ID IS NULL)\r\n    \r\n&KONDISI\r\n\r\nUNION ALL\r\n\r\n/*\r\n  BKK\r\nBANK\r\n*/\r\nSELECT\r\n  \'000101I\' AS TIPE,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  1 AS URUT,\r\n  70 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,  COA_NAME AS ITEM_DESCRIPTION ,\r\n  A.PAYMENT_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n  0  AS DEBET,\r\n  A.TOTAL_AMOUNT KREDIT\r\nFROM\r\n  PAYMENT A\r\n  INNER JOIN V_ACCOUNT C ON (A.BANK_ID = C.COA_ID)\r\nWHERE   \r\n(A.TOTAL_AMOUNT  <> 0) \r\n    \r\n&KONDISI\r\n\r\n/* END OF BKK */\r\n\r\n\r\n\r\nUNION ALL\r\n/* JURNAL MEMO RIAL  */\r\nSELECT\r\n  \'000001J\' AS TIPE,\r\n\'JURNAL MEMO \' ,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO ,\r\n  1 URUT,\r\n  80 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   a.NOTE as  ITEM_DESCRIPTION ,\r\n  a.JOURNAL_ID,\r\n  b.NOTE,   0 AS AWAL,\r\n  AMOUNT as DEBET,\r\nAMOUNT_CR as KREDIT\r\nFROM\r\n  JOURNAL a\r\n  INNER JOIN JOURNAL_detail b ON (a.JOURNAL_ID = b.JOURNAL_ID)\r\n  INNER JOIN V_ACCOUNT c ON (C.COA_ID = B.COA_ID)\r\nWHERE\r\n (STATUS_ID <> @DELETE)\r\n    \r\n&KONDISI\r\n\r\n\r\nUNION ALL\r\n\r\n/* BG */ \r\n\r\n/*  BG  CAIR / TOLAK BANK TUJUAN */\r\nSELECT\r\n   IF(a.RECEIPT_ID IS NULL, \'000101K\', \'000011K\') AS TIPE,\r\n  \'PENCAIRAN BG \' ,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  IF(STATUS_FLAG = \'M\' , 0, 1)  URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  a.CHEQUE_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT ,0)as DEBET,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT,0 )as KREDIT\r\nFROM\r\n  CHEQUE a\r\n  INNER JOIN V_ACCOUNT c ON (a.BANK_ID_DEST = C.COA_ID )\r\nWHERE\r\n (STATUS_ID in (@CLEAR, @TOLAK ))\r\n    \r\n&KONDISI\r\n\r\nUNION ALL\r\n/* BG  CAIR / TOLAK BANK ASAL */\r\nSELECT\r\n   IF(a.RECEIPT_ID IS NULL, \'000101K\', \'000011K\') AS TIPE,\r\n  \'PENCAIRAN BG \' ,\r\n  a.DOCUMENT_NO ,\r\n  a.DOCUMENT_DATE,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  IF(STATUS_FLAG = \'M\' , 1, 0)  URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  a.CHEQUE_ID,\r\n  A.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT,0 )as DEBET,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT,0 )as KREDIT\r\nFROM\r\n  CHEQUE a\r\n  INNER JOIN V_ACCOUNT c ON (a.BANK_ID  = C.COA_ID )\r\nWHERE\r\n (STATUS_ID in  (@CLEAR, @TOLAK ))\r\n    \r\n&KONDISI\r\nUNION ALL\r\n\r\n/* BG  TOLAK BANK ASAL */\r\nSELECT\r\n   IF(p.RECEIPT_ID IS NULL, \'000101K\', \'000011K\') AS TIPE,\r\n  \'TOLAKAN BG \' ,\r\n  p.DOCUMENT_NO ,\r\n  p.TGL_TOLAK,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  1 URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  p.CHEQUE_ID,\r\n  p.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT,0 )as DEBET,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT,0 )as KREDIT\r\n\r\nFROM\r\n  CHEQUE p\r\n  INNER JOIN V_ACCOUNT c ON (p.BANK_ID = c.COA_ID)\r\n  INNER JOIN PERIOD a on (EXTRACT(YEAR_MONTH FROM p.tgl_tolak)=a.PERIOD_NAME)\r\nWHERE\r\n (STATUS_ID = @TOLAK)\r\n  AND (p.TGL_TOLAK between :11 and :21 )  \r\nUNION ALL\r\n/* BG   TOLAK BANK TUJUAN */\r\nSELECT\r\n    IF(p.RECEIPT_ID IS NULL, \'000101K\', \'000011K\') AS TIPE,\r\n  \'TOLAKAN BG \' ,\r\n  p.DOCUMENT_NO ,\r\n  p.TGL_TOLAK,\r\n  \'\' DOCUMENT_REFF_NO ,\r\n  2 URUT,\r\n  90 AS URUT1,\r\n  C.COA_NAME,\r\n  C.COA_CODE,\r\n  C.COA_ID,C.ACCOUNT_ID ,   \'  \' ITEM_DESCRIPTION ,\r\n  p.CHEQUE_ID,\r\n  p.NOTE,   0 AS AWAL,\r\n IF(STATUS_FLAG = \'K\' , TOTAL_AMOUNT  ,0 )as DEBET,\r\n IF(STATUS_FLAG = \'M\' , TOTAL_AMOUNT ,0)as KREDIT\r\n\r\nFROM\r\n  CHEQUE p\r\n  INNER JOIN V_ACCOUNT c ON (p.BANK_ID_DEST = c.COA_ID)\r\n  INNER JOIN PERIOD a on (EXTRACT(YEAR_MONTH FROM p.tgl_tolak)=a.PERIOD_NAME)\r\nWHERE\r\n (STATUS_ID = @TOLAK)\r\n  AND (p.TGL_TOLAK between :11 and :21 )  \r\n)  AS XX\r\nWHERE TRUE and SUBSTRING(TIPE,5,1)=\'1\'\r\nORDER BY DOCUMENT_NO, URUT, URUT1,  COA_ID,   DOCUMENT_DATE', NULL, '', NULL),
	(1207, 1183, 0, 'SELECT\r\n  ITEM_CODE,\r\n  ITEM_DESCRIPTION,\r\n  UOM_CODE,\r\n  SUM(QTY) AS QTY ,\r\n  SUM(RP_JUAL) RP_JUAL ,\r\n  SUM(RP_HPP) AS RP_HPP ,\r\n  MAX(HPP_ITEM) AS HPP_ITEM,\r\n  AVG(HPP_JUAL) AS HPP_JUAL,\r\n  SUM(QTY_RET)*-1   QTY_RET,\r\n  SUM(RP_JUAL_RET)*-1   RP_JUAL_RET,\r\n  SUM(QTY_BELI)  QTY_BELI,\r\n  SUM(RP_BELI)   RP_BELI,\r\n  SUM(QTY_BELI_RET) *-1  QTY_BELI_RET,\r\n  SUM(RP_BELI_RET)*-1  RP_BELI_RET,\r\n MAX( type ) AS TYPE \r\n FROM\r\n(\r\nSELECT\r\n  i.ITEM_CODE,\r\n  i.ITEM_DESCRIPTION,\r\n  i.UOM_CODE,\r\n  SUM(b.BASE_QTY * b.ENTERED_QTY) AS QTY,\r\n  SUM(b.SUBTOTAL - b.DISCOUNT_PRICE1) AS RP_JUAL,\r\n  SUM(b.HPP * b.ENTERED_QTY) AS RP_HPP,\r\n  MAX(i.HPP) AS HPP_ITEM,\r\n  AVG(b.HPP) AS HPP_JUAL,\r\n  0 AS QTY_RET,\r\n  0 AS RP_JUAL_RET,\r\n  QTY_BELI,\r\n  RP_BELI,\r\n  0 as QTY_BELI_RET,\r\n  0 as RP_BELI_RET,\r\n\'non_retur\' as type\r\nFROM\r\n  nota_penjualan a\r\n  INNER JOIN nota_penjualan_detail b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\n  INNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\n  INNER JOIN person c ON (a.person_ID = c.person_ID)\r\n  INNER JOIN person_site cs ON (a.person_SITE_ID = cs.person_SITE_ID)\r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nLEFT JOIN\r\n(\r\nselect\r\n d.ITEM_ID, \r\n SUM(d.BASE_QTY * d.ENTERED_QTY) AS QTY_BELI,\r\n SUM(d.SUBTOTAL) AS RP_BELI,\r\n 0 AS QTY_BELI_RET,\r\n 0 AS RP_BELI_RET\r\n FROM\r\n  invoice v\r\n  INNER JOIN invoice_detail d ON (v.INVOICE_ID = d.INVOICE_ID)\r\n  INNER JOIN item i ON (d.ITEM_ID = i.ITEM_ID)\r\nWHERE v.DOCUMENT_TYPE_ID <> 5\r\n\r\nGROUP BY\r\n ITEM_ID\r\n) X ON (i.ITEM_ID = X.ITEM_ID)\r\n\r\nWHERE I.JENIS_ID = @GOODS AND a.DOCUMENT_TYPE_ID <> 5  \r\n\r\n&Kondisi\r\nGROUP BY\r\n  i.ITEM_CODE,\r\n  i.ITEM_DESCRIPTION,\r\n  i.UOM_CODE\r\n\r\nunion all\r\n\r\nSELECT\r\n  i.ITEM_CODE,\r\n  i.ITEM_DESCRIPTION,\r\n  i.UOM_CODE,\r\n  0 AS QTY,\r\n  0 AS RP_JUAL,\r\n  SUM(b.HPP  * b.ENTERED_QTY) AS RP_HPP,\r\n  MAX(i.HPP) AS HPP_ITEM,\r\n  AVG(b.HPP) AS HPP_JUAL,\r\n  SUM(b.BASE_QTY * b.ENTERED_QTY) AS QTY_RET,\r\n  SUM(b.SUBTOTAL - b.DISCOUNT_PRICE1) AS RP_JUAL_RET,\r\n  0 as QTY_BELI,\r\n  0 as RP_BELI,\r\n  QTY_BELI_RET,\r\n  RP_BELI_RET,\r\n\'retur\' as type\r\n\r\nFROM\r\n  nota_penjualan a\r\n  INNER JOIN nota_penjualan_detail b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\n  INNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\n  INNER JOIN person c ON (a.person_ID = c.person_ID)\r\n  INNER JOIN person_site cs ON (a.person_SITE_ID = cs.person_SITE_ID)\r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nLEFT JOIN\r\n(\r\nselect\r\n d.ITEM_ID, \r\n 0 AS QTY_BELI,\r\n 0 AS RP_BELI,\r\n SUM(d.BASE_QTY * d.ENTERED_QTY) AS QTY_BELI_RET,\r\n SUM(d.SUBTOTAL) AS RP_BELI_RET\r\nFROM\r\n  invoice v\r\n  INNER JOIN invoice_detail d ON (v.INVOICE_ID = d.INVOICE_ID)\r\n  INNER JOIN item i ON (d.ITEM_ID = i.ITEM_ID)\r\nWHERE v.DOCUMENT_TYPE_ID = 5\r\n\r\nGROUP BY\r\n ITEM_ID\r\n) X ON (i.ITEM_ID = X.ITEM_ID)\r\n\r\nWHERE I.JENIS_ID = @GOODS AND a.DOCUMENT_TYPE_ID = 5 \r\n\r\n&Kondisi\r\nGROUP BY\r\n  i.ITEM_CODE,\r\n  i.ITEM_DESCRIPTION,\r\n  i.UOM_CODE\r\n) AS XX\r\nGROUP BY\r\n  ITEM_CODE,\r\n  ITEM_DESCRIPTION,\r\n  UOM_CODE\r\n', NULL, '', NULL),
