	(1212, 100, 3, 'SELECT \r\n  b.ITEM_ID,\r\n  a.TAG_ID,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_TYPE_ID,\r\n  a.WAREHOUSE_ID,\r\n  a.TO_WH_ID,\r\n  a.PERIOD_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.CONFIRMED_FLAG,\r\n  a.PRINT_COUNT,\r\n  a.NOTE,\r\n  a.CREATED_BY,\r\n  a.CREATED_DATE,\r\n  a.LAST_UPDATE_DATE,\r\n  a.LAST_UPDATE_BY,\r\n  w.WAREHOUSE_NAME,\r\n  w2.WAREHOUSE_NAME as TUJUAN,\r\n  a.STATUS_ID,\r\n  b.ENTERED_QTY,\r\n  b.ENTERED_UOM,\r\n  b.NOTE,\r\n  s.DISPLAY_NAME AS STATUS_DOC\r\nFROM\r\n  tag a\r\n  INNER JOIN tag_detail b  ON (a.TAG_ID =b.TAG_ID)\r\n  INNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\n  INNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\n  INNER JOIN warehouse w2 ON (a.TO_WH_ID = w2.WAREHOUSE_ID)\r\n  INNER JOIN erp_lookup_value s ON (a.STATUS_ID = s.ERP_LOOKUP_VALUE_ID)\r\nWHERE true /*b.ITEM_ID = :ITEM_ID*/\r\n&Kondisi\r\nORDER BY\r\n  i.ITEM_CODE, a.DOCUMENT_DATE\r\n', NULL, '', 0),
	(1213, 65, 0, '\r\nSELECT\r\n@no := @no + 1 as Nmr,\r\n period_NAME AS PERIOD,\r\ni.item_id,\r\ni.item_code,\r\ni.item_description,\r\ni.uom_code,\r\nb.qty_awal\r\nfrom  item i \r\nLeft join \r\nitem_balance b on (b.item_id = i.item_id)\r\nwhere TRUE \r\n&Kondisi\r\ngroup by i.item_id\r\n', NULL, '', NULL),
	(1214, 1185, 3, 'SELECT\r\n    b.INVENTORY_OUT_ID,\r\n    i.ITEM_CODE,\r\n    b.ITEM_DESCRIPTION,\r\n    b.ENTERED_QTY,\r\n    b.ENTERED_UOM,\r\n    b.NOTE\r\nFROM \r\n  INVENTORY_OUT_DETAIL b\r\nLEFT JOIN ITEM I on (i.item_id=b.item_id)\r\nWHERE \r\n   b.INVENTORY_OUT_ID=:INVENTORY_OUT_ID\r\n&KONDISI\r\n', NULL, NULL, NULL),
	(1215, 130, 0, 'Select \r\n  max(x.build_id),\r\n  max(x.document_no), \r\n  a.item_id,\r\n  a.item_code, \r\n  a.item_description, \r\n  a.uom_code,\r\n  if (sum(x.entered_qty*x.base_qty) is null,0,sum(x.entered_qty*x.base_qty)) as jum\r\nfrom \r\n  item a \r\n  left join (select * from build b where true AND (DATE(b.DOCUMENT_DATE) = :11) and (b.Period_Name = :PERIOD_NAME1) AND (B.warehouse_id=:12)) as x on (a.item_id=x.item_id)\r\nwhere \r\n true\r\n  &kondisi \r\ngroup by \r\n  a.item_id, \r\n  a.item_description,\r\n  a.uom_code\r\norder by \r\n  a.item_description', NULL, '', NULL),
	(1216, 1187, 0, 'SELECT * FROM \r\n(\r\n/* SALDO AWAL  DAN PELUNASAN */\r\nSELECT a.DOCUMENT_NO,\r\n   \'SALDO AWAL \' DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.DUE_DATE AS JTEMPO,\r\n   TIPE,\r\n   k.PERSON_NAME,\r\n   k.PERSON_CODE,\r\n   k.PERSON_ID,\r\n   a.TOTAL_AMOUNT AS AWAL,\r\n   0 AS JUAL,\r\n   IFNULL(sa.AMOUNT,0) AS BAYAR_BKM,\r\n   0  AS BAYAR_BKK,\r\n   0 RETUR_JUAL\r\nFROM \r\n  PERSON_SALDO a\r\nINNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\n LEFT JOIN \r\n (SELECT s.PERSON_SALDO_ID ,\r\nSUM(AMOUNT + y.DISCOUNT ) AS AMOUNT \r\nFROM \r\nNOTA_PENJUALAN_AGING s\r\nINNER JOIN RECEIPT_DETAIL y on (s.Nota_Penjualan_aging_id = y.Nota_Penjualan_aging_id)\r\nINNER JOIN receipt x on (x.receipt_id = y.receipt_id) and (date(x.DOCUMENT_DATE) <= :13 )\r\nGROUP BY s.PERSON_SALDO_ID \r\n) AS SA ON (a.PERSON_SALDO_ID  = sA.PERSON_SALDO_ID ) \r\nWHERE true\r\nand  k.FLAG_SUPP <> 1\r\n&kondisi\r\n\r\n/* END SALDO AWAL */  \r\n\r\n\r\nUNION ALL\r\n/* DARI PENJUALAN */ \r\nSELECT\r\n   a.DOCUMENT_NO,\r\n   a.DOCUMENT_REFF_NO,\r\n   a.DOCUMENT_DATE,\r\n   a.JTEMPO,\r\n   TIPE, \r\n   k.PERSON_NAME,\r\n   k.PERSON_CODE,\r\n   k.PERSON_ID,\r\n   0 AS AWAL,\r\n   a.TOTAL_NET  AS JUAL,\r\n   IFNULL(rec.bayar,0)    +    IFNULL(rec_ret.bayar_ret,0)   AS BAYAR_BKM,\r\n   IFNULL(pay.bayar,0)  +   IFNULL(pay_ret.bayar_ret,0)  AS BAYAR_BKK,   \r\n   IFNULL( c.retur,0) RETUR_JUAL\r\n  FROM\r\n   nota_penjualan a\r\n   INNER JOIN PERSON k on (a.PERSON_ID = k.PERSON_ID)\r\n   INNER JOIN NOTA_PENJUALAN_AGING g ON (a.NOTA_PENJUALAN_ID  = g.NOTA_PENJUALAN_ID)  AND (JUAL_FLAG = \'Y\')\r\n   LEFT JOIN \r\n   /* PELUNASAN NYA */ \r\n   (SELECT y.NOTA_PENJUALAN_AGING_ID,\r\n   SUM(y.DISCOUNT + y.AMOUNT) as BAYAR\r\n    FROM\r\n      RECEIPT z        \r\n      INNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\n  WHERE(date(z.DOCUMENT_DATE))   <= :13\r\n  GROUP BY\r\n     y.NOTA_PENJUALAN_AGING_ID\r\n     \r\n   ) AS rec ON (g.NOTA_PENJUALAN_AGING_ID = rec.NOTA_PENJUALAN_AGING_ID)\r\n   LEFT JOIN \r\n   /* PELUNASAN BKK */ \r\n   (SELECT pd.NOTA_PENJUALAN_AGING_ID,\r\n      SUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR\r\n       FROM\r\n         PAYMENT p\r\n         INNER JOIN  PAYMENT_DETAIL pd ON (p.PAYMENT_ID = pd.PAYMENT_ID)\r\n     WHERE (date(p.DOCUMENT_DATE)<= :13)\r\n     and (pd.NOTA_PENJUALAN_AGING_ID IS NOT NULL)\r\n     GROUP BY pd.NOTA_PENJUALAN_AGING_ID\r\n     \r\n      ) AS pay ON (g.NOTA_PENJUALAN_AGING_ID = pay.NOTA_PENJUALAN_AGING_ID)\r\n    LEFT JOIN \r\n   /* RETUR REFER KE NO PENJUALAN */ \r\n    (SELECT a.NOTA_PENJUALAN_OLD_ID AS NOTA_PENJUALAN_ID,\r\n       SUM(a.TOTAL_NET) * -1 as RETUR\r\n        FROM\r\n          nota_penjualan a\r\n        WHERE\r\n        (DOCUMENT_TYPE_ID  >= 5 )\r\n    AND  (a.NOTA_PENJUALAN_OLD_ID IS NOT NULL )\r\n    AND   (date(DOCUMENT_DATE) <= :13 )\r\n    GROUP BY a.NOTA_PENJUALAN_OLD_ID\r\n       )  AS c on (a.nota_penjualan_id = c.nota_penjualan_id)\r\n left join\r\n/* PELUNASAN NYA RETUR */ \r\n      (SELECT r.NOTA_PENJUALAN_OLD_ID AS NOTA_PENJUALAN_ID,\r\n      SUM(y.DISCOUNT + y.AMOUNT) as BAYAR_RET\r\n       FROM\r\n         RECEIPT z        \r\n         INNER JOIN RECEIPT_DETAIL y ON (z.RECEIPT_ID = y.RECEIPT_ID)\r\n         INNER JOIN NOTA_PENJUALAN_AGING n ON (y.NOTA_PENJUALAN_AGING_ID = n.NOTA_PENJUALAN_AGING_ID  )\r\n         INNER JOIN NOTA_PENJUALAN r ON (n.NOTA_PENJUALAN_ID = r.NOTA_PENJUALAN_ID)    \r\n     WHERE (date(z.DOCUMENT_DATE)  <= :13)  \r\n     GROUP BY\r\n        r.NOTA_PENJUALAN_OLD_ID        \r\n      ) AS rec_ret ON (a.NOTA_PENJUALAN_ID = rec_ret.NOTA_PENJUALAN_ID)\r\n   left join\r\n\r\n  /* PELUNASAN RETUR DGN BKK */ \r\n   (SELECT r.NOTA_PENJUALAN_OLD_ID AS NOTA_PENJUALAN_ID,\r\n      SUM(pd.DISCOUNT + pd.AMOUNT) as BAYAR_RET\r\n       FROM\r\n         PAYMENT p\r\n         INNER JOIN  PAYMENT_DETAIL pd ON (p.PAYMENT_ID = pd.PAYMENT_ID)\r\n         INNER JOIN NOTA_PENJUALAN_AGING n ON (pd.NOTA_PENJUALAN_AGING_ID = n.NOTA_PENJUALAN_AGING_ID  )\r\n         INNER JOIN NOTA_PENJUALAN r ON (n.NOTA_PENJUALAN_ID = r.NOTA_PENJUALAN_ID)    \r\n     WHERE (date(p.DOCUMENT_DATE)<= :13)\r\n     and (pd.NOTA_PENJUALAN_AGING_ID IS NOT NULL)\r\n     GROUP BY n.NOTA_PENJUALAN_ID     \r\n   ) AS pay_ret ON (a.NOTA_PENJUALAN_ID = pay_ret.NOTA_PENJUALAN_ID)\r\n\r\n\r\nWHERE \r\n\r\n(\r\n ((a.NET_BYR1 = 0) and   (a.DOCUMENT_TYPE_ID < 5)) OR \r\n(\r\n(a.DOCUMENT_TYPE_ID   >= 5) AND (a.NOTA_PENJUALAN_OLD_ID IS NULL)  \r\n)\r\n)\r\nAND (date(a.DOCUMENT_DATE)<=  :13 )\r\n &kondisi\r\n/* end penjualan */\r\n\r\n) as yy\r\nWHERE \r\n(\r\n(YY.AWAL  + YY.JUAL - Bayar_BKM   + bayar_BKK - RETUR_JUAL) <> 0\r\n) \r\n\r\nORDER BY PERSON_NAME, DOCUMENT_DATE\r\n', NULL, '', NULL),
	(1217, 1188, 0, ' SELECT \r\nP.PERSON_NAME,\r\nP.PERSON_CODE,\r\nDOCUMENT_CLASS_CODE,\r\nDOCUMENT_NO,\r\nDOCUMENT_DATE,\r\nSALDO, \r\nKREDIT,\r\nDEBET,\r\nTUNAI,\r\np.PERSON_ID\r\nFROM\r\nPERSON P\r\nINNER JOIN (\r\n/* SALDO AWAL */\r\nSELECT \r\n\' \' AS DOCUMENT_CLASS_CODE,\r\n\' Saldo Awal\' AS DOCUMENT_NO,\r\n:12 AS DOCUMENT_DATE,\r\nsum(SALDO) as SALDO, \r\n0  AS KREDIT,\r\n0  AS DEBET,\r\n0 AS TUNAI,\r\nPERSON_ID,\r\n\'SALDO AWAL \'  AS NOTE\r\nFROM\r\n(\r\nSELECT\r\n\' \' AS DOCUMENT_CLASS_CODE,\r\n\' Saldo Awal\' AS DOCUMENT_NO,\r\n:12 AS DOCUMENT_DATE,\r\na.SALDO_AWAL AS SALDO, \r\n0  AS KREDIT,\r\n0  AS DEBET,\r\n0 AS TUNAI,\r\na.PERSON_ID,\r\n\'SALDO AWAL \'  AS NOTE\r\nFROM \r\nPERSON_balance a\r\nWHERE true\r\nand a.period_name= :PERIOD_NAME1\r\nUNION ALL\r\n/* PENJUALAN DAN RETUR */\r\nSELECT\r\n\' \' AS DOCUMENT_CLASS_CODE,\r\n\' Saldo Awal\' AS DOCUMENT_NO,\r\n:12 AS DOCUMENT_DATE,\r\na.TOTAL_NET AS SALDO,\r\n0  AS KREDIT,\r\n0  AS DEBET,\r\n0 AS TUNAI,\r\na.PERSON_ID,\r\n\'SALDO AWAL \'  AS NOTE\r\nFROM\r\nnota_penjualan a\r\nWHERE    (a.NET_BYR1 = 0) \r\nand a.period_name= :PERIOD_NAME1\r\nand a.document_date < :12\r\nUNION ALL\r\n/* PELUNASAN DGN BKM */\r\nSELECT\r\n\' \' AS DOCUMENT_CLASS_CODE,\r\n\' Saldo Awal\' AS DOCUMENT_NO,\r\n:12 AS DOCUMENT_DATE,\r\n(b.AMOUNT + b.DISCOUNT) * -1 AS SALDO,\r\n0  AS KREDIT,\r\n0  AS DEBET,\r\n0 AS TUNAI,\r\na.PERSON_ID,\r\n\'SALDO AWAL \'  AS NOTE\r\nFROM\r\nreceipt a\r\nINNER JOIN receipt_detail b ON (a.RECEIPT_ID = b.RECEIPT_ID) \r\nINNER JOIN NOTA_PENJUALAN_AGING x ON (b.NOTA_PENJUALAN_AGING_ID = x.NOTA_PENJUALAN_AGING_ID)\r\nWHERE  TRUE\r\nand a.period_name= :PERIOD_NAME1\r\nand a.document_date < :12\r\nUNION ALL\r\n/* PELUNASAN DGN BKK */ \r\nSELECT\r\n\' \' AS DOCUMENT_CLASS_CODE,\r\n\' Saldo Awal\' AS DOCUMENT_NO,\r\n:12 AS DOCUMENT_DATE,\r\nb.AMOUNT + b.DISCOUNT AS SALDO,\r\n0  AS KREDIT,\r\n0  AS DEBET,\r\n0 AS TUNAI,\r\na.PERSON_ID,\r\n\'SALDO AWAL \'  AS NOTE\r\nFROM\r\nPAYMENT a\r\nINNER JOIN PAYMENT_detail b ON (a.PAYMENT_ID = b.PAYMENT_ID) \r\nINNER JOIN NOTA_PENJUALAN_AGING x ON (b.NOTA_PENJUALAN_AGING_ID = x.NOTA_PENJUALAN_AGING_ID)\r\nWHERE  TRUE\r\nand a.period_name= :PERIOD_NAME1\r\nand a.document_date < :12\r\n) AS XX \r\nGROUP BY xx.PERSON_ID\r\nUNION ALL\r\n/* PENJUALAN DAN RETUR */\r\nSELECT\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\n0 AS SALDO,\r\n/*IF(a.TOTAL_NET<>0,0,(SELECT SUM(SUBTOTAL*-1) FROM NOTA_PENJUALAN_DETAIL b,ITEM i WHERE a.NOTA_PENJUALAN_ID=b.NOTA_PENJUALAN_ID AND i.ITEM_ID=b.ITEM_ID AND    i.JENIS_ID=@UM)) AS KREDIT,\r\nIF(a.TOTAL_NET<>0,a.TOTAL_NET,(SELECT SUM(SUBTOTAL) FROM NOTA_PENJUALAN_DETAIL b,ITEM i WHERE a.NOTA_PENJUALAN_ID=b.NOTA_PENJUALAN_ID AND i.ITEM_ID=b.ITEM_ID    AND i.JENIS_ID<>@UM)) AS    DEBET,*/\r\n0 as KREDIT,\r\na.TOTAL_NET as DEBET,\r\n0 AS TUNAI,\r\na.PERSON_ID,\r\na.NOTE\r\nFROM\r\nnota_penjualan a\r\nINNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\nWHERE    (a.NET_BYR1 = 0) \r\nAND (A.DOCUMENT_DATE BETWEEN :12 AND :22)\r\nUNION ALL\r\n/* PELUNASAN DGN BKM */\r\nSELECT\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\n0 AS SALDO,\r\nif(DATE(a.DOCUMENT_DATE) = DATE(n.DOCUMENT_DATE), 0,b.AMOUNT + b.DISCOUNT) AS KREDIT,\r\n0  AS DEBET,\r\nif(DATE(a.DOCUMENT_DATE) = DATE(n.DOCUMENT_DATE), b.AMOUNT + b.DISCOUNT, 0) AS TUNAI,\r\na.PERSON_ID,\r\nCONCAT(TRIM(IFNULL(b.NOTE, \' \' )) ,\'  \',  x.DOCUMENT_NO ) AS NOTE\r\nFROM\r\nreceipt a\r\nINNER JOIN receipt_detail b ON (a.RECEIPT_ID = b.RECEIPT_ID) \r\nINNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\nINNER JOIN NOTA_PENJUALAN_AGING x ON (b.NOTA_PENJUALAN_AGING_ID = x.NOTA_PENJUALAN_AGING_ID)\r\nleft join nota_penjualan n on (x.nota_penjualan_id=n.nota_penjualan_id)\r\nWHERE  TRUE\r\nAND (A.DOCUMENT_DATE BETWEEN :12 AND :22)\r\nUNION ALL\r\n/* PELUNASAN DGN BKK*/ \r\nSELECT\r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\n0 AS SALDO,\r\nif(DATE(a.DOCUMENT_DATE) = DATE(n.DOCUMENT_DATE), 0,(b.AMOUNT + b.DISCOUNT) * -1) AS KREDIT,\r\n0  AS DEBET,\r\nif(DATE(a.DOCUMENT_DATE) = DATE(n.DOCUMENT_DATE), (b.AMOUNT + b.DISCOUNT) * -1, 0) AS TUNAI,\r\na.PERSON_ID,\r\nCONCAT(TRIM(IFNULL(b.NOTE, \' \' )) ,\'  \',  x.DOCUMENT_NO ) AS NOTE\r\nFROM\r\nPAYMENT a\r\nINNER JOIN PAYMENT_detail b ON (a.PAYMENT_ID = b.PAYMENT_ID) \r\nINNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\nINNER JOIN NOTA_PENJUALAN_AGING x ON (b.NOTA_PENJUALAN_AGING_ID = x.NOTA_PENJUALAN_AGING_ID)\r\nleft join nota_penjualan n on (x.nota_penjualan_id=n.nota_penjualan_id)\r\nWHERE  TRUE\r\nAND (A.DOCUMENT_DATE BETWEEN :12 AND :22)\r\n) AS XX ON P.PERSON_ID=XX.PERSON_ID\r\nWHERE  RIGHT(TIPE,1) = 1 \r\nAND ABS(SALDO)>0 OR ABS(KREDIT)>0 OR ABS(DEBET)>0 OR ABS(TUNAI)>0\r\n&Kondisi\r\norder by person_name', NULL, '', NULL),
	(1218, 1189, 0, 'select\r\ni.item_description,\r\ni.item_code,\r\ni.item_id,\r\ncs.PERSON_name,\r\ncs.person_code,\r\nsum(if( nd.BASE_QTY=1,nd.ENTERED_QTY /if( to_qty is null,1,b.to_qty),nd.entered_qty)) AS total,\r\n   if(b.uom_code is null, nd.entered_uom,b.uom_code) as entered_uom,\r\nsum(nd.subtotal - nd.DISCOUNT_PRICE1)  as total_rp\r\nfrom\r\nPERSON cs\r\ninner join nota_penjualan n on(cs.PERSON_id=n.PERSON_id)\r\ninner join nota_penjualan_detail nd on (n.nota_penjualan_id=nd.nota_penjualan_id)\r\nINNER JOIN ITEM I ON (nd.ITEM_ID=I.ITEM_ID )\r\nleft join item_uom b on i.item_id=b.ITEM_ID and b.BASE_UOM_FLAG=\'y\'\r\ninner join warehouse w on (n.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nwhere true\r\nand (n.status_id  <> @delete) \r\n&kondisi\r\ngroup by \r\nnd.item_id,\r\ncs.PERSON_id\r\norder by i.item_code,cs.person_name\r\n', NULL, '', NULL),
	(1219, 1190, 0, 'SELECT\r\np.PERSON_ID,\r\nps.CITY,\r\np.PERSON_NAME,\r\nev.DISPLAY_NAME,\r\nsum(if( npd.BASE_QTY=1,npd.ENTERED_QTY /if( to_qty is null,1,to_qty),npd.entered_qty)) as QTY,\r\nsum(npd.Subtotal - npd.discount_price1) as NILAI,\r\ni.item_description\r\nFROM ITEM i\r\nINNER JOIN NOTA_PENJUALAN_DETAIL npd ON(i.ITEM_ID = npd.ITEM_ID)\r\nINNER JOIN NOTA_PENJUALAN np ON(npd.NOTA_PENJUALAN_ID = np.NOTA_PENJUALAN_ID)\r\nLEFT JOIN ERP_LOOKUP_VALUE ev ON(i.GROUP_ID = ev.ERP_LOOKUP_VALUE_ID)\r\nINNER JOIN PERSON p ON(np.PERSON_ID=p.PERSON_ID)\r\nINNER JOIN PERSON_SITE ps ON(p.PERSON_ID=ps.PERSON_ID AND np.PERSON_SITE_ID=ps.PERSON_SITE_ID)\r\nINNER JOIN WAREHOUSE w ON(np.WAREHOUSE_ID=w.WAREHOUSE_ID)\r\nLEFT JOIN KARYAWAN k on (np.KARYAWAN_ID = k.KARYAWAN_ID)\r\nleft join item_uom b on i.item_id=b.ITEM_ID and b.BASE_UOM_FLAG=\'y\'\r\n\r\nWHERE TRUE and np.STATUS_ID <> @DELETE\r\n&kondisi\r\nGROUP BY ps.PERSON_ID,i.ITEM_ID\r\nORDER BY ps.PERSON_ID, i.GROUP_ID', NULL, '', NULL),
