	(1240, 1210, 0, 'select\r\n  v.PERSON_name,\r\n  vs.address1,\r\n  v.PERSON_id,\r\n   vs.phone1,\r\n  a.document_date,\r\n  a.document_no,\r\n  if( b.BASE_QTY=1,b.ENTERED_QTY /if( to_qty is null,1,d.to_qty),b.entered_qty) as entered_qty,\r\n  if(b.BASE_QTY=1,b.UNIT_PRICE * if(to_qty is null,1,d.to_qty),b.unit_price) as unit_price,\r\n  (b.item_description) as item_description,\r\n  (if(d.uom_code is null, b.entered_uom,d.uom_code)) as ENTERED_uom,\r\n  (b.subtotal) as subtotal,\r\n  max(ii.document_date) as SJ,\r\n  max(ii.document_no) as SJ_no,\r\n  sum(if( iid.BASE_QTY=1,iid.ENTERED_QTY /if( to_qty is null,1,d.to_qty),iid.entered_qty) ) as SJ_qty,\r\n  (max(if( b.BASE_QTY=1,b.ENTERED_QTY /if( to_qty is null,1,d.to_qty),b.entered_qty)) - sum(if( iid.BASE_QTY=1,iid.ENTERED_QTY /if( to_qty is null,1,d.to_qty),iid.entered_qty))) as sisa\r\nfrom\r\n  SO a\r\n  inner join inventory_out ii ON (a.SO_id = ii.SO_id)\r\n  inner join SO_detail b ON (a.SO_id = b.SO_id)\r\n  inner join inventory_out_detail iid ON (ii.inventory_out_id = iid.inventory_out_id and iid.item_id = b.item_id)\r\n  inner join PERSON v ON (a.PERSON_id = v.PERSON_id)\r\n  left join PERSON_site vs ON (a.PERSON_ID = vs.PERSON_ID)\r\n  inner join item i on (b.item_id = i.item_id)\r\n  left join item_uom d on i.item_id=d.ITEM_ID and d.BASE_UOM_FLAG=\'y\'\r\nWHERE ((a.STATUS_ID=@complete) or (a.STATUS_ID=@partial))\r\nand (ii.STATUS_ID <> @DELETE)\r\n&kondisi\r\ngroup by b.so_detail_id\r\norder by \r\nv.person_name,\r\na.document_date,\r\nii.document_date	', NULL, '', NULL),
	(1241, 1211, 0, 'SELECT \r\n	I.ITEM_ID,\r\n	I.ITEM_CODE,\r\n	I.ITEM_DESCRIPTION,\r\n	k.STOK,\r\n	B.SO_DETAIL_ID,\r\n	B.SO_ID,\r\n	B.ITEM_ID,\r\n	B.ENTERED_QTY * B.BASE_QTY/ ifnull(to_qty,1) AS ENTERED_QTY,\r\n	B.RECEIVED_ENTERED_QTY / ifnull(to_qty,1) as RECEIVED_ENTERED_QTY,\r\n	B.SUBTOTAL,\r\n	((B.ENTERED_QTY*B.BASE_QTY)-B.RECEIVED_ENTERED_QTY)/ ifnull(to_qty,1) AS SISA_QTY,\r\n	B.UNIT_PRICE,\r\n	B.DISCOUNT_PRICE,\r\n	B.DISCOUNT_PERCEN,\r\n	B.PRICE_AFTER_DISCOUNT,\r\n	B.NOTE,\r\n	B.ENTERED_UOM,\r\n	b.ITEM_DESCRIPTION,\r\n	p.PERSON_CODE,\r\n	p.PERSON_NAME,\r\n	a.DOCUMENT_NO,\r\n	a.DOCUMENT_DATE,\r\n	a.NEED_DATE\r\nFROM\r\n	SO_DETAIL B\r\n	INNER JOIN ITEM I ON (B.ITEM_ID =I.ITEM_ID)\r\n	inner join\r\n	(\r\n		select  k.ITEM_ID, sum(k.QTY_AWAL+k.QTY_MASUK-k.QTY_KELUAR/ ifnull(to_qty,1))  STOK\r\n		from  item_stok k\r\n		inner join item i ON (I.ITEM_ID = k.ITEM_ID)\r\n		left join item_uom ik on I.item_id = ik.item_id  and ik.BASE_UOM_FLAG = \'Y\' \r\n		GROUP BY k.ITEM_ID\r\n	) k  ON (b.ITEM_ID = k.ITEM_ID)\r\n	LEFT JOIN SO a ON (a.SO_ID = b.SO_ID)\r\n	LEFT JOIN PERSON p on (a.PERSON_ID=p.PERSON_ID)\r\n	left join item_uom kk on I.item_id = kk.item_id  and kk.BASE_UOM_FLAG = \'Y\' \r\nWHERE b.ENTERED_QTY > b.RECEIVED_ENTERED_QTY\r\n	and a.status_id IN (@new, @PARTIAL)\r\n	&KONDISI\r\nORDER BY i.item_code, a.DOCUMENT_DATE\r\n', NULL, '', NULL),
	(1243, 1213, 0, 'select\r\nio.DOCUMENT_NO,\r\nio.DOCUMENT_DATE,\r\np.PERSON_NAME,\r\nio.NOTE,\r\nio.inventory_out_id,\r\ni.ITEM_DESCRIPTION,\r\niod.ENTERED_QTY,\r\niod.UNIT_PRICE,\r\niod.SUBTOTAL\r\n\r\nfrom\r\ninventory_out io\r\njoin inventory_out_detail iod on io.INVENTORY_OUT_ID = iod.INVENTORY_OUT_ID\r\njoin person p on io.PERSON_ID = p.PERSON_ID\r\njoin item i on iod.ITEM_ID = i.ITEM_ID \r\njoin warehouse w on io.warehouse_id=w.warehouse_id\r\n\r\nWHERE TRUE\r\nand io.status_id not in(@complete,@delete)  \r\n&Kondisi\r\nORDER BY  io.DOCUMENT_NO, i.ITEM_DESCRIPTION', NULL, '', NULL),
