	(1246, 1215, 0, 'SELECT \r\na.INVOICE_ID,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\na.NOTE,\r\na.DOCUMENT_CLASS_CODE,\r\nd.DOCUMENT_REFF_NO,\r\na.PERIOD_NAME,\r\na.TOTAL_AMOUNT,\r\na.TOTAL_DISCOUNT,\r\na.TOTAL_DISCOUNT_PERCEN,\r\na.PPN_PERCEN,\r\na.PPN_AMOUNT,\r\na.PPN_CODE,\r\na.JTEMPO,\r\nc.PERSON_NAME,\r\nSITE_NAME, \r\nv.DISPLAY_NAME,\r\nw.WAREHOUSE_NAME,\r\na.TOTAL_NET,\r\ncs.ADDRESS1,\r\nif( b.BASE_QTY=1,b.ENTERED_QTY /if(to_qty is null,1,to_qty),b.entered_qty) as ENTERED_QTY,\r\nif(b.BASE_QTY=1,b.UNIT_PRICE * if(to_qty is null,1,to_qty),b.unit_price) AS UNIT_PRICE,\r\nb.BASE_QTY,\r\nb.DISCOUNT_PRICE,\r\nb.DISCOUNT_PRICE1,\r\nIFNULL(b.DISCOUNT_PERCEN,0)DISCOUNT_PERCEN,\r\nb.SUBTOTAL,\r\nb.NOTE,\r\nif(z.uom_code is null, b.entered_uom,z.uom_code) as ENTERED_UOM,\r\nb.ITEM_DESCRIPTION,\r\ni.ITEM_CODE,\r\nd.DOCUMENT_NO as SJ\r\nFROM\r\nINVOICE a\r\nINNER JOIN INVOICE_detail b ON (a.INVOICE_ID = b.INVOICE_ID)\r\nINNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nleft join item_uom z on i.item_id=z.ITEM_ID and z.BASE_UOM_FLAG=\'y\'\r\nINNER JOIN erp_lookup_value v ON  (a.STATUS_ID = v.ERP_LOOKUP_VALUE_ID)\r\nINNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nINNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\nINNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nLEFT JOIN INVENTORY_IN d on (d.INVENTORY_IN_ID=a.INVENTORY_IN_ID)\r\nWHERE a.DOCUMENT_TYPE_ID<5\r\n&KONDISI\r\nORDER BY\r\na.DOCUMENT_NO\r\n', NULL, '', NULL),
