	(1261, 1227, 0, 'select\r\ncs.PERSON_id,\r\ncs.PERSON_name,\r\ncs.person_code,\r\ni.item_description,\r\ni.item_code,\r\nsum(nd.entered_qty) as total,\r\nsum(nd.subtotal - nd.discount_price1)  as total_rp,\r\nsum(nd.entered_qty  * nd.HPP)  as total_hpp\r\nfrom\r\nPERSON cs\r\ninner join nota_penjualan n on(cs.PERSON_id=n.PERSON_id)\r\ninner join nota_penjualan_detail nd on (n.nota_penjualan_id=nd.nota_penjualan_id)\r\ninner join item i on (nd.Item_id = i.ITEM_ID)\r\ninner join warehouse w on (n.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nwhere true \r\nand (n.status_id  <> @delete) \r\nand (i.JENIS_ID <> @UM)\r\nand (n.DOCUMENT_TYPE_ID <> @PREPAYMENT)\r\n&kondisi\r\ngroup by \r\ncs.person_id,\r\ni.item_description,\r\ni.item_code\r\norder by cs.person_name,i.item_description', NULL, '', NULL),
