	(1278, 80, 3, '\r\nSELECT * FROM \r\n( \r\nSELECT\r\n   GROUP_ID,\r\n   ITEM_DESCRIPTION, ITEM_CODE,\r\n   UOM_CODE AS SATUAN,\r\n   SUM(SALDO) AS SALDO,\r\n   SUM(SALDO_RP) AS SALDO_RP,\r\n   SUM(MASUK) AS MASUK,\r\n   SUM(MASUK_RP) AS MASUK_RP,\r\n   SUM(KELUAR) AS KELUAR,\r\n   SUM(KELUAR_RP) AS KELUAR_RP,\r\n   SUM(BAHAN) AS BAHAN,\r\n   SUM(BAHAN_RP) AS BAHAN_RP,\r\n   SUM(JADI) AS JADI, \r\n   SUM(JADI_RP) AS JADI_RP,\r\n     SUM(ADJ) AS ADJ, \r\n   SUM(ADJ_RP) AS ADJ_RP\r\n FROM \r\n     ( SELECT\r\n  NOL as URUT,\r\n  0 as TIPE,\r\n  A.PERIOD_NAME,\r\n  NOL AS HPP_FLAG,  \r\n  \'SALDO\' AS DOCUMENT_CLASS_CODE,\r\n  \'\' AS DOCUMENT_NO,\r\n  \'2000/01/01\' as DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID ,\r\n  a.ITEM_ID,\r\n  \'\' as DTL_ID, \r\n  a.QTY_AWAL AS SALDO,\r\n  a.QTY_AWAL * a.HPP AS SALDO_RP,\r\n  NOL AS MASUK,\r\n  NOL AS MASUK_RP,\r\n  NOL AS KELUAR,\r\n  NOL AS KELUAR_RP,  \r\n  NOL as BAHAN,  \r\n  NOL AS BAHAN_RP,  \r\n  NOL  AS JADI ,  \r\n  NOL AS JADI_RP,\r\n  NOL  AS ADJ ,  \r\n  NOL AS ADJ_RP,\r\n  a.HPP as HARGA, \r\n  a.QTY_AWAL * a.HPP AS SUBTOTAL\r\nFROM\r\n    item_balance a    \r\n    INNER JOIN ITEM i ON (a.ITEM_ID = i.ITEM_ID),\r\n   NOL\r\nWHERE (i.JENIS_ID = @GOODS)\r\n /*AND (i.GROUP_ID = :GROUP_ID)  *//*ini tidak ada*/\r\n and (A.period_name = :PERIOD_NAME1)\r\n\r\nUNION ALL\r\n/* TRANSAKSI BELI */\r\n/*     INVENTORY_IN */\r\nSELECT\r\n  1 as URUT,\r\n  1 as TIPE,\r\n  A.PERIOD_NAME,\r\n  1  AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.INVENTORY_IN_DETAIL_ID as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n  b.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n  b.Subtotal -discount_price1   AS MASUK_RP,\r\n  0 AS KELUAR,\r\n  0 AS KELUAR_RP, \r\n  0 as BAHAN,  \r\n  0 AS BAHAN_RP,  \r\n  0  AS JADI ,  \r\n  0 AS JADI_RP,\r\n  0  AS ADJ ,  \r\n  0 AS ADJ_RP,\r\n\r\n  b.UNIT_PRICE - (b.discount_price1/b.ENTERED_QTY )    as HARGA,\r\n  b.Subtotal-b.discount_price1  AS SUBTOTAL\r\n\r\nFROM\r\n   INVENTORY_IN a\r\n  INNER JOIN INVENTORY_IN_DETAIL b ON (a.INVENTORY_IN_ID = b.INVENTORY_IN_ID )  \r\n    INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\n  \r\n  \r\nWHERE (i.JENIS_ID = @GOODS)\r\n /*AND (i.GROUP_ID = :GROUP_ID)  */\r\n and (A.period_name = :PERIOD_NAME1)\r\nUNION ALL\r\n/*     INVOICE tanpa matching barang datang    */\r\nSELECT\r\n  1 as URUT,\r\n  3 as TIPE,\r\n  A.PERIOD_NAME,\r\n  1  AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.INVOICE_DETAIL_ID as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n  b.ENTERED_QTY * b.BASE_QTY  AS MASUK,\r\n  b.Subtotal -discount_price1   AS MASUK_RP,\r\n  0 AS KELUAR,\r\n  0 AS KELUAR_RP,  \r\n  0 as BAHAN,  \r\n  0 AS BAHAN_RP,  \r\n  0  AS JADI ,  \r\n  0 AS JADI_RP,\r\n0  AS ADJ ,  \r\n  0 AS ADJ_RP,\r\n\r\n\r\n  b.UNIT_PRICE - (b.discount_price1/b.ENTERED_QTY ) as HARGA,\r\n  b.Subtotal -discount_price1 AS SUBTOTAL\r\nFROM\r\n   INVOICE a\r\n   INNER JOIN INVOICE_DETAIL b ON (a.INVOICE_ID = b.INVOICE_ID )\r\n   INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE (i.JENIS_ID = @GOODS) AND  \r\n  (b.INVENTORY_IN_ID IS NULL)\r\n /*AND (i.GROUP_ID = :GROUP_ID)  */\r\n and (A.period_name = :PERIOD_NAME1)\r\nUNION ALL\r\n/* TRANSAKSI JUAL */\r\n/* NOTA_PENJUALAN            */\r\nSELECT\r\n  4 as URUT,\r\n  4 as TIPE,\r\n  A.PERIOD_NAME,\r\n  0  AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.NOTA_PENJUALAN_DETAIL_ID as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n  0 AS MASUK,\r\n0  AS MASUK_RP,\r\n  b.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\n  b.ENTERED_QTY * b.HPP  AS KELUAR_RP,  \r\n  0 as BAHAN,  0 AS BAHAN_RP,  0  AS JADI ,  0 AS JADI_RP,\r\n0  AS ADJ ,  \r\n  0 AS ADJ_RP,\r\n b.HPP    as HARGA,\r\n  b.ENTERED_QTY *  b.HPP    AS SUBTOTAL\r\nFROM\r\n   NOTA_PENJUALAN a\r\n  INNER JOIN NOTA_PENJUALAN_DETAIL b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID )\r\n    INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE \r\n\r\n(b.inventory_out_detail_id is null) and\r\n(i.JENIS_ID = @GOODS) \r\n /*AND (i.GROUP_ID = :GROUP_ID)  */\r\n and (A.period_name = :PERIOD_NAME1)\r\n\r\nUNION ALL\r\n/* S.JALAN */\r\n/* INVENTORY OUT            */\r\nSELECT\r\n  4 as URUT,\r\n  4 as TIPE,\r\n  A.PERIOD_NAME,\r\n  0  AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.INVENTORY_OUT_DETAIL_ID as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n  0 AS MASUK,\r\n0  AS MASUK_RP,\r\n  b.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\n  b.ENTERED_QTY * b.HPP  AS KELUAR_RP,  \r\n  0 as BAHAN,  0 AS BAHAN_RP,  0  AS JADI ,  0 AS JADI_RP,\r\n0  AS ADJ ,  \r\n  0 AS ADJ_RP,\r\n b.HPP    as HARGA,\r\n  b.ENTERED_QTY *  b.HPP    AS SUBTOTAL\r\nFROM\r\n   INVENTORY_OUT  a\r\n  INNER JOIN INVENTORY_OUT_DETAIL b ON (a.INVENTORY_OUT_ID = b.INVENTORY_OUT_ID)\r\n    INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE \r\n(i.JENIS_ID = @GOODS) \r\n /*AND (i.GROUP_ID = :GROUP_ID)  */\r\n and (A.period_name = :PERIOD_NAME1)\r\n\r\nUNION ALL\r\n\r\n/* TRANSAKSI ADJUSTMENT QTY */\r\n/* ADJ_QTY           */\r\nSELECT\r\n  2 as URUT,\r\n  5 as TIPE,\r\n  A.PERIOD_NAME,\r\n  IF(a.COA_ID = @HPP, 0,1)  AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.adjust_qty_detail_ID  as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n0  AS MASUK,\r\n0 AS MASUK_RP,\r\n  0 AS KELUAR,\r\n  0 AS KELUAR_RP,  \r\n  0 as BAHAN,  0 AS BAHAN_RP,  0  AS JADI ,  0 AS JADI_RP,\r\n  b.ENTERED_QTY * b.BASE_QTY AS ADJ,\r\nb.Subtotal AS ADJ_RP,\r\n  b.UNIT_PRICE as HARGA,\r\n  b.Subtotal AS SUBTOTAL\r\nFROM\r\n  adjust_qty a\r\n  INNER JOIN adjust_qty_detail b ON (a.ADJUST_QTY_ID = b.ADJUST_QTY_ID)\r\n  \r\n  INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE \r\n(i.JENIS_ID = @GOODS) \r\n\r\n /*AND (i.GROUP_ID = :GROUP_ID)  */\r\n and (A.period_name = :PERIOD_NAME1)\r\nUNION ALL\r\n\r\n/* TRANSAKSI ADJUSTMENT AMOUNT */\r\n/* ADJ_AMOUNT           */\r\nSELECT\r\n  2 as URUT,\r\n  6 as TIPE,\r\n  A.PERIOD_NAME,\r\n  IF(a.COA_ID = @HPP, 0,1)  AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  0 AS WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.adjust_AMOUNT_detail_ID  as DTL_ID, \r\n  0 AS SALDO,\r\n  0 AS SALDO_RP,\r\n  0 AS MASUK,\r\n  0 AS MASUK_RP,\r\n  0 AS KELUAR,\r\n0  AS KELUAR_RP,  \r\n  0 as BAHAN,  0 AS BAHAN_RP,  0  AS JADI ,  0 AS JADI_RP,\r\n0  AS ADJ ,  \r\n    b.ENTERED_AMOUNT AS ADJ_RP,\r\n\r\n  0 as HARGA,\r\n  b.ENTERED_AMOUNT AS ENTERED_AMOUNT\r\nFROM\r\n  adjust_AMOUNT a\r\n  INNER JOIN adjust_AMOUNT_detail b ON (a.ADJUST_AMOUNT_ID = b.ADJUST_AMOUNT_ID)\r\n  \r\n  INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE\r\n(i.JENIS_ID = @GOODS) \r\n /*AND (i.GROUP_ID = :GROUP_ID)  */\r\n and (A.period_name = :PERIOD_NAME1)\r\nUNION ALL\r\n/* TRANSAKSI RAKIT */\r\n/* BUILD  BAHAN BAKU       */\r\nSELECT\r\n  5 as URUT,\r\n  7 as TIPE,\r\n  A.PERIOD_NAME,\r\n  0 AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.build_detail_ID as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n 0 AS MASUK,\r\n 0 AS MASUK_RP,\r\n  0 AS KELUAR,\r\n 0 AS KELUAR_RP,  \r\n  b.ENTERED_QTY * b.BASE_QTY AS BAHAN,  \r\n   b.SUBTOTAL  AS BAHAN_RP, \r\n  0  AS JADI ,  \r\n  0 AS JADI_RP,\r\n0  AS ADJ ,  \r\n  0 AS ADJ_RP,\r\n\r\n  b.UNIT_PRICE  AS HARGA,\r\n  b.SUBTOTAL  AS SUBTOTAL\r\nFROM\r\n  build a\r\n  INNER JOIN build_detail b ON (a.build_ID = b.build_ID)\r\n  \r\n    INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE  (i.JENIS_ID = @GOODS)\r\n /*AND (i.GROUP_ID = :GROUP_ID)  */\r\n and (A.period_name = :PERIOD_NAME1)\r\nUNION ALL\r\n\r\n\r\n/* TRANSAKSI RAKIT */\r\n/* BUILD  ITEM YG DIBUAT       */\r\nSELECT\r\n  100 as URUT,\r\n  7 as TIPE,\r\n  A.PERIOD_NAME,\r\n  1 AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  a.ITEM_ID ,\r\n  a.build_ID as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n  0 AS MASUK,\r\n  0 MASUK_RP,\r\n  0 AS KELUAR,\r\n  0  AS KELUAR_RP,  \r\n  0 as BAHAN,  \r\n  0 AS BAHAN_RP,  \r\n  a.ENTERED_QTY   * a.BASE_QTY AS JADI ,\r\n  a.TOTAL_AMOUNT AS JADI_RP,\r\n   0  AS ADJ ,  \r\n  0 AS ADJ_RP,\r\n\r\n   a.TOTAL_AMOUNT / (a.ENTERED_QTY * a.BASE_QTY)  AS HARGA,\r\n  a.TOTAL_AMOUNT  AS SUBTOTAL\r\nFROM\r\n  build a\r\n    INNER JOIN ITEM i ON (a.ITEM_ID = i.ITEM_ID)\r\n  \r\nWHERE (i.JENIS_ID = @GOODS)\r\n /*AND (i.GROUP_ID = :GROUP_ID)  */\r\n and (A.period_name = :PERIOD_NAME1))\r\nAS XX\r\n   INNER JOIN ITEM i ON (XX.ITEM_ID = i.ITEM_ID)\r\nWHERE\r\n   \r\n   \r\nTRUE\r\n  &KONDISI\r\nGROUP BY\r\n  XX.ITEM_ID\r\nORDER BY ITEM_CODE\r\n) AS YY WHERE \r\n(ABS(SALDO) + ABS(KELUAR)+    ABS(BAHAN)+  ABS(JADI)+  ABS(ADJ)+  ABS( MASUK ) > 0 )\r\nOR \r\n(ABS(SALDO_RP)+ABS(MASUK_RP)+ABS(KELUAR_RP)+  ABS( BAHAN_RP)+ABS(JADI_RP)+  ABS(ADJ_RP) > 0 )\r\n\r\n', NULL, '', 0),
	(1279, 1231, 9, 'SELECT \r\na.PERSON_NAME,\r\na.PERSON_ID,\r\na.PERSON_CODE\r\nFROM\r\nPERSON a  \r\nWHERE (active_flag = \'Y\') AND \r\nRIGHT(TIPE,1) = 1 \r\n&kondisi\r\nOrder by   a.PERSON_NAME', NULL, ' ', NULL),
	(1280, 1231, 0, 'SELECT \r\na.PERSON_ID,\r\n:12 AS TANGGALAN, \r\nSUM(SALDO - KREDIT + DEBET ) AS SALDO\r\nFROM PERSON a \r\nINNER JOIN\r\n(\r\n/* KARTU PIUTANG INI\r\nX1 = PERIODE UTK SALDO AWAL \r\nV1 = TRANSAKSI \r\nT1 = TANGGAL / PERIODE\r\n*/\r\n/* SALDO AWAL */\r\nSELECT\r\n0 AS URUT, \r\n\'SALDO\' as DOCUMENT_CLASS_CODE,\r\n\'\' as DOCUMENT_NO,\r\nnull as DOCUMENT_DATE,\r\nc.PERSON_NAME,\r\nc.PERSON_CODE,\r\na.SALDO_AWAL AS SALDO, \r\n0 AS KREDIT,\r\n0 AS DEBET,\r\na.PERSON_ID,\r\n\'SALDO AWAL\' as NOTE,\r\na.person_balance_id as TRANS_ID,\r\nnull as METODE,\r\nnull as JTEMPO\r\nFROM \r\nPERSON_balance a\r\nINNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nWHERE  (A.PERIOD_NAME = :PERIOD_NAME1)\r\n AND (A.PERIOD_NAME = :PERIOD_NAME1)\r\nUNION ALL\r\n/* PENJUALAN DAN RETUR */\r\nSELECT\r\n3 AS URUT, \r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\nc.PERSON_NAME,\r\nc.PERSON_CODE,\r\n0 AS SALDO,\r\n0 AS KREDIT,\r\na.TOTAL_NET AS DEBET,\r\na.PERSON_ID,\r\na.NOTE,\r\na.NOTA_PENJUALAN_ID  as TRANS_ID,\r\nnull as METODE,\r\nnull as JTEMPO\r\nFROM\r\nnota_penjualan a\r\nINNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\nINNER JOIN PERSON c On (a.PERSON_ID = c.PERSON_ID)\r\nWHERE   (a.NET_BYR1 = 0) \r\nand  (A.PERIOD_NAME = :PERIOD_NAME1) \r\n AND (A.DOCUMENT_DATE < :12)\r\nUNION ALL\r\n/* PELUNASAN DGN BKM */\r\nSELECT\r\n4 AS URUT, \r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\nc.PERSON_NAME,\r\nc.PERSON_CODE,\r\n0 AS SALDO,\r\nb.AMOUNT + b.DISCOUNT AS KREDIT,\r\n0  AS DEBET,\r\na.PERSON_ID,\r\nCONCAT(TRIM(IFNULL(b.NOTE, \' \' )) ,\'  \',  x.DOCUMENT_NO ) AS NOTE,\r\na.receipt_id  as TRANS_ID,\r\nCONCAT(COA.COA_NAME,\' \',IFNULL(cek.DOCUMENT_NO,\'\')  ) as METODE,\r\na.JTEMPO as JTEMPO\r\nFROM\r\nreceipt a\r\nINNER JOIN receipt_detail b ON (a.RECEIPT_ID = b.RECEIPT_ID) \r\nINNER JOIN PERSON c On (a.PERSON_ID = c.PERSON_ID)\r\nINNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\nINNER JOIN NOTA_PENJUALAN_AGING x ON (b.NOTA_PENJUALAN_AGING_ID =              x.NOTA_PENJUALAN_AGING_ID)\r\nLEFT JOIN COA ON (COA.COA_ID=a.BANK_ID)\r\nLEFT JOIN CHEQUE cek ON (cek.CHEQUE_ID=a.CHEQUE_ID)\r\nWHERE  TRUE\r\n\r\n\r\n AND (A.PERIOD_NAME = :PERIOD_NAME1)  \r\n AND (A.DOCUMENT_DATE < :12)\r\nUNION ALL\r\n/* PELUNASAN DGN BKK */ \r\nSELECT\r\n5 AS URUT, \r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\nc.PERSON_NAME,\r\nc.PERSON_CODE,\r\n0 AS SALDO,\r\n(b.AMOUNT + b.DISCOUNT) * -1 AS KREDIT,\r\n0  AS DEBET,\r\na.PERSON_ID,\r\nCONCAT(TRIM(IFNULL(b.NOTE, \' \' )) ,\'  \',  x.DOCUMENT_NO ) AS NOTE,\r\na.PAYMENT_ID  as TRANS_ID,\r\nCONCAT(COA.COA_NAME,\' \',IFNULL(cek.DOCUMENT_NO,\'\')) as METODE,\r\na.JTEMPO as JTEMPO\r\nFROM\r\nPAYMENT a\r\nINNER JOIN PAYMENT_detail b ON (a.PAYMENT_ID = b.PAYMENT_ID) \r\nINNER JOIN PERSON c On (a.PERSON_ID = c.PERSON_ID)\r\nINNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\nINNER JOIN NOTA_PENJUALAN_AGING x ON (b.NOTA_PENJUALAN_AGING_ID = x.NOTA_PENJUALAN_AGING_ID)\r\nLEFT JOIN COA ON (COA.COA_ID=a.BANK_ID)\r\nLEFT JOIN CHEQUE cek ON (cek.CHEQUE_ID=a.CHEQUE_ID)\r\nWHERE  TRUE\r\n\r\n\r\n AND (A.PERIOD_NAME = :PERIOD_NAME1)  \r\n AND (A.DOCUMENT_DATE < :12)\r\n) AS XX ON a.PERSON_ID=xx.PERSON_ID\r\nWHERE TRUE\r\n&KONDISI\r\nGROUP BY xx.PERSON_ID\r\nOrder by   XX.PERSON_NAME', NULL, '', NULL),
	(1281, 1231, 3, 'SELECT \r\n* \r\nFROM PERSON a \r\nINNER JOIN\r\n(\r\n/* KARTU PIUTANG INI\r\nX1 = PERIODE UTK SALDO AWAL \r\nV1 = TRANSAKSI \r\nT1 = TANGGAL / PERIODE\r\n*/\r\n/* SALDO AWAL */\r\nSELECT\r\n0 AS URUT, \r\n\'SALDO\' as DOCUMENT_CLASS_CODE,\r\n\'\' as DOCUMENT_NO,\r\nnull as DOCUMENT_DATE,\r\nc.PERSON_NAME,\r\nc.PERSON_CODE,\r\na.SALDO_AWAL AS SALDO, \r\n0 AS KREDIT,\r\n0 AS DEBET,\r\na.PERSON_ID,\r\n\'SALDO AWAL\' as NOTE,\r\na.person_balance_id as TRANS_ID,\r\nnull as METODE,\r\nnull as JTEMPO\r\nFROM \r\nPERSON_balance a\r\nINNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nWHERE  TRUE\r\n AND FALSE\r\nUNION ALL\r\n/* PENJUALAN DAN RETUR */\r\nSELECT\r\n3 AS URUT, \r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\nc.PERSON_NAME,\r\nc.PERSON_CODE,\r\n0 AS SALDO,\r\n0 AS KREDIT,\r\na.TOTAL_NET AS DEBET,\r\na.PERSON_ID,\r\na.NOTE,\r\na.NOTA_PENJUALAN_ID  as TRANS_ID,\r\nnull as METODE,\r\nnull as JTEMPO\r\nFROM\r\nnota_penjualan a\r\nINNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\nINNER JOIN PERSON c On (a.PERSON_ID = c.PERSON_ID)\r\nWHERE   (a.NET_BYR1 = 0) \r\n AND (A.DOCUMENT_DATE BETWEEN :12 AND :22)\r\nUNION ALL\r\n/* PELUNASAN DGN BKM */\r\nSELECT\r\n4 AS URUT, \r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\nc.PERSON_NAME,\r\nc.PERSON_CODE,\r\n0 AS SALDO,\r\nb.AMOUNT + b.DISCOUNT AS KREDIT,\r\n0  AS DEBET,\r\na.PERSON_ID,\r\nCONCAT(TRIM(IFNULL(b.NOTE, \' \' )) ,\'  \',  x.DOCUMENT_NO ) AS NOTE,\r\na.receipt_id  as TRANS_ID,\r\nCONCAT(COA.COA_NAME,\' \',IFNULL(cek.DOCUMENT_NO,\'\')  ) as METODE,\r\na.JTEMPO as JTEMPO\r\nFROM\r\nreceipt a\r\nINNER JOIN receipt_detail b ON (a.RECEIPT_ID = b.RECEIPT_ID) \r\nINNER JOIN PERSON c On (a.PERSON_ID = c.PERSON_ID)\r\nINNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\nINNER JOIN NOTA_PENJUALAN_AGING x ON (b.NOTA_PENJUALAN_AGING_ID =              x.NOTA_PENJUALAN_AGING_ID)\r\nLEFT JOIN COA ON (COA.COA_ID=a.BANK_ID)\r\nLEFT JOIN CHEQUE cek ON (cek.CHEQUE_ID=a.CHEQUE_ID)\r\nWHERE  TRUE \r\n AND (A.DOCUMENT_DATE BETWEEN :12 AND :22)\r\nUNION ALL\r\n/* PELUNASAN DGN BKK */ \r\nSELECT\r\n5 AS URUT, \r\na.DOCUMENT_CLASS_CODE,\r\na.DOCUMENT_NO,\r\na.DOCUMENT_DATE,\r\nc.PERSON_NAME,\r\nc.PERSON_CODE,\r\n0 AS SALDO,\r\n(b.AMOUNT + b.DISCOUNT) * -1 AS KREDIT,\r\n0  AS DEBET,\r\na.PERSON_ID,\r\nCONCAT(TRIM(IFNULL(b.NOTE, \' \' )) ,\'  \',  x.DOCUMENT_NO ) AS NOTE,\r\na.PAYMENT_ID  as TRANS_ID,\r\nCONCAT(COA.COA_NAME,\' \',IFNULL(cek.DOCUMENT_NO,\'\')) as METODE,\r\na.JTEMPO as JTEMPO\r\nFROM\r\nPAYMENT a\r\nINNER JOIN PAYMENT_detail b ON (a.PAYMENT_ID = b.PAYMENT_ID) \r\nINNER JOIN PERSON c On (a.PERSON_ID = c.PERSON_ID)\r\nINNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\nINNER JOIN NOTA_PENJUALAN_AGING x ON (b.NOTA_PENJUALAN_AGING_ID = x.NOTA_PENJUALAN_AGING_ID)\r\nLEFT JOIN COA ON (COA.COA_ID=a.BANK_ID)\r\nLEFT JOIN CHEQUE cek ON (cek.CHEQUE_ID=a.CHEQUE_ID)\r\nWHERE  TRUE \r\n AND (A.DOCUMENT_DATE BETWEEN :12 AND :22)\r\n) AS XX ON a.PERSON_ID=xx.PERSON_ID\r\nWHERE TRUE\r\n&KONDISI\r\nORDER BY xx.PERSON_NAME,DOCUMENT_DATE', NULL, '', NULL),
	(1282, 1232, 0, 'SELECT   \r\na.CREATED_BY,\r\nU.ERP_USER_NAME,\r\na.NOTA_PENJUALAN_ID,\r\na.DOCUMENT_DATE,\r\na.DOCUMENT_NO,\r\nc.PERSON_NAME,\r\nw.WAREHOUSE_NAME,\r\na.NOTE,\r\na.JTEMPO,\r\na.TOTAL_AMOUNT,\r\na.TOTAL_DISCOUNT,\r\na.PPN_AMOUNT,\r\nv.DISPLAY_NAME as STATUS_DOC,\r\nb.ITEM_DESCRIPTION,\r\nb.ENTERED_QTY,\r\nb.UNIT_PRICE,\r\nb.DISCOUNT_PRICE,\r\nb.SUBTOTAL\r\nFROM\r\nnota_penjualan a\r\nINNER JOIN nota_penjualan_detail b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID)\r\nINNER JOIN item i ON (b.ITEM_ID = i.ITEM_ID)\r\nINNER JOIN ERP_USER U ON (a.CREATED_BY = U.ERP_USER_ID)\r\nINNER JOIN erp_lookup_value v ON  (a.STATUS_ID = v.ERP_LOOKUP_VALUE_ID)\r\nINNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nINNER JOIN PERSON_site cs ON (a.PERSON_SITE_ID = cs.PERSON_SITE_ID)\r\nINNER JOIN warehouse w ON (a.WAREHOUSE_ID = w.WAREHOUSE_ID)\r\nLEFT JOIN INVENTORY_OUT d on (d.INVENTORY_OUT_ID=a.INVENTORY_OUT_ID)\r\nWHERE a.DOCUMENT_TYPE_ID<5\r\n&KONDISI\r\nORDER BY U.ERP_USER_NAME, a.DOCUMENT_NO', NULL, '', NULL),
	(1285, 1233, 0, 'SELECT @ABC2 :=0,YY.*,@NOTA_PENJUALAN_ID:=0 FROM\r\n(\r\nSELECT \r\n@NOTA_PENJUALAN_ID,\r\nIF(@NOTA_PENJUALAN_ID<>xx.NOTA_PENJUALAN_ID,(((@ABC2 := 0 + 1)-1) DIV @BARIS )+1,(((@ABC2 := @ABC2 + 1)-1) DIV @BARIS )+1) AS X1,\r\n@NOTA_PENJUALAN_ID:=xx.NOTA_PENJUALAN_ID,\r\nxx.*\r\nFROM(\r\nSELECT A.*,i.ITEM_CODE,b.DOCUMENT_NO,b.DOCUMENT_REFF_NO,b.DOCUMENT_DATE,b.JTEMPO, b.NOTE AS KET, p.PERSON_NAME,ps.SITE_NAME, ps.ADDRESS1, ps.PHONE1, k.FIRST_NAME\r\nFROM\r\nNOTA_PENJUALAN_DETAIL A\r\nINNER JOIN NOTA_PENJUALAN b ON b.NOTA_PENJUALAN_ID=a.NOTA_PENJUALAN_ID\r\nINNER JOIN PERSON_SITE ps ON b.PERSON_SITE_ID=ps.PERSON_SITE_ID\r\nINNER JOIN PERSON p ON b.PERSON_ID=p.PERSON_ID\r\nLEFT JOIN KARYAWAN k ON b.KARYAWAN_ID=k.KARYAWAN_ID\r\nINNER JOIN ITEM I ON(A.ITEM_ID = I.ITEM_ID)\r\nWHERE TRUE \r\n&KONDISI\r\nORDER BY A.NOTA_PENJUALAN_DETAIL_ID\r\n)AS xx\r\n) AS YY', NULL, '', NULL),
	(1288, 1234, 0, '\r\nSELECT * FROM (\r\n\r\nSELECT\r\n	1 AS URUT,\r\n    NULL as document_no,\r\n    NULL as DOCUMENT_REFF_NO,\r\n    p.START_DATE DOCUMENT_DATE,\r\n    (C.COA_SALDO + C.COA_DEBET - C.COA_CREDIT)*-1 TOTAL_NET,\r\n    \'\' as NO_SJ,\r\n    0 NOTA_PENJUALAN_ID,\r\n	NULL NO_NOTA,	\r\n	NULL REFF_NOTA,\r\n    NULL TGL_NOTA,\r\n    0 NET_NOTA,\r\n    0 as UM,\r\n	\'SALDO AWAL\' PERSON_NAME\r\n\r\nFROM \r\nCOA_BALANCE c \r\nINNER JOIN PERIOD p ON c.PERIOD_NAME=p.PERIOD_NAME\r\nWHERE COA_ID=@UMUKA_JUAL AND c.PERIOD_NAME=(\r\nSELECT \r\nIF(:PERIOD_NAME1=\'\',FALSE,PERIOD_NAME)PERIOD_NAME\r\nFROM PERIOD\r\nWHERE PERIOD_NAME<:PERIOD_NAME1\r\nORDER BY PERIOD_NAME DESC\r\nLIMIT 1\r\n)\r\nAND ABS(C.COA_SALDO + C.COA_DEBET - C.COA_CREDIT)>0\r\n\r\nUNION ALL\r\n\r\nSELECT \r\n	1 AS URUT,\r\n	xx.DOCUMENT_NO,\r\n	xx.DOCUMENT_REFF_NO,\r\n	xx.DOCUMENT_DATE,\r\n	xx.TOTAL_NET,\r\n	xx.NO_SJ,\r\n	xx.NOTA_PENJUALAN_ID,	\r\n	a.DOCUMENT_NO NO_NOTA,	\r\n	a.DOCUMENT_REFF_NO REFF_NOTA,\r\n    a.DOCUMENT_DATE TGL_NOTA,\r\n    a.TOTAL_NET NET_NOTA,\r\n    (b.SUBTOTAL) as UM,\r\n	\'SALDO AWAL\' PERSON_NAME\r\nFROM\r\n(SELECT\r\n    ifnull(a1.DOCUMENT_NO,a.document_no) as document_no,\r\n    ifnull(a1.DOCUMENT_REFF_NO,a.DOCUMENT_REFF_NO) as DOCUMENT_REFF_NO,\r\n    a.DOCUMENT_DATE,\r\n    a.TOTAL_AMOUNT as TOTAL_NET,\r\n    a2.DOCUMENT_NO as NO_SJ,\r\n    a.NOTA_PENJUALAN_ID\r\nFROM \r\n  NOTA_PENJUALAN a\r\n\r\nLEFT JOIN SO a1 ON a1.SO_ID=a.SO_ID\r\nLEFT JOIN INVENTORY_OUT a2 ON (a2.INVENTORY_OUT_ID=a.INVENTORY_OUT_ID)\r\nLEFT JOIN \r\n   (SELECT b.NOTA_PENJUALAN_PRE_ID,\r\n                   sum(b.SUBTOTAL) as UM_APPLY\r\n     FROM\r\n         NOTA_PENJUALAN_DETAIL b \r\n     INNER  JOIN NOTA_PENJUALAN c ON (c.NOTA_PENJUALAN_ID=b.NOTA_PENJUALAN_ID)\r\n     WHERE \r\n          TRUE and (DATE(c.DOCUMENT_DATE) <:13 )\r\n			AND PERIOD_NAME=:PERIOD_NAME1\r\n    GROUP BY b.NOTA_PENJUALAN_PRE_ID\r\n   ) as X ON  (x.NOTA_PENJUALAN_PRE_ID=a.NOTA_PENJUALAN_ID)\r\n\r\nWHERE \r\n   a.INVOICE_TYPE_ID=@PREPAYMENT \r\n   AND a.STATUS_ID=@FULL_PAID\r\n  /*AND ((a.TOTAL_NET + UMUKA_OUT) > 0)*/\r\n  AND (  (x.UM_APPLY IS NOT NULL  AND (a.TOTAL_NET + x.UM_APPLY) > 0 ) or x.UM_APPLY is NULL   ) \r\n  AND a.TOTAL_NET<>0\r\n  AND a.PERIOD_NAME=:PERIOD_NAME1\r\n   and a.DOCUMENT_DATE < :13\r\n)AS XX\r\nLEFT JOIN NOTA_PENJUALAN_DETAIL b ON b.NOTA_PENJUALAN_PRE_ID =xx.NOTA_PENJUALAN_ID\r\nLEFT JOIN NOTA_PENJUALAN a ON a.NOTA_PENJUALAN_ID=b.NOTA_PENJUALAN_ID\r\n\r\nUNION ALL \r\n\r\nSELECT\r\n	2 AS URUT,\r\n    ifnull(a1.DOCUMENT_NO,a.document_no) as document_no,\r\n    ifnull(a1.DOCUMENT_REFF_NO,a.DOCUMENT_REFF_NO) as DOCUMENT_REFF_NO,\r\n    a.DOCUMENT_DATE,\r\n    a.TOTAL_AMOUNT TOTAL_NET,\r\n    a2.DOCUMENT_NO as NO_SJ,\r\n    a.NOTA_PENJUALAN_ID,	\r\n	na.DOCUMENT_NO NO_NOTA,	\r\n	na.DOCUMENT_REFF_NO REFF_NOTA,\r\n    na.DOCUMENT_DATE TGL_NOTA,\r\n    na.TOTAL_NET NET_NOTA,\r\n    (b.SUBTOTAL) as UM,\r\n	p.PERSON_NAME\r\n\r\nFROM \r\n  NOTA_PENJUALAN a\r\nINNER JOIN PERSON p ON a.PERSON_ID=p.PERSON_ID\r\nLEFT JOIN PERSON_SITE S ON P.PERSON_ID=S.PERSON_ID\r\nLEFT JOIN SO a1 ON a1.SO_ID=a.SO_ID\r\nLEFT JOIN INVENTORY_OUT a2 ON (a2.INVENTORY_OUT_ID=a.INVENTORY_OUT_ID)\r\n\r\nLEFT JOIN NOTA_PENJUALAN_DETAIL b ON b.NOTA_PENJUALAN_PRE_ID=a.NOTA_PENJUALAN_ID\r\nLEFT JOIN NOTA_PENJUALAN na ON b.NOTA_PENJUALAN_ID=na.NOTA_PENJUALAN_ID\r\n\r\nWHERE \r\n  a.INVOICE_TYPE_ID=@PREPAYMENT \r\n  AND a.STATUS_ID=@FULL_PAID\r\n  AND a.TOTAL_NET<>0\r\n&KONDISI\r\n      \r\n) AS XX\r\n\r\nORDER BY URUT,PERSON_NAME,DOCUMENT_DATE   ', NULL, '', NULL),
	(1291, 1235, 0, 'Select \r\nb.karyawan_id,\r\nb.first_name,\r\na.person_id,   \r\nperson_name, \r\nperson_CODE,\r\na.document_date, \r\na.document_no,   \r\ns.document_no NO_SO,\r\nSUM(subtotal - discount_price1)  as total_amount\r\n from\r\nnota_penjualan a\r\nINNER JOIN karyawan b on (a.karyawan_id = b.karyawan_id)\r\nINNER JOIN person  c ON (a.person_id = c.person_id)\r\nINNER JOIN nota_penjualan_detail ad on (a.nota_penjualan_id = ad.nota_penjualan_id)\r\nINNER JOIN warehouse w on (a.warehouse_id = w.warehouse_id)\r\nINNER JOIN ITEM i on (ad.item_id = i.item_id)\r\nLEFT JOIN SO s ON (a.so_id = s.so_id)\r\nwhere \r\n  (a.invoice_type_id <>  @PREPAYMENT) \r\n  and (i.JENIS_ID <>  @UM) \r\n  and (a.status_id  <> @delete) \r\n&kondisi\r\ngroup by \r\nb.karyawan_id,\r\na.person_id, \r\na.document_date, \r\na.document_no,   \r\ns.document_no \r\nOrder by b.first_name,person_code,document_date', NULL, '', NULL),
	(1294, 1236, 0, 'Select \r\nb.karyawan_id,\r\nb.first_name,\r\n\r\ni.item_id,\r\ni.item_description, \r\ni.item_code, \r\nsum(if( x.BASE_QTY=1,X.ENTERED_QTY /if( to_qty is null,1,to_qty),x.entered_qty)) as qty,\r\n\r\na.document_date, \r\na.document_no,   \r\ns.DOCUMENT_NO NO_SO,\r\nSUM(subtotal - discount_price1)  as total_amount, \r\nsum(if( x.BASE_QTY=1,x.ENTERED_QTY /if( to_qty is null,1,to_qty),x.entered_qty)) as QTY, \r\nc.Person_name, \r\nc.Person_code, \r\nif(d.uom_code is null, x.entered_uom,d.uom_code) as entered_uom\r\n from\r\nnota_penjualan a\r\nINNER JOIN nota_penjualan_detail x on (a.nota_penjualan_id = x.nota_penjualan_id)\r\ninner join karyawan b on (a.karyawan_id = b.karyawan_id)\r\ninner join warehouse w on (a.warehouse_id = w.warehouse_id)\r\nINNER JOIN ITEM i on (x.item_id = i.item_id)\r\nINNER JOIN PERSON c on(a.PERSON_ID = c.PERSON_ID)\r\nleft join item_uom d on i.item_id=d.ITEM_ID and d.BASE_UOM_FLAG=\'y\'\r\nLEFT JOIN SO s ON (a.so_id = s.so_id)\r\n\r\nwhere \r\n  (a.invoice_type_id <>  @PREPAYMENT) \r\n  and (i.JENIS_ID <>  @UM) \r\n  and (a.status_id  <> @delete) \r\n\r\n&kondisi\r\ngroup by  b.first_name, i.item_id, a.document_no, s.DOCUMENT_NO \r\nOrder by b.first_name, item_code, i.item_description, document_date', NULL, '', NULL),
	(1297, 1237, 0, 'SELECT xx.*, :12 as TGL FROM(\r\nSELECT \r\n	PERSON_ID,\r\n	\'SALDO\' AS TIPE,\r\n	\'SALDO\' DOCUMENT_CLASS_CODE,\r\n	SUM(SALDO + KREDIT - DEBET ) AS SALDO,\r\n	NULL  DOCUMENT_NO,\r\n	NULL  DOCUMENT_DATE,\r\n	PERSON_NAME,\r\n	PERSON_CODE,\r\n	0 KREDIT,\r\n	0 DEBET,\r\n	NULL NOTE\r\nFROM\r\n(\r\n \r\n/* \r\nHUTANG \r\nX1 = PERIODE UTK SALDO AWAL \r\nV1 = TRANSAKSI \r\nT1 = TANGGAL / PERIODE\r\n\r\n*/\r\n\r\n/* SALDO AWAL */ \r\nSELECT\r\n  0 AS URUT, \r\n \'SALDO\' as DOCUMENT_CLASS_CODE,\r\n  \'\' as DOCUMENT_NO,\r\n  null as DOCUMENT_DATE,\r\n  c.PERSON_NAME,\r\n  c.PERSON_CODE,\r\n  a.SALDO_HUTANG AS SALDO, \r\n  0 AS KREDIT,\r\n  0 AS DEBET,\r\n  a.PERSON_ID,\r\n  \'SALDO AWAL\' as NOTE\r\n FROM \r\n  PERSON_balance a\r\n INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nWHERE   LEFT(c.TIPE,1) = 1  \r\n AND (A.PERIOD_NAME = :PERIOD_NAME1)\r\n AND (A.PERIOD_NAME = :PERIOD_NAME1)\r\n\r\nUNION ALL\r\n/* PEMBELIAN */ \r\nSELECT\r\n  1 AS URUT, \r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  c.PERSON_NAME,\r\n  c.PERSON_CODE,\r\n  0 AS SALDO,\r\n  a.TOTAL_NET as KREDIT,\r\n  0 as DEBET,\r\n  a.PERSON_ID,\r\n  a.NOTE\r\nFROM\r\n  INVOICE a\r\n  INNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\n  INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nWHERE TRUE\r\n AND (A.PERIOD_NAME = :PERIOD_NAME1)\r\n AND (A.DOCUMENT_DATE < :12)\r\n\r\nUNION ALL\r\n/* PELUNASAN DGN BKK */\r\nSELECT\r\n  2 AS URUT, \r\n a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  c.PERSON_NAME,\r\n  c.PERSON_CODE,\r\n0 AS SALDO,\r\n  0  AS KREDIT,\r\n  b.AMOUNT+b.DISCOUNT AS DEBET,\r\n\r\n  a.PERSON_ID,\r\n  CONCAT(TRIM(IFNULL(b.NOTE, \' \' )) ,\'  \',  x.DOCUMENT_NO ) AS NOTE\r\nFROM\r\n  PAYMENT a\r\n  INNER JOIN PAYMENT_detail b ON (a.PAYMENT_ID = b.PAYMENT_ID)\r\n  INNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\n  INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN INVOICE_DUE x ON ( b.INVOICE_DUE_ID = x.INVOICE_DUE_ID)\r\nWHERE TRUE\r\n AND (A.PERIOD_NAME = :PERIOD_NAME1)\r\n AND (A.DOCUMENT_DATE < :12)\r\n\r\n\r\nUNION ALL\r\n\r\n/* PELUNASAN DGN BKM */\r\n\r\nSELECT\r\n  4 AS URUT, \r\n a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  c.PERSON_NAME,\r\n  c.PERSON_CODE,\r\n  0 AS SALDO,\r\n  0  AS KREDIT,  \r\n  (b.AMOUNT+b.DISCOUNT) * -1  AS DEBET,\r\n  \r\n  a.PERSON_ID,\r\n  CONCAT(TRIM(IFNULL(b.NOTE, \' \' )) ,\'  \',  x.DOCUMENT_NO ) AS NOTE\r\nFROM\r\n  receipt a\r\n   INNER JOIN receipt_detail b ON (a.RECEIPT_ID = b.RECEIPT_ID) \r\n INNER JOIN PERSON c On (a.PERSON_ID = c.PERSON_ID)\r\n  INNER JOIN period p ON (a.PERIOD_NAME = p.PERIOD_NAME)\r\n  INNER JOIN INVOICE_DUE x ON ( b.INVOICE_DUE_ID = x.INVOICE_DUE_ID)\r\nWHERE  TRUE\r\n   AND (A.PERIOD_NAME = :PERIOD_NAME1)  \r\n   AND (A.DOCUMENT_DATE < :12)\r\n\r\n\r\n\r\n) AS XX\r\nGROUP BY PERSON_ID\r\n\r\nUNION ALL\r\n\r\nSELECT \r\n	PERSON_ID,\r\n	NULL AS TIPE,\r\n	DOCUMENT_CLASS_CODE,\r\n	0 AS SALDO,\r\n	DOCUMENT_NO,\r\n	DOCUMENT_DATE,\r\n	PERSON_NAME,\r\n	PERSON_CODE,\r\n	KREDIT,\r\n	DEBET,\r\n	NOTE\r\nFROM\r\n(\r\n \r\n/* SALDO AWAL */ \r\nSELECT\r\n  0 AS URUT, \r\n \'SALDO\' as DOCUMENT_CLASS_CODE,\r\n  \'\' as DOCUMENT_NO,\r\n  null as DOCUMENT_DATE,\r\n  c.PERSON_NAME,\r\n  c.PERSON_CODE,\r\n  a.SALDO_HUTANG AS SALDO, \r\n  0 AS KREDIT,\r\n  0 AS DEBET,\r\n  a.PERSON_ID,\r\n  \'SALDO AWAL\' as NOTE\r\n FROM \r\n  PERSON_balance a\r\n INNER JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\nWHERE   LEFT(c.TIPE,1) = 1  \r\n\r\n AND FALSE\r\n\r\nUNION ALL\r\n/* PEMBELIAN */ \r\nSELECT\r\n  1 AS URUT, \r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  c.PERSON_NAME,\r\n  c.PERSON_CODE,\r\n  0 AS SALDO,\r\n  a.TOTAL_NET as KREDIT,\r\n  0 as DEBET,\r\n  /*IF(a.TOTAL_NET<>0,a.TOTAL_NET,(SELECT SUM(SUBTOTAL) FROM INVOICE_DETAIL b,ITEM i WHERE a.INVOICE_ID=b.INVOICE_ID AND i.ITEM_ID=b.ITEM_ID AND i.JENIS_ID<>@UM)) AS    KREDIT,\r\n  IF(a.TOTAL_NET<>0,0,