	(1300, 1238, 0, 'SELECT * FROM (\r\n\r\n\r\nSELECT\r\n	1 AS URUT,\r\n    NULL as document_no,\r\n    NULL as DOCUMENT_REFF_NO,\r\n    p.START_DATE DOCUMENT_DATE,\r\n    (C.COA_SALDO + C.COA_DEBET - C.COA_CREDIT)*-1 TOTAL_NET,\r\n    \'\' as NO_SJ,\r\n    0 INVOICE_ID,\r\n	NULL NO_NOTA,	\r\n	NULL REFF_NOTA,\r\n    NULL TGL_NOTA,\r\n    0 NET_NOTA,\r\n    0 as UM,\r\n	\'SALDO AWAL\' PERSON_NAME\r\n\r\nFROM \r\nCOA_BALANCE c \r\nINNER JOIN PERIOD p ON c.PERIOD_NAME=p.PERIOD_NAME\r\nWHERE COA_ID=@UMUKA_JUAL AND c.PERIOD_NAME=(\r\nSELECT \r\nIF(:PERIOD_NAME1=\'\',FALSE,PERIOD_NAME)PERIOD_NAME\r\nFROM PERIOD\r\nWHERE PERIOD_NAME<:PERIOD_NAME1\r\nORDER BY PERIOD_NAME DESC\r\nLIMIT 1\r\n)\r\nAND ABS(C.COA_SALDO + C.COA_DEBET - C.COA_CREDIT)>0\r\n\r\nUNION ALL\r\n\r\nSELECT \r\n	1 AS URUT,\r\n	xx.DOCUMENT_NO,\r\n	xx.DOCUMENT_REFF_NO,\r\n	xx.DOCUMENT_DATE,\r\n	xx.TOTAL_NET,\r\n	xx.NO_SJ,\r\n	xx.INVOICE_ID,	\r\n	a.DOCUMENT_NO NO_NOTA,	\r\n	a.DOCUMENT_REFF_NO REFF_NOTA,\r\n    a.DOCUMENT_DATE TGL_NOTA,\r\n    a.TOTAL_NET NET_NOTA,\r\n    (b.SUBTOTAL) as UM,\r\n	\'SALDO AWAL\' PERSON_NAME\r\nFROM\r\n(SELECT\r\n    ifnull(a1.DOCUMENT_NO,a.document_no) as document_no,\r\n    ifnull(a1.DOCUMENT_REFF_NO,a.DOCUMENT_REFF_NO) as DOCUMENT_REFF_NO,\r\n    a.DOCUMENT_DATE,\r\n    a.TOTAL_AMOUNT as TOTAL_NET,\r\n    a2.DOCUMENT_NO as NO_SJ,\r\n    a.INVOICE_ID\r\nFROM \r\n  INVOICE a\r\n\r\nLEFT JOIN PO a1 ON a1.PO_ID=a.PO_ID\r\nLEFT JOIN INVENTORY_IN a2 ON (a2.INVENTORY_IN_ID=a.INVENTORY_IN_ID)\r\nLEFT JOIN \r\n   (SELECT b.INVOICE_PRE_ID,\r\n                   sum(b.SUBTOTAL) as UM_APPLY\r\n     FROM\r\n         INVOICE_DETAIL b \r\n     INNER  JOIN INVOICE c ON (c.INVOICE_ID=b.INVOICE_ID)\r\n     WHERE \r\n          TRUE and (DATE(c.DOCUMENT_DATE) <:13 )\r\n			AND PERIOD_NAME=:PERIOD_NAME1\r\n    GROUP BY b.INVOICE_PRE_ID\r\n   ) as X ON  (x.INVOICE_PRE_ID=a.INVOICE_ID)\r\n\r\nWHERE \r\n   a.INVOICE_TYPE_ID=@PREPAYMENT \r\n   AND a.STATUS_ID=@FULL_PAID\r\n  /*AND ((a.TOTAL_NET + UMUKA_OUT) > 0)*/\r\n  AND (  (x.UM_APPLY IS NOT NULL  AND (a.TOTAL_AMOUNT + x.UM_APPLY) > 0 ) or x.UM_APPLY is NULL   ) \r\n  AND a.TOTAL_NET<>0\r\n  AND a.PERIOD_NAME=:PERIOD_NAME1\r\n   and a.DOCUMENT_DATE < :13\r\n)AS XX\r\nLEFT JOIN INVOICE_DETAIL b ON b.INVOICE_PRE_ID =xx.INVOICE_ID\r\nLEFT JOIN INVOICE a ON a.INVOICE_ID=b.INVOICE_ID\r\n\r\nUNION ALL \r\n\r\nSELECT \r\n    2 as URUT,\r\n	xx.DOCUMENT_NO,\r\n	xx.DOCUMENT_REFF_NO,\r\n	xx.DOCUMENT_DATE,\r\n	xx.TOTAL_NET,\r\n	xx.NO_SJ,\r\n	xx.INVOICE_ID,	\r\n	a.DOCUMENT_NO NO_NOTA,	\r\n	a.DOCUMENT_REFF_NO REFF_NOTA,\r\n    a.DOCUMENT_DATE TGL_NOTA,\r\n    a.TOTAL_NET NET_NOTA,\r\n    (b.SUBTOTAL) as UM,\r\n	xx.PERSON_NAME\r\nFROM\r\n(SELECT\r\n    ifnull(a1.DOCUMENT_NO,a.document_no) as document_no,\r\n    ifnull(a1.DOCUMENT_REFF_NO,a.DOCUMENT_REFF_NO) as DOCUMENT_REFF_NO,\r\n    a.DOCUMENT_DATE,\r\n    a.TOTAL_AMOUNT TOTAL_NET,\r\n    a2.DOCUMENT_NO as NO_SJ,\r\n    a.INVOICE_ID,\r\n    P.PERSON_NAME\r\nFROM \r\n   PERSON P\r\n   LEFT JOIN PERSON_SITE S ON P.PERSON_ID=S.PERSON_ID\r\n   inner join INVOICE a on a.PERSON_ID=p.PERSON_ID\r\nLEFT JOIN PO a1 ON a1.PO_ID=a.PO_ID\r\nLEFT JOIN INVENTORY_IN a2 ON (a2.INVENTORY_IN_ID=a.INVENTORY_IN_ID)\r\n   LEFT JOIN \r\n   (SELECT b.INVOICE_PRE_ID,\r\n                   sum(b.SUBTOTAL) as UM_APPLY\r\n     FROM\r\n         INVOICE_DETAIL b \r\n     INNER  JOIN INVOICE c ON (c.INVOICE_ID=b.INVOICE_ID)\r\n     WHERE \r\n          TRUE and (DATE(c.DOCUMENT_DATE) between :13 and :23) \r\n    GROUP BY b.INVOICE_PRE_ID\r\n   ) as X ON  (x.INVOICE_PRE_ID=a.INVOICE_ID )\r\nWHERE \r\n   a.INVOICE_TYPE_ID=@PREPAYMENT \r\n   AND a.STATUS_ID=@FULL_PAID\r\n  AND (  (x.UM_APPLY IS NOT NULL  AND (a.TOTAL_AMOUNT + x.UM_APPLY) > 0 ) or x.UM_APPLY is NULL   ) \r\n   AND a.TOTAL_NET<>0\r\n   and  LEFT(TIPE,1) = 1 \r\n&KONDISI)AS XX\r\nLEFT JOIN INVOICE_DETAIL b ON b.INVOICE_PRE_ID =xx.INVOICE_ID\r\nLEFT JOIN INVOICE a ON a.INVOICE_ID=b.INVOICE_ID\r\n\r\n) AS YY\r\n\r\nORDER BY PERSON_NAME, DOCUMENT_DATE   	  \r\n	  ', NULL, '', NULL),
	(1303, 1239, 0, '\r\nSELECT * FROM (\r\n\r\nSELECT\r\n	1 AS URUT,\r\n    NULL as document_no,\r\n    NULL as DOCUMENT_REFF_NO,\r\n    p.START_DATE DOCUMENT_DATE,\r\n    (C.COA_SALDO + C.COA_DEBET - C.COA_CREDIT)*-1 TOTAL_NET,\r\n    \'\' as NO_SJ,\r\n    0 INVOICE_ID,\r\n	NULL NO_NOTA,	\r\n	NULL REFF_NOTA,\r\n    NULL TGL_NOTA,\r\n    0 NET_NOTA,\r\n    0 as UM,\r\n	\'SALDO AWAL\' PERSON_NAME\r\n\r\nFROM \r\nCOA_BALANCE c \r\nINNER JOIN PERIOD p ON c.PERIOD_NAME=p.PERIOD_NAME\r\nWHERE COA_ID=@UMUKA_JUAL AND c.PERIOD_NAME=(\r\nSELECT \r\nIF(:PERIOD_NAME1=\'\',FALSE,PERIOD_NAME)PERIOD_NAME\r\nFROM PERIOD\r\nWHERE PERIOD_NAME<:PERIOD_NAME1\r\nORDER BY PERIOD_NAME DESC\r\nLIMIT 1\r\n)\r\nAND ABS(C.COA_SALDO + C.COA_DEBET - C.COA_CREDIT)>0\r\n\r\nUNION ALL\r\n\r\nSELECT \r\n	1 AS URUT,\r\n	xx.DOCUMENT_NO,\r\n	xx.DOCUMENT_REFF_NO,\r\n	xx.DOCUMENT_DATE,\r\n	xx.TOTAL_NET,\r\n	xx.NO_SJ,\r\n	xx.INVOICE_ID,	\r\n	a.DOCUMENT_NO NO_NOTA,	\r\n	a.DOCUMENT_REFF_NO REFF_NOTA,\r\n    a.DOCUMENT_DATE TGL_NOTA,\r\n    a.TOTAL_NET NET_NOTA,\r\n    (b.SUBTOTAL) as UM,\r\n	\'SALDO AWAL\' PERSON_NAME\r\nFROM\r\n(SELECT\r\n    ifnull(a1.DOCUMENT_NO,a.document_no) as document_no,\r\n    ifnull(a1.DOCUMENT_REFF_NO,a.DOCUMENT_REFF_NO) as DOCUMENT_REFF_NO,\r\n    a.DOCUMENT_DATE,\r\n    a.TOTAL_AMOUNT as TOTAL_NET,\r\n    a2.DOCUMENT_NO as NO_SJ,\r\n    a.INVOICE_ID\r\nFROM \r\n  INVOICE a\r\n\r\nLEFT JOIN PO a1 ON a1.PO_ID=a.PO_ID\r\nLEFT JOIN INVENTORY_IN a2 ON (a2.INVENTORY_IN_ID=a.INVENTORY_IN_ID)\r\nLEFT JOIN \r\n   (SELECT b.INVOICE_PRE_ID,\r\n                   sum(b.SUBTOTAL) as UM_APPLY\r\n     FROM\r\n         INVOICE_DETAIL b \r\n     INNER  JOIN INVOICE c ON (c.INVOICE_ID=b.INVOICE_ID)\r\n     WHERE \r\n          TRUE and (DATE(c.DOCUMENT_DATE) <:13 )\r\n			AND PERIOD_NAME=:PERIOD_NAME1\r\n    GROUP BY b.INVOICE_PRE_ID\r\n   ) as X ON  (x.INVOICE_PRE_ID=a.INVOICE_ID)\r\n\r\nWHERE \r\n   a.INVOICE_TYPE_ID=@PREPAYMENT \r\n   AND a.STATUS_ID=@FULL_PAID\r\n  /*AND ((a.TOTAL_NET + UMUKA_