	(1367, 1155, 3, 'SELECT\r\n   SUM(SALDO) AS SALDO,\r\n   SUM(SALDO_RP) AS SALDO_RP,\r\n   SUM(MASUK) +      SUM(ADJ)  AS MASUK,\r\n   SUM(MASUK_RP +  ADJ_RP * HPP_FLAG)   AS MASUK_RP ,\r\n   SUM(KELUAR) AS KELUAR,\r\n   SUM(KELUAR_RP) *-1  AS KELUAR_RP,\r\n   SUM(SALDO_RP + MASUK_RP - KELUAR_RP +   ADJ_RP ) AS AKHIR_RP\r\n FROM \r\n     ( SELECT\r\n  NOL as URUT,\r\n  0 as TIPE,\r\n  A.PERIOD_NAME,\r\n  NOL AS HPP_FLAG,  \r\n  \'SALDO\' AS DOCUMENT_CLASS_CODE,\r\n  \'\' AS DOCUMENT_NO,\r\n  \'2000/01/01\' as DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID ,\r\n  a.ITEM_ID,\r\n  \'\' as DTL_ID, \r\n  a.QTY_AWAL AS SALDO,\r\n  a.QTY_AWAL * a.HPP AS SALDO_RP,\r\n  NOL AS MASUK,\r\n  NOL AS MASUK_RP,\r\n  NOL AS KELUAR,\r\n  NOL AS KELUAR_RP,  \r\n  NOL as BAHAN,  \r\n  NOL AS BAHAN_RP,  \r\n  NOL  AS JADI ,  \r\n  NOL AS JADI_RP,\r\n  NOL  AS ADJ ,  \r\n  NOL AS ADJ_RP,\r\n  a.HPP as HARGA, \r\n  a.QTY_AWAL * a.HPP AS SUBTOTAL\r\nFROM\r\n    item_balance a    \r\n    INNER JOIN ITEM i ON (a.ITEM_ID = i.ITEM_ID),\r\n   NOL\r\nWHERE (i.JENIS_ID = @GOODS)\r\n/*ini tidak ada*/\r\n and (A.period_name = :11)\r\n\r\nUNION ALL\r\n/* TRANSAKSI BELI */\r\n/*     INVENTORY_IN */\r\nSELECT\r\n  1 as URUT,\r\n  1 as TIPE,\r\n  A.PERIOD_NAME,\r\n  1  AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.INVENTORY_IN_DETAIL_ID as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n  b.ENTERED_QTY * b.BASE_QTY AS MASUK,\r\n  b.Subtotal -discount_price1   AS MASUK_RP,\r\n  0 AS KELUAR,\r\n  0 AS KELUAR_RP, \r\n  0 as BAHAN,  \r\n  0 AS BAHAN_RP,  \r\n  0  AS JADI ,  \r\n  0 AS JADI_RP,\r\n  0  AS ADJ ,  \r\n  0 AS ADJ_RP,\r\n\r\n  b.UNIT_PRICE - (b.discount_price1/b.ENTERED_QTY )    as HARGA,\r\n  b.Subtotal-b.discount_price1  AS SUBTOTAL\r\n\r\nFROM\r\n   INVENTORY_IN a\r\n  INNER JOIN INVENTORY_IN_DETAIL b ON (a.INVENTORY_IN_ID = b.INVENTORY_IN_ID )  \r\n    INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\n  \r\n  \r\nWHERE (i.JENIS_ID = @GOODS)\r\n\r\n and (A.period_name = :11)\r\nUNION ALL\r\n/*     INVOICE tanpa matching barang datang    */\r\nSELECT\r\n  1 as URUT,\r\n  3 as TIPE,\r\n  A.PERIOD_NAME,\r\n  1  AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.INVOICE_DETAIL_ID as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n  b.ENTERED_QTY * b.BASE_QTY  AS MASUK,\r\n  b.Subtotal -discount_price1   AS MASUK_RP,\r\n  0 AS KELUAR,\r\n  0 AS KELUAR_RP,  \r\n  0 as BAHAN,  \r\n  0 AS BAHAN_RP,  \r\n  0  AS JADI ,  \r\n  0 AS JADI_RP,\r\n0  AS ADJ ,  \r\n  0 AS ADJ_RP,\r\n\r\n\r\n  b.UNIT_PRICE - (b.discount_price1/b.ENTERED_QTY ) as HARGA,\r\n  b.Subtotal -discount_price1 AS SUBTOTAL\r\nFROM\r\n   INVOICE a\r\n   INNER JOIN INVOICE_DETAIL b ON (a.INVOICE_ID = b.INVOICE_ID )\r\n   INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE (i.JENIS_ID = @GOODS) AND  \r\n  (b.INVENTORY_IN_ID IS NULL)\r\n\r\n and (A.period_name = :11)\r\nUNION ALL\r\n/* TRANSAKSI JUAL */\r\n/* NOTA_PENJUALAN            */\r\nSELECT\r\n  4 as URUT,\r\n  4 as TIPE,\r\n  A.PERIOD_NAME,\r\n  0  AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.NOTA_PENJUALAN_DETAIL_ID as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n  0 AS MASUK,\r\n0  AS MASUK_RP,\r\n  b.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\n  b.ENTERED_QTY * b.HPP  AS KELUAR_RP,  \r\n  0 as BAHAN,  0 AS BAHAN_RP,  0  AS JADI ,  0 AS JADI_RP,\r\n0  AS ADJ ,  \r\n  0 AS ADJ_RP,\r\n b.HPP    as HARGA,\r\n  b.ENTERED_QTY *  b.HPP    AS SUBTOTAL\r\nFROM\r\n   NOTA_PENJUALAN a\r\n  INNER JOIN NOTA_PENJUALAN_DETAIL b ON (a.NOTA_PENJUALAN_ID = b.NOTA_PENJUALAN_ID )\r\n    INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE \r\n\r\n(b.inventory_out_detail_id is null) and\r\n(i.JENIS_ID = @GOODS) \r\n\r\n and (A.period_name = :11)\r\n\r\nUNION ALL\r\n/* S.JALAN */\r\n/* INVENTORY OUT            */\r\nSELECT\r\n  4 as URUT,\r\n  4 as TIPE,\r\n  A.PERIOD_NAME,\r\n  0  AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.INVENTORY_OUT_DETAIL_ID as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n  0 AS MASUK,\r\n0  AS MASUK_RP,\r\n  b.ENTERED_QTY * b.BASE_QTY AS KELUAR,\r\n  b.ENTERED_QTY * b.HPP  AS KELUAR_RP,  \r\n  0 as BAHAN,  0 AS BAHAN_RP,  0  AS JADI ,  0 AS JADI_RP,\r\n0  AS ADJ ,  \r\n  0 AS ADJ_RP,\r\n b.HPP    as HARGA,\r\n  b.ENTERED_QTY *  b.HPP    AS SUBTOTAL\r\nFROM\r\n   INVENTORY_OUT  a\r\n  INNER JOIN INVENTORY_OUT_DETAIL b ON (a.INVENTORY_OUT_ID = b.INVENTORY_OUT_ID)\r\n    INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE \r\n(i.JENIS_ID = @GOODS) \r\n\r\n and (A.period_name = :11)\r\n\r\nUNION ALL\r\n\r\n/* TRANSAKSI ADJUSTMENT QTY */\r\n/* ADJ_QTY           */\r\nSELECT\r\n  2 as URUT,\r\n  5 as TIPE,\r\n  A.PERIOD_NAME,\r\n  IF(a.COA_ID = @HPP, 0,1)  AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.adjust_qty_detail_ID  as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n0  AS MASUK,\r\n0 AS MASUK_RP,\r\n  0 AS KELUAR,\r\n  0 AS KELUAR_RP,  \r\n  0 as BAHAN,  0 AS BAHAN_RP,  0  AS JADI ,  0 AS JADI_RP,\r\n  b.ENTERED_QTY * b.BASE_QTY AS ADJ,\r\nb.Subtotal AS ADJ_RP,\r\n  b.UNIT_PRICE as HARGA,\r\n  b.Subtotal AS SUBTOTAL\r\nFROM\r\n  adjust_qty a\r\n  INNER JOIN adjust_qty_detail b ON (a.ADJUST_QTY_ID = b.ADJUST_QTY_ID)\r\n  \r\n  INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE \r\n(i.JENIS_ID = @GOODS) \r\n\r\n\r\n and (A.period_name = :11)\r\nUNION ALL\r\n\r\n/* TRANSAKSI ADJUSTMENT AMOUNT */\r\n/* ADJ_AMOUNT           */\r\nSELECT\r\n  2 as URUT,\r\n  6 as TIPE,\r\n  A.PERIOD_NAME,\r\n  IF(a.COA_ID = @HPP, 0,1)  AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  0 AS WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.adjust_AMOUNT_detail_ID  as DTL_ID, \r\n  0 AS SALDO,\r\n  0 AS SALDO_RP,\r\n  0 AS MASUK,\r\n  0 AS MASUK_RP,\r\n  0 AS KELUAR,\r\n0  AS KELUAR_RP,  \r\n  0 as BAHAN,  0 AS BAHAN_RP,  0  AS JADI ,  0 AS JADI_RP,\r\n0  AS ADJ ,  \r\n    b.ENTERED_AMOUNT AS ADJ_RP,\r\n\r\n  0 as HARGA,\r\n  b.ENTERED_AMOUNT AS ENTERED_AMOUNT\r\nFROM\r\n  adjust_AMOUNT a\r\n  INNER JOIN adjust_AMOUNT_detail b ON (a.ADJUST_AMOUNT_ID = b.ADJUST_AMOUNT_ID)\r\n  \r\n  INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE\r\n(i.JENIS_ID = @GOODS) \r\n\r\n and (A.period_name = :11)\r\nUNION ALL\r\n/* TRANSAKSI RAKIT */\r\n/* BUILD  BAHAN BAKU       */\r\nSELECT\r\n  5 as URUT,\r\n  7 as TIPE,\r\n  A.PERIOD_NAME,\r\n  0 AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  b.ITEM_ID ,\r\n  b.build_detail_ID as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n 0 AS MASUK,\r\n 0 AS MASUK_RP,\r\n  0 AS KELUAR,\r\n 0 AS KELUAR_RP,  \r\n  b.ENTERED_QTY * b.BASE_QTY AS BAHAN,  \r\n   b.SUBTOTAL  AS BAHAN_RP, \r\n  0  AS JADI ,  \r\n  0 AS JADI_RP,\r\n0  AS ADJ ,  \r\n  0 AS ADJ_RP,\r\n\r\n  b.UNIT_PRICE  AS HARGA,\r\n  b.SUBTOTAL  AS SUBTOTAL\r\nFROM\r\n  build a\r\n  INNER JOIN build_detail b ON (a.build_ID = b.build_ID)\r\n  \r\n    INNER JOIN ITEM i ON (b.ITEM_ID = i.ITEM_ID)\r\nWHERE  (i.JENIS_ID = @GOODS)\r\n\r\n and (A.period_name = :11)\r\nUNION ALL\r\n\r\n\r\n/* TRANSAKSI RAKIT */\r\n/* BUILD  ITEM YG DIBUAT       */\r\nSELECT\r\n  100 as URUT,\r\n  7 as TIPE,\r\n  A.PERIOD_NAME,\r\n  1 AS HPP_FLAG,\r\n  a.DOCUMENT_CLASS_CODE,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_DATE,\r\n  a.WAREHOUSE_ID,\r\n  a.ITEM_ID ,\r\n  a.build_ID as DTL_ID, \r\n  0 AS SALDO,\r\n 0 AS SALDO_RP,\r\n  0 AS MASUK,\r\n  0 MASUK_RP,\r\n  0 AS KELUAR,\r\n  0  AS KELUAR_RP,  \r\n  0 as BAHAN,  \r\n  0 AS BAHAN_RP,  \r\n  a.ENTERED_QTY   * a.BASE_QTY AS JADI ,\r\n  a.TOTAL_AMOUNT AS JADI_RP,\r\n   0  AS ADJ ,  \r\n  0 AS ADJ_RP,\r\n\r\n   a.TOTAL_AMOUNT / (a.ENTERED_QTY * a.BASE_QTY)  AS HARGA,\r\n  a.TOTAL_AMOUNT  AS SUBTOTAL\r\nFROM\r\n  build a\r\n    INNER JOIN ITEM i ON (a.ITEM_ID = i.ITEM_ID)\r\n  \r\nWHERE (i.JENIS_ID = @GOODS)\r\n\r\n and (A.period_name = :11))\r\nAS XX\r\nWHERE\r\nTRUE\r\n&KONDISI', NULL, '', NULL),
