	(1371, 1150, 0, ' SELECT\r\n   C.ACCOUNT_CODE,\r\n   C.ACCOUNT_NAME,\r\n   SUM(TOTAL) AS TOTAL,\r\n   SUM(TOTAL_YEAR) AS TOTAL_YEAR\r\n  FROM\r\n   ACCOUNT C\r\n  INNER JOIN COA O ON (C.ACCOUNT_ID = O.ACCOUNT_ID)\r\n  INNER JOIN \r\n( SELECT\r\n   COA_ID, \r\n   sum(COA_DEBET-  COA_CREDIT) *-1 TOTAL,\r\n   0 TOTAL_YEAR\r\n  FROM COA_BALANCE  \r\n  WHERE TRUE &KONDISI\r\nGROUP BY COA_ID\r\nUNION ALL\r\n SELECT\r\n   COA_ID, \r\n   0 AS TOTAL,\r\n   sum(COA_DEBET-  COA_CREDIT) *-1 TOTAL_YEAR\r\n  FROM COA_BALANCE  \r\n WHERE TRUE  AND PERIOD_NAME  <= :11 AND (LEFT(PERIOD_NAME,4) = LEFT(CONCAT(:11,\'\'),4))\r\nGROUP BY COA_ID\r\n\r\n) AS S ON (O.COA_ID = S.COA_ID)\r\nWHERE (C.ACCOUNT_TYPE_ID = @R1)\r\nGROUP BY ACCOUNT_CODE, ACCOUNT_NAME\r\nHAVING (TOTAL <> 0 ) OR (TOTAL_YEAR <> 0 )', NULL, '', NULL),
