	(1386, 1257, 0, 'SELECT ZZ.COA_ID,SUM(ZZ.SALDO) AS SALDO,SUM(ZZ.DEBET) AS DEBET,SUM(ZZ.KREDIT) AS KREDIT,ZZ.BANK_NAME FROM (\r\nSELECT \r\nxx.*,b.ACCOUNT_NAME AS BANK_NAME\r\nFROM(\r\nSELECT\r\nCOA_ID,\r\nSUM(SALDO + DEBET - KREDIT) AS SALDO,\r\n0 DEBET,\r\n0 KREDIT\r\n  FROM \r\n( /* \r\nX1 = COA  UTK SALDO AWAL \r\nX2 = COA  UTK TRANSAKSI \r\nP = PERIODE\r\nT = TANGGAL\r\nT2= TGL_TOLAK\r\nP2= PERIODE TOLAK\r\n\r\n*/\r\n/* SALDO AWAL*/\r\nSELECT \r\n  0 AS URUT, \r\n  \'SALDO AWAL\' as TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  a.COA_SALDO as SALDO,\r\n   0 AS DEBET,\r\n  0 as KREDIT,\r\n  null as PERSON_ID,\r\n  null as PERSON_CODE,\r\n  null as PERSON_NAME,\r\n  null as DOCUMENT_NO,\r\n  null as DOCUMENT_REFF_NO,\r\n  null as DOCUMENT_DATE,\r\n  \'SALDO AWAL\' as NOTE,\r\n  a.PERIOD_NAME,\r\n  \'\' as check_no\r\nFROM\r\nCOA b\r\nINNER JOIN COA_BALANCE a on (a.COA_ID=b.COA_ID)\r\nINNER JOIN ACCOUNT c on (c.ACCOUNT_ID=b.ACCOUNT_ID)\r\nWHERE c.ACCOUNT_TYPE_ID IN (@ACC_KASBANK,@ACC_GIRO)\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1)\r\n\r\nUNION ALL\r\n/* BKM*/\r\nSELECT \r\n  1 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 as SALDO,\r\n  a.TOTAL_AMOUNT as DEBET,\r\n  0 as KREDIT,\r\n  P.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS  NOTE,\r\n  a.PERIOD_NAME,\r\n  a.check_no\r\nFROM\r\nRECEIPT a\r\nINNER JOIN COA b on (a.BANK_ID=b.COA_ID)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE a.Status_ID<>@DELETE and a.TOTAL_AMOUNT<>0\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1)\r\n AND (a.DOCUMENT_DATE < :12 )\r\n\r\nUNION ALL\r\n/* PENJUALAN TUNAI pembayaran I */\r\nSELECT \r\n  1 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 as SALDO,\r\n  a.NET_BYR1  as DEBET,\r\n  0 as KREDIT,\r\n  P.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS NOTE,\r\n  a.PERIOD_NAME,\r\n  \'\' as check_no\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER JOIN COA b on (a.COA_BYR1 =b.COA_ID)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE \r\n (  a.NET_BYR1  > 0 )\r\nand (a.TOTAL_NET > 0) \r\nAND ( a.Status_ID<>@DELETE )\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1)\r\n AND (a.DOCUMENT_DATE < :12 )\r\n\r\nUNION ALL\r\n/* PENJUALAN TUNAI pembayaran II */\r\nSELECT \r\n  1 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n  0 as SALDO,\r\n  a.TOTAL_BYR2  as DEBET,\r\n  0 as KREDIT,\r\n a.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS  NOTE,\r\n  a.PERIOD_NAME,\r\n  \'\' as check_no\r\nFROM\r\nNOTA_PENJUALAN a\r\nINNER JOIN COA b on (a.COA_BYR2 =b.COA_ID)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE \r\n  ( a.TOTAL_BYR2   > 0 )\r\nand (a.TOTAL_NET > 0) \r\nAND ( a.Status_ID<>@DELETE )\r\nAND (a.TOTAL_AMOUNT<>0 )\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1)\r\n AND (a.DOCUMENT_DATE < :12 )\r\n\r\nUNION ALL\r\n/* BKK*/\r\nSELECT \r\n  5 AS URUT, \r\n  DOCUMENT_CLASS_CODE AS TIPE,\r\n  b.COA_ID,\r\n  b.COA_CODE,\r\n  b.COA_NAME,\r\n 0 as SALDO,\r\n  0 as DEBET,\r\n  a.TOTAL_AMOUNT as KREDIT,\r\n  P.PERSON_ID,\r\n  P.PERSON_CODE,\r\n  P.PERSON_NAME,\r\n  a.DOCUMENT_NO,\r\n  a.DOCUMENT_REFF_NO,\r\n  a.DOCUMENT_DATE,\r\n  CONCAT(  IFNULL(PERSON_NAME,\'\'),\' - \', IFNULL(a.NOTE,\'\')) AS  NOTE,\r\n  a.PERIOD_NAME,\r\n  a.check_no\r\nFROM\r\nPAYMENT a\r\nINNER JOIN COA b on (a.BANK_ID=b.COA_ID)\r\nLEFT JOIN PERSON P ON (a.PERSON_ID=P.PERSON_ID)\r\nWHERE a.Status_ID<>@DELETE and a.TOTAL_AMOUNT<>0\r\n AND (a.PERIOD_NAME = :PERIOD_NAME1)\r\n AND (a.DOCUMENT_DATE < :12 )\r\n\r\