	(1416, 1261, 0, '  SELECT\r\nSUM(LABA) AS LABA,\r\nSUM(LABA_LALU) AS LABA_LALU\r\nFROM\r\n(\r\n   SELECT  sum(COA_CREDIT- COA_DEBET)   as LABA,\r\n  0 AS LABA_LALU\r\n   FROM\r\n     ACCOUNT v\r\n     INNER JOIN COA c ON (v.ACCOUNT_ID = c.ACCOUNT_ID) \r\n     INNER JOIN COA_BALANCE a ON (c.COA_ID = a.COA_ID) \r\n   WHERE  ACCOUNT_TYPE_ID IN ( @R1,@R2,@R4,@R7,@R8)\r\nAND (PERIOD_NAME = :PERIOD_NAME1)\r\n&KONDISI\r\nUNION ALL\r\n   SELECT  \r\n0 AS TOTAL  ,\r\nsum(COA_CREDIT- COA_DEBET)   as LABA_LALU\r\n   FROM\r\n     ACCOUNT v\r\n     INNER JOIN COA c ON (v.ACCOUNT_ID = c.ACCOUNT_ID) \r\n     INNER JOIN   COA_BALANCE a ON (c.COA_ID = a.COA_ID) \r\n   WHERE  ACCOUNT_TYPE_ID IN ( @R1,@R2,@R4,@R7,@R8)\r\n\r\nAND (PERIOD_NAME = :PERIOD_LALU)\r\n&KONDISI\r\n) AS XX \r\n', NULL, '', NULL),
