	(1424, 1262, 3, 'SELECT  P.NOTE, P2.DOCUMENT_NO,P2.DOCUMENT_DATE, C.COA_CODE,C.COA_NAME, P.AMOUNT  AS AMOUNT,  P2.PERIOD_NAME\r\nFROM RECEIPT P2  \r\nINNER JOIN RECEIPT_DETAIL P ON (P2.RECEIPT_ID=P.RECEIPT_ID)\r\nINNER  JOIN COA C ON (C.COA_ID=P.COA_ID)\r\nINNER JOIN ACCOUNT V ON (C.ACCOUNT_ID = V.ACCOUNT_ID)\r\nLEFT JOIN CHEQUE Q ON P2.CHEQUE_ID = Q.CHEQUE_ID\r\nWHERE \r\nIF (P2.CHEQUE_ID IS NOT NULL,q.PERIOD_NAME=:11,p2.PERIOD_NAME=:11) AND\r\n(P.AMOUNT > 0) AND \r\nV.ACCOUNT_TYPE_ID NOT IN (@ACC_GIRO)\r\n&KONDISI\r\n\r\nUNION ALL\r\n \r\nSELECT  P.NOTE, P2.DOCUMENT_NO,P2.DOCUMENT_DATE,C.COA_CODE, C.COA_NAME, P.AMOUNT *-1 ,P2.PERIOD_NAME\r\nFROM PAYMENT P2\r\nINNER JOIN PAYMENT_DETAIL P  ON (P2.PAYMENT_ID=P.PAYMENT_ID)\r\nINNER  JOIN COA C ON (C.COA_ID=P.COA_ID)\r\nINNER JOIN ACCOUNT V ON (C.ACCOUNT_ID = V.ACCOUNT_ID)\r\nLEFT JOIN CHEQUE Q ON P2.CHEQUE_ID = Q.CHEQUE_ID\r\nWHERE \r\nIF (P2.CHEQUE_ID IS NOT NULL,q.PERIOD_NAME=:11,p2.PERIOD_NAME=:11) AND\r\n(P.AMOUNT < 0) AND \r\nISNULL(P.COA_ID)=FALSE AND P2.PERIOD_NAME=:11\r\nAND \r\nV.ACCOUNT_TYPE_ID NOT IN (@ACC_GIRO)\r\n&KONDISI\r\n\r\nUNION ALL\r\n\r\nselect j.note, j.document_no, j.document_date, c.coa_code, c.coa_name, jd.amount, j.period_name\r\nfrom \r\n Journal j\r\n	inner join journal_detail jd on (j.journal_id = jd.journal_id)\r\n	inner join coa c on (jd.coa_id = c.coa_id)\r\n	inner join account ac on (c.coa_code = ac.account_code)\r\n\r\n               where \r\n j.period_name = :11\r\n		\r\nand  ac.account_type_id = @acc_kasbank\r\nand jd.amount <> 0\r\n\r\n\r\n ORDER BY DOCUMENT_DATE  , DOCUMENT_NO', NULL, '', NULL),
	(1425, 1262, 6, 'SELECT \r\nP.NOTE, P2.DOCUMENT_NO,P2.DOCUMENT_DATE, \r\nC.COA_CODE,C.COA_NAME, P.AMOUNT as AMOUNT,P2.PERIOD_NAME\r\nFROM \r\nPAYMENT P2\r\nINNER JOIN PAYMENT_DETAIL P  ON (P2.PAYMENT_ID=P.PAYMENT_ID)\r\nINNER JOIN COA C ON (C.COA_ID=P.COA_ID)\r\nINNER JOIN ACCOUNT V ON (C.ACCOUNT_ID = V.ACCOUNT_ID)\r\nLEFT JOIN CHEQUE Q ON P2.CHEQUE_ID = Q.CHEQUE_ID\r\nWHERE \r\nIF (P2.CHEQUE_ID IS NOT NULL,q.PERIOD_NAME=:11,p2.PERIOD_NAME=:11) AND\r\n(P.AMOUNT > 0) AND \r\nISNULL(P.COA_ID)=FALSE AND \r\nV.ACCOUNT_TYPE_ID NOT IN ( @ACC_GIRO)\r\n&KONDISI\r\n\r\nUNION ALL\r\n\r\nSELECT  \r\nP.NOTE, P2.DOCUMENT_NO,P2.DOCUMENT_DATE,\r\nC.COA_CODE,C.COA_NAME, P.AMOUNT * -1,P2.PERIOD_NAME\r\nFROM \r\nRECEIPT P2  \r\nINNER JOIN RECEIPT_DETAIL P ON (P2.RECEIPT_ID=P.RECEIPT_ID)\r\nINNER JOIN COA C ON (C.COA_ID=P.COA_ID)\r\nINNER JOIN ACCOUNT V ON (C.ACCOUNT_ID = V.ACCOUNT_ID)\r\nLEFT JOIN CHEQUE Q ON P2.CHEQUE_ID = Q.CHEQUE_ID\r\nWHERE \r\nIF (P2.CHEQUE_ID IS NOT NULL,q.PERIOD_NAME=:11,p2.PERIOD_NAME=:11) AND\r\n(P.AMOUNT < 0) AND \r\nV.ACCOUNT_TYPE_ID NOT IN (@ACC_GIRO)\r\n&KONDISI\r\n \r\nUNION ALL\r\n\r\nselect j.note, j.document_no, j.document_date, c.coa_code, c.coa_name, jd.amount_cr, j.period_name\r\n	from journal j\r\n	inner join journal_detail jd on (j.journal_id = jd.journal_id)\r\n	inner join coa c on (jd.coa_id = c.coa_id)\r\n	inner join account ac on (c.coa_code = ac.account_code)\r\n\r\n               where \r\n j.period_name = :11\r\n		\r\nand  ac.account_type_id = @acc_kasbank\r\nand jd.amount_cr <> 0\r\n\r\nORDER BY DOCUMENT_DATE  , DOCUMENT_NO', NULL, '', NULL),
	(1426, 1262, 7, 'SELECT SUM(P2.COA_SALDO) AS SALDO_AWAL,P2.PERIOD_NAME,\r\nSUM(P2.COA_SALDO+P2.COA_DEBET-P2.COA_CREDIT) AS SALDO_AKHIR\r\nFROM COA_BALANCE P2\r\nLEFT JOIN COA C ON (C.COA_ID=P2.COA_ID)\r\nLEFT JOIN ACCOUNT A ON (A.ACCOUNT_ID=C.ACCOUNT_ID)\r\nWHERE A.ACCOUNT_TYPE_ID in (@ACC_KASBANK) AND \r\nP2.PERIOD_NAME=:11\r\n&KONDISI\r\nGROUP BY P2.PERIOD_NAME', NULL, '', NULL),
	(1427, 1262, 8, 'SELECT \r\n MONTHNAME(Q.DOCUMENT_NO) AS BULAN, \r\n q.DOCUMENT_NO as CHECK_NO,  a.DOCUMENT_NO,\r\n q.CREATED_date as DOCUMENT_DATE,   \r\nq.document_date AS TGL_CAIR,   \r\nq.Jtempo,\r\n  a.NAMA_BANK,\r\nq.TOTAL_AMOUNT - IF( (MONTH(q.DOCUMENT_DATE) = right(:PERIOD_NAME1,2))  AND (q.STATUS_ID in (@CLEAR,@TOLAK)) , q.TOTAL_AMOUNT, 0)  \r\n - IF( (MONTH(q.TGL_TOLAK) = right(:PERIOD_NAME1,2)) AND (q.STATUS_ID = @TOLAK) , q.TOTAL_AMOUNT, 0)  + \r\n+  IFNULL(GANTI_TOLAK.KREDIT,0)\r\nTOTAL_NILAI,   \r\n  k.COA_NAME ,\r\n  c.PERSON_NAME,\r\n  X.DISPLAY_NAME AS STATUS\r\nFROM\r\n  CHEQUE Q \r\n  LEFT JOIN RECEIPT a ON (q.CHEQUE_ID = a.CHEQUE_ID)\r\n  INNER JOIN ERP_LOOKUP_VALUE X on (Q.STATUS_ID = X.ERP_LOOKUP_VALUE_ID)  \r\n  LEFT JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n    LEFT JOIN coa k ON (q.BANK_ID_DEST = k.COA_ID)\r\n  LEFT JOIN \r\n(\r\nSELECT  Y.CHEQUE_ID , SUM(AMOUNT)  AS KREDIT FROM \r\n  RECEIPT X\r\n  INNER JOIN RECEIPT_detail Y ON (X.RECEIPT_ID = Y.RECEIPT_ID)\r\n  WHERE ((MONTH(x.DOCUMENT_DATE) = right(:PERIOD_NAME1,2)))\r\nGROUP BY Y.CHEQUE_ID \r\n) AS GANTI_TOLAK ON ( a.CHEQUE_ID = GANTI_TOLAK.CHEQUE_ID)\r\n\r\nWHERE (q.status_id <> @DELETE)\r\nAND (STATUS_FLAG = \'M\')\r\nAND MONTH(q.CREATED_DATE) = right(:PERIOD_NAME1,2)\r\nAND q.TOTAL_AMOUNT - IF( (MONTH(q.DOCUMENT_DATE) = right(:PERIOD_NAME1,2))  AND (q.STATUS_ID in (@CLEAR,@TOLAK)) , q.TOTAL_AMOUNT, 0)  \r\n - IF( (MONTH(q.TGL_TOLAK) = right(:PERIOD_NAME1,2)) AND (q.STATUS_ID = @TOLAK) , q.TOTAL_AMOUNT, 0)  + \r\n+  IFNULL(GANTI_TOLAK.KREDIT,0) <> 0\r\n\r\n&KONDISI\r\nORDER BY  Q.CREATED_DATE ', NULL, '', NULL),
	(1428, 1262, 10, 'SELECT \r\n\r\nMONTHNAME(Q.DOCUMENT_NO) AS BULAN,\r\nq.DOCUMENT_NO as CHECK_NO,  a.DOCUMENT_NO,\r\nq.CREATED_date as DOCUMENT_DATE,   \r\nq.document_date AS TGL_CAIR,   \r\nq.Jtempo,\r\na.NAMA_BANK,\r\nk.COA_NAME ,\r\nc.PERSON_NAME,\r\nX.DISPLAY_NAME AS STATUS,\r\nq.TOTAL_AMOUNT -  IF( (MONTH(q.DOCUMENT_DATE) = right(:PERIOD_NAME1,2)) AND (q.STATUS_ID in (230103,230104)) , q.TOTAL_AMOUNT, 0)  \r\n- IF( (MONTH(q.TGL_TOLAK) = right(:PERIOD_NAME1,2)) AND (q.STATUS_ID = 230104) , q.TOTAL_AMOUNT, 0)  + \r\n+ IFNULL(GANTI_TOLAK.DEBET,0) AS TOTAL_NILAI\r\n\r\nFROM\r\n\r\n  CHEQUE Q \r\n  LEFT JOIN PAYMENT a ON (Q.CHEQUE_ID = a.CHEQUE_ID)\r\n  INNER JOIN ERP_LOOKUP_VALUE X on (Q.STATUS_ID = X.ERP_LOOKUP_VALUE_ID)  \r\n  LEFT JOIN PERSON c ON (a.PERSON_ID = c.PERSON_ID)\r\n  LEFT JOIN coa k ON (q.BANK_ID_DEST = k.COA_ID)\r\n  LEFT JOIN \r\n(\r\nSELECT  Y.CHEQUE_ID , SUM(AMOUNT)  AS DEBET FROM \r\n  PAYMENT X\r\n  INNER JOIN PAYMENT_detail Y ON (X.PAYMENT_ID = Y.PAYMENT_ID)\r\n  WHERE ((MONTH(x.DOCUMENT_DATE) = right(:PERIOD_NAME1,2)))\r\nGROUP BY Y.CHEQUE_ID \r\n) AS GANTI_TOLAK ON ( a.CHEQUE_ID = GANTI_TOLAK.CHEQUE_ID)\r\n\r\nWHERE (q.status_id <> 230082)\r\n&kondisi\r\nAND (STATUS_FLAG = \'K\')\r\nAND MONTH(q.CREATED_DATE) = right(:PERIOD_NAME1,2)\r\nAND q.TOTAL_AMOUNT -  IF( (MONTH(q.DOCUMENT_DATE) = right(:PERIOD_NAME1,2)) \r\nAND (q.STATUS_ID in (230103,230104)) , q.TOTAL_AMOUNT, 0)  \r\n- IF( (MONTH(q.TGL_TOLAK) = right(:PERIOD_NAME1,2)) AND (q.STATUS_ID = 230104) , q.TOTAL_AMOUNT, 0)  + \r\n+ IFNULL(GANTI_TOLAK.DEBET,0) <> 0\r\nORDER BY Q.DOCUMENT_DATE', NULL, '', NULL),
	(1429, 1263, 0, '\r\nSELECT   \r\nA.ACCOUNT_CODE,\r\nA.ACCOUNT_NAME,\r\n0 AS TOTAL,\r\n0 AS TOTAL_LALU\r\nFROM  \r\n  account A \r\nWHERE  \r\n  A.ACTIVE_FLAG = \'Y\' AND   \r\n  LEFT(A.ACCOUNT_CODE,1) = \'1\' and LENGTH(A.ACCOUNT_CODE) = 2\r\n\r\n\r\nUNION ALL\r\n\r\nSELECT   \r\nA.ACCOUNT_CODE,\r\nCONCAT( \'  \',A.ACCOUNT_NAME) ACCOUNT_NAME,\r\n0 AS TOTAL,\r\n0 AS TOTAL_LALU\r\nFROM  \r\n  account A \r\nWHERE  \r\n  A.ACTIVE_FLAG = \'Y\' AND\r\n  LEFT(A.ACCOUNT_CODE,1) = \'1\' and LENGTH(A.ACCOUNT_CODE) = 3\r\n  /* LEFT(A.ACCOUNT_CODE,2) = \'10\' and LENGTH(A.ACCOUNT_CODE) = 3 */\r\n\r\n\r\nUNION ALL\r\n\r\nSELECT\r\nA.ACCOUNT_CODE,\r\nCONCAT( \'     -  \',A.ACCOUNT_NAME) ACCOUNT_NAME,\r\nSUM(COA_SALDO + COA_DEBET - COA_CREDIT) AS TOTAL,\r\nSUM(TOTAL_LALU) AS TOTAL_LALU  \r\nFROM\r\naccount A\r\nLEFT JOIN\r\n(\r\n\r\nSELECT LEFT(d.ACCOUNT_CODE,4) AS ACCOUNT_CODE,\r\n sum(COA_SALDO)  as COA_SALDO,\r\n sum(COA_DEBET)  as COA_DEBET,\r\n sum(COA_CREDIT)  as COA_CREDIT,\r\n 0 as TOTAL_LALU\r\nFROM\r\n COA_BALANCE a\r\n  INNER JOIN COA c ON (a.COA_ID = C.COA_ID)\r\n  INNER JOIN account d ON (c.ACCOUNT_ID = d.ACCOUNT_ID)\r\n  WHERE TRUE\r\n  &KONDISI\r\n\r\n  GROUP BY LEFT(d.ACCOUNT_CODE,4)\r\nunion all\r\nSELECT LEFT(d.ACCOUNT_CODE,4) AS ACCOUNT_CODE,\r\n 0 as COA_SALDO,\r\n 0 as COA_DEBET,\r\n 0 as COA_CREDIT,\r\nsum(COA_SALDO + COA_DEBET - COA_CREDIT) AS TOTAL\r\nFROM\r\n COA_BALANCE a\r\n  INNER JOIN COA c ON (a.COA_ID = C.COA_ID)\r\n  INNER JOIN account d ON (c.ACCOUNT_ID = d.ACCOUNT_ID)\r\n  WHERE TRUE\r\n AND PERIOD_NAME = :PERIOD_LALU\r\n  GROUP BY LEFT(d.ACCOUNT_CODE,4)\r\n  \r\n)AS B ON (A.ACCOUNT_CODE = B.ACCOUNT_CODE)\r\nWHERE  LEFT(A.ACCOUNT_CODE,1) = \'1\' and LENGTH(A.ACCOUNT_CODE) = 4\r\nGROUP BY A.ACCOUNT_CODE\r\n\r\nORDER BY ACCOUNT_CODE', NULL, '', NULL),
	(1430, 1263, 3, 'SELECT   \r\nA.ACCOUNT_CODE,\r\nA.ACCOUNT_NAME,\r\n0 AS TOTAL,\r\n0 AS TOTAL_LALU,\r\nA.ACCOUNT_ID\r\nFROM  \r\n  account A \r\nWHERE  \r\n  A.ACTIVE_FLAG = \'Y\' AND    \r\n  LEFT(A.ACCOUNT_CODE,1)  > \'1\' AND LEFT(A.ACCOUNT_CODE,1)  < \'4\'   and LENGTH(A.ACCOUNT_CODE) = 2\r\n\r\n\r\nUNION ALL\r\n\r\nSELECT   \r\nA.ACCOUNT_CODE,\r\nCONCAT( \'  \',A.ACCOUNT_NAME) ACCOUNT_NAME,\r\n0 AS TOTAL,\r\n0 AS TOTAL_LALU,\r\nA.ACCOUNT_ID\r\nFROM  \r\n  account A \r\nWHERE  \r\n  A.ACTIVE_FLAG = \'Y\' AND\r\n  LEFT(A.ACCOUNT_CODE,1)  > \'1\' AND LENGTH(A.ACCOUNT_CODE) = 3\r\n  /* LEFT(A.ACCOUNT_CODE,2) = \'10\' and LENGTH(A.ACCOUNT_CODE) = 3 */\r\n\r\n\r\nUNION ALL\r\n\r\nSELECT\r\nA.ACCOUNT_CODE,\r\nCONCAT( \'     -  \',A.ACCOUNT_NAME) ACCOUNT_NAME,\r\nSUM(COA_SALDO + COA_DEBET - COA_CREDIT) AS TOTAL,\r\nSUM(TOTAL_LALU) AS TOTAL_LALU  ,\r\nA.ACCOUNT_ID\r\nFROM\r\naccount A\r\nLEFT JOIN\r\n(\r\n\r\nSELECT LEFT(d.ACCOUNT_CODE,4) AS ACCOUNT_CODE,\r\n sum(COA_SALDO)  as COA_SALDO,\r\n sum(COA_DEBET)  as COA_DEBET,\r\n sum(COA_CREDIT)  as COA_CREDIT,\r\n 0 as TOTAL_LALU\r\nFROM\r\n COA_BALANCE a\r\n  INNER JOIN COA c ON (a.COA_ID = C.COA_ID)\r\n  INNER JOIN account d ON (c.ACCOUNT_ID = d.ACCOUNT_ID)\r\n  WHERE TRUE\r\n  &KONDISI\r\n\r\n  GROUP BY LEFT(d.ACCOUNT_CODE,4)\r\nunion all\r\nSELECT LEFT(d.ACCOUNT_CODE,4) AS ACCOUNT_CODE,\r\n 0 as COA_SALDO,\r\n 0 as COA_DEBET,\r\n 0 as COA_CREDIT,\r\nsum(COA_SALDO + COA_DEBET - COA_CREDIT) AS TOTAL\r\nFROM\r\n COA_BALANCE a\r\n  INNER JOIN COA c ON (a.COA_ID = C.COA_ID)\r\n  INNER JOIN account d ON (c.ACCOUNT_ID = d.ACCOUNT_ID)\r\n  WHERE TRUE\r\n AND PERIOD_NAME = :PERIOD_LALU\r\n  GROUP BY LEFT(d.ACCOUNT_CODE,4)\r\n  \r\n)AS B ON (A.ACCOUNT_CODE = B.ACCOUNT_CODE)\r\nWHERE  LEFT(A.ACCOUNT_CODE,1)  > \'1\' AND LENGTH(A.ACCOUNT_CODE) = 4\r\nGROUP BY A.ACCOUNT_CODE\r\n\r\nORDER BY ACCOUNT_CODE', NULL, '', NULL),
	(1431, 1263, 6, '  SELECT\r\nSUM(LABA) AS LABA,\r\nSUM(LABA_LALU) AS LABA_LALU\r\nFROM\r\n(\r\n   SELECT  sum(COA_CREDIT- COA_DEBET)   as LABA,\r\n  0 AS LABA_LALU\r\n   FROM\r\n     COA_BALANCE a \r\n     INNER JOIN v_account c ON (a.COA_ID = c.COA_ID) \r\n   WHERE  left(c.ACCOUNT_CODE,1) > \'3\'\r\n   &kondisi\r\nUNION ALL\r\n   SELECT  \r\n0 AS TOTAL  ,\r\nsum(COA_CREDIT- COA_DEBET)   as LABA_LALU\r\n   FROM\r\n     COA_BALANCE a \r\n     INNER JOIN v_account c ON (a.COA_ID = c.COA_ID) \r\n   WHERE  left(c.ACCOUNT_CODE,1) > \'3\'\r\nAND PERIOD_NAME = :PERIOD_LALU\r\n) AS XX \r\n/*  \r\n   SELECT  sum(amount - amount_cr) * -1  as total\r\n   FROM\r\n\r\n     account acc \r\n     INNER JOIN coa c ON (acc.ACCOUNT_ID = c.ACCOUNT_ID) \r\n     INNER JOIN journal_detail b ON (c.COA_ID = b.COA_ID) \r\n     INNER JOIN journal a ON (b.JOURNAL_ID = a.JOURNAL_ID) \r\n   WHERE \r\n     (acc.STATUS_FLAG = \'R\') \r\n   &kondisi\r\n*/', NULL, '', NULL),
